INSERT INTO `news` VALUES ('<p style=\"text-align:center\"><strong><span style=\"font-family:黑体;font-size:24px;font-weight:bold\">这个春天，我和成理有个约定</span></strong><span style=\"font-family:宋体;font-size:16px\">&nbsp;</span> &nbsp;</p><p style=\"text-align:center\"><strong><span style=\"font-family:宋体;font-size:21px;font-weight:bold\">第二期“双百双梦”教育扶贫活动全程回顾</span></strong> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-family:宋体;font-size:16px\">&nbsp;</span> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-family:宋体;font-size:16px\">&nbsp;</span> &nbsp;</p><p style=\"text-align:center\"><strong><span style=\"font-family:仿宋;font-size:16px;font-weight:bold\">2018“双百双梦”活动之参观图书馆</span></strong><span style=\"font-family:宋体;font-size:16px\">&nbsp;</span> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-family:宋体;font-size:16px\"><img title=\"0\" alt=\"0\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180423/20180423144936_544.jpg\"/></span> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-family:宋体;font-size:16px\"><img title=\"0 (1)\" alt=\"0 (1)\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180423/20180423144944_755.jpg\"/></span> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-family:宋体;font-size:16px\"><img title=\"0 (2)\" alt=\"0 (2)\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180423/20180423144954_406.jpg\"/></span> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-family:宋体;font-size:16px\"><img title=\"0 (3)\" alt=\"0 (3)\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180423/20180423145032_225.jpg\"/></span> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-family:宋体;font-size:16px\"><img title=\"0 (4)\" alt=\"0 (4)\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180423/20180423145057_229.jpg\"/></span> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-family:宋体;font-size:16px\"><img title=\"0 (5)\" alt=\"0 (5)\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180423/20180423145109_591.jpg\"/></span> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-family:宋体;font-size:16px\"><img title=\"0 (6)\" alt=\"0 (6)\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180423/20180423145117_271.jpg\"/></span> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-family:宋体;font-size:16px\"><img title=\"0 (7)\" alt=\"0 (7)\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180423/20180423145126_273.jpg\"/></span> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-family:宋体;font-size:16px\"><img title=\"0 (8)\" alt=\"0 (8)\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180423/20180423145133_603.jpg\"/></span> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-family:宋体;font-size:16px\"><img title=\"0 (9)\" alt=\"0 (9)\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180423/20180423145140_444.jpg\"/></span> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-family:宋体;font-size:16px\"><img title=\"0 (10)\" alt=\"0 (10)\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180423/20180423145149_261.jpg\"/></span> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-family:宋体;font-size:16px\"><img title=\"0 (11)\" alt=\"0 (11)\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180423/20180423145200_19.jpg\"/></span> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-family:宋体;font-size:16px\"><img title=\"0 (12)\" alt=\"0 (12)\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180423/20180423145211_601.jpg\"/></span> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-family:宋体;font-size:16px\"><img title=\"0 (13)\" alt=\"0 (13)\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180423/20180423145220_178.jpg\"/></span> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-family:宋体;font-size:16px\"><img title=\"0 (14)\" alt=\"0 (14)\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180423/20180423145235_577.jpg\"/></span> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-family:宋体;font-size:16px\"><img title=\"0 (15)\" alt=\"0 (15)\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180423/20180423145249_786.jpg\"/></span> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-family:宋体;font-size:16px\"><img title=\"0 (16)\" alt=\"0 (16)\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180423/20180423145256_626.jpg\"/></span> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-family:宋体;font-size:16px\"><img title=\"0 (17)\" alt=\"0 (17)\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180423/20180423145306_437.jpg\"/></span> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-family:宋体;font-size:16px\"><img title=\"0 (18)\" alt=\"0 (18)\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180423/20180423145315_116.jpg\"/></span> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-family:宋体;font-size:16px\"><img title=\"0 (19)\" alt=\"0 (19)\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180423/20180423145324_399.jpg\"/></span> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-family:宋体;font-size:16px\"><img title=\"0 (20)\" alt=\"0 (20)\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180423/20180423145333_672.jpg\"/></span> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-family:宋体;font-size:16px\"><img title=\"0 (21)\" alt=\"0 (21)\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180423/20180423145343_524.jpg\"/></span> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-family:宋体;font-size:16px\"><img title=\"0 (22)\" alt=\"0 (22)\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180423/20180423145350_81.jpg\"/></span> &nbsp;</p><p style=\"text-align:center\">&nbsp;</p><p style=\"text-align:left;text-indent:32px\"><strong><span style=\"font-family:宋体;font-size:16px;font-weight:bold\">活动背景：</span></strong><span style=\"font-family:宋体;font-size:16px\">为深入贯彻中央和省委坚决打赢、打好脱贫攻坚战的工作部署，充分发挥学校科技、人力、智力等优势，精准实施教育帮扶，自2017至2020年，学校每年邀请组织定点帮扶的高县和黑水县各100名初中优秀学生、贫困学生，来校体验大学生活，开展为期6天的 “思梦圆梦·黑水高县学子成理行”教育扶贫活动，帮助孩子筑牢“大学梦”、“中国梦”（简称“双百双梦”教育扶贫活动）。</span> &nbsp;</p><p style=\"text-align:left;text-indent:32px\">&nbsp;</p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-family:宋体;font-size:16px\">&nbsp;</span> &nbsp;</p><p style=\"text-align:left;text-indent:32px\"><br/></p><p style=\"text-align:center;text-indent:0;margin-left:0\"><span style=\"font-family:宋体;font-size:14px\">“云立方”学生传媒中心 出品</span> &nbsp;</p><p style=\"text-align:center;text-indent:0;margin-left:0\"><span style=\"font-family:宋体;font-size:14px\">统筹：新媒体工作室 杨佳欣 肖博 魏文奕</span> &nbsp;</p><p style=\"text-align:center;text-indent:0;margin-left:0\"><span style=\"font-family:宋体;font-size:14px\">视频：理工电视台 张琦 雷乐昊 靳扬 尹敏</span> &nbsp;</p><p style=\"text-align:center;text-indent:0;margin-left:0\"><span style=\"font-family:宋体;font-size:14px\">杜嘉恒 许啸 李亦涵 姜晨 马世杰</span> &nbsp;</p><p style=\"text-align:center;text-indent:0;margin-left:0\"><span style=\"font-family:宋体;font-size:14px\">图片：新闻嗅觉图片社 新媒体工作室</span> &nbsp;</p><p style=\"text-align:center;text-indent:0;margin-left:0\"><span style=\"font-family:宋体;font-size:14px\">文案：新媒体工作室 魏文奕 徐倪帆 曾怀宇</span> &nbsp;</p><p style=\"text-align:center;text-indent:0;margin-left:0\"><span style=\"font-family:宋体;font-size:14px\">制图：新媒体工作室 李栋 卢雨薇</span> &nbsp;</p><p style=\"text-align:center;text-indent:0;margin-left:0\"><span style=\"font-family:宋体;font-size:14px\">责任编辑：王潇 黄于鉴 曾灵</span> &nbsp;</p><p><br/></p>', '2018-05-24 20:33', '成都理工大学网上党支部', '这个春天，我和成理有个约定', null, '5', '159');
INSERT INTO `news` VALUES ('<p style=\"text-align:center\"><strong><span style=\"font-family:黑体;font-size:29px\">我校赴凉山州盐源县开展</span></strong> &nbsp;</p><p style=\"text-align:center\"><strong><span style=\"font-family:黑体;font-size:29px\">“三区三州”旅游规划扶贫考察</span></strong> &nbsp;</p><p style=\"text-align:center\">&nbsp;</p><p style=\"text-indent:28px\"><span style=\"font-family:宋体;font-size:14px\"><span style=\"font-size:16px\">为积极响应国家文化和旅游部关于持续开展“三区三州”深度贫困地区旅游规划扶贫公益行动的号召，旅游与城乡规划学院参加了</span><span style=\"font-size:16px\">2018</span><span style=\"font-size:16px\">年文化和旅游部组织的</span><span style=\"font-size:16px\">“</span><span style=\"font-size:16px\">三区三州</span><span style=\"font-size:16px\">”</span><span style=\"font-size:16px\">深度贫困地区旅游规划扶贫公益行动，承担了凉山州盐源县泸沽湖镇山南村乡村旅游扶贫规划编制工作。</span></span> &nbsp;</p><p style=\"text-indent:28px\"><span style=\"font-family:宋体;font-size:16px\">5</span><span style=\"font-family:宋体;font-size:14px\"><span style=\"font-size:16px\">月</span><span style=\"font-size:16px\">6</span><span style=\"font-size:16px\">日至</span><span style=\"font-size:16px\">10</span><span style=\"font-size:16px\">日，旅游与城乡规划学院院长朱创业、对外合作与交流处副处长兼扶贫办常务副主任刘晓玲一行</span><span style=\"font-size:16px\">10</span><span style=\"font-size:16px\">人前往山南村进行实地考察。规划课题组对山南村的布尔脚、密洼、陈家湾、纳洼等</span><span style=\"font-size:16px\">10</span><span style=\"font-size:16px\">个村组进行了走访、调研，详细了解山南村的资源条件和旅游扶贫需求，尤其是对各村组传统民居的保护状况、摩梭民俗文化的保护与传承状况，以及田园风貌的保护与利用状况进行了深入了解。我校规划课题组在与山南村村委会的座谈交流会上建议，山南村应在保护好泸沽湖绿水青山的前提下，充分发挥摩梭文化的独特资源优势，大力发展发展乡村旅游，将绿水青山变为老百姓脱贫致富奔康的金山银山。</span> </span> </p><p style=\"text-indent:28px\"><span style=\"font-family:宋体;font-size:16px\">本次调研考察收集了丰富的第一手数据与资料。下一步，规划课题组将按照国家文化和旅游部的要求，以乡村振兴为目标，高质量完成山南村旅游扶贫规划编制任务。</span> &nbsp;</p><p style=\"text-indent:28px\">&nbsp;</p><p style=\"text-align:center\"><strong><img title=\"111\" alt=\"111\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180514/20180514091856_588.jpg\" width=\"500\" height=\"333\"/></strong> &nbsp;</p><p style=\"text-align:center\"><strong><span style=\"font-family:仿宋;background:white;color:#333333;font-size:19px\"><span style=\"font-size:16px\">课题组在山南村实地考察</span></span></strong> &nbsp;</p><p style=\"text-align:center\"><strong><img title=\"222\" alt=\"222\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180514/20180514091919_834.jpg\" width=\"500\" height=\"333\"/></strong> &nbsp;</p><p style=\"text-align:center\"><strong><span style=\"font-family:仿宋;background:white;color:#333333;font-size:19px\"><span style=\"font-size:16px\">课题组深入贫困户进行访谈</span></span></strong> &nbsp;</p><p style=\"text-align:center\"><strong><img title=\"333\" alt=\"333\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180514/20180514091945_651.jpg\" width=\"500\" height=\"333\"/></strong> &nbsp;</p><p style=\"text-align:center\"><strong><span style=\"font-family:仿宋;background:white;color:#333333;font-size:19px\"><span style=\"font-size:16px\">课题组在山南村村委会召开座谈会</span></span></strong> &nbsp;</p><p style=\"text-align:center\"><strong><img title=\"444\" alt=\"444\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180514/20180514092014_967.jpg\" width=\"500\" height=\"333\"/></strong> &nbsp;</p><p><strong><span style=\"font-family:仿宋;background:white;color:#333333;font-size:19px\"><span style=\"font-size:16px\">课题组在山南村摩梭文化传习所考察</span></span></strong></p><p><br/></p>', '2018-05-24 20:35', '成都理工大学网上党支部', '我校赴凉山州盐源县开展    “三区三州”旅游规划扶贫考察', null, '5', '161');
INSERT INTO `news` VALUES ('<p style=\"text-align: center;\"><strong><span style=\"font-size:22pt;font-family:黑体;background:white;color:#333333;line-height:200%;\">四川省知识产权局谢商华局长一行来我校调研座谈</span></strong><strong><span style=\"font-size:22pt;font-family:黑体;line-height:200%;\"></span></strong></p><p>\r\n	<span style=\"font-size:16px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; \r\n5月3日，为落实省委办公厅《关于在全省开展“大学习、大讨论、大调研”活动的通知》精神，四川省知识产权局谢商华局长一行三人到我校调研职务发明知识产权归属和利益分享制度改革开展情况。校领导黄涛、郭朝辉，科技处、社会科学处、地质灾害防治与地质环境保护国家重点实验室、大学科技园及环境学院等相关部门和学院负责人以及部分教师代表参加了座谈。座谈会由郭朝辉副校长主持。</span></p><p>\r\n	<span style=\"font-size:16px;\">&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;谢商华局长首先介绍了此行调研目的。认真听取了学校职务发明知识产权归属和利益分享制度改革试点情况汇报和意见建议，并和与会专家学者深入探讨了改革的重点、难点和关键问题，对学校进一步深化此项改革提出了希望和要求。</span></p><p>\r\n	<span style=\"font-size:16px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; \r\n黄涛副书记简要介绍了学校改革发展情况，希望通过试点改革有效发挥职务发明知识产权归属和利益分享改革的政策效应，完善配套制度，积极探索中国特色的知识产权促进经济发展之路。郭朝辉副校长从学校的“双一流建设”、产学研协同创新以及科技服务社会等方面重点介绍了科技成果转化支撑本科评估、校地合作及科技扶贫等工作。</span></p><p>\r\n	<span style=\"font-size:16px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; \r\n会上，科技处对试点改革情况做了简要汇报，重点包括制度改革、重点转化项目和正在开展的改革措施。地质灾害防治与地质环境保护国家重点实验室介绍了在进行校企合作过程中，职务科技成果实施转化存在的制度障碍以及缺乏有力的科技服务体系。教师代表交流探讨了专利技术入股、法律服务、发明人及学校的责任和权益等具体问题。</span></p><p>\r\n	<br/></p><p>\r\n	<img style=\"margin-left:220px\" title=\"1\" alt=\"1\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180508/20180508174830_315.jpg\" width=\"500\" height=\"374\"/> </p><p>\r\n	<span style=\"font-size:16px;margin-left:250px\">座谈会现场</span></p><p><br/></p>', '2018-05-24 20:40', '成都理工大学网上党支部', '四川省知识产权局谢商华局长一行来我校调研座谈', null, '6', '162');
INSERT INTO `news` VALUES ('<p style=\"text-align:center;text-indent:59px\"><strong><span style=\"line-height:200%;font-family:黑体;font-size:29px\">三角草坪，结缘成理情与梦</span></strong> &nbsp;</p><p style=\"text-align:center;text-indent:43px\"><strong><span style=\"line-height:200%;font-family:宋体;font-size:21px\">——成都理工大学“双百双梦”教育扶贫活动侧记</span></strong> &nbsp;</p><p style=\"text-align:center;text-indent:43px\">&nbsp;</p><p style=\"text-align:center;text-indent:43px\"><strong><span style=\"line-height:200%;font-family:宋体;font-size:21px\"></span></strong>&nbsp;</p><p style=\"text-indent:32px\"><br/></p><p style=\"text-indent:32px\"><br/></p><p style=\"text-indent:32px\"><span style=\"line-height:200%;font-family:宋体;font-size:16px\">孩子们来校的那天是个大晴天。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"line-height:200%;font-family:宋体;font-size:16px\">日光猛烈，三角草坪鲜有遮阳处，负责带领黑水县孩子的50名志愿者晒着太阳等了半个多小时，终于看见大巴远远地驶来了。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"line-height:200%;font-family:宋体;font-size:16px\">车开近了，只见一个个小脑袋贴在车窗向外张望。一个男孩把头探出窗来，没一会儿又害羞地躲了回去。下车后，孩子们自觉有序地排好队等待点名。这些藏族的孩子，肤色偏棕黑，两团红云浮在脸上。女孩大都是长长的辫子，男生的看起来则比同龄汉族男孩更壮实些。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"line-height:200%;font-family:宋体;font-size:16px\">经过300公里，近6小时的车程，200名来自高县与黑水县的孩子抵达成理，三角草坪成了他们落脚成理的首个地标。在这里，有关成理情与梦正在四月的阳光里，缓缓地铺展开来。</span> &nbsp;</p><p style=\"text-indent:32px\">&nbsp;</p><p style=\"text-align:center;text-indent:32px\"><span style=\"line-height:200%;font-family:宋体;font-size:16px\"><img title=\"1\" alt=\"1\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180427/20180427110841_474.jpg\" width=\"500\" height=\"331\"/></span> &nbsp;</p><p style=\"text-align:center;text-indent:32px\"><span style=\"line-height:200%;font-family:宋体;font-size:16px\">志愿者给孩子们分发名牌</span> &nbsp;</p><p style=\"text-align:center;text-indent:32px\">&nbsp;</p><p style=\"text-align:center;text-indent:43px\"></p><p style=\"text-align:center;text-indent:43px\"><strong><span style=\"line-height:200%;font-family:宋体;font-size:21px\">一周兄妹情</span></strong> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"line-height:200%;font-family:宋体;font-size:16px\">在送孩子去宿舍的路上，记者与志愿者罗刚边走边谈，他是黑水县藏族人，我校旅游与城乡规划学院大三学生。这已不是他第一次以志愿者身份参加“双百双梦”活动。去年担任志愿者时，罗刚与自己初中时期的体育老师重逢于成理，这让他激动了很久。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"line-height:200%;font-family:宋体;font-size:16px\">和队里的其他志愿者相比，孩子们对罗刚有更强的亲近感。用初中生兰克初的话来说，这是因为“他是我们家那边的人”，而来自黑水的康明却向我表达了更深一层的缘由：“比起是志愿者，他更像家里的哥哥。”</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"line-height:200%;font-family:宋体;font-size:16px\">在相处的一周时间里，细心的罗刚像兄长一般关注孩子的感受、照顾孩子的身体。康明记得，自己因为帽子丢了闷闷不乐，是罗刚一直柔声安慰，想办法逗自己开心。说起“像兄长一样”的罗刚，一向害羞的格西拉木话也多了起来。来校第二天，格西拉木就开始肠胃不适，一连几天，罗刚都准时准点督促格西拉木吃药：“格西拉木，药吃了没？”格西拉木打不开药瓶，罗刚就帮她开；格西拉木吃不下饭，罗刚就特地下楼帮她买了一碗面。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"line-height:200%;font-family:宋体;font-size:16px\">即使课业繁忙，即使在“双百双梦”同期还要准备运动会开幕式的舞蹈表演，罗刚还是尽量抽出时间陪伴孩子们。志愿者张银妹告诉我，他们调配工作时，罗刚最常说的一句话就是：“我来。”</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"line-height:200%;font-family:宋体;font-size:16px\">罗刚直言，之所以今年再次报名志愿者，是因为他在孩子们身上看见了曾经的自己，也像这样憧憬着未来，也像这样对外面的世界了解甚少。罗刚觉得，自己的存在也许能给孩子们带来同乡的亲近感，更容易在交流中打开孩子的心门，向他们传递走出大山的信念。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"line-height:200%;font-family:宋体;font-size:16px\">一周的相处中，罗刚与孩子们的“兄妹情”在细节处可见一二。周二晚，“双百双梦”的孩子们进行运动会入场式彩排，罗刚因为有事没能准时到来。女孩们不停在第四小队的QQ群里问：“哥哥，你到了吗？” </span> </p><p style=\"text-align:center;text-indent:43px\"></p><p style=\"text-align:center;text-indent:43px\"><strong><span style=\"line-height:200%;font-family:宋体;font-size:21px\">雁江姐妹的梦想</span></strong> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"line-height:200%;font-family:宋体;font-size:16px\">罗刚带队的第四小队里，10个女孩都来自黑水县雁江寄宿中学。当被问及：“长大后想做什么”时，康明最先回答：“我不喜欢理科，以后想学汉语言文学。”内敛的叶西况主没有发言，说自己还没想好。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"line-height:200%;font-family:宋体;font-size:16px\">两天后，对于“长大后想做什么”这个问题，女孩们的答案已正在无形中悄然更易。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"line-height:200%;font-family:宋体;font-size:16px\">19</span><span style=\"line-height:200%;font-family:宋体;font-size:16px\">号下午，孩子们参观完化学实验室，康明向记者描述起刚做完的化学实验：“往一杯透明的水里倒入三种粉末，水里就会出现绿色和蓝色的珊瑚形沉淀，太神奇了！”她边说边比划使用滴管的动作，“我觉得理科有点意思，好像也没那么讨厌。”康明形容道。</span> &nbsp;</p><p style=\"text-indent:32px\">&nbsp;</p><p style=\"text-align:center;text-indent:32px\"><span style=\"line-height:200%;font-family:宋体;font-size:16px\"><img title=\"2\" alt=\"2\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180427/20180427110909_866.jpg\" width=\"500\" height=\"331\"/></span> &nbsp;</p><p style=\"text-align:center;text-indent:32px\"><span style=\"line-height:200%;font-family:宋体;font-size:16px\">动手做实验</span> &nbsp;</p><p style=\"text-align:center;text-indent:32px\">&nbsp;</p><p style=\"text-indent:32px\"><span style=\"line-height:200%;font-family:宋体;font-size:16px\">参观恐龙博物馆。走到合川马门溪龙的巨大骨架前时，叶西况主轻声感叹：“这个学校太好了，我都不想走了。”她抿一抿嘴唇，想了一会，又问：“多少分能够考到这个学校？”正说着，她开始对比自己目前在班上的成绩排名，估算着还要努力多久才能把成绩提上来。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"line-height:200%;font-family:宋体;font-size:16px\">从不喜欢理科到觉得“有点意思”，从“没想好”到思考如何考取大学， &nbsp; “双百双梦”的力量大概并不是简单的“体验大学生活”，而在于“开眼界”的同时给孩子们“开脑洞”，启发他们梦想未来。曾有孩子在作文里表白心迹：“我感觉我们像真的大学生。”对于参加“双百双梦”的孩子来讲，成理是个全新的世界，是大学校园从电视屏幕里变现，成为可感可知的现实。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"line-height:200%;font-family:宋体;font-size:16px\">一周的“大学生活”也让大家的梦想更具体了些，回到黑水第二晚，罗刚收到了几位女孩发来的消息，内容大致都为“哥，我想考成理。” </span> </p><p style=\"text-indent:32px\"><span style=\"line-height:200%;font-family:宋体;font-size:16px\"></span>&nbsp;</p><p style=\"text-align:center;text-indent:43px\"></p><p style=\"text-align:center;text-indent:43px\"><strong><span style=\"line-height:200%;font-family:宋体;font-size:21px\">虽别离，仍续情与梦</span></strong> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"line-height:200%;font-family:宋体;font-size:16px\">周五晚的联谊会是临行的尾音。有孩子在表演节目时给志愿者送花，志愿者代表合唱歌曲《天使》时，不少孩子把手机、电筒当作荧光棒挥舞，此情此景惹得观众泪水涟涟。联谊会结束，不少孩子泣不成声，一些男生志愿者也止不住红了眼眶。有的孩子拿出纸笔，请志愿者留言。罗刚在每个孩子的留言本上都写下了自己的手机号，他跟孩子们说：“有什么困难也好，难过也好，如果没有人能分享，可以给我打电话。”</span> &nbsp;</p><p style=\"text-indent:32px\">&nbsp;</p><p style=\"text-align:center;text-indent:32px\"><span style=\"line-height:200%;font-family:宋体;font-size:16px\"><img title=\"3\" alt=\"3\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180427/20180427110938_261.jpg\" width=\"500\" height=\"335\"/></span> &nbsp;</p><p style=\"text-align:center;text-indent:32px\"><span style=\"line-height:200%;font-family:宋体;font-size:16px\">依依惜别</span> &nbsp;</p><p style=\"text-align:center;text-indent:32px\">&nbsp;</p><p style=\"text-indent:32px\"><span style=\"line-height:200%;font-family:宋体;font-size:16px\">随着经典藏舞曲目《纳西里莫》响起，欢腾的鼓点冲淡了别离的悲伤。第四小队的成员们手拉着手，在操场上跳起锅庄。随着其他队的志愿者和小朋友的加入舞蹈，跳舞的圆圈越扩越大。此夜无风，唯有歌与舞相送。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"line-height:200%;font-family:宋体;font-size:16px\">不同于首日的阳光灿烂，离别的周六天色阴沉，好似将有阵雨到。许是因为分离在即，大家的表情都稍显凝重。格西拉木说，假如和小伙伴讲起在成理的一周，自己会先讲起在这里遇到的人：“我会先告诉他们，我遇到的志愿者都是怎样的大学生。” </span> </p><p style=\"text-indent:32px\"><span style=\"line-height:200%;font-family:宋体;font-size:16px\">大巴像来时那样停在三角草坪。车门前，志愿者们和孩子依次拥抱话别。车声远了，志愿者穿红马甲的身影远了，只有三角草坪还在那里，静静地。虽别离，情缘仍在续写，梦想正在发芽。送走孩子们后，志愿者周科宇发了一条空间动态：“眼泪是真挚的，不舍也是真挚的，如果可以，下次再会。” </span> </p><p style=\"text-indent:32px\"><span style=\"line-height:200%;font-family:宋体;font-size:16px\"></span>&nbsp;</p><p style=\"text-indent:32px\"><span style=\"line-height:200%;font-family:宋体;font-size:16px\"></span>&nbsp;</p><p style=\"text-indent:32px\"><br/></p><p style=\"text-indent:32px\"><br/></p><p><strong><span style=\"line-height:200%;font-family:宋体;font-size:16px\">记者手记：</span></strong><span style=\"line-height:200%;font-family:仿宋;font-size:16px\">在“双百双梦”活动里，我一直跟随着黑水县小组的第四小队，想要在他们为期一周的成理生活中，看到大手拉小手，看到一颗心推动另一颗心。志愿者罗刚、雁江中学的10个女孩——他们是“双百双梦”的缩影，是笑泪悲欢的凝结，他们的小瞬间动人地讲述着“双百双梦”的大故事，也为“思梦圆梦”的活动主题留下最好注脚。</span></p><p><br/></p>', '2018-05-24 20:45', '成都理工大学网上党支部', '三角草坪，结缘成理情与梦', null, '6', '163');
INSERT INTO `news` VALUES ('<p style=\"text-align: center;\"><strong><span style=\"font-size:29px;font-family:黑体;line-height:200%\">我校召开传达学习全国“两会”精神大会</span></strong> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">3</span><span style=\"font-size:16px;font-family:宋体;line-height:200%\">月28日下午，我校在地灾国家重点实验室学术报告厅召开党委中心组学习扩大会暨全国“两会”精神传达会。校领导谭书敏、龚灏、孙文、黄涛、刘树根、郭朝辉、王敏，校长助理徐仕海、谷建国以及全校处级以上干部参加了会议。会议由校长龚灏主持。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\"></span>&nbsp;</p><p style=\"text-align:center;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\"><img title=\"tim图片20180329093132\" alt=\"tim图片20180329093132\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180329/20180329152106_154.png\" width=\"500\" height=\"330\"/></span> &nbsp;</p><p style=\"text-align:center;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">会议现场</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">全国政协委员、地质灾害防治与地质环境保护国家重点实验室常务副主任许强结合自己参政议政的经历，向与会者传达了全国政协十三届一次会议精神。<span style=\"color:black\">他认为此次两会体现了四个新：新形式、新要求、新内容、新班子。许强从“两会”基本情况、政府工作报告、深化机构改革、宪法修正、热点问题五个方面对今年的“两会”进行了解读。在简要介绍了“两会”的基本情况，传达了政府工作报告的内容和要点后，还就宪法修正案、党和国家机构改革等“两会”热点话题作了相关解读。结合我校情况，他以“九龙治水”的生动比喻引出国家为何要进行深化机构改革的话题，重点解读了自然资源部、生态环境部、应急管理部、科学技术部的改革情况及管理范围。作为西部省属高校的代表，许强在政协会议小组会上针对如何破解西部高校“人才荒”的问题作出发言，他提出要加大财政投入、根据地区设置人才引进的门槛、建立健全人才流失基金保障、建立人才评价考核体系、营造成果转化氛围等。</span></span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\"><span style=\"color:black\"></span></span>&nbsp;</p><p style=\"text-align:center;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;color:black;line-height:200%\"><img title=\"tim图片20180329093045\" alt=\"tim图片20180329093045\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180329/20180329152140_259.png\" width=\"500\" height=\"403\"/></span> &nbsp;</p><p style=\"text-align:center;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;color:black;line-height:200%\">许强教授传达全国政协十三届一次会议精神</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">党委书记谭书敏传达了第十三届全国人大一次会议的主要内容、大会取得的重要成果，重点传达了习近平总书记重要讲话精神，宪法修改、机构改革和职能转变等相关内容，以及我省代表团的主要活动和工作。他指出，十三届全国人大一次会议是一次高举中国特色社会主义伟大旗帜、全面贯彻习近平新时代中国特色社会主义思想和党的十九大精神的大会，是一次民主、团结、求实、奋进的大会。这次会议议题多、分量重，受到了举国上下和全世界的广泛关注。习近平总书记在会议期间与代表共商国是，就脱贫攻坚、政治生态、高质量发展、强军兴军、乡村振兴等一些重大问题发表重要讲话，强调发展是第一要务，人才是第一资源，创新是第一动力。全国“两会”为我校在“十三五”期间全面深化综合改革、加快推进“双一流”和高水平综合性大学建设提供了方向和指南。他强调，全校干部要深刻领会“两会”精神，提高政治站位，充分认清学习两会精神的重大意义；要将贯彻落实“两会”精神贯穿于学校的各项工作中去，认真思考学校如何在改革的大潮中克服困难，抓住机遇；要进一步坚定深化综合改革的信心和决心，以实际行动为学校迎接本科教学评估和推动“双一流”建设贡献智慧和力量，共同书写“迎接挑战，迎接改革”这篇成理文章。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\"></span>&nbsp;</p><p style=\"text-align:center;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\"><img title=\"tim图片20180329093256\" alt=\"tim图片20180329093256\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180329/20180329152310_245.png\" width=\"500\" height=\"416\"/></span> &nbsp;</p><p style=\"text-align:center;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">党委书记谭书敏传达第十三届全国人大一次会议精神</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;color:black;line-height:200%\">校长龚灏就学校传达学习和贯彻落实两会精神作出强调，一是“两会”的召开对于进入“新时代”后继续建设好中国特色社会主义具有里程碑意义。全校上下要准确把握全国两会主要成果与重大意义，把握当前学校发展的历史机遇，围绕建设高水平综合性大学的目标，贯彻落实好全国两会精神；二是面对国家深化改革带来的机遇和挑战，尤其是和学校相关的国家部委发生了变化，各单位、学院要做好思想准备，及时转变观念，为学校的未来发展赢得先机，为推动“双一流”建设奠定好基础；三是要加快推进学校改革建设发展，把学习贯彻“两会”精神同抓好学校重点工作结合起来，继续深化学校管理体制机制改革。龚灏校长分享了3月28日，教育部副部长杜占元在西南片区“双一流”建设高校座谈会上的观点，杜副部长指出，下一步“双一流”建设将分阶段实施、分层次推进、分类型建设，高校要转变角色，打开思路，推进高等教育向内涵式发展，制定有针对性、可操作性、创新性的发展举措，发掘、聚集、配置各方资源。龚灏号召全校上下要转变理念，认真思考一流学科要怎么干，各单位、学院要认真梳理重点工作，做好提前谋划，杜绝“等、靠、要”思想。全校上下要团结一心，集中力量办大事，师生员工要立足本职岗位，在实现学校百年发展目标的征程中建功立业，为“双一流”建设提供新动能，为学校发展贡献新的更大力量。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;color:black;line-height:200%\"></span>&nbsp;</p><p style=\"text-align:center;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;color:black;line-height:200%\"><img title=\"tim图片20180329092946\" alt=\"tim图片20180329092946\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180329/20180329152401_527.png\" width=\"500\" height=\"405\"/></span> &nbsp;</p><p style=\"text-align:center;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;color:black;line-height:200%\">校长龚灏讲话</span> &nbsp;</p><p><br/></p>', '2018-05-24 20:47', '成都理工大学网上党支部', '我校召开传达学习全国“两会”精神大会', null, '6', '165');
INSERT INTO `news` VALUES ('<p style=\"text-align:center\"><strong><span style=\"font-size:29px;font-family:黑体;line-height:200%\">学校召开2018年全面从严治党工作会</span></strong> &nbsp;</p><p style=\"text-align:left\"><br/></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">为贯彻落实十九届中纪委二次全会、省纪委十一届二次全会以及教育系统全面从严治党工作会议精神，总结学校2017年党风廉政建设工作，安排部署2018年党风廉政建设和反腐败工作，3月30日上午，学校在芙蓉食堂3楼学术报告厅召开2018年全面从严治党工作会。校领导谭书敏、龚灏、孙文、黄涛、刘树根、郭朝辉、王敏，校长助理徐仕海、谷建国、施泽进出席了大会，学校科级以上党政管理干部、系部主任、基层纪委委员参加了大会。会议由校长龚灏主持。</span> &nbsp;</p><p style=\"text-align:left;text-indent:32px\">&nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\"><img title=\"会场全景\" alt=\"会场全景\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180330/20180330171133_449.jpg\" width=\"500\" height=\"281\"/></span> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">会议现场</span> &nbsp;</p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">校党委副书记、纪委书记孙文作了题为《强化纪律建设 &nbsp;扎紧制度篱笆 &nbsp;奋力推动全面从严治党向纵深发展》的党风廉政建设工作报告。她从层层压实管党治党政治责任、从严从实持续抓好作风建设、认真实践监督执纪四种形态、大力开展廉政文化宣传教育、狠抓纪检监察干部队伍建设五个方面全面回顾了2017年学校在党风廉政方面做的工作后。她指出，过去一年廉政建设工作取得了一定成绩，但也要清醒认识到目前廉政建设中存在的问题：一是部分党员干部和教职员工对进入新时代后全面从严治党的政治形势缺乏清醒认识和正确判断；二是部分基层党组织没有把抓党风廉政建设的主体责任扛在肩上；三是部分单位制度执行不到位；四是纪检干部队伍建设仍然存在短板。</span> &nbsp;</p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">在部署2018年学校党风廉政建设的重点任务时，孙文要求重点抓好六个方面的工作：一要始终把严明政治纪律和政治规矩放在首位；二要以重整行装再出发的奋斗姿态推进全面从严治党，要在责任分解、责任落实、问责追究上下足功夫；三要以钉钉子精神持续巩固拓展作风建设成果，坚持制度先行，用制度约束和规范行为、狠抓制度规范执行、切实转变作风、坚持不懈突出关键时间节点做好廉政提醒；四要突出抓好“三个到位”确保纪律教育取得实效，做到学习领会、宣传教育、提醒警示到位；五要用四种形态谱写新时代监督执纪工作新篇章，开展问题线索大清理、强化日常监督、强化专项监督检查、突出纪律审查重点；六要高标准严要求锻造忠诚干净担当的纪检队伍。</span> </p><p style=\"text-align:left;text-indent:32px\">&nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\"><img title=\"tim图片20180330170149\" alt=\"tim图片20180330170149\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180330/20180330171205_683.png\" width=\"500\" height=\"419\"/></span> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">校党委副书记、纪委书记孙文作报告</span>&nbsp;</p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">校党委书记谭书敏在讲话中指出，学校把以往的“党风廉政建设暨纪检监察工作会”改为了今年的“全面从严治党工作会”——从“党风廉政建设”到“全面从严治党”的变化，彰显了新时代学校党委推进全面从严治党向纵深发展的坚定决心和意志。他就下一步如何做好党风廉政建设工作强调了四点意见：一要旗帜鲜明讲政治，坚持把党的政治建设摆在首位。全校党员干部必须牢固树立“四个意识”，坚定“四个自信”，坚决杜绝“两面人”“墙头草”；二要坚持敢抓敢管，担负起管党治党政治责任；三要纠正“四风”不止步，锲而不舍推进作风建设。坚持以上率下，发挥各级党员干部的“头雁效应”、坚决反对形式主义、官僚主义，重点解决不作为、乱作为、冷硬横推的问题、筑牢思想“防火墙”，强化廉洁自律、加强师德师风建设；四是全力支持学校纪委切实履行监督责任，努力形成全面从严治党的强大合力，支持纪委健全制度抓规范、支持纪委坚持不懈抓督查、支持纪委严肃纪律抓惩戒、支持纪委聚焦主业推“三转</span><span style=\"font-size:16px;font-family:宋体;line-height:200%\">”</span><span style=\"font-size:16px;font-family:宋体;line-height:200%\">。</span> &nbsp;</p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">谭书敏强调，今年是学习贯彻党的十九大精神的开局之年，是改革开放40周年，是国家决胜全面建成小康社会、实施“十三五”规划承上启下的关键一年，全校上下要扎实深入推进巡视整改工作</span><span style=\"font-size:16px;font-family:宋体;line-height:200%\">,</span><span style=\"font-size:16px;font-family:宋体;line-height:200%\">推进学校管党治党向纵深发展，着力形成风清气正的良好政治生态和育人环境，统筹推进一流大学和一流学科建设，奋力开创高水平综合性大学建设新局面。</span> &nbsp;</p><p style=\"text-align:left;text-indent:32px\">&nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\"><img title=\"领导特写\" alt=\"领导特写\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180330/20180330171315_519.jpg\" width=\"500\" height=\"326\"/></span> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">校党委书记谭书敏讲话</span> &nbsp;</p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">校长龚灏就贯彻落实会议精神、认真扎实开展好本年度全面从严治党工作提出三点要求：一要提高政治站位，准确把握深入推进全面从严治党向纵深发展的精髓要义，坚持不懈抓党的政治建设，层层落实管党治党政治责任；二要狠抓工作落实，为学校营造风清气正的良好政治生态环境。具体做到全面加强学校党的政治建设、思想建设、组织建设、作风建设、纪律建设，压紧压实全面从严治党责任，坚持不懈纠正“四风”，把巡视整改作为最严肃的政治任务抓紧抓好；三要认真组织学习，全面及时传达本次会议精神。领导干部作为部门和学院的“关键少数”，要率先学习，各单位要以高度的政治责任感和使命感，采取切实有效的形式传达落实会议精神，及时报送传达贯彻落实情况，把思想和行动统一到学校2018年工作部署和要求上。</span> &nbsp;</p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">龚灏要求，此次大会为学校全面从严治党绘就了“施工图”，各单位要把自身全面从严治党实际、干部教师职工思想实际摆进去，统一思想、提高认识，进一步增强全面从严治党责任感使命感，坚定不移地把学校全面从严治党引向深入，为学校建设“双一流”和高水平综合性大学提供根本保障。</span> &nbsp;</p><p style=\"text-align:left;text-indent:32px\">&nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\"><img title=\"tim图片20180330170544\" alt=\"tim图片20180330170544\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180330/20180330171402_783.png\" width=\"500\" height=\"375\"/></span> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">校长龚灏讲话</span> &nbsp;</p><p><br/></p>', '2018-05-24 20:48', '成都理工大学网上党支部', '学校召开2018年全面从严治党工作会', null, '6', '166');
INSERT INTO `news` VALUES ('<p style=\"text-align:center\"><strong><span style=\"font-size:29px;font-family:黑体;line-height:200%\"><strong>“黄大年式教师团队”炼成记</strong></span></strong> &nbsp;</p><p><br/></p><p><span style=\"font-size:16px;font-family:楷体;line-height:200%\"><strong>背景资料：</strong></span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:楷体;line-height:200%\">全国高校黄大年式教师团队是为贯彻落实习近平总书记对黄大年同志先进事迹重要指示精神，以黄大年同志为榜样，心有大我、至诚报国，教书育人、敢为人先，淡泊名利、甘于奉献，把爱国之情、报国之志融入祖国改革发展的伟大事业之中、融入人民创造历史的伟大奋斗之中，从自己做起，从本职岗位做起，为实现“两个一百年”奋斗目标、实现中华民族伟大复兴的中国梦贡献智慧和力量。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:楷体;line-height:200%\">2018</span><span style=\"font-size:16px;font-family:楷体;line-height:200%\">年1月22日，国家教育部主页公布了首批全国高校黄大年式教师团队名单，在全国1000多所高校中，共有200所高校的201个团队入选。因在师德师风、教育教学、科研创新、社会服务等方面成绩突出，为教育改革发展稳定作出重要贡献，我校核技术与自动化工程学院葛良全教授负责的核科学与技术教师团队名列其中。长期以来，核科学与技术教师团队全面贯彻党的教育方针，坚持教育教学改革，以德立身、以德立学、以德立教，以民族振兴为已任，培养了国家核能和核技术建设急需的工程技术人才。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:楷体;line-height:200%\">作为学院主体之一的原核原料与核技术工程系（因特定时期需要对外称“三系”）筹建于1956年。2001年，三校合并组建成都理工大学之际，以原核原料与核技术工程系为主，联合原勘察与机电工程系机电工程专业和原信物系电气工程及其自动化专业组建成为核技术与自动化工程学院。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:楷体;line-height:200%\">听闻黄大年因病逝世的消息，葛良全和团队成员十分震惊，“那个背着双肩包，做起事来雷厉风行的科学家怎么就走了？”回忆起与黄大年教授的相识，葛良全仍记忆犹新：“黄大年老师曾路过成都，在双流机场旁的一家小饭店，是我第一次见到他。”葛良全团队在核地球物理方面的研究在全国首屈一指，主攻地球物理深地资源探测的黄大年教授对此十分赞赏，团队因此开启了与黄大年的缘分。在团队攻克“十二五”863计划课题期间，黄大年作为指导专家，曾多次莅临我校，就项目展开探讨。团队着眼点符合国际战略，在核地球物理方面有许多想法与黄大年一拍即合，葛良全表示：“晚上十一点多给黄大年教授发邮件，对方都会及时回复，他做事的干劲感染了整个团队。”</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:楷体;line-height:200%\"></span>&nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:楷体;line-height:200%\"><img title=\"tim图片20180327154341\" alt=\"tim图片20180327154341\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180327/20180327154430_958.png\" width=\"700\" height=\"302\"/></span> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:SimSun;line-height:200%\">团队主要成员合影</span> &nbsp;</p><p>&nbsp;<img title=\"5c~_eqyvv[sra$j0dd`)j8n\" alt=\"5c~_eqyvv[sra$j0dd`)j8n\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180330/20180330093459_581.png\"/> </p><p>&nbsp;</p><p><br/></p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">深部矿产资源的放射性勘探技术、核辐射探测技术和现场核分析技术……这些听起来晦涩难懂的词汇，却是我校葛良全教授负责的核科学与技术教师团队多年来深耕的领域。核科学与技术团队在“原三系”基础上不断发展，一群“核工人”的故事在成理起航。2018年，团队在首批全国高校黄大年式教师团队榜上有名，对此，葛良全感触颇深——这不仅是一种荣誉，更是对“核工团队”发挥不甘人后的科研精神、立德树人的教育精神、以老带新的团队精神、代代传承的奉献精神的激励。</span> &nbsp;</p><p><strong><span style=\"font-size:20px;font-family:黑体;line-height:200%\">&nbsp;&nbsp;</span></strong> &nbsp;</p><p><strong><span style=\"font-size:20px;font-family:黑体;line-height:200%\">&nbsp; 做科研：从跟跑到并跑，从并跑到争取领跑</span></strong> &nbsp;</p><p style=\"text-align:center\">&nbsp;</p><p style=\"text-align:center\"><strong><span style=\"font-size:16px;font-family:宋体;line-height:200%\">突破国外技术禁锢，团队攻克“十一五”和“十二五”项目</span></strong> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">在“十一五”期间，团队承担了863计划“资源环境领域”重大项目的课题“航空伽马能谱勘查技术系统研发”，课题总经费1862万元，重点解决我国矿产资源勘查和核事故应急工作中航空伽马能谱测量的技术难题。以四年的技术攻关，研制了具有完全自主知识产权的全数字化航空伽玛能谱测量仪，在无源和无辅助装置下实现了航空伽玛能谱的全天候测量，打破了少数发达国家的技术封锁和垄断，实现了我国航空伽马能谱勘查技术与国外同类技术的比肩并跑。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">在“十二五”期间，葛良全团队承担了863计划主题项目的课题《高精度能谱探测仪器开发》，总经费为1347万元，重点解决放射性矿产勘查工作中放射性元素（铀、钍、钾、氡）、非放射性元素、蚀变矿物等野外快速测定的技术难题。</span> &nbsp;</p><p><span style=\"font-size:16px;font-family:宋体;line-height:200%\">&nbsp;&nbsp;&nbsp; 项目下设六个子课题，其中，团队成员曾国强教授负责研发的X射线光管发生器、电制冷X射线探测器、数字化多道采集器等核心器部件，是制约我国野外现场射线能谱测量仪的技术瓶颈，主要被西方发达国家垄断。“多少次项目组的深夜开会讨论，都是被门卫的敲门声打断，”曾国强表示，无数日夜，团队自主研发了真空电制冷高分辨率X射线探测器、微型X射线管及数字化X射线处理器等核心部件，实现了高稳定度的X射线产生，高精度的X射线探测与高分辨率的X射线信号测量，实现了首个达到国际同等级水平的现场X荧光测量仪器。目前高精度手提式X荧光仪已经被社会广泛应用，即使在西藏海拔6000米以上恶劣环境下，也能完成矿产勘查任务。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">此外，微束微区野外 X 荧光矿物探针、高精度伽玛能谱仪和高灵敏度野外测氡仪……“十二五”项目期间，团队攻坚克难，形成了多种具有自主知识产权的测量新仪器、新技术，培养了一批中青年放射性矿产勘查技术人才。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\"></span>&nbsp;</p><p style=\"text-align:center;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\"><img title=\"tim图片20180327154543\" alt=\"tim图片20180327154543\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180327/20180327154631_151.png\" width=\"500\" height=\"378\"/></span> &nbsp;</p><p style=\"text-align:center;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">团队成员曾国强（右）与张庆贤（左）携带手提式核辐射探测仪去往核辐射区</span> &nbsp;</p><p style=\"text-align:center;text-indent:32px\">&nbsp;</p><p style=\"text-align:center\"><strong><span style=\"font-size:16px;font-family:宋体;line-height:200%\">空中、地面、井中、海洋立体勘查技术体系形成</span></strong> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">依托项目，团队开发了航空飞行器、汽车和船等移动平台的射线能谱测量技术，形成了空中、地面、井中、海洋等立体放射性勘探技术体系；研制了基于核辐射测量的三十余种系列核仪器与装备，以及在线（载流或离线）辐射检测系统，主要技术指标均优于国外同类产品水平。面对众多仪器设备、系统技术的研发，葛良全表示：“团队放眼国家战略做科研，落脚点仍是科研成果的转化以及为国家、社会做出的贡献。”</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">据悉，团队研制成功的“全数字式航空伽马能谱仪”及测量软件已列装中国地质调查装备，被中国航空物探遥感中心、江苏省地质勘查研究院等单位生产应用，已完成20000多公里的生产飞行，并成功发现了一批地质普查异常点和找矿靶区；团队开发的手提式X荧光仪、高精度伽马能谱仪、高灵敏度野外测氡仪等核仪器已被重庆地质仪器厂、成都新核泰科科技有限公司、中广核成都久源测控仪器公司、成都中辐科技有限公司等企业成果转化并形成定型产品，在我国地质矿产和环境监测部门得到广泛应用……</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">从以前依赖国外技术，到打破禁锢，追求领跑国内外，60余年，团队在一代代人的努力下，实际地提升了我国核地球物理勘探技术与装备水平，及环境核辐射测量技术与装备的技术水平。葛良全表示，高校科研要有气势，要有国际视野和国家战略思想，“获评黄大年式教师团队，是激励我们向黄大年教授学习，克难攻坚，在技术上实现国际领跑。”</span> &nbsp;</p><p style=\"text-align:center\"><strong><span style=\"font-size:16px;font-family:宋体;line-height:200%\">备战“十三五”，从“天上”切入</span></strong> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">六十多年来，核科学与技术团队在全球化竞争中，直面挑战与机遇，向国家战略看齐。葛良全说：“现代化进程中，团队对核科学与技术的研究曾受限于国外技术封锁禁锢，在国际上处于跟跑水平，‘十一五’和‘十二五’863计划课题完成后我们基本实现了并跑。”在“十三五”阶段，团队将深耕地球深部资源探测，不仅做到研发技术，还要做到研发领先国际的先进技术。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">葛良全团队基于对“十一五”阶段对航空领域射线能谱测量的研究，2017年，以《高分辨率航空伽马能谱测量与机载成像光谱测量系统研制》获批科技部重点研发计划项目，资助经费达2470万元。葛良全介绍到：“此次团队将继续基于飞行器平台的核辐射测量技术研究。”据悉，此项目将聚焦地球深部矿产资源勘探能力，提升对地面核辐射探测的灵敏度和测量精度，并研发相应技术设备。</span> &nbsp;</p><p><span style=\"font-size:16px;font-family:宋体;line-height:200%\">&nbsp;&nbsp;&nbsp; 近年在“十一五”、“十二五”和“十三五”期间承担的科研项目，是团队的重要节点，都代表着团队致力解决国家需求问题的决心和实力。节点项目的开展，不仅为团队增加信心，也实现了与其他多个项目相辅相成。多年来，团队先后承担了国际原子能机构CRP项目1项、863计划课题5项、国家重大仪器专项课题2项，以及国家自然科学基金（含青年基金）项目、地质大调查项目和四川省科技项目等纵向科研课题共40余项，以及30多项横向合作项目。</span> &nbsp;</p><p><strong><span style=\"font-size:20px;font-family:黑体;line-height:200%\">&nbsp; </span></strong> </p><p><strong><span style=\"font-size:20px;font-family:黑体;line-height:200%\">&nbsp; 抓教育：兴趣是动力，“学懂”是目标</span></strong> &nbsp;</p><p style=\"text-align:center\">&nbsp;</p><p style=\"text-align:center\"><strong><span style=\"font-size:16px;font-family:宋体;line-height:200%\">学科竞赛、“5A”实践教学，兴趣是动力</span></strong> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">本科是团队人才培养的起点，兴趣很重要。以学科竞赛激发学生专业兴趣，一直以来是团队探索实践的方式。对学生杨剑来说，团队老师的培养帮助他走上了专业之路。杨剑在核自学院完成了本科、研究生阶段的学习，并在硕士研究生阶段提前毕业，成功考取了清华大学博士，“对专业的兴趣，从一场比赛开始”。2012年，大三的杨剑第一次走上全国舞台。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">曾国强带着杨剑和另外两个小伙伴一起参加了2012年IEEE全国“电脑鼠走迷宫”竞赛，在全国12个分赛区的近500支参赛队伍中脱颖而出，作为96支优胜队之一代表学校去北京参加总决赛。最终，杨剑团队的电老鼠以3.9秒的绝对优势通过迷宫，取得全国总决赛的冠军。曾国强表示：“学科竞赛与创新创业、科学研究、教学工作是协同联动的。”多年来，团队指导学生在全国机器人竞赛、电子设计大赛、“创青春”挑战杯等学科竞赛中取得优异成绩，进而孵化了多个创新创业项目，促进了科研、教学工作。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">除了学科竞赛，在传统授课的基础上，团队开创了5A实践教学方法（学生自主设计、自主实验、自主评价、自主进步，以及教师参谋）以增加学生专业学习的兴趣。葛良全介绍：“5A的核心就是自主，我们将教学的重点聚焦到学生，以学生为主体，教师为辅助。他们能从老师的讲解中学到什么，其所学所用是否能得到市场的肯定，是我们所关心的问题。”杨剑回忆，上课不是他学习的唯一途径，课后的自主让他更能“钻进去”，“要想把理论学扎实，就学数学、看文献、听公开课，要想做实验，就反复实践，争取最优性能。”</span> &nbsp;</p><p style=\"text-align:center\"><strong><span style=\"font-size:16px;font-family:宋体;line-height:200%\">“学徒制”教学，“学懂”是目标</span></strong> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">无论是理论学习还是实验指导，教师随时和学生在一起，如同师傅带学徒一般，一方面及时纠正理论基础偏差，一方面在实践上给予试错空间。杨剑在研究生阶段，在导师曾国强指导下，完成了“8L大体积车载伽玛能谱仪”的整机系统设计和“低本底水体放射性在线实时测量”的控制系统等实验室项目。在参与项目的同时，总结相关成果发表了SCI论文3篇，EI论文1篇，中文核心论文1篇。杨剑表示，成果的取得离不开“师傅”的教导，“老师们对理论基础严格要求，在实践中却给了我们反复试验的机会，让我们收获了经验，吃透了理论。”</span> &nbsp;</p><p><span style=\"font-size:16px;font-family:宋体;line-height:200%\">&nbsp;&nbsp; &nbsp;“学徒制”的理念，来源于团队对“学懂”的追求。葛良全不断向老师们强调，要让学生“学懂”，教师应该先“懂”。所谓教学，不仅是要让学生“学会”，更要让学生“学懂”。所谓“学会”，就是通过看、听等过程来完成认知，这是学习的第一个过程，但也仅是停留在表面的学习。所谓“学懂”，则是在“学会”的基础上通过思考、实践等去搞明白每个理论的前因后果、知其然还要知其所以然。</span> &nbsp;</p><p><span style=\"font-size:16px;font-family:宋体;line-height:200%\">&nbsp;&nbsp;&nbsp; &nbsp;目前，由该团队培养的六百余名学生很多成为了诸如阿里、百度、华为等大公司的高管、技术骨干，也不乏出国深造、自主创业的优秀学子。“我们不仅要将学生培养为一名合格的工程师，而且要把学生培养成为着眼于科技前沿和国家经济社会发展的高素质科技创新人才。”葛良全认为。</span> &nbsp;</p><p><strong><span style=\"font-size:20px;font-family:黑体;line-height:200%\">&nbsp; </span></strong> </p><p><strong><span style=\"font-size:20px;font-family:黑体;line-height:200%\">&nbsp;&nbsp; 建团队：“传帮带”助力青年教师拔节成长</span></strong> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">青年教师胡传皓刚博士毕业，入职才半年，他指着桌上的一个长宽高约为15厘米的“金属盒”，将他在这个不大的实验室里得到的想法启迪和技术指导娓娓道来。这个“金属盒”，是“十二五”863计划课题《高分辨率能谱测量》的关键技术之一——“现场X荧光探测技术”中的重要部件。现场X荧光探测技术在地球深部找矿、大洋海底矿产资源勘探、深空地质分析等领域得到广泛应用。但传统的现场X荧光探测仪存在分辨率低，有无法精确快速测量的技术难点。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">为解决这一难点，胡传皓按照理论知识与原理，做出了一个半导体核辐射探测器样机，在复位型电荷灵敏放大器的研制过程中，如何实现放大器与不同类型探测器的最佳匹配成为了一大难题，反复的原理推敲，多次的电路改进，问题依旧没有完全解决。带着困惑，胡传皓请教了曾国强。曾国强根据不同类型探测器的特点，分析了探测器结电容存在显著差异，提出需要实现放大器与探测器的最佳电容匹配才能实现最优效果。根据曾国强的指导再次改进电路，最终攻克了这一难题。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">科研之外，胡传皓回忆人生中第一次写教学大纲，“从学生转变为青年老师，只能依靠书本理论。”但在团队中，他收获了资深老师的帮助。“团队的资深老师从经验出发，细心告诉我学生常出现的问题以及上课的注意事项，这是我在书里学不到的。”胡传皓说道。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">葛良全向团队强调，照本宣科地“读课”不等于讲课，想要把一门课程讲透彻，主讲教师既要吃透课本内容，还要将自己的在工程经验、科研经历中的所见所得渗透到理论讲解上。“工科学生的培养必须依靠理论和实践的结合，青年老师只有反复实践，积累经验，才能读透理论，教懂学生。”</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\"></span>&nbsp;</p><p style=\"text-align:center;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\"><img title=\"qq图片20180324145409\" alt=\"qq图片20180324145409\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180327/20180327154836_270.jpg\" width=\"500\" height=\"334\"/></span> &nbsp;</p><p style=\"text-align:center;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">葛良全（右）在实验室给学生作指导</span> &nbsp;</p><p><strong><span style=\"font-size:20px;font-family:黑体;line-height:200%\">&nbsp;&nbsp; </span></strong> </p><p><strong><span style=\"font-size:20px;font-family:黑体;line-height:200%\">&nbsp;&nbsp;&nbsp;谈传承：责任感是关键词</span></strong> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">你是否见过凌晨的核工楼？核科学与技术团队每位成员的答案都是肯定的。这栋时常24小时亮着灯的楼，孵化了无数国际领先的技术，也承载了这个“核工团队”的奋斗日常。</span> &nbsp;</p><p><span style=\"font-size:16px;font-family:宋体;line-height:200%\">&nbsp;&nbsp; </span><span style=\"font-size:16px;font-family:宋体;line-height:200%\">“加班加点是常态，这在我们团队，甚至学校都不是特别了不起的事。”张庆贤教授表示，团队的教授、副教授都要一面承担科研、研究生培养，一面承担本科教学任务。“东西做了一半，就算回了家也想赶快回实验室完成，”葛青是团队里为数不多的女老师，“作为女老师，偶尔会有重心向家庭偏移的情况，但是葛良全老师对她们都非常体贴，常叮嘱我们要好好带小孩，合理分配工作和家庭的时间。”这种体谅反过来促使葛青压缩时间，提前完成任务。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">葛良全表示，团队发展到今天，最重要的是成员们对这份事业的责任感，“团队的奋斗精神、奉献精神不是一两天养成，60多年的积淀，诸如金景福等老一代人不仅为我们打下基础，其社会责任感、民族责任感也在代代传承。”2010年，团队成员刘明哲教授在新西兰梅西大学深造并获得了计算机科学专业一级荣誉硕士学位和博士学位，为了将科研成果的署名落在中国科研院校上，他毅然放弃留在新西兰的工作机会，回到了大学母校——成都理工大学任教。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">“我们每个人都是小人物，或许只是干些焊电路、分析数据的小事，但我们合在一起，就能干出有意义的大事。”正是这样一种合力，在团队中形成了共同学习进步的氛围。葛良全说到：“团队布置的任务，成员们从不以完成差事为目的，他们常常想出多种解决办法，最后选择一种最佳的。”他直言，正是这种对专业、事业的追求，承担责任的担当，才促使团队不断发展壮大。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">团队中时常流传着葛良全的一句话：“仰望星空，脚踏实地。”在他看来，科研不仅要抬起头，朝着国家、社会需求定目标，更要埋着头，勤勤恳恳、踏踏实实干实事。“此次获评黄大年式教师团队，既是一种荣誉，一种肯定，对团队而言也是一种促进和鼓励。”葛良全表示，如今，学校走在“双一流”学科建设行列，团队也在向国际水准靠齐，团队将继续发挥优势，稳扎稳打在科研教学、人才培养、社会服务方面绽放光彩。</span> &nbsp;</p><p style=\"text-indent:32px\">&nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:FangSong_GB2312\">（</span><span style=\"font-size:16px;font-family:FangSong_GB2312\">部分图文</span><span style=\"font-size:16px;font-family:FangSong_GB2312\">资料由该团队提供 </span><span style=\"font-size:16px;font-family:FangSong_GB2312\">资料整理：郑婉玉、代春兰）</span> &nbsp;</p><p><br/></p>', '2018-05-24 20:49', '成都理工大学网上党支部', '“黄大年式教师团队”炼成记', null, '6', '167');
INSERT INTO `news` VALUES ('<p style=\"line-height: 200%\"><strong><span style=\"line-height: 200%;font-family: 黑体;font-size: 16px\">编者按</span></strong></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"font-family: 仿宋_GB2312\"><span style=\"line-height: 200%;font-size: 16px\">在茫茫的高原深山，有这样一群人，他们迎着朝阳出发, 背起馒头爬山, 挑着仪器下山, 踏着晚霞凯旋, 头顶苍穹、脚踏荒原,战严寒、斗酷暑。他们就是我们身边平凡而又动人的地质工作者。</span></span><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\"></span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"font-family: 仿宋_GB2312\"><span style=\"line-height: 200%;font-size: 16px\">为了进一步贯彻党的十八大精神，践行社会主义核心价值观，弘扬地矿行业“三光荣”（以地质事业为荣、以艰苦奋斗为荣、以找矿立功为荣）和“四特别”（特别能吃苦、特别能忍耐、特别能战斗、特别能奉献）精神，向杨衍忠同志为代表的地质工作者学习致敬，【深度】将陆续推出“地质•行者无疆“系列报道，让我们一起走进理工地质工作者，讲述他们与地质的故事。<span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\"></span></span></span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"font-family: 仿宋_GB2312\"><span style=\"line-height: 200%;font-size: 16px\">本次【深度】聚焦地球科学学院钟康惠教授，让我们一起倾听他在雪域高原奋斗的足音，倾心于地质事业和高校教育事业的过往与当下。</span></span></p><p style=\"line-height: 200%;text-indent: 32px\">&nbsp;</p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\"></span></p><p style=\"text-align: center;line-height: 200%\"><strong><span style=\"line-height: 200%;font-family: 黑体;font-size: 29px\">钟康惠:跋涉在雪域高原的寻宝者</span></strong></p><p style=\"line-height: 200%;text-indent: 32px\"><strong><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">人物名片：</span></strong><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">钟康惠，我校地球科学学院教授，四川省教育厅创新团队构造成矿学理论发展与实践带头人，国土资源部构造成矿成藏重点实验室副主任，国土资源领域“卓越工程师教育培养计划”专家，国土资源部矿产资源储量司特聘专家。长期从事扬子西缘、西南三江、藏南冈底斯、东昆仑等西部地区大地构造背景演化、构造与成矿耦合关系、构造年代学等诸方面研究，主持或参与国家科技攻关、国家973、国家科技支撑、省部级等数十项科研项目。2012年被国土资源部授予“青藏高原地质理论创新与找矿重大突破先进个人”称号，现已被我校推荐申报“四川省有突出贡献的优秀专家”。</span></p><p style=\"text-align: center;line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\"><img alt=\"\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/141923827071810985.jpg\" width=\"500\" height=\"284\"/></span></p><p style=\"text-align: center;line-height: 200%;text-indent: 32px\">&nbsp;</p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">美丽神奇的南伽巴瓦雪山，见证着一辈辈地质勘探者踏上雪域高原的足迹。这里是世界屋脊的西藏，分布着冰缘地貌、岩溶地貌、风沙地貌、火山地貌，这些地貌不仅构成了大自然的壮丽奇观，还蕴育了一座座宝藏。天神将这些宝藏赋予了西藏120多万平方公里的土地，等待着寻宝者的到来。</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">为了祖国的富强，上世纪50年代至今，一支支地质勘探队不畏艰险来到这里“寻宝”，他们常年行进于干燥寒冷，空气稀薄，荒无人烟的草甸和陡峭的山崖上。每年5至10月是进藏考察的最佳时段，他们在这里一待就是好几个月。在全国顶尖地质专家的联合勘探团队中，有一个中年男人，身着红色冲锋衣，背着行李，嘴角露出自信微笑，透过厚厚眼镜片的眼神流露出坚定……他就是成都理工大学的钟康惠教授。</span></p><p style=\"text-align: center;line-height: 200%\"><strong><span style=\"line-height: 200%;font-family: 黑体;font-size: 16px\">坚强意志演绎“山矮人高”</span></strong></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">西藏120多万平方公里的土地，占了全中国国土的1/8。这里是亚欧板块与印度洋板块长期强烈作用的地方，地质结构复杂，成矿条件优越，因此西藏被誉为地质“天然实验场”。然而地质勘探者在这里，却要忍受常人难以想象的恶劣地势和气候，以及面临意想不到的危险。</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">1999</span><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">年，钟康惠毅然踏上了这条通往雪域高原的漫长勘探之路。初上高原，身体极不适应，他忍受着高原反应折磨，在1999年到2001年两年期间，指导了藏东三江源地区丁钦弄的铅锌银矿勘查。</span><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">藏东一带，多为深切割地势，险峭异常，山与山之间，看似很近实则很远，站在山顶可与对山的人交谈，可是要想见面握手，那恐怕得走上一天。在这个地区可以见到葱郁的树木和高山远处迷人的草甸。勘探途中，有时还会遇到一种被称为“砍头树”的树——这是一种从岩石上横向长出来的树，人骑在马上，它正好到达脖子的高度，由于马儿走起来快，要是碰到它了，马儿一时停不下来，树上的横枝就会劈向人来。有一次，一个队员骑马在前，不幸碰到这种情况，险些遇难。</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">然而在藏南冈底斯带，平均5000多米的海拔常常让人望而却步，不见树木，光秃秃的崖壁，层峦叠嶂，乱石嶙峋，难以攀援。从山脚到山顶穿越悬崖陡壁的过程，往高处望去，有时天如一线，有时天如井口，车不能到的地方就用马匹，马不能走的地方，钟康惠和同事们就猫着腰，用手爬着岩壁，慢慢地一步一步往上挪。</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">越往西走越不见树木，到了藏西藏北一带就是湖滩草甸了，氧气极为稀薄。经暴雨冲刷后，湖滩改道，钟康惠他们经常迷路，特别是越野车行驶在这里，可能打滑后团团旋转，也可能深陷下去难以拔出。迷路困在原地是常有的事，有一次困在山里40多天，与外界失去联系。钟康惠说这些年经常会遇到这种情况，开始他的家人特别担心，后来也就觉得没什么大惊小怪的了。一旦困在荒野，方便面饼干是常用的食物，现在一想起方便面那味儿他就直反胃。</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">经过湖滩草甸，钟康惠他们经常与野驴和藏羚羊擦肩而过，有时还会遇到野牦牛。这些野牦牛经常混在家养牦牛群里，车子经过惊动它，它若发怒就会掀翻越野车。山里还有熊和狼，队员是不能单独行动的。除了提防野兽外，睡觉也不会踏实，因为山里天气恶劣，这里“一山分四季，十里不同天”的情况是常见的。如遇大雪封山，他们不得不就地宿营，夜间要多次起来撑撑帐篷，抖掉堆在篷顶的积雪，以免帐篷被压垮。睡在帐篷中也会久久难眠，因为高寒地带总让人处于浅睡眠状态。</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">高原空气稀薄，爬山时越往上越吃力，还会伴随嗓子疼，耳鸣。有一次登山途中钟康惠感到腿脚已经酸软麻木，就停下来坐在岩石上休息，突然发现岩缝里长出两朵雪莲花，叶子上封满冰凌，却开得那么绚烂。此时他想，即便环境再恶劣，也要像雪莲那样顽强的展示自己的生命力，随即他取出相机拍下了这两朵冰山雪莲，然后打起精神继续往高处攀援。就这样，他越过重重艰难险阻，从一座山峰到另一座山峰，演绎了哲人所言的“山矮人高”。</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">世界上没有攀援不上的高峰，只有畏惧攀援高峰的人。有顽强意志的人永远会高于任何一座山峰，钟康惠就是这样一位在勘探途中不断站立于新的山峰之上的地质工作者。</span><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\"></span></p><p style=\"text-align: center;line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\"><img alt=\"\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/141924171296828814.jpg\" width=\"500\" height=\"309\"/></span></p><p style=\"text-align: center;line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 图中左一为钟康惠</span></p><p style=\"text-align: center;line-height: 200%\"><strong><span style=\"line-height: 200%;font-family: 黑体;font-size: 16px\">探寻一块岩石的前世今生</span></strong></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">钟康惠确信“矿藏就在科学家的脑海里”。于他来说，面对一块岩石，就试图去探究它的前世今生，就会推想它曾经历经过的沧海桑田并预测它的将来。每到一个地区考察，他走出飞机场，就开始观察周围的环境，从路边的石头开始一直到周围的群山，他相信一切事物都有着内在联系，从石头可以推测考察区曾经发生的地质事件，及其相伴的各种作用和作用的产物。凭着对地质科学理论的深刻理解和多年分析岩石样本的丰富经验，他会条件反射地推测，这里曾经发生过哪些地质事件，会形成哪些矿藏，矿藏的分布走向，矿藏量有多少，以及矿藏品质的好坏等等。</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">藏南冈底斯山被誉为万山之王，平均海拔五千多米。2003年，钟康惠提出了冈底斯地带雄村北西向褶断带对成矿的找矿勘探模型，直接指导了雄村Ⅰ号矿体的勘查，仅Ⅰ号矿体探获铜约104万吨、金约144吨、银917余吨。后来雄村矿床由加拿大大陆公司承接勘探，加拿大的地质专家马克通过对雄村的考查，认为矿藏呈西东向分布，但事实证明按钟康惠提出的西北300～320度勘探方向才是正确的，现已陆续发现了雄村Ⅱ、Ⅲ号矿体。</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">2012</span><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">年国土资源部将我校列为特等奖项目“青藏高原地质理论创新与找矿重大突破”的获得者，对参与该项目的钟康惠如此评价：“长期从事青藏高原一线地质科研工作，作为大调查项目校方第二负责人，勘探现场负责人，科技支撑项目专题负责人，是‘大陆碰撞成矿论’之‘逆冲/推覆/走滑/剪切驱动壳幔混源岩浆侵位与成矿’，‘成矿预测理论与方法’之‘推覆-滑覆构造控岩控矿论’的主要贡献者，以及雄村矿床，甲玛矿床，丁钦弄矿床，蒙亚啊矿床，洞中拉矿床等勘查成的重要贡献者……”为此，钟康惠在2012年被国土资源部授予“青藏高原地质理论创新与找矿重大突破先进个人”称号。</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">钟康惠在西藏实地考察勘探之路从1999年延续至今。2010年在措那县拉木尤塔矿区，他和西藏区调队技术人员已登至海拔5600米山崖，雪风挂在脸上如刀割一般，头疼耳鸣，腿脚酸软麻木，在他的坚持和鼓励下，团队咬紧牙关又往上攀援了十多米，就在快站不住的时候，他们惊喜的发现磁铁矿就滑脱躺在鞘褶皱里，那一刻，他们都惊喜得屏住了呼吸。2014年在蒙亚，他们走在一条已被先前地质工作者否定有矿藏的路线上，但也就在这条路线上，钟康惠发现了新的铅锌矿化点。在2014年5月举办的全国地质队长会议上，专门报道了他在甲玛找矿的事迹，会上展出了这样一张照片，西藏瓦蓝的天空下，钟康惠身后是甲玛雪山，他头戴红色安全帽，神情坚毅，宛如一座沉稳的雕塑衬映着雪山。钟康惠在2007年提出的甲玛铜钼多金属矿床“推覆-滑覆构造控矿模式与找矿模型”，成功指导了甲玛矿床勘查。探明铜444.83万吨，钼52.79万吨，铅56.15万吨，锌16.49万吨，金75.7吨，银5324.23吨，潜在经济价值达3300亿元人民币，这是钟康惠在西藏找矿史上的重大突破。据中金集团华泰龙公司材料证明——“2007-2009年钟康惠教授及团队在西藏墨竹工卡县甲玛铜矿通过矿区填图和路线调查，结合室内研究，首次提出推——滑覆构造系理论，有效指导了找矿方向，避免了12000m无效钻探工作。为西藏墨竹工卡县甲玛铜矿的勘查工作提供了有力的技术支撑。找矿取得了突出的进展，同时取得了良好的社会效益。”</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">长期主持参与野外地质勘探的钟康惠，一年就有好几个月行走在荒山野岭，面临饥饿、寒冷、野兽甚至疾病的威胁，经受着生命极限考验的同时也照顾不了家里。2006年他母亲病危时，哥哥打来电话：“知道你忙，有时间你就回来，没时间就不回了吧。”正在主持雄村矿床研究项目讨论的他，连夜租车于次日凌晨赶回老家，四小时后母亲就辞世了。2009年，钟康惠和队友们在会理天宝山考察时，有一段时间感到浑身乏力，他连走上三楼的力气都没有了，脸色也很难看，学生多次提醒他去医院，但他想把本科生毕业实习带完再去。直到有一天，被强行送进医院，才发现他的血色素只有5点几，不及正常人的一半，原因是慢性胃出血导致的贫血，随时都有休克危险，在那里紧急治疗了一周，后转到成都又治疗了半个月才得以康复。</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">除了在野外考察、出差和教学，钟康惠几乎成天待在他的办公室里，这里也是他们团队的室内战场。他在这里分析样本、整理资料、写科研报告、指导学生…….每天他早上来，晚上十一点后才回家。这里不断有人来找他，没有周末，没有寒暑假，二十多年如一日的这样工作着。</span><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\"></span></p><p style=\"text-align: center;line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\"><img alt=\"\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/141929552409369476.jpg\" width=\"500\" height=\"375\"/></span></p><p style=\"text-align: center;line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;钟康惠与学生在多不杂南考察（右三为钟康惠）</span></p><p style=\"text-align: center;line-height: 200%\"><strong><span style=\"line-height: 200%;font-family: 黑体;font-size: 16px\">走出“小蚂蚁”的领地</span></strong></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">钟康惠认为自己的成长和意志的培养都离不开几位老师的直接影响。当年徐迟的报告文学《哥德巴赫猜想》在光明日报发表时，上中学的他被陈景润攻克数学难关的故事深深的吸引和鼓舞。后来他被昆明工学院地质系录取，开始了地质专业的学习，为其后的地质生涯打下了坚实的基础。到成都工作后，在刘肇昌教授带领下，即使在被人们称为“地质冬天”的1990年，他也不间断地承担国家和省部级科研项目。刘肇昌教授曾是前国务院总理温家宝本科毕业论文的指导老师，治学严谨而勤奋，至今已年高八旬，仍坚持到办公室从事研究，指导学生。刘老师的治学精神和态度对钟康惠产生了深远的影响。在南京大学读博时，导师施央申的思想境界和治学精神也给了他很大影响，这位在南大地质事业上做出重要贡献的专家，要求学生探索老师没有涉及的领域，谁要是马马虎虎对待科研就会受到严厉的责备。钟康惠庆幸自己是没有被施老师训过的学生之一。</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">现在钟康惠也是这样教育他的学生。他说那种急功近利的心态，往往一事无成，科研工作应为社会做出实实在在的贡献，解决具体问题。他不仅以严谨的科学态度要求自己，还用这样的科学精神去影响、启示、感化他的学生。在教学中他把学生的思维方法看得与勤奋刻苦同样重要，他认为对待科学既要有愚公移山吃苦耐劳的精神，更要掌握科学的方法，学会吸取自己所需的有用知识，否则就会事倍功半。因此他在教学和方法上都有独到之处，在每一届新生到来之际，他都要为他们开一门职业规划课程，在这门课上，他将教与学以及教学的效益（正因子、负因子）、知识与目标性实践能力的提高等关系归纳为函数关系，并以公式的形式证明推导。他说提高能力比增加知识更重要，要求学生汲取“活知识”和注重提高目标性实践能力，在教学中他多次谈到科研和学习方法的问题，如用“小蚂蚁拥有一个小小的地盘，常常辨不清方向，着急得盲目打转，若一旦确定目标，很快就会扩大自己的视野”告诫学生做学问要有高境界宽视野和广思路。关于这些思想方法，他还把它写成了教育论文。</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">钟康惠为学生做出了榜样，他注重开阔学术视野，确定自己的科研目标，在科研和实践中不断拓展研究方向，取得新的成果。他先后出版专著4部，在《地质学报》等刊物上发表论文50余篇，其中2篇被EI收录，其中《扬子西源的铜矿成矿背景研究》获国土资源部科技进步一等奖，《四川西部海象火山岩》获四川省科技进步三等奖。</span><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\"></span></p><p style=\"text-align: center;line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\"><img alt=\"\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/141929557820399378.jpg\" width=\"500\" height=\"375\"/></span></p><p style=\"text-align: center;line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 钟康惠团队在蒙亚啊考察（左六为钟康惠）</span></p><p style=\"text-align: center;line-height: 200%\"><strong><span style=\"line-height: 200%;font-family: 黑体;font-size: 16px\">叩开“阿里巴巴大门”</span></strong></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">成功，获得的是鲜花和掌声。而追求成功的路上，到底经历多少坎坷、多少风雨，钟康惠沉默不语，目光久久凝视在《西藏矿产资源潜力评价测评报告》上。封面那高耸的南伽巴瓦雪山在阳光下异常耀眼，一本本厚厚的报告册是他办公室里那些堆积成山的科考资料浓缩成的，这是长达八年西藏矿产资源潜力评价的成果。</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">全国矿产资源潜力评价是国家为“摸清家底”的重大科技专项。因历史原因，西藏在这方面的工作几乎为零。这次科学评价矿产资源潜力是要将占国家陆地面积1/8的西藏从和平解放以来，所有的地质、矿产资料进行整合统计分析，这是一件史无前例的事。2009年我校作为外协单位与西藏项目组签署《西藏矿产资源潜力评价项目协议》。项目合作协议签订后，学校立即整合地球科学学院、地球物理学院、信息科学与技术学院等学院的技术力量成立了项目组。钟康惠作为外协主要负责人之一，先后兼任成矿地质背景组组长、项目综合组组长、基础研究及成果集成课题组组长。</span></p><p style=\"line-height: 200%;text-indent: 32px\"><br/></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">从合同签订以来，我校与西藏项目组一道组建了西藏模式团队，在2007年全国地勘单位做出的1:25万图的基础上，通过素材大汇集、大清理、电子化形成了系列基础1:100万图。钟康惠及其团队和西藏地勘人一起，整合了建国以来西藏矿产资源研究的资料碎片，编制了全区地质图、矿产图、构建各种基础框架，评价了矿产资源潜力，提出了勘查开发建议，与相关地勘单位通过五年的艰苦努力，西藏矿产资源潜力评价全面结束。在这次潜力评价成果的指导下，新发现矿产处9处，取得勘查突破58处。为国家相关决策提供了支撑，为国家初步掌握矿产资源蕴藏、西藏铁路选线工程、西藏地震研究等提供了技术参考材料，构建了西藏地勘局与成都理工大学校地合作的人才培养机制，为西藏培养输送了大批地质人才。以西藏矿产资源潜力评价项目为载体，我校有1000多名学生得到了实际锻炼。</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">作为西藏矿产资源潜力评价的外协单位，我校被评为先进单位——专家对《1:100万西藏自治区地质图及说明书》高度肯定，全国矿产资源潜力评价总工程师叶天竺对依托西藏矿产资源潜力评价项目形成的“西藏模式”高度赞赏。</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">为了写出西藏矿产资源成矿地质背景测评报告，钟康惠有数个晚上，通宵达旦地待在办公室里，在灯光下仔细观察图片、分析资料。桌子上、地上、那堆积成山的资料是他们实地考察的结果，各种图纸中，岩石样本图片就有几万张，这些都要逐次分析记录总结。熬夜时，渴了就喝很浓的茶，困了就抽烟，有时候天快亮了，实在熬不住了，他不知不觉趴在桌子上就睡了。早晨学生们推开门，看见钟老师趴在桌上睡着了，旁边是一叠叠资料，电脑还开着，烟灰缸装满了烟头，知道他又熬了一个通宵。</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">多年来钟康惠的足迹遍及青藏与川滇，他先后主持了国家科技攻关、国家973、国家科技支撑、省部级等数十项科研项目，还指导了青海五龙沟金矿集区，四川天宝山、骑骡沟铅锌矿床，云南会泽铅锌矿床的构造成矿学与矿床学等研究，均取得了实质性成绩。如通过对青海省都兰县五龙沟地区金矿成矿规律研究及大比例尺成矿预测，经部分处于首期开发区的靶区勘查验证，探获金属量100多吨，潜在价值400多亿元人民币。通过云南会泽大水井找矿靶区的圈定和勘查，探获铅锌金属60万吨，潜在经济价值约184亿元。在与有关专家一道提出扬子西缘铜多金属矿床的构造成矿模式的指导下，里伍铜矿经过16年勘探，至2012年确认查明50万吨铜资源量，成为四川省第一大铜矿。通过这些实践逐渐形成了他的构造成矿学理论体系，叩开了地矿的阿里巴巴大门。</span></p><p style=\"text-align: center;line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\"></span></p><p style=\"text-align: center;line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\"><img alt=\"\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/141929561293732234.jpg\" width=\"500\" height=\"375\"/></span></p><p style=\"text-align: center;line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">钟康惠在鲁日矿区给学生们授课（右三为钟康惠）</span></p><p>&nbsp;</p><p><strong>关键字阅读</strong><span style=\"font-family: 宋体,Arial,sans-serif;padding-left:10px;font-size:14px\">地质 行者无疆 理工人物</span>·<a href=\"http://www.cdut.edu.cn/xww/news/null/news/152713307592887160.html\">沉积地质研究院#2018年博士研究生复试录取工作安排#</a> <span style=\"color: #C0C0C0\">2018-05-24</span><br/>·<a href=\"http://www.cdut.edu.cn/xww/news/152706053103825044.html\">数学地质四川省重点实验室2018年公众开放活动特邀专家报告</a> <span style=\"color: #C0C0C0\">2018-05-23</span><br/>·<a href=\"http://www.cdut.edu.cn/xww/news/152689088520093251.html\">第五届强震地质灾害及后效应国际学术大会暨纪念汶川地震十周年国际学术研讨会圆满闭幕</a> <span style=\"color: #C0C0C0\">2018-05-21</span><br/>·<a href=\"http://www.cdut.edu.cn/xww/news/152661295738612657.html\">中国自然资源报：中外专家研讨强震地质灾害及后效应</a> <span style=\"color: #C0C0C0\">2018-05-18</span><br/>·<a href=\"http://www.cdut.edu.cn/xww/news/152637322116768972.html\">数学地质四川省重点实验室关于举办2018年公众开放活动的通知</a> <span style=\"color: #C0C0C0\">2018-05-15</span></p><p><br/></p>', '2018-05-24 20:51', '成都理工大学网上党支部', '钟康惠:跋涉在雪域高原的寻宝者', null, '2', '168');
INSERT INTO `news` VALUES ('<p style=\"text-align: center;line-height: 200%\"><strong><span style=\"line-height: 200%;font-family: 黑体;font-size: 29px\">刘明哲：追“核”的人</span></strong></p><p style=\"line-height: 200%;text-indent: 32px\"><strong><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">人物名片：</span></strong><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">刘明哲，教授，核技术与自动化工程学院副院长，本科及硕士均毕业于我校，1997至2004年在中国工程物理研究院工作，2006年和2010年在新西兰梅西大学计算机科学专业分别获得一级荣誉硕士学位和计算机科学博士学位。2009年获得国家优秀自费留学生奖学金，2010年9月至今在我校工作，2012年入选四川省“百人计划”长期项目，2013年入选四川省学术和技术带头人后备人选，2014年受聘为第三届四川省专家评议（审）委员会委员。</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">近年来主要从事核信息获取与处理、辐射环境监测、复杂系统建模以及蒙特卡罗模拟等方面的研究工作，已发表SCI论文30余篇，出版英文学术专著1部，申请及授权发明专利9项；主持和参与了国家自然科学基金、科技部重大仪器专项、863项目、973项目、国际合作计划、省部级项目等20余项研究课题。作为受奖人之一获得四川省科技进步一等奖1项（2013年度），国土资源科技进步二等奖1项（2011年度）。</span></p><p style=\"text-align: center;line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\"></span></p><p style=\"text-align: center;line-height: 200%;text-indent: 32px\">&nbsp;</p><p style=\"text-align: center;line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\"><img alt=\"\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/141801178757811697.jpg\" width=\"500\" height=\"361\"/></span></p><p style=\"text-align: center;line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">（图中居中者为刘明哲）</span></p><p style=\"text-align: center;line-height: 200%\"><strong><span style=\"line-height: 200%;font-family: 黑体;font-size: 16px\">为梦想不倦不悔</span></strong></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">“为你，千千万万遍”，这句来自小说《追风筝的人》里的话，因其显露出的执着和热爱打动了无数人。而在现实生活中，有一群人，他们追着看似虚无缥缈的“核”，做着外人看来非常神秘的工作——核探测。即使核的特性是多么难以触碰和难以测量，但这群追核的人依然不倦不悔。计算机专业出身的刘明哲就是其中之一。</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">1990</span><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">年，高中毕业的刘明哲带着对新兴学科的好奇与憧憬进入我校计算机应用专业学习，本科毕业后在学业上有所追求的他选择在我校数学地质专业继续读研。从本科到研究生，同学们有人选择了利用计算机应用技术进行矿产勘查、有人当了一名程序员、有人进入银行工作……而刘明哲选择了去中国工程物理研究院追逐科技报国的梦想。在这个国家计划单列的核武器研制生产单位，刘明哲与“核”结下了不解之缘，在粒子输运的微尺度数值模拟方面开展了长期研究。</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">2005</span><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">年，已经工作7年的刘明哲为了开阔视野、增进学术，远赴新西兰梅西大学深造并获得了计算机科学专业一级荣誉硕士学位和博士学位。新西兰是一个无核国家，但对从事核信息处理的刘明哲来说两者并不矛盾，“这里的学者做学问非常踏实、纯粹，他们对待科学的严谨、热爱、单纯让我感受到了非常浓郁的学术氛围，潜心致学是一件非常快乐和愉悦的事情”。</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">2010</span><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">年，刘明哲博士毕业。此时的他面临两个选择：留在国外或是回国。“我的‘根’在中国，我的一切都在中国。在国内接受了这么多年的教育，在新西兰有了些研究成果，署名却都是那边的大学。是时候让研究成果署上中国科研院所的名字了。我们应该为国家的繁荣出力。”就这样，刘明哲义无反顾地回到了祖国，回到了他的大学母校——成都理工大学任教。</span></p><p style=\"text-align: center;line-height: 200%\"><strong><span style=\"line-height: 200%;font-family: 黑体;font-size: 16px\">辛勤的“园丁”</span></strong></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">回国后的刘明哲积极参与到对青年学生的培养工作中。2012年，他担任了2012级核工程与核技术本科二班班主任。青年强则国强，青年才是祖国的未来。担任班主任后，刘明哲在学习上给予青年学生鼓励、指导和帮助，告诉大家既要脚踏实地，立足当前，又要有一个5-10年的人生规划；在生活上对同学们给予关心和引领，每学期开始和结束都与同学们深入交谈，甚至自掏腰包奖励表现出色的同学。在刘明哲的鼓励指导下，该班级同学积极参加国际数学建模、全国电子竞赛中并都有获奖，英语四、六级过级率居学院同年级首位。</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">“崇尚学术、瞄准前沿”在本科毕业论文设计和研究生培养中得到了体现。在刘明哲指导的学生中，至今已有3名同学的本科毕业论文获得了校优秀论文，相关成果整理后已被SCI期刊接收。</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">刘明哲指导的第一届研究生已顺利毕业。两名学生赴国外继续深造，其中一名获得国家留学基金委的全额资助，即将去新西兰攻读博士学位，另一个目前已在瑞典查尔姆斯理工学院核聚变方向就读。</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">刘明哲认为，“教书育人、立德树人始终是一名人民教师最基本的职责。作为高校教师，不仅要做好科研，解决专业领域内的问题，还要肩负起传道授业解惑的重要使命。这也是高校与专门的科研单位最重要的区别之一。”</span></p><p style=\"text-align: center;line-height: 200%\"><strong><span style=\"line-height: 200%;font-family: 黑体;font-size: 16px\">科研之树开花结果</span></strong></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">经过刘明哲多年来孜孜不倦的努力付出，他当初科技报国的梦想已经开始结出茂盛的果实。</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">理解非平衡系统的稳态特征是非平衡系统理论研究的中心问题。在一个基本的范例下通过调研大量的数学模型来寻求非平衡系统的基本共同特征和涌现行为成为了研究非平衡系统的主要手段。刘明哲在该方向取得了突出的研究成果，与国外的研究基本保持同步。一部英文专著，两篇英文综述，多篇英文论文的发表使他成为该领域活跃的研究人员。美国、德国和印度的知名学者纷纷邀请他开展合作研究。</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">在核信息获取与处理方向，针对放射性测量中的不确定性问题，刘明哲提出了偏最小二乘加权神经网络的非线性校正方法，有效地解决了核地球物理勘探低本底检测的关键技术问题；提出的基于遗传算法的多目标优化算法已经用于中子反应装置中多重屏蔽材料厚度的组合优化；针对核废物桶核素分析扫描实际耗时较长，蒙特卡罗模拟效率也较低的情况，提出了改进的数值计算方法提高了核废物桶层析伽马扫描的效率，减小了误差，节约了时间；基于WebGIS的核辐射环境监测预警智能信息管理平台已经搭建完成。上述部分工作已经作为支撑材料获得了国土资源科技进步二等奖和四川省科技进步一等奖。</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">在中子探测中，伽马射线总是与中子相伴而生的，而且对中子探测灵敏的探测器对伽马射线也很灵敏，使得中子和伽马射线难以区分。因此，如何从中子和伽马混合场中挑选出中子信号、甄别掉伽马信号就成了快中子探测的关键问题。刘明哲等人研究中发现中子和伽马具有不同的分形频谱特征，根据这一发现提出了基于分形频谱的中子伽马甄别方法，与常用的电荷比较法和脉冲梯度法相比，优势明显。</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">如今，刘明哲又把目光投向了核安保与核应急领域。国内外多起暴恐事件的发生，把公共安全保障推到了风口浪尖。研究灵敏度高、机动性好的具有快速反应能力的核辐射探测技术与装备系统势在必行，具有极强的现实意义和需求。目前该团队正在自主研发新型的塑料闪烁体探测器，使其具备更好的中子、伽马分辨能力。进一步集成机器视觉，研发具备快速定位功能的移动伽马谱仪。</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">在科学事业上孜孜追求的刘明哲已经收获了许多，这些收获是他多年来不断追求、厚积薄发的结果。如同登山一般，一步一个脚印，每前进一步都是通向另一个辉煌的开始。</span></p><p><strong>关键字阅读</strong><span style=\"font-family: 宋体,Arial,sans-serif;padding-left:10px;font-size:14px\">理工人物</span></p><p><br/></p>', '2018-05-24 20:51', '成都理工大学网上党支部', '刘明哲：追“核”的人', null, '2', '169');
INSERT INTO `news` VALUES ('<p style=\"line-height: 200%\"><strong><span style=\"line-height: 200%;font-family: 黑体;font-size: 16px\">编者按</span></strong></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"font-family: 仿宋_GB2312\"><span style=\"line-height: 200%;font-size: 16px\">在茫茫的高原深山，有这样一群人，他们迎着朝阳出发, 背起馒头爬山, 挑着仪器下山, 踏着晚霞凯旋, 头顶苍穹、脚踏荒原,战严寒、斗酷暑。他们就是我们身边平凡而又伟大的地质工作者。</span></span><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\"></span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"font-family: 仿宋_GB2312\"><span style=\"line-height: 200%;font-size: 16px\">为了进一步贯彻党的十八大精神，践行社会主义核心价值观，弘扬地矿行业“三光荣”（以地质事业为荣、以艰苦奋斗为荣、以找矿立功为荣）和“四特别”（特别能吃苦、特别能忍耐、特别能战斗、特别能奉献）精神，向杨衍忠同志为代表的地质工作者学习致敬，【深度】将陆续推出“地质•行者无疆“系列报道，让我们一起走进理工地质工作者，讲述他们与地质的故事。</span></span><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\"></span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"font-family: 仿宋_GB2312\"><span style=\"line-height: 200%;font-size: 16px\">本次【深度】聚焦朱利东教授。迄今，他从事西藏地质研究20年，为地质事业兢兢业业，不忘初心，仍将继续前行。</span>&nbsp;</span></p><p style=\"text-align: center;line-height: 200%\"><strong><span style=\"line-height: 200%;font-family: 黑体;font-size: 29px\">朱利东：在青藏高原谱写20载地质之歌</span></strong></p><p style=\"line-height: 200%;text-indent: 32px\"><strong><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">人物名片：</span></strong><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">朱利东，49岁，教授，我校地质调查研究院副院长，博士生导师，国土资源部派驻武警黄金部队区调专家。主要从事青藏高原地区的地层与沉积学方面研究。近年承担本科《古生物地层学》和研究生《古生物学新进展》等课程教学，主持中国地调局“西藏改则东4幅1:5万区调”等研究项目。在中文核心期刊发表论文100余篇，刊出外文期刊论文11篇。获国土资源部科技成果二等奖，成都理工大学科研成果一、二等奖3项。</span><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\"></span></p><p style=\"text-align: center;line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\"><img alt=\"\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/141819555262560230.jpg\" width=\"400\" height=\"513\"/></span></p><p style=\"text-align: center;line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">　　　　　　　　　　　　　　　　　　　　朱利东近照</span></span></p><p style=\"text-align: center;line-height: 200%;text-indent: 32px\">&nbsp;</p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">“我还想再干十年，还想跑十年的高原。”49岁的朱利东，目光深邃，语气坚定。至今，他从事西藏地质研究已整整20年。朝看水东流,暮看日西沉 。20年弹指一挥间，他为何选择结缘地质，且仍然坚守前行？又与地质有哪些不得不说的故事呢？</span><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\"></span></p><p style=\"text-align: center;line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\"><img alt=\"\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/141819567493722237.jpg\" width=\"500\" height=\"243\"/></span></p><p style=\"text-align: center;line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">　　　　　　　　　　　　西藏双湖三师一十二团合影（右四为朱利东）</span></p><p style=\"text-align: center;line-height: 200%;text-indent: 32px\">&nbsp;</p><p style=\"text-align: center;line-height: 200%\"><strong><span style=\"line-height: 200%;font-family: 黑体;font-size: 16px\">选择地质向外闯，从东北到西南</span></strong></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">一个生长在松嫩平原的男儿伫立于祖国大地的东北方，面临人生重大选择时，因想闯，选择了地质。因敢闯，选择了青藏高原。</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">“我渴望看到外面的世界，年轻时就想往外闯荡”，朱利东谈及与地质结缘时说到。“那时父母希望我考到交通铁路类的大学念书，因为父亲与姐姐都在家乡黑龙江齐齐哈尔的铁路车辆系统工作，以后好接他们的班。但我一直向往地质人的野外豪情，渴望了解地质方面的知识。”</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">1983</span><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">年，18岁的朱利东站在人生的十字路口，带着那颗年轻想闯荡的心、对地质知识的渴求欲来到武汉地质学院（现中国地质大学）学习，先后获得古生物学与地层学专业本、硕士学位。在这段求学路上，朱利东刻苦钻研，拥有极大的热情与拼劲。硕士导师丁梅华教授指导他开展早三叠世牙形石地层的研究时，他为了提交高质量论文成果，独自一人去湖北大冶做地质调查。白天，他一个人上山，完成剖面踏勘、测量、记录和样品采集工作，晚上住在煤矿没有窗子、只能放下一张床的土坯房。“每个牙形石样品重达3公斤，整个剖面需要2、3百件样品，一个人每天就是拉导线、记录和采样，这样的工作一做就是两个月”，朱利东忆起那段岁月时说到。</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">积累了扎实的理论知识与丰富的野外实践经验，朱利东想往更远的地方闯一闯。1992年，他来到成都理工大学任教，刚放下背包，就和刘登忠等老师跑川北、三江一带做区域地质调研，开始了他的区调生涯。“地质人总是不断地行走，做完一个项目又有另一个项目，哪里需要我就往哪走”，1995年，朱利东第一次开始了在西藏的地质调查工作，去到了藏北无人区——羌塘，参与了学校承担的中石油羌塘盆地石油地质调查项目。由于羌塘盆地是我国勘探程度最低的海相中生代盆地，且工作环境很有限，朱利东等人的工作主要是露头尺度的石油地质调查，第一次在羌塘盆地做较为系统的油气地质调查，获取了丰富的一手资料，为之后的油气地质工作奠定了基础。</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">“青藏高原在地学界的关注度极高，有很多地学的热点问题，但由于环境艰苦，当时后勤保障也不完善，能在那里开展野外工作很难。”朱利东谈及选择西藏时真切的说到，“1998年，我来到成都理工大学读博师从王成善教授。王老师在沉积学与青藏高原隆升等研究领域卓有建树，他传授给我的地学知识和对西藏地质研究的热衷感染了我，他正是我走上西藏地质研究之路的引路人！”</span></p><p style=\"text-align: center;line-height: 200%\"><strong><span style=\"line-height: 200%;font-family: 黑体;font-size: 16px\">20</span></strong><strong><span style=\"line-height: 200%;font-family: 黑体;font-size: 16px\">年西藏地质研究，6进无人区</span></strong></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">“那一世，我转山转水转佛塔，不为轮回，只为途中与你相见”，仓央嘉措在西藏谱写了这动人情歌，只为心中的玛吉阿米。而朱利东20年间从事西藏的地质研究，几近把整个青藏高原都跑遍。从喜马拉雅山到祁连山，从三江到阿里……也曾转山转水转高原，不为名利，只为地质事业而奋斗。</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">1995</span><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">年，朱利东第一次入藏，那年刚入藏就遇上了三次惊险的遭遇。“有一次令我印象最深，因为一些队员还因此落下了病，腿还经常疼”，他回忆那是在羌北的白龙冰河无人区，已经离开驻地两天的三台车都没油了，只能走回驻地寻求救援。夏季的白龙冰河也有近一公里的宽度，河面有厚达5米的冰盖，绕行行不通，“完全可以在冰面上开坦克了，在东北老家可是见怪不怪的”，在冰寒地冻间体力不断消耗，本以为是条暗流河，可直达对岸，然而谁知，行至河中间涌现出湍急的明流，明流两岸形成三米多高的冰雪陡崖，要回到基地就必须穿过这条河。朱利东二话未说，和队员一起脱下军大衣铺在冰崖边溜到河谷，寒风凛冽，河面冰块漂浮，浸泡在冰河里的双腿已麻木，但他和队员们仍在河里咬牙坚持，走了两个多小时才到对岸。谈及此事时，胆战心惊的画面历历在目，但朱利东声音依旧低沉，又语：“平常心对待就好，搞地质工作总会遇到这种事。”</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">回望过去的种种经历，朱利东认为在西藏无人区的地质工作需要个人英雄主义，但更需要团队协作精神。当时的救援大多都是队伍内部的自救，由于环境与通信的限制，根本无法依靠外部力量，团队的组织和安全管理是野外地质考察最重要的前提。“我希望我的团队在面对困难的时候永远不要放弃，因为身后有你的战友，我们是一个团队！”</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">在西藏出野外地调考察时，朱利东是个敢闯敢拼，乐观豁达的地质人。在做科研项目的研究报告时，朱利东又是个锲而不舍，严谨谦虚的科研人。</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">科研无止境，朱利东在西藏主要研究基础地质与油气地质两个方面。他从事西藏地质研究20年，曾在班公湖—怒江结合洞错发现榴辉岩，在青藏高原从事新生代盆记录与高原隆升，参与的科研项目《兰坪盆地演化及其成矿条件研究》项目获得国土资源部科技成果二等奖&nbsp; ……“对于地质人来说，每一项研究成果都是团队合作的结果。没有团队的同心协力，是无法做好科研的！”朱利东坚定地说到。</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">对于做地质科研项目，朱利东认为这是一个循序渐进，不断探索发现的过程，“我们都是站在前人的肩膀上，从前人的认识出发，通过思考和工作提出新的问题，再在调查研究中解决问题和提出新的认识。”</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">可可西里无人区是世界第三大，也是中国最大的一片无人区，这里地势高峻，平均海拔高度在5000米以上，气候寒冷，常年大风，空气稀薄。朱利东六次进入这片“生命的禁区”进行地质考察，勇者无畏。</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">“在无人区即使环境气候再恶劣，队员们都会勇敢面对。即使是黑熊豺狼也已习惯。丢鞭炮、吹哨子，就能把猛兽给吓跑。但与野牦牛相遇时，就要和它来场生命的追逐了。失群的野牦牛十分凶猛，大的野牦牛能把车子给顶翻，当遇到它时，我们只能掉头就跑，那里的野牛跑地比车还快，就是转弯没有车子灵活，我们必须在坎坷的路况下加大马力，接近了就急转弯，这样才能摆脱了独牛。”朱利东讲述这场“勇敢者的游戏”时一点也不后怕。</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">陷车、挖车、迷路、受困、高寒、缺氧、体力透支……这些都只是朱利东地质研究路上的小插曲，“进去了就要相信一定能出的来”，他以这样的信念鼓舞着自己和队员前行。在他心中，6进可可西里早已风清云淡，于他眼中，是翻不完的岩层经卷，望不尽的雪峰塔尖。</span></p><p style=\"text-align: center;line-height: 200%\"><strong><span style=\"line-height: 200%;font-family: 黑体;font-size: 16px\">携热爱与责任，愿为地质再干20年</span></strong></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">在西藏，湛蓝的苍穹与圣洁的神域雪山，丰富的矿产油气资源与多样的地形地貌等无一不牵动着一位地质人的心。“在这里，心灵会获得一方净土，没有城市的喧嚣，没有任何的杂念。” 朱利东几乎每年都要在西藏待3-4个月，最长待上了6个月。他对西藏这片圣土爱得深沉。</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">“朱老师特别喜欢西藏，哪年没去西藏，就会特别难受，心里会一直挂念着那个地方。”朱利东的学生这样说道。在出野外时,朱利东常和学生深夜聊天，谈其他问题他无多大兴趣,但只要一说起西藏,他眼睛就发亮，滔滔不绝地讲与西藏有关的一切，一讲就是3、4个小时。忙完一天的工作大家都精疲力尽了，而他却精力充沛，第二天也依然8点起来刷牙洗脸，继续新一天的工作。</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">奔赴西藏，朱利东把这当做地质人的交流会，老朋友的相聚会。在那个离太阳最近星星最亮的地方，与一群铁血的地质人饮酒“吹牛”是他认为最惬意的事，“我们吹牛就是谈些彼此近期地质工作的进展和发现，交流的过程会刺激我更多的探求欲，给予我更多向前的动力。”</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">朱利东热爱着西藏，热爱着地质。同时，他承担着将前辈地质人的精神和研究成果传承、传授给同样热爱地质的学生的责任，担负着为国家地质事业不断探索不断研究的重任。即将面临知天命之年，他愿携热爱与责任，为地质再奋斗20年。于未来，他想好了，“在未来的十年继续带着自己的团队在西藏开展野外工作，之后的十年帮助年轻人在西藏开展地质工作。”</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">从事地质研究工作20载，谈收获时，朱利东说：“能把一些同样热爱地质的学生教育好，能和其他地质人交流并共同为地质事业奋斗是最无价的收获。”</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">头顶苍穹，脚踏荒原 ，不畏高寒，哪管险阻，兢兢业业，刻苦钻研。在平凡中中蕴含着伟大，于伟大中渗透着平凡。朱利东愿在“离太阳最近”的地方，谱写自己的地质之歌。</span></p><p style=\"text-align: center;line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\"><img alt=\"\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/141819578671878008.jpg\" width=\"500\" height=\"332\"/></span></p><p style=\"text-align: center;line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">　　　　　　　　　　　　　　　　　　　　　　　　　　　野外考察</span></p><p style=\"text-align: center;line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\"></span></span></p><p>&nbsp;<strong><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">【学生心语】</span></strong>&nbsp;</p><p style=\"text-align: left;line-height: 200%;text-indent: 32px\"><strong><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">博士研究生陶刚：</span></strong><span style=\"font-family: 仿宋_GB2312\"><span style=\"line-height: 200%;font-size: 16px\">二十年的青藏高原地质研究经历使他成为圈内的专家，他丰富的经验、吃苦耐劳、坚定不移的精神深深影响朱家山团队中的每一人。他礼貌谦逊、以他人利益为先的优质品质深深感染着朱家山团队的每一人。</span></span></p><p style=\"text-align: left;line-height: 200%;text-indent: 32px\"><strong><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">硕士研究生李杜文：</span></strong><span style=\"font-family: 仿宋_GB2312\"><span style=\"line-height: 200%;font-size: 16px\">人生有多少20年，然而朱老师将他的青春和岁月投入到了青藏高原地质研究当中！20年的青藏经历让我们惊叹，他那无畏的探索精神和渊博的知识、经验带领我们走向更美好的明天。</span></span><span style=\"line-height: 200%;font-family: 仿宋_GB2312;font-size: 16px\"></span></p><p style=\"text-align: left;line-height: 200%;text-indent: 32px\"><strong><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">硕士研究生李超：</span></strong><span style=\"font-family: 仿宋_GB2312\"><span style=\"line-height: 200%;font-size: 16px\">也许在二十年前踏入青藏高原的那一刻起，他已经决定将自己的一生奉献给祖国伟大的地质事业。面对着极其艰苦的高原工作条件，他没有半步退缩，取得了大量的科研成果，还培养了一批一批的地质人才。有导师如斯，何其幸哉。</span><span style=\"line-height: 200%;font-size: 16px\">&nbsp;</span></span><span style=\"font-family: 仿宋_GB2312\"><span style=\"line-height: 200%\"><span style=\"font-size:16px\">&nbsp;</span></span></span><span style=\"font-family: 仿宋_GB2312\">&nbsp;</span></p><p>&nbsp;</p><p style=\"line-height: 200%\"><strong><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">【记者手记】</span></strong></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"font-family: 仿宋_GB2312\"><span style=\"line-height: 200%;font-size: 16px\">说起“地质”，我想起了艾青 《鱼化石》诗：“过了多少亿年，地质勘察队员，在岩层里发现你，依然栩栩如生。” 谈及“西藏”，我眼前浮现的是纯净湛蓝的天空、雄奇壮美的神山圣湖……而采访完朱利东后，我对这两者有了更多的认识。</span></span><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\"></span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"font-family: 仿宋_GB2312\"><span style=\"line-height: 200%;font-size: 16px\">“地质”、“西藏”这是我还未采访朱利东时得知的两个关键词。他是我校地质研究方面的专家，他经常去西藏做地质调研。获取这样简单的信息，我该如何去采访他呢？他是个怎样的人呢？而对于地质方面我更是门外汉，怎样才能与一位地质队员对话呢？但当我走进他，采访他，种种疑问与忐忑已渐渐消散了。</span></span><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\"></span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"font-family: 仿宋_GB2312\"><span style=\"line-height: 200%;font-size: 16px\">为了深入了解朱利东，为正式采访他做好准备，我提前和他教过的几个学生进行交谈。“朱老师就是我们的大哥，他带我们去西藏做区域调研，遇到任何困难，他都鼓励我们，给我们力量”，“朱老师为人十分低调，他从不追名逐利，他对地质是真的非常热爱”，“朱老师特别喜欢西藏，哪年没去心里就难受”……听了种种学生们的话语，我陷入无限的向往和憧憬，是怎样的一位老师，一位地质队员使他的学生们如此的敬佩他、赞美他、爱他？我多想早日见到他，听他诉说他与地质，与西藏的故事。那一定很精彩吧。</span></span><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\"></span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"font-family: 仿宋_GB2312\"><span style=\"line-height: 200%;font-size: 16px\">11月的一个午后，我带着之前的疑问与他展开了交流。当我以为朱利东一定有很多地质工作中艰辛的故事与我分享时，“做地质工作就这样，也没什么艰辛，没什么好说的”他这样一句话使我些许惊讶。难道去缺氧的青藏高原，去环境艰苦的无人区真的没什么好说的吗？我又带着好奇心渴求他与我分享些许故事，他才回忆起慢慢与我道来。听着那些胆战心惊的遭遇我心里都在颤栗，但他却很平静，那一刻，他给我的印象，就像一位淡看云卷云舒、心中无波澜的长者。</span></span><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\"></span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"font-family: 仿宋_GB2312\"><span style=\"line-height: 200%;font-size: 16px\">谈及6次进入无人区，是我无法想象的。曾看过电影《可可西里》，那里恶劣的环境，高寒的气候，豺狼野兽……这些对于生命都是巨大的威胁，但却不知这样一位地质人为何6次进入无人区，如此的无畏？采访中我明白了，是那丰富的矿产油气资源与多样的地形地貌，是为地质事业不断探索不断奋斗的决心与勇气。进入无人区就意味着用生命在下一场赌注，但朱利东坐在我面前平淡的讲述，似乎并无任何惊险，其实不然，是信念在支撑着他走进去，又能走出来。正如他说的：进去了就要相信一定能出的来。</span></span><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\"></span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"font-family: 仿宋_GB2312\"><span style=\"line-height: 200%;font-size: 16px\">“你在地质研究方面有哪些成就呢？”当我拿着他的简历，注视着他获得的奖项，希望他能细谈获奖的项目时，他却一直强调做地质研究需要团队的合作，需要不断的探索，成就谈不上。即使有一点成绩也是团队合作的结果。</span></span><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\"></span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"font-family: 仿宋_GB2312\"><span style=\"line-height: 200%;font-size: 16px\">采访中，我还了解到一个有趣的事。朱利东与他学生们有一个共同的家，叫“朱家山”。这是由朱利东和他的学生们组成的小团队，大概20多个人，这些人都是朱利东带的硕博士生，“朱家山”经常一起出野外，一起做地质研究。朱利东对学生十分照顾，学生们也十分爱戴朱利东，朱家山的人都说那是一个温暖的家，朱利东就是大家长。</span></span><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\"></span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"font-family: 仿宋_GB2312\"><span style=\"line-height: 200%;font-size: 16px\">20年从事西藏地质研究，其中的险阻与坚持，艰辛与收获，太多太多，我都无法用一篇报道来讲述。我只能作为一个记录者，也只能用拙笔记录朱利东这20年的只影片刻，往事二三。20年的岁月悠悠，沧海桑田、斗转星移，朱利东依旧坚守。下一个20年，他还将与地质同行，我心怀敬佩，献上祝福。</span>&nbsp;</span></p><p>&nbsp;</p><p>&nbsp;</p><p><strong>关键字阅读</strong><span style=\"font-family: 宋体,Arial,sans-serif;padding-left:10px;font-size:14px\">理工人物 朱利东</span>·<a href=\"http://www.cdut.edu.cn/xww/news/141819602818742201.html\">【深度】朱利东：在青藏高原谱写20载地质之歌</a> <span style=\"color: #C0C0C0\">2014-12-10</span><br/>·<a href=\"http://www.cdut.edu.cn/xww/news/null/news/1922.html\">朱利东同志申报高级专业技术职务人员个人业绩贡献公示一览表</a> <span style=\"color: #C0C0C0\">2005-09-30</span><br/>·<a href=\"http://www.cdut.edu.cn/xww/news/null\">朱利东同志申报高级专业技术职务人员个人业绩贡献公示一览表</a> <span style=\"color: #C0C0C0\">2005-09-30</span></p><p><br/></p>', '2018-05-24 20:52', '成都理工大学网上党支部', '朱利东：在青藏高原谱写20载地质之歌', null, '2', '170');
INSERT INTO `news` VALUES ('<p style=\"text-align: center;line-height: 200%\"><strong><span style=\"line-height: 200%;font-family: 黑体;font-size: 29px\">“80后”的执着科研路</span></strong></p><p style=\"line-height: 200%;text-indent: 32px\"><strong><span style=\"line-height: 200%;font-family: 黑体;font-size: 16px\">编者按：</span></strong><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">日前，我校地球科学学院“80后”教师于欢的第一地址论文《情景分析法和多智能体技术在土地利用规划中的应用：以三江湿地为例》被SSCI收录，弥补了我校在SSCI源刊上发表论文的空白。SSCI由美国科学信息研究所创建，是SCIE的姊妹篇，目前在世界上可对不同国家和地区的社会科学论文进行统计分析的大型检索工具。</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">科研路任重道远，于欢在这条路上坚持自己，探索未知。他对科学的信仰，逐渐从怀中梦想渗透成日常中最普通又无法或缺的一部分。对他来说，所写论文被SSCI收录，是对自己执著科研的肯定，更是一种鼓励和动力，也是一种对话科学的回应。</span></p><p style=\"line-height: 200%;text-indent: 32px\"><strong><span style=\"line-height: 200%;font-family: 黑体;font-size: 16px\">人物名片：</span></strong><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">于欢，副教授，博士后，1981年6月出生，辽宁省沈阳市人，国际数字地球协会数字山地专业委员会委员，中国地理学会会员，中国图象图形学会会员。2010年毕业于中国科学院东北地理与农业生态研究所地图学与地理信息系统专业，获理学博士学位。主要从事景观地球化学、生态环境遥感、地表景观时空演化智能模拟等方面的研究工作。</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\"></span></p><p style=\"text-align: center;line-height: 200%;text-indent: 32px\"><br/></p><p style=\"text-align: center;line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\"><img alt=\"\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/141619224476566343.jpg\" width=\"500\" height=\"331\"/></span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">在一间十来平米的工作室内，记者见到了于欢，他正和自己的科研团队忙碌着。安静的环境里，只能听见啪啪的键盘敲击声和偶尔轻轻的讨论声，团队成员不经意的一个哈欠透露出了大家的疲惫。“昨晚他们又在这里熬了个通宵。”看到记者的到来，于欢笑笑说道。</span></p><p style=\"text-align: center;line-height: 200%\"><strong><span style=\"line-height: 200%;font-family: 黑体;font-size: 16px\">科研：不求功名利禄，只为让世界变得更美好</span></strong></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">学生时代的于欢就很喜欢思考、钻研，所以显得比同龄的孩子深沉和成熟一些。高二的时候他就开始思考自己未来的发展方向，“中学的时候，我最喜欢的科目是数学，尤其喜欢研究那些比较有难度的题目。数学很有趣，很能锻炼人的思维。”抱着对研究的兴趣，于欢走上了科学研究的道路。</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">2010</span><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">年，于欢毕业于中国科学院地图学与地理信息系统专业并获理学博士学位。他选择了到成都理工大学工作，从祖国的东北到西南，于欢放弃了很多机会，也克服了很多困难，但是他无怨无悔。“成都理工大学是一个在地质科研上走在前沿的地方，它的一些研究特色很吸引我，对我来说是一个很好的学习与发展平台。” &nbsp;来到理工的短短四年间，于欢没有停下学习的步伐，他不仅跟随倪师军教授继续博士后的学习，还主持了包括国家自然科学基金、中国博士后科学基金等10余项国家和省部级项目，并且拥有两项国家专利，以第一作者发表在核心期刊的论文就多达30余篇。</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">谈及被SSCI收录的论文，于欢显得坦然而平静。在读博期间，他便有了研究关于“情景分析法和多智能体技术在土地利用中的应用”这个想法。工作之后，经过反复的调研和升华，并结合我校的实际情况，于欢在多智能体模拟与情景分析方法的理论基础上，提出了两种方法相结合用于地表景观模拟与规划的总体框架，并以三江平原典型内陆淡水湿地区为实验对象，检验了模拟与规划工作的实际效果。结果表明：多智能体模拟与情景分析方法相结合切实可行，强调定量过程的多智能体模拟技术有效弥补了情景分析方法过于强调定性过程的不足，实现了景观规划工作中定性与定量的有机结合，为地表景观时空演化模拟及规划研究提供了一种新的技术思路与方法。</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">&nbsp;</span><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">他所研究的“土地利用规划”属于自然学科与社会科学交叉的学科，目前也是国外很多科研工作者比较热门研究的领域。在于欢看来，一个科研工作者的目的不是获得荣誉和奖励，而是在不断地发现与解决问题中让世界变得更美好。“科研之路是永无止境的，我可以通过这样一个平台，让学校被更多世界科研人知道，让我与国内外更多科研牛人有了更多的交流与学习的机会，使我在科研之路上越走越远。”</span></p><p style=\"text-align: center;line-height: 200%\"><strong><span style=\"line-height: 200%;font-family: 黑体;font-size: 16px\">教学：打破常规思维，培养学生的求知欲望</span></strong></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">繁重的科研任务并没有影响于欢的教学工作。对于教学，于欢有着自己独到的见解，他认为研究和教学是相辅相成的，讲课的很多内容都来源于科研。于欢不喜欢照本宣科，他常常把在科研当中遇到的有趣问题和学生一起分享和交流。在每次上课之前，于欢会把要讲给学生的知识烂熟于心并且把学生不容易理解的问题分析透彻，然后在课堂上生动地传授给学生。</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">“世界是没有颜色的，颜色是由光的折射与反射现象产生的。”这是于欢常在课堂上给学生提出的问题。他总是在打破学生的常有思维之后，引导他们建立一种全新的思维，培养学生的好奇心，激发他们的学习的自主性，同时也让他们明白了知识的重要性。于欢特别提倡让学生独立思考的教育理念，他认为很多老师常常是扶着学生走，而忽略了让学生自己去尝试，所以在他的教学中，培养学生的自主独立性显得尤为重要。学生在专业上或者研究中遇到问题时，于欢从来不会直接告诉学生结果，而是以讨论的形式了解学生的观点，然后通过引导让学生找到答案。</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">于欢每天早上6点就起床，一直工作到第二天凌晨两点，周末几乎不休息。很多人不能理解他夜以继日地艰辛工作是如何坚持下来的，他只是说：“时间就像海绵里的水，挤挤总会有的，科研人的体质都很好，精力也很旺盛。”其实，他是在中国科学院的博士学习期间，受那些老科学家的影响，渐渐养成了晚睡早起的习惯。</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">于欢对自己的学生要求很严格：“我带的本科生，我都要求他们在本科期间就要在核心期刊上发表至少一篇文章，因为只有你做出了成绩，别人才会认可你。”于欢用自己的教育模式，培养出很多优秀的学生，有的保送进了名牌大学继续深造，有的在自己的工作领域里取得了很好的成绩。</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">于欢觉得为人师表很重要，当学生看到老师都如此勤奋，自己当然没有了偷懒的理由。所以他总是在教学中不断摸索，为学生树立榜样。“全国高校GIS青年讲课竞赛三等奖、”“全国大学生GIS应用技能大赛特等奖优秀指导老师”等教学荣誉对于他来说都是通过努力得到的肯定。</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">作为两个班的班主任，于欢不仅常常给学生开班会，还经常深入寝室关心大家的生活。专业上的引导，生活中的关心，无微不至的关怀让于欢深受学生的喜爱。学贵为师，亦贵得友。于欢用严谨的教学态度和温暖的生活关爱赢得了学生的尊重，成为学生的良师益友。</span></p><p style=\"text-align: center;line-height: 200%\"><strong><span style=\"line-height: 200%;font-family: 黑体;font-size: 16px\">生活：做好自己的事情，沿着科研之路一直前行</span></strong></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">科研和教学占据了于欢大部分的生活，但是他并不觉得辛苦，“科研就像生命一样存在于我的身体里，一年365天，几乎天天都在想科研的事儿，没有了科研，总觉得心里空落落的，有了科研，才能感觉到生活的乐趣。除了工作时间在学校上课以外，周六周日也几乎会到实验室里坐坐，与学生交流，与伙伴交流。所以说，科研人都是‘闲’不住的。”</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">在工作中于欢是一个“较真”的人，有时为了一个小数点他都可以“较真”到深夜。但是生活中的他特别不会较真——因为这样会有更多属于自己的时间去从事科研工作，好的心情也能让他更好的投入工作，所以于欢会非常巧妙地处理好各种人际关系。“常在电视上看见有一些人说工作太忙对不起家庭，其实我也觉得自己挺亏欠家庭的，为了工作，家里的事操心太少。”于欢说到这里充满了愧疚之情。</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">在工作之余于欢唯一的兴趣爱好就是看一些既有科教意义，又节约时间的英文原版短剧，比如《老友记》、《国家地理》等，谈及为何一定要看英文原版的短剧时，他认为作为一个科研工作者，英文上的学习是不能停止的，看短剧的同时还能了解外国人的思考方式，这些对于科学研究都是有帮助的。</span></p><p><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">谈到自己最崇拜的人，于欢说：“我崇拜的不是一个人，而是一群人。这群人从年轻到白发，还坚持在科研的岗位上。我在中国科学院读书的时候，看见有的院士在去世的前几天还在教室给学生上课，身怀重病仍然坚持在实验室，他们那种精神一直让我特别感动和崇敬。我就想成为这样的一个人，做好自己的事，沿着科研之路一直前行。”</span></p><p><br/></p>', '2018-05-24 20:52', '成都理工大学网上党支部', '“80后”的执着科研路', null, '2', '171');
INSERT INTO `news` VALUES ('<p style=\"text-align: center;line-height: 200%\"><strong><span style=\"line-height: 200%;font-family: 黑体;font-size: 29px\">梁春涛：“喜欢”地震的专家</span></strong></p><p style=\"text-align: center;line-height: 200%\"><strong><span style=\"line-height: 200%;font-family: 黑体;font-size: 29px\"><img alt=\"\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/141568952174780335.jpg\" width=\"400\" height=\"264\"/></span></strong></p><p style=\"line-height: 200%;text-indent: 32px\"><strong><span style=\"line-height: 200%;font-family: 黑体;font-size: 16px\">人物简介：</span></strong><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">梁春涛，地球物理系主任，本科与硕士毕业于成都理工大学，获美国孟菲斯大学博士学位。毕业后先后在在美国MicroSeismic 公司和GeoTomo LLC 公司任高级地球物理研究员。2012年入选四川省“百人计划”并回国任教。2012年2月至今，任成都理工大学研究员。主要从事地震学的研究和教学。</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">目前作为第一发明人申请美国专利两项。以第一作者发表英文文章7篇，中文文章2篇。以第二作者发表英文文章1篇。获国土资源二等奖1项。</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">2012</span><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">年，对于梁春涛来说是人生的又一个重要转折点。在这一年，他回到了阔别11年的母校——成都理工大学。但是这一次，他是从海外学成归来，入选四川省“百人计划”，作为高层次人才引进母校。</span></p><p style=\"text-align: center;line-height: 200%;text-indent: 32px\"><strong><span style=\"line-height: 200%;font-family: 黑体;font-size: 16px\">心系祖国，心系教育</span></strong></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">1992</span><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">年9月，作为一名满怀憧憬的大一新生，梁春涛第一次踏进成都理工大学（当时为成都地质学院），并在这里完成了他本科和硕士研究生学业。</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">2001</span><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">年，硕士毕业的梁春涛为了自己的专业梦想选择去美国继续深造。博士毕业后，他去了美国一家石油公司工作。然而，国外优良的硬件环境并不足以使梁春涛下定决心留在美国。</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">“</span><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">美国自然环境优美，大学学术研究氛围自由而浓厚，学术交流频繁，在那里完成博士学业令我收获很多。工作后，我发现在石油公司的工作并不是我心中向往的职业。我的专业兴趣是在地震学。我希望能有机会和平台继续从事自己的学科研究。此外，我一直对‘教师’职业心存敬仰，认为教书育人是一个充满奉献而很有价值的事业。所以，我很愿意来高校任教。”</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">在记者问到为什么选择到成都理工大学工作时，梁春涛回答：“虽然去美国多年，但是我一直没有忘记自己在理工读书的那段岁月。是理工大学老师们的培养奠定了我的专业基础。回到理工，我觉得这里的一切都很熟悉，充满亲切感。同时，成都理工大学距离龙门山断裂带比较近，研究地震有得天独厚的优势，地球物理学科的研究在全国也排在前列，我认为发展的平台很好。”</span></p><p style=\"text-align: center;line-height: 200%;text-indent: 32px\"><strong><span style=\"line-height: 200%;font-family: 黑体;font-size: 16px\">行走在科学前沿</span></strong></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">梁春涛的研究兴趣一直围绕着两个“地震”，一是如“汶川地震”、“芦山地震”那样的天然地震；二是“微地震”。何谓“微地震”？梁春涛解释到，“这是由油气开发中注水压裂诱发的地震活动，通常定义为在开采油气管道附近的岩体内因应力场变化导致岩石破坏而引起的那些地震事件。”</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">回国不到两年，梁春涛主持申报了3项纵向课题。其中，国家自然科学基金应急项目1项，《芦山地震后的微地震监测》；国家自然科学基金面上项目1项，《波形梯度法研究》；2013优秀留学人员重点项目一项，《页岩气油气田微地震监测》。此外，还有若干横向项目。</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">以地震学研究为中心，梁春涛逐步建立起以地震震源机制研究、龙门山断裂带构造研究、微地震研究为主要研究方向的地震学实验室。在此基础上同川庆钻探工程有限公司合作开发的微地震监测软件已经正式对外发布，投入到生产实践中，为企业节省了大量的经济成本和时间成本。该研究打破了国际公司在该领域的长期垄断。他介绍到，页岩气被致密的页岩层包裹，开采中通常会采用人工注水的方式导致页岩层产生人工裂缝，而人工裂缝的产生过程中会有微地震产生，通过监测微地震可以观测到裂缝的发育情况，从而有助于页岩气的开采。</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">梁春涛积极推进学术交流。2013年12月，他作为召集人和主持人在美国旧金山召开的该年度美国地球物理年会上发起了“汶川地震研究回顾”专题。日前，他基本完成了汶川地震科研信息平台（www.wceq.org）建设。该网站是信息化时代地球科学科研信息化的一个尝试，目前已经得到相关行业的广泛关注。</span></p><p style=\"text-align: center;line-height: 200%;text-indent: 32px\"><strong><span style=\"line-height: 200%;font-family: 黑体;font-size: 16px\">三尺讲台——心中的神圣净土</span></strong></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">“</span><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">几经周折，终于就要回国，接过老爸的衣钵，成为一个教书匠了。有点激动，也有点紧张。激动的原因自不必说。而担心的就是这传道、授业和解惑的工作有想象的那么轻松吗？”</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">2011</span><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">年12月17日</span><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">，梁春涛还在美国，他在科学网的博客上写下上面这段文字，并在其后的博文中，尽他所能把有关教育和治学方面的论述从《论语》中归纳出来加以适当点评。</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">梁春涛很注重对学生学术视野的培养。每周二的下午，他都会按照惯例与3名学生在他的办公室举行小范围的学术交流。同学们轮流组织，每周都要找一篇自身研究领域具有代表性的论文进行讨论。梁春涛希望，“能促进他们阅读最新文献，把握科学前沿；此外，借此培养他们演讲的水平。”他认为应当为研究生提供比较好的学术交流平台。去年，他从申请的科研经费中专门划拨一笔资金支持同学们出去参加学术研讨会。</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">梁春涛很重视同学们的英文水平，他认为做学术要有国际化的视野，英语就要过关，要习惯阅读英文文献，要提高英文的听力和口语水平。为此，他给同学们布置了大量的英文文献阅读，甚至要求他们每周用英文写工作总结。</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">在梁春涛的严格要求下，预计到今年年底，他的每一名学生都会发表一篇SCI 论文。“按照我的培养方式，我认为他们通过两三年的学习，发表一篇SCI 是水到渠成的事情。”</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">他的学生杨宜海告诉记者：“梁老师比较平易近人，常常主动关心我们的生活。在专业上，他的要求也非常严格。一开始，我们都有点不太适应。但是经过一段时间的学习，我们觉得自己收获很大。所以，我们心里还是很感激梁老师的这份严格。梁老师指导我们花费了大量的时间和心血，我们想对他道声‘谢谢’。”</span></p><p>&nbsp;</p><p><strong>关键字阅读</strong><span style=\"font-family: 宋体,Arial,sans-serif;padding-left:10px;font-size:14px\">梁春涛 地球物理</span>·<a href=\"http://www.cdut.edu.cn/xww/news/152403895438396462.html\">勘探地球物理学进展学术研讨会通知</a> <span style=\"color: #C0C0C0\">2018-04-18</span><br/>·<a href=\"http://www.cdut.edu.cn/xww/news/151304299242241590.html\">地球物理学院举办纪念“一二·九” 运动 82周年合唱比赛</a> <span style=\"color: #C0C0C0\">2017-12-12</span><br/>·<a href=\"http://www.cdut.edu.cn/xww/news/151021634310158561.html\">地球物理学院召开本科教学工作审核评估评建工作动员会</a> <span style=\"color: #C0C0C0\">2017-11-09</span><br/>·<a href=\"http://www.cdut.edu.cn/xww/news/150967139473523190.html\">地球物理学院举行首届“邓林研究生奖学金”颁奖仪式</a> <span style=\"color: #C0C0C0\">2017-11-03</span><br/>·<a href=\"http://www.cdut.edu.cn/xww/news/150820176343712262.html\">地物学院获全国大学生地球物理知识竞赛团体特等奖</a> <span style=\"color: #C0C0C0\">2017-10-17</span></p><p><br/></p>', '2018-05-24 20:53', '成都理工大学网上党支部', '梁春涛：“喜欢”地震的专家', null, '2', '172');
INSERT INTO `news` VALUES ('<p style=\"text-align: center;margin-top: auto;margin-bottom: auto\"><strong><span style=\"font-family: 黑体;font-size: 29px\">二十四年“民心代表”但求无愧于心</span></strong></p><p style=\"text-align: center;margin-top: auto;margin-bottom: auto\"><strong><span style=\"font-family: 宋体;font-size: 21px\">——</span></strong><strong><span style=\"font-family: 宋体;font-size: 21px\">访成都市人大代表、我校教授李之权</span></strong><span style=\"font-family: 宋体;font-size: 16px\">&nbsp;</span></p><p style=\"text-align: left;text-indent: 32px;margin-top: auto;margin-bottom: auto\">&nbsp;</p><p style=\"text-align: left;text-indent: 32px;margin-top: auto;margin-bottom: auto\">&nbsp;</p><p style=\"text-align: left;text-indent: 32px;margin-top: auto;margin-bottom: auto\"><strong><span style=\"font-family: 宋体;font-size: 16px\">人物名片：李之权，生于1934年，我校原电子工程系退休教授，第11届至第15届成都市人大代表</span></strong><span style=\"font-family: 宋体;font-size: 16px\">。</span></p><p style=\"text-align: left;text-indent: 32px;margin-top: auto;margin-bottom: auto\">&nbsp;</p><p style=\"text-align: left;text-indent: 32px;margin-top: auto;margin-bottom: auto\"><span style=\"font-family: 宋体;font-size: 16px\">累计提出300余条建议，手写记录30余册“人大代表资料”，还有书架上摞满了的文件、法律、法规……</span></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\">&nbsp;</p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><span style=\"font-family: 宋体;font-size: 16px\">　　这些，都是李之权尽心尽力履职成都市人大代表24年的“证据”。</span></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\">&nbsp;</p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><span style=\"font-family: 宋体;font-size: 16px\">　　还有一年即将卸任的李之权，已经78岁了。这个干干瘦瘦、神清气爽的“小老头”，在举手投足、字里行间散发出一种让人心生敬佩的魅力。这种魅力来自于知识的积累、人格的积淀，来自于对人大代表精神的践行。</span></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\">&nbsp;</p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><span style=\"font-family: 宋体;font-size: 16px\">　　李之权说：“我只是在努力做一名合格的人大代表。”而这样一位合格的人大代表却在成都市的民主政治生活中“出了名”。</span></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\">&nbsp;</p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><span style=\"font-family: 宋体;font-size: 16px\">　　到底如何做才能做到合格？这个问题值得每一位人大代表深思。笔者也希望，在理工能够涌现出更多的“李之权”，能够代表理工在参政议政、民主政治生活中发出自己的声音。</span></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\">&nbsp;</p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><span style=\"font-family: 宋体;font-size: 16px\">&nbsp;&nbsp;&nbsp; 他是“民心代表”，也是“明星代表”。</span></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\">　　</p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><span style=\"font-family: 宋体;font-size: 16px\">&nbsp;&nbsp;&nbsp; 他这个人，有个很大的特点就是“爱提意见”。</span></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\">&nbsp;</p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><span style=\"font-family: 宋体;font-size: 16px\">&nbsp;&nbsp; “什么事情，只要我答应下来，我就会认真去做，除非我没有答应。”</span></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\">&nbsp;</p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><span style=\"font-family: 宋体;font-size: 16px\">　 “作为一名公民，就应该成为合格的公民；作为一名教师，就应该是合格的教师；作为一名人大代表，就应该是合格的代表。凡事能做到合格就很不容易了。”</span></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\">&nbsp;</p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><span style=\"font-family: 宋体;font-size: 16px\">　　提到李之权，理工的很多师生都知道，他不仅是连任5届的成都市人大代表，更被外界称之为“民心代表”。普通群众送过他“为人民代表，敢仗义执言”的锦旗。市中院、市检察院、市公安局等多家单位及城市重点建设项目连续多年聘他为特邀监督员并担任多个监督组长职务。他也是颇有些名气的人大代表了。</span></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\">&nbsp;</p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><strong><span style=\"font-family: 宋体;font-size: 16px\">&nbsp;“</span></strong><strong><span style=\"font-family: 宋体;font-size: 16px\">爱提意见”的人大代表</span></strong></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\">&nbsp;</p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><span style=\"font-family: 宋体;font-size: 16px\">　　“当初啊，我就是因为爱提意见，所以才被选上人大代表的”，李之权幽默地如此说。原来，1988年，上级领导来学校考察李之权的时候，对于“爱提意见”这个“特点”，上级领导说了句：“哎，这个好，我们选人大代表就是喜欢那种爱提意见的嘛！”</span></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\">&nbsp;</p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><span style=\"font-family: 宋体;font-size: 16px\">　　当上人大代表的李之权，时刻不敢忘记肩头的重任，将党和人民的嘱托牢记于心。在24年的代表生涯中，他累计一共提出300余条建议。其中，“六条城市道路改扩建”、“生命河（沙河）的整治与保护”、“空气质量的检测与信息发布”、“马路经济的治理”、“东郊工业区结构调整”、“建立人大代表与政府领导直接沟通的 ‘绿色通道’”等等比较重要的议案都已实施。这些议案和建议的提出对成都市民主法治建设、城市规划建设、环境整治等重大决策都起到了推动作用。</span></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\">&nbsp;</p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><span style=\"font-family: 宋体;font-size: 16px\">　　还有他公文包里的那30多册笔记本，李之权称它们为“代表资料库”。那里面有他对重要法律法规的学习体会，有他参与视察、监督的全程记录、也有会内会外代表们的发言摘记。他风趣地说：“这些笔记本，记录了我履职人大代表的点点滴滴。有的时候，事情过去得久了，一些细节记不起来了，这些手写的资料可都是‘证据’”。</span></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\">&nbsp;</p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><span style=\"font-family: 宋体;font-size: 16px\">　　为了更好地了解社情民意，爱好体育、身体硬朗的李之权在60多岁的时候仍然骑着自行车走访成都市的大街小巷，进行实地调研。在采访中，他告诉记者：“代表反映的东西，一定要是真实客观的，这样才是对人民负责。如果你说的话不可信，人民不会信任你，领导也不会信任你。这样的代表还能当得下去吗？”不仅如此，李之权的书架上还放了一摞摞的资料，规划局一袋、公安局一袋、法院检察院一袋……那些都是他这么多年来搜集的关于政府各部门发布的文件、重大事记。他说：“要提出合理、正确的意见，必然要对问题有比较深入的认识才可以啊。我搜集这些资料，也是想对政府的政策有更加深入的了解。”</span></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\">&nbsp;</p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><span style=\"font-family: 宋体;font-size: 16px\">　　曾经，二仙桥东路上有一段铁路，火车要从路的中间穿过，这给人民群众的生活带来很大不便。每次火车快要到来时，不管当时你有多急的事儿要办，道路都要戒严，人必须给火车让道。悲剧最终还是不可避免的发生了，直到第二名学生被火车轧死，原本内心就十分不安的李之权再也按捺不住了。他给当时的铁道部部长写了一封信。信中有这样一句话：“……如果被轧死的是你的孙子、小孩，你这个时候又会是什么样的心情呢……”</span></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\">&nbsp;</p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><span style=\"font-family: 宋体;font-size: 16px\">　　“这封委托全国人大代表递交的信件，不知道是否起到作用，但是，这个问题最终还是解决了”，李之权告诉记者：“其实，人大代表起到的作用就是一个沟通的桥梁作用。任何一个问题的解决，都是需要很多方面的条件的。也许有的领导很想做事，但是领导们也不可能知道这个社会的每一个问题。我们要做的就是把问题反映给他们。”</span></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\">&nbsp;</p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><strong><span style=\"font-family: 宋体;font-size: 16px\">心系人民，心系理工</span></strong></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\">&nbsp;</p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><span style=\"font-family: 宋体;font-size: 16px\">　　24年了，这个“爱提意见”的李之权因为提出的意见涉及到社会方方面面，打交道的政府部门也涉及比较广，渐渐地积累了一些“人气”。而他这个人大代表，既属于成都市人民，也属于理工这个大家庭。为了学校的发展、为了师生员工的生活，他有时候也会“利用”自己的职务之便“徇私”一下。</span></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\">&nbsp;</p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><span style=\"font-family: 宋体;font-size: 16px\">　　上世纪90年代初，学校周边的公交系统尚不完善，这给学校师生和周边居民的生活带来很大不便。李之</span></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><span style=\"font-family: 宋体;font-size: 16px\">权虽然跟当时的公交系统不是很熟，但是他一趟趟地跑公交系统反映情况，希望能够解决这个问题。最终，问题解决了，而他与公交系统的不少人也建立了深厚的情感。</span></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\">&nbsp;</p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><span style=\"font-family: 宋体;font-size: 16px\">　　采访过程中，李之权再三强调：“经过很多年的工作，我的确积攒了一些‘关系’。但是这个‘关系’并不是要通过不正当渠道以权谋私的关系，而是双方在打交道过程中，对彼此了解之后产生的尊重；是我认识他们，而他们也觉得我人不错，我可以把一些实际情况实事求是地反映给他们。”</span></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\">&nbsp;</p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><span style=\"font-family: 宋体;font-size: 16px\">　　提到李之权，理工人感谢他，感谢他“不搬倒热电厂的烟囱死不瞑目”的誓言和决心。</span></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\">&nbsp;</p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><span style=\"font-family: 宋体;font-size: 16px\">　　曾经，在理工的周围矗立着三大热电厂的烟囱，生活在这里人们都不怎么敢开窗户，因为一开窗，屋子里就是厚厚一层黑色的灰。</span></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\">&nbsp;</p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><span style=\"font-family: 宋体;font-size: 16px\">　　李之权是老理工人了，他热爱这个家园，热爱那些要成长为国家栋梁之材的学生。想到环境污染给周边居民带来的危害，想到理工越来越多的学生们的健康将会受到烟尘的威胁，李之权对这三个烟囱的存在简直是痛心疾首。于是，多年来，他连续给人大、给政府部门反映情况，不断地跑热电厂。因为一些特殊原因，他吃过 “闭门羹”，但是他下定决心“一定要把这几个烟囱扳倒”。</span></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\">&nbsp;</p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><span style=\"font-family: 宋体;font-size: 16px\">　　他曾作为一名老知识分子对热电厂的领导和技术人员质问到：</span></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\">&nbsp;</p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><span style=\"font-family: 宋体;font-size: 16px\">　　“是否可以认为曾为成都市的建设与发展做过一些贡献，就可以长期不顾对环境的破坏和对广大人民身体健康的危害？”</span></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\">&nbsp;</p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><span style=\"font-family: 宋体;font-size: 16px\">　　“作为一名知识分子，你们在危害人民身体健康的企业中工作，不觉得有负罪感吗？知识分子的良心何在？”</span></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\">&nbsp;</p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><span style=\"font-family: 宋体;font-size: 16px\">　　“你们愿意你们的家人和后代长期在这种黑雨、浓烟、二氧化硫超标的环境中工作和生活吗？”</span></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\">&nbsp;</p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><span style=\"font-family: 宋体;font-size: 16px\">　　字如玑珠，落地有声。</span></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\">&nbsp;</p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><span style=\"font-family: 宋体;font-size: 16px\">　　在广大人民群众的反对声中，在上级领导部门的关心下，最终，热电厂的问题解决了，三根烟囱陆续倒下。而李之权曾经的誓言和他干干瘦瘦、神清气爽的模样却长长久久地留在了理工人的心中。</span></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\">&nbsp;</p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><span style=\"font-family: 宋体;font-size: 16px\">　　如今78岁高龄的李之权，再有一年就届满卸任了。回顾这么多年的代表历程，他想起了自己第一次做代表去开会，政府要招待大家，吃完饭之后，他竟然掏出钱来要为自己买单。李之权的代表生涯，还剩下最后的一年，他曾经匆匆的脚步终于渐渐地放缓。“在接下来的一年中，我还想提两个建议：一个是希望能为成华区的‘棚户区’、‘城中村’做点事，希望他们的居住环境和设施能得到改善；另一个就是希望能为学校的周边环境建设做点事，学校门前的‘六叉路口’存在重大的交通隐患，周边环境也亟待改善。一年的时间有限，但是能做多少就做多少吧……” </span></p><p style=\";margin-top: auto;margin-bottom: auto\"><strong><span style=\"font-family: 宋体;font-size: 16px\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></strong></p><p style=\"text-align: center;margin-top: auto;margin-bottom: auto\"><span style=\"font-size:16px;font-family:Calibri\">&nbsp;<img alt=\"\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/136775383303043059.jpg\" width=\"500\" height=\"337\"/></span></p><p><br/></p>', '2018-05-24 20:54', '成都理工大学网上党支部', '二十四年“民心代表”但求无愧于心', null, '2', '173');
INSERT INTO `news` VALUES ('<p style=\"text-align: center;margin-top: auto;margin-bottom: auto\"><strong><span style=\"font-family: 黑体;font-size: 29px\">为老百姓办实事的“高产代表”</span></strong></p><p style=\"text-align: center;margin-top: auto;margin-bottom: auto\"><strong><span style=\"font-family: 宋体;font-size: 21px\">——</span></strong><strong><span style=\"font-family: 宋体;font-size: 21px\">访成都市人大代表、我校教授方方</span></strong></p><p style=\"text-align: center;margin-top: auto;margin-bottom: auto\"><strong><span style=\"font-family: 宋体;font-size: 21px\">&nbsp; </span></strong></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><strong><span style=\"font-family: 宋体;font-size: 21px\">&nbsp;&nbsp; </span></strong><strong><span style=\"font-family: 宋体;font-size: 16px\">人物名片：</span></strong><span style=\"font-family: 宋体;font-size: 16px\">方方，现为我校信息科学与技术学院副院长，2003年加入中国农工民主党，并担任农工党成都理工大学支部副主委，2008年首次当选成都市人大代表并连任至今。受成都市人大推荐，方方还担任多个社会监督员，如四川省检查机关人民监督员、文明地铁、环境保护、公交行风、房管行风社会监督员等等。</span></p><p><span style=\"font-family: 宋体;font-size: 16px\">&nbsp; 　在今年成都市“两会”上，方方一口气呈上了10份建议，其中绝大多数都与成都道路交通有关——《关于尽快启动新成华大道十里店（理工大学）路口节点立交工程建设的建议》、《关于加强二环快速路运行管理的建议》、《关于加强二环快速路运行管理的建议》、《关于进一步完善地铁站口引导路牌的建议》……</span></p><p style=\"text-align: left;margin: 0 0 16px\"><span style=\"font-family: 宋体;font-size: 16px\">&nbsp;</span></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><span style=\"font-family: 宋体;font-size: 16px\">　　从2008年当选成都市人大代表以后，方方曾连续四年提交关于调整道路转向车道的建议、关于解决十里店路口拥堵的建议，连续三年提交关于改变渣土运输车辆管理方式的建议、关于立法扶持弱势群体的建议。</span></p><p style=\"text-align: left;margin: 0 0 16px\"><span style=\"font-family: 宋体;font-size: 16px\">&nbsp;</span></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><span style=\"font-family: 宋体;font-size: 16px\">　　在担任人大代表的这五年期间，方方共提交了79份建议。这些建议涉及到了成都经济发展的热点话题和群众生活的方方面面，其中近三分之一的建议获得成都市委、市政府的采纳和实施，对于加快成都经济发展、加强道路交通安全、治理城市环境污染、改善群众生活质量、促进社会和谐起到了积极的作用。</span></p><p style=\"text-align: left;margin: 0 0 16px\"><span style=\"font-family: 宋体;font-size: 16px\">&nbsp;</span></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><span style=\"font-family: 宋体;font-size: 16px\">　　《成都日报》等多家新闻媒体也因此称他为“高产代表”。　</span></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><span style=\"font-family: 宋体;font-size: 16px\"><strong>“我不是专家，我只是老百姓的传声筒”</strong></span></p><p style=\"text-align: left;margin: 0 0 16px\"><span style=\"font-family: 宋体;font-size: 16px\">&nbsp;</span></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><span style=\"font-family: 宋体;font-size: 16px\">　　2007年的一天晚上，一位男孩不慎掉入十里店公交站附近的无盖窨井当中，这一幕恰巧被下班回家的方方看到，于是他二话不说就与前来帮忙的路人一起将男孩从窨井救出，避免了一次人身事故的发生。为了防止再次有人不留心掉入无盖窨井当中，方方连忙找来树枝，并细心的将树枝挡在窨井周围以示警告，提醒行人避开。</span></p><p style=\"text-align: left;margin: 0 0 16px\"><span style=\"font-family: 宋体;font-size: 16px\">&nbsp;</span></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><span style=\"font-family: 宋体;font-size: 16px\">　　2008年方方当选成都市人大代表后，他关注的第一个问题就是井盖缺失问题，做出的第一件事就是提交《关于成立成都市市容环境监督信息员的建议》。</span></p><p style=\"text-align: left;margin: 0 0 16px\"><span style=\"font-family: 宋体;font-size: 16px\">&nbsp;</span></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><span style=\"font-family: 宋体;font-size: 16px\">　　不积跬步，无以至千里；不积小流，无以成江海。秉承着“群众生活无小事”的信念，方方通过细致观察周围生活，把提建议的热情贯穿到他日常生活的每一刻，从身边繁琐的小事着手，开始了他的人大代表履职生涯。</span></p><p style=\"text-align: left;margin: 0 0 16px\"><span style=\"font-family: 宋体;font-size: 16px\">&nbsp;</span></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><span style=\"font-family: 宋体;font-size: 16px\">　　“以前三环路上的立交桥没有名字，这很容易使外地来蓉的司机犯迷糊，搞不清楚方向，于是我在人代会就提出了《关于完善成都立交桥命名，规范道路指示牌的建议》、《关于清理和规范道路指示标志的建议》和《关于完善大型路口指示标牌的建议》。”</span></p><p style=\"text-align: left;margin: 0 0 16px\"><span style=\"font-family: 宋体;font-size: 16px\">&nbsp;</span></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><span style=\"font-family: 宋体;font-size: 16px\">　　“在上班的路上发现跳蹬河路段的信号灯有问题，这容易造成一些司机危险驾驶，也威胁行人的安全，我就提出了《关于优化调整成华大道跳蹬河路段信号灯的建议》。”</span></p><p style=\"text-align: left;margin: 0 0 16px\"><span style=\"font-family: 宋体;font-size: 16px\">&nbsp;</span></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><span style=\"font-family: 宋体;font-size: 16px\">　　“开车的时候发现一些道路设置的左转、直行线路不合理，这不仅造成交通堵塞，更使得一些司机稍不注意就会被罚款、扣分，于是我就建议调整二仙桥路口转向车道标志、调整成华大道跳蹬河路段道路左转导向标志。”</span></p><p style=\"text-align: left;margin: 0 0 16px\"><span style=\"font-family: 宋体;font-size: 16px\">&nbsp;</span></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><span style=\"font-family: 宋体;font-size: 16px\">　　……方方的这些建议很快得到了相关部门的反馈信息，并大多数被采纳和实施，但是方方一再表示：“在提建议方面，我不是专家，我只是一个普通的市民，我的建议就是一个普普通通的成都市民所关心的事情，就是我的体会，就是我希望成都市发展成什么样子。我希望我是能够代表老百姓来说话的。”<br/> </span></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\">&nbsp;</p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><span style=\"font-family: 宋体;font-size: 16px\"><strong>“</strong><strong>只要理工的事情与成都市的发展有关，我都会尽力反映”</strong></span></p><p style=\"text-align: left;margin: 0 0 16px\"><span style=\"font-family: 宋体;font-size: 16px\">&nbsp;</span></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><span style=\"font-family: 宋体;font-size: 16px\">　　方方不仅心系人民，更是心系理工。作为一名老理工人，方方对于学校门口十里店的交通问题甚为关注。</span></p><p style=\"text-align: left;margin: 0 0 16px\"><span style=\"font-family: 宋体;font-size: 16px\">&nbsp;</span></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><span style=\"font-family: 宋体;font-size: 16px\">　　“十里店路口是一个‘六岔口’，形成木字型，有多条道路在这里汇聚，一直以来车辆比较胶着，加之在学校门口，人流量也大，交通复杂程度全市罕见，关系到全校师生的出行安全，必须尽快进行整改。”</span></p><p style=\"text-align: left;margin: 0 0 16px\"><span style=\"font-family: 宋体;font-size: 16px\">&nbsp;</span></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><span style=\"font-family: 宋体;font-size: 16px\">　　但是在解决十里店“六岔口”的问题上，学校与政府却出现了分歧。“成都市政府希望通过建立高架桥的方式来解决十里店的拥堵问题，但是学校认为，开凿下穿隧道的做法可能更有利于学校周边的环境美观，也有利成都市特别是成华区的发展，从长远上看更有好处。”</span></p><p style=\"text-align: left;margin: 0 0 16px\"><span style=\"font-family: 宋体;font-size: 16px\">&nbsp;</span></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><span style=\"font-family: 宋体;font-size: 16px\">　　对此，方方一方面多次参加校园周边建设会议，听取学校领导、老师对十里店“六岔口”问题的看法，以便向市政府反映，为学校争取更大的利益；另一方面，他也经常跑到规划局、国土局、建设局、交管局、公安交警支队、北改办等部门了解成都市政府对此事的最新解决方案，希望能够在两者的想法之间寻求平衡。</span></p><p style=\"text-align: left;margin: 0 0 16px\"><span style=\"font-family: 宋体;font-size: 16px\">&nbsp;</span></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><span style=\"font-family: 宋体;font-size: 16px\">　　除此之外，方方还多次参加调研考察，积极参照武汉大学等高校门口道路建设的方案，完善自己提交的建议。记者了解到，方方已经连续四年提交关于解决十里店路口拥堵问题的建议——《关于尽快解决十里店路口交通拥堵问题的建议》、《关于加快建设十里店路口立交交通道路的建议》、《关于成都市十里店“六路口”交通进行双下穿规划的建议》、《关于合理规划成都市十里店路口立交工程的建议》、《关于尽快启动新成华大道十里店（理工大学）路口节点立交工程建设的建议》。</span></p><p style=\"text-align: left;margin: 0 0 16px\"><span style=\"font-family: 宋体;font-size: 16px\">&nbsp;</span></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><span style=\"font-family: 宋体;font-size: 16px\">　　“我是来自理工这个大家庭的，而理工也是社会大家庭中的一员，只要理工的事情与成都市的发展有关，我都会尽力向市领导反映。”<br/> 　　<br/> <strong>“我的生活主要有四件大事：教学、科研、人大代表工作和照顾孙女”</strong></span></p><p style=\"text-align: left;margin: 0 0 16px\"><span style=\"font-family: 宋体;font-size: 16px\">&nbsp;</span></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><span style=\"font-family: 宋体;font-size: 16px\">　　方方说，现在他的生活主要有四件大事：做好学院的教学管理工作、做好自己的科研教学工作、做好人大代表工作和陪他的小外孙女。</span></p><p style=\"text-align: left;margin: 0 0 16px\"><span style=\"font-family: 宋体;font-size: 16px\">&nbsp;</span></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><span style=\"font-family: 宋体;font-size: 16px\">　　方方是我校信息科学与技术学院的副院长，分管教学工作，平时的工作非常繁杂琐碎。所以在与他采访交流的过程中，经常会被来访的人和他的手机铃声打断，有专升本的学生向他反映毕业学分绩点问题的，有本科生向他反映班级上课学习情况的，有研究生找他探讨科研项目进展的，有政府工作人员打电话来通知他下午开会的，也有老师找他签署文件的……</span></p><p style=\"text-align: left;margin: 0 0 16px\"><span style=\"font-family: 宋体;font-size: 16px\">&nbsp;</span></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><span style=\"font-family: 宋体;font-size: 16px\">　　但是方方说，他不怕麻烦，不怕别人来找他。“别人来找你，肯定是有事情要找你办，不然别人干吗来找你呢？”</span></p><p style=\"text-align: left;margin: 0 0 16px\"><span style=\"font-family: 宋体;font-size: 16px\">&nbsp;</span></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><span style=\"font-family: 宋体;font-size: 16px\">　　记者看到，在方方办公室的门上一直有个便条，上面写着“有急事可到5408或5410找何强或朱杰联系方方”。</span></p><p style=\"text-align: left;margin: 0 0 16px\"><span style=\"font-family: 宋体;font-size: 16px\">&nbsp;</span></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><span style=\"font-family: 宋体;font-size: 16px\">　　“不要怕麻烦，不要回避事情，要勇于面对事情，这样才能把事情做好。做人大代表如此，做人也是如此，当官更是如此。”</span></p><p style=\"text-align: left;margin: 0 0 16px\"><span style=\"font-family: 宋体;font-size: 16px\">&nbsp;</span></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><span style=\"font-family: 宋体;font-size: 16px\">　　与当人大代表相似，方方也将热情发挥在他的科研教学工作中，发现新问题就想要立刻去分析问题、解决问题。</span></p><p style=\"text-align: left;margin: 0 0 16px\"><span style=\"font-family: 宋体;font-size: 16px\">&nbsp;</span></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><span style=\"font-family: 宋体;font-size: 16px\">　　“我现在带本科生实习，在教一些原本已经讲得滚瓜烂熟的东西时，我的头脑中还是会不时的迸发出一些以前没有想到的新奇想法，发现别人没有涉足研究的领域，或者发现一个重大理论中还有很多方面是以前没有研究到的，这都会瞬间激发我的思维和创造力，想要去立刻解决这个新问题。”</span></p><p style=\"text-align: left;margin: 0 0 16px\"><span style=\"font-family: 宋体;font-size: 16px\">&nbsp;</span></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><span style=\"font-family: 宋体;font-size: 16px\">　　除了工作以外，方方的还有一件大事就是陪伴自己的小外孙女。他说，他每天晚上都会抽出一小时的时间陪小外孙女玩。此时，他的脸上洋溢出幸福与知足的笑容。</span></p><p style=\"text-align: left;margin: 0 0 16px\"><span style=\"font-family: 宋体;font-size: 16px\">&nbsp;</span></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><span style=\"font-family: 宋体;font-size: 16px\">　　“我能够把大部分的时间和精力都放在做好人大代表、科研和教学管理的工作上，必然来源于家庭对我的全方位支持、理解与包容，让我没有后顾之忧，我对我的家人是充满感激的。”</span></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\">&nbsp;</p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><strong><span style=\"font-family: 宋体;font-size: 16px\">“</span></strong><strong><span style=\"font-family: 宋体;font-size: 16px\">人大代表要避事平生耻”</span></strong></p><p style=\"text-align: left;margin: 0 0 16px\"><span style=\"font-family: 宋体;font-size: 16px\">&nbsp;</span></p><p style=\"text-align: left;margin: 0 0 16px\"><span style=\"font-family: 宋体;font-size: 16px\">　　在采访的过程中，记者的头脑中一直出现这样的问题，人的精力总是有限的，为什么方方会对人大代表的工作有这么大的热情？为什么他对任何事情都如此的投入？他的动力来自于哪里？</span></p><p style=\"text-align: left;margin: 0 0 16px\"><span style=\"font-family: 宋体;font-size: 16px\">&nbsp;</span></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><span style=\"font-family: 宋体;font-size: 16px\">　　方方表示：“李之权老师是我校的老代表，他总是代表老百姓在发声，因此也被人们称为‘民心代表’。李老师总说‘人大代表避事平生耻’，这句话让我印象深刻。”</span></p><p style=\"text-align: left;margin: 0 0 16px\"><span style=\"font-family: 宋体;font-size: 16px\">&nbsp;</span></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><span style=\"font-family: 宋体;font-size: 16px\">　　“人大代表既然肩负了代表民意的神圣职责，就应当具有勇于做事、勇于担责的意识和觉悟，不能一味避事、无所作为。”因此，“代表避事平生耻”也成为了方方的座右铭。</span></p><p style=\"text-align: left;margin: 0 0 16px\"><span style=\"font-family: 宋体;font-size: 16px\">&nbsp;</span></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><span style=\"font-family: 宋体;font-size: 16px\">　　此外，他还补充道：“自己提出的建议能够得到采纳，取得一定的积极效果，让我感觉很有成就感，说明人大代表的一言一行是有效果的、有用的，这也给我增加了动力，激励着我坚定的去履行人大代表的职责、去践行人大代表的精神。”</span></p><p style=\"text-align: left;margin: 0 0 16px\"><span style=\"font-family: 宋体;font-size: 16px\">&nbsp;</span></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><span style=\"font-family: 宋体;font-size: 16px\">　　在谈到如何当好人大代表的问题时，方方提到了以李之权代表为榜样，按照“有理推定”的原则听取群众意见。“听取和接待群众反映情况和意见时应‘有理推定’，保持谦卑和对群众尊重的态度，特别应该关心弱势群体。”</span></p><p style=\"text-align: left;margin: 0 0 16px\"><span style=\"font-family: 宋体;font-size: 16px\">&nbsp;</span></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><span style=\"font-family: 宋体;font-size: 16px\">　　“只要一有群众给我打电话反映问题，我就会马上了解情况，如果是我能够理解的问题，我就会立刻将群众的意见转达给相关部门，并敦促有关部门尽快解决问题；如果我实在对群众反映的情况不了解，我就会告诉他到哪个部门去反映这个问题。”</span></p><p style=\"text-align: left;margin: 0 0 16px\"><span style=\"font-family: 宋体;font-size: 16px\">&nbsp;</span></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><span style=\"font-family: 宋体;font-size: 16px\">　　“不管是不是我能明白的问题，只要是群众反映的事情，我都会记录在我的本子上。”方方的本子上布满了密密麻麻的字迹，而这个备忘录也成为他人大代表履职多年来最好的证据。</span></p><p style=\"text-align: left;margin: 0 0 16px\"><span style=\"font-family: 宋体;font-size: 16px\">&nbsp;</span></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><span style=\"font-family: 宋体;font-size: 16px\">　　采访最后，方方向记者简要介绍了在接下来的人大代表工作中，他还会继续关注的两个事情：“一个是成都交通问题，特别是通过制定公交专行道路，建立快速公交，提倡公交先行的方式来解决成都的交通拥堵问题；另一个就是希望能够为学校做些事情，比如继续关注学校门口十里店‘六岔口’问题，以及提交我校恐龙自然博物馆能够获得政府支持免费向公众开放的建议。”</span></p><p style=\"text-align: left;margin: 0 0 16px\"><span style=\"font-family: 宋体;font-size: 16px\">&nbsp;</span></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><span style=\"font-family: 宋体;font-size: 16px\">　“另外，对于有些还没有被采纳的建议或者还没有解决的问题，只要我还是人大代表，我都会继续关注，不断地促进问题的解决，尽力而为。”</span></p><p style=\"text-align: left;text-indent: 417px;margin-top: auto;margin-bottom: auto\"><strong><span style=\"font-family: 宋体;font-size: 16px\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></strong><span style=\"font-size:16px;font-family:Calibri\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></p><p style=\"text-indent: 417px;margin-top: auto;margin-bottom: auto\"><span style=\"font-size:16px;font-family:Calibri\">&nbsp; <img alt=\"\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/136775230954675293.jpg\" width=\"500\" height=\"544\"/></span></p><p><br/></p>', '2018-05-24 20:54', '成都理工大学网上党支部', '为老百姓办实事的“高产代表”', null, '2', '174');
INSERT INTO `news` VALUES ('<p style=\"text-align: center;margin-top: auto;margin-bottom: auto\"><strong><span style=\"font-family: 黑体;font-size: 29px\">“80”后地质人的科普世界</span></strong></p><p><span style=\"font-family: 宋体;font-size: 16px\"><strong>&nbsp;&nbsp;&nbsp; 导读：</strong> 他已经81岁了，但是他总说：“80岁哪里老了，我不是‘80后’嘛。‘80后’喜欢的我都喜欢。”</span></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\">&nbsp;<span style=\"font-family: 宋体;font-size: 16px\">　 2月14日，我校81岁高龄的刘兴诗教授站上了2011年度国家科学技术奖励大会的领奖台，成为年纪最大的获奖者之一。此次刘兴诗所获的奖项属于国家科技进步奖中的科普项目奖，该奖项设立于2005年，他在作品的创新性、创作编辑难度、社会效益、普及程度、示范作用上都有较高的要求。刘兴诗教授凭借科普著作《讲给孩子的中国大自然》获国家科学技术进步二等奖，成为四川省首个以科普类作品获奖的个人先例。</span></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\">&nbsp;</p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><span style=\"font-family: 宋体;font-size: 16px\">　　至今为止，刘兴诗教授从1945年发表第一篇作品开始，已经出版了217本书，今年预计还会出版30多本书。</span></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\">&nbsp;</p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><span style=\"font-family: 宋体;font-size: 16px\">　　2月22日，在我校九教圆桌会议室召开的新闻见面会上，刘兴诗教授谈及国殇罹难时不禁潸然泪下，有人递过纸巾，他却用手抹了一把泪，断然拒绝：“搞地质的人，不需要！”</span></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\">&nbsp;</p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><span style=\"font-family: 宋体;font-size: 16px\">　　刘兴诗教授为我们摊开他在人民大会堂领奖时的照片，他坐在第一排，左边坐的是一位特意从德国来的著名教授。他兴奋的为我们讲诉他与国家领导人间的对话，神情自豪。就让我们重现下当时的殊荣吧，他的确应该感到自豪！</span></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\">&nbsp;</p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><span style=\"font-family: 宋体;font-size: 16px\">　　胡锦涛主席走到他面前，与之握手，刘兴诗教授道：“我是成都理工大学的。”</span></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\">&nbsp;</p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><span style=\"font-family: 宋体;font-size: 16px\">　　胡锦涛主席：“好。”</span></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\">&nbsp;</p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><span style=\"font-family: 宋体;font-size: 16px\">　　温家宝总理走到他面前，与之握手，刘兴诗教授道：“我是南开北大毕业，目前在成都理工大学。”</span></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\">&nbsp;</p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><span style=\"font-family: 宋体;font-size: 16px\">　　温家宝总理道：“那是老师兄哟。”</span></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\">&nbsp;</p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><span style=\"font-family: 宋体;font-size: 16px\">　　自然，这份荣耀与自豪不是伸手便能轻易得来的，就让我们进入刘兴诗教授的过去，了解这么多年来他为科普创作做出的重大贡献。</span></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\">&nbsp;</p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><strong><span style=\"font-family: 宋体;font-size: 16px\">小心求证背后故事</span></strong></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\">&nbsp;</p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><span style=\"font-family: 宋体;font-size: 16px\">　　当问及科普作品从何而来的时候，刘兴诗教授道出了他作品背后的故事。这次获奖作品《讲给孩子的中国大自然》中有一篇文章：要测出三千年前的长江枯水位就像“科学破案”，必须要在水位最低的时候取到标本并进行科学分析。而水位最低时正是春节。为了得到一个准确的结果，他连续多年都没有在家里过春节。从金沙江一步一步走出去，他穿过三峡直至湖北，旁及支流嘉陵江、乌江，取得大量资料，最终在泸州测得3000年前古长江的平均枯水位低于现代长江最枯水位两米。从最惊险的地方一路走来，他脚踏实地，用亲身经历书写出生动的文学作品。</span></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\">&nbsp;</p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><span style=\"font-family: 宋体;font-size: 16px\">　　多年来，他一直秉承着严谨的创作态度。代表作《美洲来的哥伦布》是历经十多年的细心考证而诞生的。从60年代初开始研究，发现问题后，作为科学工作者的他执着“求甚解”，真正做到了“穷究于理”。每一个细节都不放过，一丝一毫的精细使这本书直到1979年才得以完成，被认为是“中国科学小说重科学流派”的代表作。</span></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\">&nbsp;</p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><span style=\"font-family: 宋体;font-size: 16px\">　　刘兴诗教授还讲诉了关于他即将面市的新书——《抗战苦儿流浪记》背后的一些故事。上世纪90年代，当时的台湾教材中讲的全是台湾历史、台湾地理。台湾一家出版社找到他，希望他能够出一套讲中国地理、中国历史的书，以反击台独分子。谈到这段来历，刘兴诗教授颇为骄傲地说到：“选中我的原因，是因为我了解中国的全面情况，也有很强的民族气节！”可惜的是，这本书当时在台湾不能出版，而直到现在才得以在大陆面市。说到书中的内容，他表示写的都是真实的事情，“对于战斗场面的描述，我不敢有一个字的歪曲。”</span></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\">&nbsp;</p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><strong><span style=\"font-family: 宋体;font-size: 16px\">搁笔充电规律生活</span></strong></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\">&nbsp;</p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><span style=\"font-family: 宋体;font-size: 16px\">　　当有记者问及他去年搁笔科普的计划是否有变化时，他如是说：“我现在八十多岁，时日不多了，我需要把时间用在更有意义的事情上，也不是说不写科普了，只是很少写。”对于刘兴诗教授而言，搞科普最关键的问题是一定要建立在自己的科研结果上，做到写出来的内容要“真”！66多年的写作经历已经让他的科研结果几乎用尽，他需要搁下笔，继续做科研，为自己充充电。</span></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\">&nbsp;</p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><span style=\"font-family: 宋体;font-size: 16px\">　　人家退休了，种种花，养养草，过上闲情雅致的生活，但是刘兴诗教授却选择放弃了一切休息、一切娱乐、一切爱好，争分夺秒地做工作，只是为了能让生命的最后一把火熊熊燃烧。有一次他对友人说，“我孙子现在可能干了。”友人问，“你孙子是干什么的？”他自豪地答：“我孙子会爬了。”把孙子放在心尖上的刘兴诗教授，每日不断工作，连去看看孙子的时间都是挤出来的。</span></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\">&nbsp;</p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><span style=\"font-family: 宋体;font-size: 16px\">　　“我以前不听医生的话，随便乱吃东西，现在我想，应该要听医生的话，健康的身体是好好工作的前提。”刘兴诗教授三句话不离开工作，每日七点左右起床，中午休息半小时，晚上十点之前睡觉，可以说他每日的安排只有工作和睡觉。唯一能让刘兴诗教授熬夜的，可能就是深夜里的足球赛了。他开玩笑地说起年轻时加入足球队，成为球队多出的一个后卫，为什么是多出的？因为他老是漏脚，常常挡了守门员的视线促成对方进球，所以对方特许他存在于足球场上。此话一出，逗乐了在场所有的人。</span></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\">&nbsp;</p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><span style=\"font-family: 宋体;font-size: 16px\">　　今年2月，刘兴诗教授的两本新书——《2012》和《抗战苦儿流浪记》将出版，而“讲给孩子”系列也将推出世界科学、长江、黄河、中国朝代四大套12本。这么多的工作计划，刘兴诗教授说起时兴致勃勃，但他仍会感概：“岁数大了，时间不够用，把玩的时间留在梦中吧。”</span></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\">&nbsp;</p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><strong><span style=\"font-family: 宋体;font-size: 16px\">民苦国难激昂愤慨</span></strong></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\">&nbsp;</p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><span style=\"font-family: 宋体;font-size: 16px\">　　出生那年武汉遭受洪水的侵袭。日本发动 “一二·八”战争中他的家被毁坏，后来“八·一三”爆发，上海沦陷，南京也未能幸免战火的吞噬。一家人惶恐逃难到重庆，日本飞机狂轰滥炸、学生运动此起彼伏，哪里都没有太平的角落。在烽火连天战不休的年代，东奔西走。在自己的领土上见证着帝国主义的罪恶行径，在艰苦危难的日子里被善良的同胞保护……这就是刘兴诗教授苦难的童年。</span></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\">&nbsp;</p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><span style=\"font-family: 宋体;font-size: 16px\">　　正因为从乱世中走来，成长中的他才深深明白，唯有祖国强大起来，才会不受帝国主义的欺凌。而他选择的每一步，都是这个苦难时代的需求。这一生，为祖国奉献，向人民感恩，即便历尽千艰万难，他从不后悔。</span></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\">&nbsp;</p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><span style=\"font-family: 宋体;font-size: 16px\">　　谈到战争中的英雄故事时，刘兴诗教授充满了对敌人的愤懑和对英雄的敬意，并讲述了许多真实的故事：“陈怀民，保卫大武汉时，飞机受伤了，被几架敌机包围，他反过来对着敌机撞上去。武汉现在还有一条路，就叫陈怀民路！”“那时候，整个天空只有云，只有风，只有猖狂的敌机。我们的空军哪里去了？没有了……”一谈到这些爱国志士，他充满敬佩的说：“不管什么朝代，不管什么政党，不管什么民族，不管什么信仰，只要是保卫国家而牺牲的，都是英雄，都是烈士。”</span></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\">&nbsp;</p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><span style=\"font-family: 宋体;font-size: 16px\">　　汶川地震发生的时候，刘兴诗教授正在北京，友人对他说，“你既然逃出来了，就不要再回去了。”可是，此时此刻的他哪里还顾得上自己的安危，毅然去了地震灾区。将近八十岁的老人，没有带助手，穿一身红衣戴着红帽子，做足了思想准备，踏上第一线。他不会逃，不会不管不顾，他用这样的态度战斗。正如他慷慨激昂的那句话：难道说为国服务，还有退休这个说法吗？</span></p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\">&nbsp;</p><p style=\"text-align: left;margin-top: auto;margin-bottom: auto\"><span style=\"font-family: 宋体;font-size: 16px\">　　“如果有朝一日，我去了。那就热情洋溢地送我走吧。不要放哀乐、不要一般的照片。让豪情激荡的毕业歌送我一程，挂一幅我在山野进行地质工作的照片，让我好好地在我魂牵梦萦的祖国大地上入眠。”生老病死，是谁也无法逃脱的，刘兴诗教授面对死亡，他骨子里的那把火让他彻底燃烧起来，“大家都说我的性格是一团火，那就让我在死后再燃烧一次，把爱国的情感传递给青年一代吧。”</span></p><p style=\"text-align: center;margin-top: auto;margin-bottom: auto\"><strong><span style=\"font-family: 宋体;font-size: 16px\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></strong><strong><span style=\"font-family: 宋体;font-size: 16px\">（作者： 胡雪 刘爱莎 李亚楠）</span></strong></p><p><span style=\"font-size:16px;font-family:Calibri\">&nbsp;</span></p><p style=\"text-align: center\">&nbsp;<img alt=\"\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/136775122576555218.jpg\" width=\"500\" height=\"282\"/></p><p style=\"text-align: center\"><strong><span style=\"font-family: 宋体;font-size: 16px\">笔耕科普60年，这些证书是他工作的最好证明</span></strong></p><p style=\"text-align: center\">&nbsp;</p><p style=\"text-align: center\"><span style=\"font-family: 宋体;font-size: 16px\"><img alt=\"\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/136775136676589765.jpg\" width=\"500\" height=\"349\"/></span></p><p>&nbsp;</p><p>&nbsp;</p><p style=\"text-align: center\">&nbsp;</p><p>&nbsp;</p><p style=\"text-align: center\">&nbsp;</p><p><br/></p><p>&nbsp;</p><p><strong><span style=\"font-family: 宋体;font-size: 16px\">1993</span><span style=\"font-family: 宋体;font-size: 16px\">年，日内瓦联合国欧洲总部，与英籍著名作家韩素英合影</span></strong></p><p><br/></p>', '2018-05-24 20:55', '成都理工大学网上党支部', '“80”后地质人的科普世界', null, '2', '175');
INSERT INTO `news` VALUES ('<p style=\"text-align: center;line-height: 200%\"><strong><span style=\"line-height: 200%;font-family: 黑体;font-size: 29px\">校工会“三八”节慰问困难女教职工</span></strong></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">3</span><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">月8日</span><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">下午，校党委常委、校工会主席王敏一行慰问看望了我校困难女教职工，把学校的温暖和关爱送到了她们身边。</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">此次慰问看望了家庭遭遇重大变故、本人或亲人长期罹患重病等困难情况的女教职工16名。通过这次“关爱行动”，营造了一个和谐、团结、友爱的校园氛围。接受慰问的女教职工们感谢学校的关怀，并表示一定会以乐观向上的态度对待生活中的逆境，战胜困难，努力做好本职工作。</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">今年学校加大了对困难教职工的帮扶力度，校工会女工委办理的女教职工互助保险的赔付也在一定程度上缓解了受困教职工的燃眉之急。</span></p><p>&nbsp;</p><p>&nbsp;</p><p style=\"text-align: center\"><img alt=\"\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/142596979370346792.jpg\" width=\"500\" height=\"333\"/></p><p style=\"text-align: center\"><span style=\"font-size: 16px\">看望慰问困难女教职工</span></p><p><br/></p>', '2018-05-24 20:57', '成都理工大学网上党支部', '校工会“三八”节慰问困难女教职工', null, '3', '177');
INSERT INTO `news` VALUES ('<p style=\"text-align: center;line-height: 200%\"><strong><span style=\"line-height: 200%;font-family: 黑体;font-size: 29px\">时间煮雨，为遇见更美的自己</span></strong></p><p style=\"text-align: center;line-height: 200%\"><strong><span style=\"line-height: 200%;font-family: 宋体;font-size: 21px\">心教中心举办第四届女生节心理访谈节目</span></strong></p><p style=\"text-align: center;line-height: 200%\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\"><span style=\"font-family:Calibri\">&nbsp;</span></span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">“风吹雨成沙，时间追不上白马；风吹亮雪花，吹白我们的头发，天真岁月不忍欺，青春荒唐我不负你……”一首《时间煮雨》唱出每个人心中关于青春的憧憬、回忆和思考——怎样在大学最美的青春年华里遇见最美的自己？3月19日晚，由心理健康教育中心主办的第四届女生节心理访谈活动“时间煮雨，为遇见更美的自己”在学生就业创业指导服务中心新精英厅举行。本次活动特邀请校党委宣传部周图伽副部长和地球科学学院史先琳老师作为访谈嘉宾。</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">两位嘉宾从如何平衡大学学业、工作和生活，怎样经营大学期间的爱情和友情，怎么成为一名独立的女性以及把握易逝青春四个方面与在校学生进行一场深度心灵对话，并对“90后”女大学生提出期许。传播科学与艺术学院李凤鹏担任此次访谈的主持人。</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">周图伽毕业于我校外国语学院，从青涩胆怯的女大学生成长为今天深受同事学生尊重爱戴的知性老师，她感慨命运像是一场奇幻漂流；史先琳，同学们亲切地称呼她为先琳姐，地球科学学院测绘工程女博士，课堂幽默，颜值爆棚，连主持人也坦言，“要是我初中的数学是先琳姐教的，我也不会不及格。” &nbsp;</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">“如何让自己成为最美的大学生？”主持人的第一个提问就引起了大家的兴趣。周图伽用著名画家黄永玉老先生的话道出自己对美的理解：“美比好看好，而好，比美好。”她认为，美是一种气质，一种由内而外的气息，它比好看——单纯的颜值更吸引人，好看只能悦目，美则悦心，“好比美好”则是指一个人应该有独立的人格，不依附他人，不人云亦云，提升自己的人文情怀。史先琳相信“相由心生”，随着年龄的增长，容颜会老去，让自己不断沉淀，心灵的美才会愈发饱满和充实。</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">&nbsp;</span><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">“我们不仅要知道自己应该成为怎样的人，更要知道自己不应该成为怎样的人”。两位老师建议同学们以平衡的智慧处理好学习、生活以及社团工作的关系，做到“不负如来不负卿”。在谈到浅阅读时代如何与一缕书香作伴时，她们强调读书应是一种文化自觉，深阅读更是一种文化自信，如果只靠刷微博和微信朋友圈去获取碎片化的信息以及个人存在感，那么终究只能成为知道分子，而不是知识分子。“生活中不该只有苟且，还应有诗歌和远方”，她们建议女大学生要在行走、读书和交友中获取丰富的生命体验，以赏识之心悦人悦己，由此理解“美人之美，各美其美”的大美情怀。</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">“男性的胸怀有时是被委屈撑大的，女性的胸怀有时是被狭隘堵塞的”，两位老师同时也围绕如何处理好两性关系及同性关系表达了自己的观点。她们认为，两性相处或许可以从“长期共存、互相监督、肝胆相照、荣辱与共”的统一战线思维中获取理念，学会相濡以沫，反求诸己，不用愤怒的情绪灼伤他人，但同时保持自身独立的品质和天真的心性；与此同时，同性之间尤其是女生在相处时，建议细腻但不必敏感，修成一生闺蜜如烹小鲜，不仅靠平日分享美好事物，更需要跳脱个人小我，接受时间煮雨的一场修炼。</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\"></span></p><p style=\"text-align: center;line-height: 200%;text-indent: 32px\"><img alt=\"\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/142682915159320289.png\" width=\"500\" height=\"333\"/></p><p style=\"text-align: center;line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">访谈现场1</span>&nbsp;</p><p style=\"text-align: center;line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\"><img alt=\"\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/142684357756220653.jpg\" width=\"500\" height=\"341\"/></span></p><p style=\"text-align: center;line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">访谈现场2</span></p><p><strong>关键字阅读</strong><span style=\"font-family: 宋体,Arial,sans-serif;padding-left:10px;font-size:14px\">女生节 心理访谈节目</span>·<a href=\"http://www.cdut.edu.cn/xww/news/148902983277739649.html\">“3.7”女生节系列活动纷呈</a> <span style=\"color: #C0C0C0\">2017-03-09</span><br/>·<a href=\"http://www.cdut.edu.cn/xww/news/145707638046310887.html\">“芙蓉花开”女生节心理访谈活动通知</a> <span style=\"color: #C0C0C0\">2016-03-07</span><br/>·<a href=\"http://www.cdut.edu.cn/xww/news/142682924853166422.html\">时间煮雨——心教中心举办第四届女生节心理访谈节目</a> <span style=\"color: #C0C0C0\">2015-03-23</span><br/>·<a href=\"http://www.cdut.edu.cn/xww/news/142646886742117433.html\">三月女生节心理访谈</a> <span style=\"color: #C0C0C0\">2015-03-16</span><br/>·<a href=\"http://www.cdut.edu.cn/xww/news/null/news/143452754514099997.html\">政治学院学生会成功举办女生节活动</a> <span style=\"color: #C0C0C0\">2015-03-09</span></p><p><br/></p>', '2018-05-24 21:13', '成都理工大学网上党支部', '时间煮雨，为遇见更美的自己', null, '3', '178');
INSERT INTO `news` VALUES ('<p style=\"text-align: center\"><strong><span style=\"font-family: 黑体;font-size: 29px\">机关党委组织开展</span></strong></p><p style=\"text-align: center\"><strong><span style=\"font-family: 黑体;font-size: 29px\">“勿忘国耻，我辈自强”主题活动</span></strong></p><p style=\"line-height: 200%;text-indent: 32px\">&nbsp;</p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">值全国首个南京大屠杀死难者国家公祭日来临之际，为进一步培养机关党员干部的爱国主义、集体主义精神和社会主义道德风尚，培育和践行社会主义核心价值观，凝聚奋进力量，11月27日，机关党委在四川省建川博物馆组织开展了“勿忘国耻，我辈自强”主题活动，机关党委委员、在职教职工党支部书记及支部委员参加了此次纪念活动。</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">活动开始之初，由机关党委书记谢飞领誓，25名机关党员干部在“中流砥柱馆”馆前进行了庄严的宣誓，重温了入党誓词。谢飞书记强调，机关党委举办此次主题纪念活动，旨在让党员干部们深入了解那个战火纷飞的年代，铭记华夏民族那段饱受蹂躏的历史，沉痛悼念南京大屠杀30多万死难同胞，深切缅怀无数用血肉之躯抵挡日本帝国主义铁蹄的英雄烈士们，弘扬烈士精神，继承烈士遗志，进一步增强历史责任感和使命感，在学校在全社会广泛传播正能量。</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">随后，机关党委副书记王雪飞带领大家参观了“正面战场馆”、“中流砥柱馆”、“不屈战俘馆”、“飞虎奇兵馆”及“川军抗战馆”等，并亲自担任讲解员，为在场人员详细讲解各馆陈列品承载的历史与意义，给党员干部们上了一堂生动的党课。</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">在整个纪念和参观活动中，机关党员干部们以郑重之心、肃穆之情重温了党的历史，也在这次“红色洗礼”中受到了教育和启迪。大家纷纷表示，将不忘历史，敬畏生命，永远铭记烈士的英名和壮举，树立继往开来、接续拼搏的坚定信心，为学校的发展建设开拓创新，为实现中华民族伟大复兴的中国梦而努力奋斗。</span>&nbsp;</p><p style=\"text-align: center\">&nbsp;&nbsp;</p><p style=\"text-align: center\"><span style=\"font-size: 16px\"><img alt=\"\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/141717256075981620.jpg\" width=\"500\" height=\"333\"/></span></p><p style=\"text-align: center\"><span style=\"font-size: 16px\">集体宣誓仪式</span></p><p style=\"text-align: center\"><span style=\"font-size: 16px\"><img alt=\"\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/141717244650612283.jpg\" width=\"500\" height=\"333\"/></span></p><p style=\"text-align: center\"><span style=\"font-size: 16px\">纪念活动现场</span></p><p style=\"text-align: center\"><span style=\"font-size: 16px\"><img alt=\"\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/141717265383556332.jpg\" width=\"500\" height=\"333\"/></span></p><p style=\"text-align: center\"><span style=\"font-size: 16px\">参观活动现场</span></p><p style=\"text-align: center\">&nbsp;</p><p>&nbsp;</p><p style=\"line-height: 200%;text-indent: 32px\">&nbsp;</p><p><strong>关键字阅读</strong><span style=\"font-family: 宋体,Arial,sans-serif;padding-left:10px;font-size:14px\">国家公祭日 南京大屠杀 机关党委</span>·<a href=\"http://www.cdut.edu.cn/xww/news/null/news/152464931543233858.html\">一次观摩+二层学习+三化建设--机关党委组织开展组织生活观摩学习活动</a> <span style=\"color: #C0C0C0\">2018-04-25</span><br/>·<a href=\"http://www.cdut.edu.cn/xww/news/null/news/152142792040150395.html\">机关党委召开2018年第二次离退休教职工党支部书记会议</a> <span style=\"color: #C0C0C0\">2018-03-19</span><br/>·<a href=\"http://www.cdut.edu.cn/xww/news/null/news/151616128508767886.html\">机关党委召开离退休教职工党支部书记会</a> <span style=\"color: #C0C0C0\">2018-01-17</span><br/>·<a href=\"http://www.cdut.edu.cn/xww/news/null/news/151546005083376388.html\">机关党委召开2017年度党委书记述职述责述廉会</a> <span style=\"color: #C0C0C0\">2018-01-05</span><br/>·<a href=\"http://www.cdut.edu.cn/xww/news/null/news/151502821848327917.html\">机关党委学习参考2017年第4期</a> <span style=\"color: #C0C0C0\">2017-12-30</span></p><p><br/></p>', '2018-05-24 21:14', '成都理工大学网上党支部', '机关党委组织开展  “勿忘国耻，我辈自强”主题活动', null, '3', '179');
INSERT INTO `news` VALUES ('<p style=\"text-align: center\"><strong><span style=\"font-family: 黑体;font-size: 29px\">离退休工作办公室举办“继承与遗嘱公证”</span></strong></p><p style=\"text-align: center\"><strong><span style=\"font-family: 黑体;font-size: 29px\">法律知识主题讲座</span></strong></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">为了切实贯彻《关于印发&lt;成都理工大学深入推进“法律进学校”的实施方案&gt;的通知》（成理校发【2014】83号）文件精神，增强老同志法律意识，维护老同志合法权益，离退休工作办公室在老同志中开展了“学法维财权，公正捍权益，共建和谐”法律进校园主题活动。</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">12</span><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">月9日</span><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">上午，四川省成都市律政公证处的逯迪、林思思等同志应邀在离退休活动室举办了一场关于“继承和遗嘱公证”的法律知识主题讲座。</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">离退休工作办公室主任许跃安在讲座前强调，开展法律知识讲座是贯彻十八届四中全会精神和实施“法律进校园”的实际行动，对增强老同志的法律意识，维护老同志的合法权益具有重要作用；过去个别老同志法律意识不足，在处理财产纠纷的时候受到了重大损失，他希望通过讲座开展学习和交流，进一步增强大家的法律意识，让老同志能够知法、懂法、守法、用法，获得法律带来的保障。</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">讲座围绕“继承和遗嘱公证”主题，利用案例分析法，向老同志们深入浅出地介绍了有关继承法定顺序、公证遗嘱与自书遗嘱、代书遗嘱的区别和法律效力等方面的法律知识。针对老同志们的咨询，成都市律政公证处的同志都耐心地一一做了详细的解答。相关内容切合老同志的关注点，整个讲座现场氛围热烈，得到了老同志的一致好评。</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">政治学院“求实社”同学们参与了此次讲座的组织工作。</span></p><p style=\"text-align: center;line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\"></span></p><p style=\"text-align: center;line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\"><img alt=\"\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/141817463210925261.jpg\" width=\"500\" height=\"318\"/></span></p><p style=\"text-align: center;line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">讲座现场1</span></p><p style=\"text-align: center;line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\"><img alt=\"\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/141817465726598038.jpg\" width=\"500\" height=\"345\"/></span></p><p style=\"text-align: center;line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">讲座现场2</span></p><p style=\"text-align: center;line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\"><img alt=\"\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/141817467375062660.jpg\" width=\"500\" height=\"326\"/></span></p><p style=\"text-align: center;line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">答疑解惑1</span></p><p style=\"text-align: center;line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\"><img alt=\"\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/141817469028140443.jpg\" width=\"500\" height=\"335\"/></span></p><p style=\"text-align: center;line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">答疑解惑2</span></p><p style=\"text-align: center;line-height: 200%;text-indent: 32px\">&nbsp;</p><p><strong>关键字阅读</strong><span style=\"font-family: 宋体,Arial,sans-serif;padding-left:10px;font-size:14px\">离退休工作 法律知识</span>·<a href=\"http://www.cdut.edu.cn/xww/news/null/news/152047099606967271.html\">离退休工作办公室党支部召开党员大会 传达上级会议精神</a> <span style=\"color: #C0C0C0\">2018-03-08</span><br/>·<a href=\"http://www.cdut.edu.cn/xww/news/null/news/149924665601164616.html\">离退休工作办公室党支部开展## “喜迎十九大 永远跟党走”庆七一诗歌朗诵活动</a> <span style=\"color: #C0C0C0\">2017-07-05</span><br/>·<a href=\"http://www.cdut.edu.cn/xww/news/null/news/149812015284679918.html\">离退休工作办公室党支部组织开展学习贯彻四川省第十一次党代会精神专题组织生活会</a> <span style=\"color: #C0C0C0\">2017-06-22</span><br/>·<a href=\"http://www.cdut.edu.cn/xww/news/null/news/149630914420889763.html\">机关党委委员、人事处处长张静一行到离退休工作办公室党支部走访调研</a> <span style=\"color: #C0C0C0\">2017-06-01</span><br/>·<a href=\"http://www.cdut.edu.cn/xww/news/null/news/146649446246310686.html\">离退休工作办公室党支部开展“重温入党誓词讲入党故事做合格党员”主题党日活动</a> <span style=\"color: #C0C0C0\">2016-06-21</span></p><p><br/></p>', '2018-05-24 21:15', '成都理工大学网上党支部', '离退休工作办公室举办“继承与遗嘱公证”  法律知识主题讲座', null, '3', '181');
INSERT INTO `news` VALUES ('<p style=\"text-align: center\"><strong><span style=\"font-family: 黑体;font-size: 29px\">马边山区的一封来信</span></strong></p><p style=\"text-align: center\"><strong><span style=\"font-family: 宋体;font-size: 21px\">政治学院爱心助学侧记</span></strong><span style=\"font-family: 华文仿宋;color: #231f1f;font-size: 15px\">&nbsp;</span>&nbsp;</p><p style=\"text-align: center\">&nbsp;</p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-size: 16px\"><span style=\"font-family:Calibri\">4</span></span><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">月</span><span style=\"line-height: 200%;font-size: 16px\"><span style=\"font-family:Calibri\">30</span></span><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">日，政治学院收到一封来自马边彝族自治县羊子坪小学刘金文老师写的亲笔感谢信。信中，刘老师诚挚地感谢成都理工大学的师生们给予羊子坪小学全体师生（共</span><span style=\"line-height: 200%;font-size: 16px\"><span style=\"font-family:Calibri\">19</span></span><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">名同学</span><span style=\"line-height: 200%;font-size: 16px\"><span style=\"font-family:Calibri\">1</span></span><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">名教师）的关心与帮助，并表示绝不辜负大家的一片期望，希望孩子们在好心人的帮助下顺利完成学业，长大后振兴家乡，回报社会。</span><span style=\"line-height: 200%;font-size: 16px\"><span style=\"font-family:Calibri\">&nbsp;</span></span></p><p style=\"text-align: center;line-height: 200%\"><span style=\"line-height: 200%;font-size: 16px\"><span style=\"font-family:Calibri\">&nbsp;<img alt=\"\" src=\"http://www.cdut.edu.cn/zzxy/UserFiles/Image/143038508976576077.jpg\" width=\"500\" height=\"242\"/></span></span></p><p style=\"text-align: center;line-height: 200%\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">羊子坪小学刘金文老师的亲笔感谢信</span></p><p style=\"text-align: center;line-height: 200%\"><span style=\"line-height: 200%;font-size: 16px\"><span style=\"font-family:Calibri\">&nbsp;</span></span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-size: 16px\"><span style=\"font-family:Calibri\">2014</span></span><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">年</span><span style=\"line-height: 200%;font-size: 16px\"><span style=\"font-family:Calibri\">10</span></span><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">月</span><span style=\"line-height: 200%;font-size: 16px\"><span style=\"font-family:Calibri\">8</span></span><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">日，</span><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">我校求实社郭甜同学得知自己的朋友刘金文老师支教的山区学校——马边县羊子坪小学的困难情况：学校</span><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">位于乐山市马边县民主乡小角溪村，地处边远山区，教学设施简陋，教学环境恶劣，全校</span><span style=\"line-height: 200%;font-size: 16px\"><span style=\"font-family:Calibri\">6</span></span><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">个年级仅有</span><span style=\"line-height: 200%;font-size: 16px\"><span style=\"font-family:Calibri\">19</span></span><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">名学生</span><span style=\"line-height: 200%;font-size: 16px\"><span style=\"font-family:Calibri\">1</span></span><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">名教师，师资力量薄弱，教育资源严重匮乏。于是，她产生了在理工寻求帮助的想法，举行一次为该小学的师生们募捐生活及学习用品的活动。</span></p><p style=\"text-align: center;line-height: 200%\"><span style=\"line-height: 200%;font-size: 16px\"><span style=\"font-family:Calibri\">&nbsp;</span></span></p><p style=\"text-align: center;line-height: 200%\"><span style=\"line-height: 200%;font-size: 16px\"><span style=\"font-family:Calibri\"><img alt=\"\" src=\"http://www.cdut.edu.cn/zzxy/UserFiles/Image/143038511825049270.jpg\" width=\"500\" height=\"375\"/>&nbsp;</span></span></p><p style=\"text-align: center;line-height: 200%\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">羊子坪小学的孩子们</span><span style=\"line-height: 200%;font-size: 16px\"><span style=\"font-family:Calibri\">&nbsp;</span></span></p><p style=\"text-align: center;line-height: 200%\"><span style=\"line-height: 200%;font-size: 16px\"><span style=\"font-family:Calibri\"><img alt=\"\" src=\"http://www.cdut.edu.cn/zzxy/UserFiles/Image/143038513287564210.jpg\" width=\"500\" height=\"376\"/>&nbsp;</span></span></p><p style=\"text-align: center;line-height: 200%\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">羊子坪小学全貌</span></p><p style=\"text-align: center;line-height: 200%\"><span style=\"line-height: 200%;font-size: 16px\"><span style=\"font-family:Calibri\">&nbsp;</span></span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">在得知这一情况之后，为帮助羊子坪小学的小朋友们顺利完成学业，成都理工大学求实社与政治学院青年志愿者协会于</span><span style=\"line-height: 200%;font-size: 16px\"><span style=\"font-family:Calibri\">2014</span></span><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">年</span><span style=\"line-height: 200%;font-size: 16px\"><span style=\"font-family:Calibri\">11</span></span><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">月面向全校师生开展了“聚爱成理·牵手马边”募捐活动，活动开展以来，受到了全校上下密切关注。师生们纷纷向马边县羊子坪小学伸出援助之手，献出自己的爱心，并详细询问山区孩子们的近况。活动共筹集到爱心捐款</span><span style=\"line-height: 200%;font-size: 16px\"><span style=\"font-family:Calibri\">7082.1</span></span><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">元，衣物、文具等若干，募捐所得款项全部用于为羊子坪小学的师生购置校服、雨靴、教具和文体用具等生活学习必需品。为鼓励孩子们勤奋学习，以积极乐观的心态面对生活，为实现自己的梦想而努力奋斗，许多学生还以写信的方式向山区孩子们表达自己的关爱之情。</span><span style=\"line-height: 200%;font-size: 16px\"><span style=\"font-family:Calibri\">&nbsp;</span></span></p><p style=\"text-align: center;line-height: 200%\"><span style=\"line-height: 200%;font-size: 16px\"><span style=\"font-family:Calibri\"><img alt=\"\" src=\"http://www.cdut.edu.cn/zzxy/UserFiles/Image/143038514720328426.jpg\" width=\"500\" height=\"333\"/>&nbsp;</span></span></p><p style=\"text-align: center;line-height: 200%\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">“聚爱成理·牵手马边”募捐现场</span><span style=\"line-height: 200%;font-size: 16px\"><span style=\"font-family:Calibri\">&nbsp;</span></span></p><p style=\"text-align: center;line-height: 200%\"><span style=\"line-height: 200%;font-size: 16px\"><span style=\"font-family:Calibri\"><img alt=\"\" src=\"http://www.cdut.edu.cn/zzxy/UserFiles/Image/143038516207857202.jpg\" width=\"500\" height=\"333\"/>&nbsp;</span></span></p><p style=\"text-align: center;line-height: 200%\"><span style=\"line-height: 200%;font-size: 16px\"><span style=\"font-family:Calibri\">&nbsp;</span></span><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">“聚爱成理·牵手马边”募捐现场</span><span style=\"line-height: 200%;font-size: 16px\"><span style=\"font-family:Calibri\">&nbsp;</span></span></p><p style=\"text-align: center;line-height: 200%\"><span style=\"line-height: 200%;font-size: 16px\"><span style=\"font-family:Calibri\"><img alt=\"\" src=\"http://www.cdut.edu.cn/zzxy/UserFiles/Image/143038517751599781.jpg\" width=\"500\" height=\"333\"/>&nbsp;</span></span></p><p style=\"text-align: center;line-height: 200%\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">成都理工大学的同学们写给马边小朋友的信</span></p><p style=\"text-align: center;line-height: 200%\"><span style=\"line-height: 200%;font-size: 16px\"><span style=\"font-family:Calibri\">&nbsp;</span></span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-size: 16px\"><span style=\"font-family:Calibri\">2015</span></span><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">年</span><span style=\"line-height: 200%;font-size: 16px\"><span style=\"font-family:Calibri\">4</span></span><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">月</span><span style=\"line-height: 200%;font-size: 16px\"><span style=\"font-family:Calibri\">8</span></span><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">日，在成都理工大学求实社、政治学院青志协成员和部分物品捐赠人的见证下，“聚爱成理·牵手马边”爱心活动获捐物资通过中国邮政寄送至马边县羊子坪小学，为孩子们送去了来自成都理工大学的关爱与祝福。</span><span style=\"line-height: 200%;font-size: 16px\"><span style=\"font-family:Calibri\">&nbsp;</span></span></p><p style=\"text-align: center;line-height: 200%\"><span style=\"line-height: 200%;font-size: 16px\"><span style=\"font-family:Calibri\"><img alt=\"\" src=\"http://www.cdut.edu.cn/zzxy/UserFiles/Image/143038519395386255.jpg\" width=\"500\" height=\"255\"/>&nbsp;</span></span></p><p style=\"text-align: center;line-height: 200%\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">“聚爱成理·牵手马边”的捐赠师生代表合影</span><span style=\"line-height: 200%;font-size: 16px\"><span style=\"font-family:Calibri\">&nbsp;</span></span><span style=\"line-height: 200%;font-size: 16px\"><span style=\"font-family:Calibri\">&nbsp;</span></span></p><p style=\"text-align: center;line-height: 200%\"><span style=\"line-height: 200%;font-size: 16px\"><span style=\"font-family:Calibri\"><img alt=\"\" src=\"http://www.cdut.edu.cn/zzxy/UserFiles/Image/143038520857819540.jpg\" width=\"500\" height=\"334\"/>&nbsp;</span></span></p><p style=\"text-align: center;line-height: 200%\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">部分所捐物品</span><span style=\"line-height: 200%;font-size: 16px\"><span style=\"font-family:Calibri\">&nbsp;</span></span></p><p style=\"text-align: center;line-height: 200%\"><span style=\"line-height: 200%;font-size: 16px\"><span style=\"font-family:Calibri\"><img alt=\"\" src=\"http://www.cdut.edu.cn/zzxy/UserFiles/Image/143038522082846419.jpg\" width=\"500\" height=\"334\"/>&nbsp;</span></span></p><p style=\"text-align: center;line-height: 200%\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">同学们通过中国邮政寄送爱心物品</span></p><p style=\"text-align: center;line-height: 200%\"><span style=\"line-height: 200%;font-size: 16px\"><span style=\"font-family:Calibri\">&nbsp;</span></span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-size: 16px\"><span style=\"font-family:Calibri\">2015</span></span><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">年</span><span style=\"line-height: 200%;font-size: 16px\"><span style=\"font-family:Calibri\">4</span></span><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">月底，羊子坪小学的师生已收到全部所捐物资，孩子们绽放出灿烂的笑容。</span><span style=\"line-height: 200%;font-size: 16px\"><span style=\"font-family:Calibri\">&nbsp;</span></span></p><p style=\"text-align: center;line-height: 200%\"><span style=\"line-height: 200%;font-size: 16px\"><span style=\"font-family:Calibri\"><img alt=\"\" src=\"http://www.cdut.edu.cn/zzxy/UserFiles/Image/143038523443730485.jpg\" width=\"500\" height=\"375\"/>&nbsp;</span></span></p><p style=\"text-align: center;line-height: 200%\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">收到校服的羊子坪小学师生</span><span style=\"line-height: 200%;font-size: 16px\"><span style=\"font-family:Calibri\">&nbsp;</span></span></p><p style=\"text-align: center;line-height: 200%\"><span style=\"line-height: 200%;font-size: 16px\"><span style=\"font-family:Calibri\"><img alt=\"\" src=\"http://www.cdut.edu.cn/zzxy/UserFiles/Image/143038524745345081.jpg\" width=\"500\" height=\"375\"/>&nbsp;</span></span></p><p style=\"text-align: center;line-height: 200%\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">收到学习用具的羊子坪小学学生</span><span style=\"line-height: 200%;font-size: 16px\"><span style=\"font-family:Calibri\">&nbsp;</span></span></p><p style=\"text-align: center;line-height: 200%\"><span style=\"line-height: 200%;font-size: 16px\"><span style=\"font-family:Calibri\"><img alt=\"\" src=\"http://www.cdut.edu.cn/zzxy/UserFiles/Image/143038526287597318.jpg\" width=\"500\" height=\"667\"/>&nbsp;</span></span></p><p style=\"text-align: center;line-height: 200%\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">收到校服的孩子们笑容灿烂</span><span style=\"line-height: 200%;font-size: 16px\"><span style=\"font-family:Calibri\">&nbsp;</span></span></p><p style=\"text-align: right;line-height: 200%\"><br/></p><p>&nbsp;</p><p><strong>关键字阅读</strong><span style=\"font-family: 宋体,Arial,sans-serif;padding-left:10px;font-size:14px\">爱心助学</span>·<a href=\"http://www.cdut.edu.cn/xww/news/143038551764098133.html\">马边山区的一封来信——政治学院爱心助学侧记</a> <span style=\"color: #C0C0C0\">2015-05-04</span><br/>·<a href=\"http://www.cdut.edu.cn/xww/news/null/news/6357.html\">栋梁工程成都商报爱心助学??暑假售报活动</a> <span style=\"color: #C0C0C0\">2007-07-14</span><br/>·<a href=\"http://www.cdut.edu.cn/xww/news/null\">栋梁工程成都商报爱心助学??暑假售报活动</a> <span style=\"color: #C0C0C0\">2007-07-14</span></p><p><br/></p>', '2018-05-24 21:17', '成都理工大学网上党支部', '马边山区的一封来信', null, '1', '183');
INSERT INTO `news` VALUES ('<p style=\"text-align: center;line-height: 200%\"><strong><span style=\"line-height: 200%;font-family: 黑体;color: black;font-size: 29px\">校领导参加义务植树活动</span></strong></p><p style=\"text-align: left;line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;color: black;font-size: 16px\">阳春三月，草长莺飞间，3月12日阳光明媚，迎来了我国第37个植树节。上午10时，校领导倪师军、王元君、郭朝辉，校党委常委王敏，校长助理徐仕海、谷建国、施泽进和学校相关职能部门负责人参加了校园义务植树活动。</span></p><p style=\"text-align: left;line-height: 200%;text-indent: 28px\"><span style=\"line-height: 200%;font-family: 宋体;color: black;font-size: 16px\">在学校香樟公寓旁的绿化林区，校领导们挥锹铲土，移苗栽植，陆续栽下20余株金桂树苗，校园更显一片生机盎然。在植树现场，校领导详细询问了校园绿化工作，要求有关部门加大绿化工作力度，促进校园生态文明，共建绿色理工。</span></p><p style=\"text-align: left;line-height: 200%;text-indent: 28px\"><span style=\"font-family: 宋体;color: black;font-size: 16px\">植树活动后，校领导一行还前往视察了渠东校区和理工东苑的绿化和道路建设情况，并要求有关部门加快渠东校区的建设，做到以环境优先、教学保障设施优先，切实做好这项惠及全校师生的工程。</span></p><p style=\"text-align: center;line-height: 200%;text-indent: 28px\"><span style=\"font-family: 宋体;color: black;font-size: 16px\"><img alt=\"\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/142621027528131953.jpg\" width=\"500\" height=\"361\"/></span></p><p style=\"text-align: center;line-height: 200%;text-indent: 28px\"><span style=\"font-family: 宋体;color: black;font-size: 16px\">校领导参加义务植树活动1</span></p><p style=\"text-align: center;line-height: 200%;text-indent: 28px\"><span style=\"font-family: 宋体;color: black;font-size: 16px\"><img alt=\"\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/142621031562581785.jpg\" width=\"500\" height=\"345\"/></span></p><p style=\"text-align: center;line-height: 200%;text-indent: 28px\"><span style=\"font-family: 宋体;color: black;font-size: 16px\"><span style=\"font-family: 宋体;color: black;font-size: 16px\">校领导参加义务植树活动2</span></span></p><p style=\"text-align: center;line-height: 200%;text-indent: 28px\"><span style=\"font-family: 宋体;color: black;font-size: 16px\"><span style=\"font-family: 宋体;color: black;font-size: 16px\"><img alt=\"\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/142621032857897290.jpg\" width=\"500\" height=\"325\"/></span></span></p><p style=\"text-align: center;line-height: 200%;text-indent: 28px\"><span style=\"font-family: 宋体;color: black;font-size: 16px\"><span style=\"font-family: 宋体;color: black;font-size: 16px\"><span style=\"font-family: 宋体;color: black;font-size: 16px\">校领导参加义务植树活动3</span></span></span></p><p style=\"text-align: center;line-height: 200%;text-indent: 28px\"><span style=\"font-family: 宋体;color: black;font-size: 16px\"><span style=\"font-family: 宋体;color: black;font-size: 16px\"><span style=\"font-family: 宋体;color: black;font-size: 16px\"><img alt=\"\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/142621034364059856.jpg\" width=\"500\" height=\"429\"/></span></span></span></p><p style=\"text-align: center;line-height: 200%;text-indent: 28px\"><span style=\"font-family: 宋体;color: black;font-size: 16px\"><span style=\"font-family: 宋体;color: black;font-size: 16px\"><span style=\"font-family: 宋体;color: black;font-size: 16px\"><span style=\"font-family: 宋体;color: black;font-size: 16px\">校领导参加义务植树活动4</span></span></span></span></p><p style=\"text-align: center;line-height: 200%;text-indent: 28px\"><span style=\"font-family: 宋体;color: black;font-size: 16px\"><span style=\"font-family: 宋体;color: black;font-size: 16px\"><span style=\"font-family: 宋体;color: black;font-size: 16px\"><span style=\"font-family: 宋体;color: black;font-size: 16px\"><img alt=\"\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/142621036007857111.jpg\" width=\"500\" height=\"319\"/></span></span></span></span></p><p style=\"text-align: center;line-height: 200%;text-indent: 28px\"><span style=\"font-family: 宋体;color: black;font-size: 16px\"><span style=\"font-family: 宋体;color: black;font-size: 16px\"><span style=\"font-family: 宋体;color: black;font-size: 16px\"><span style=\"font-family: 宋体;color: black;font-size: 16px\"><span style=\"font-family: 宋体;color: black;font-size: 16px\">校领导参加义务植树活动5</span></span></span></span></span></p><p style=\"text-align: center;line-height: 200%;text-indent: 28px\"><span style=\"font-family: 宋体;color: black;font-size: 16px\"><span style=\"font-family: 宋体;color: black;font-size: 16px\"><span style=\"font-family: 宋体;color: black;font-size: 16px\"><span style=\"font-family: 宋体;color: black;font-size: 16px\"><span style=\"font-family: 宋体;color: black;font-size: 16px\"><img alt=\"\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/142621037339042925.jpg\" width=\"500\" height=\"419\"/></span></span></span></span></span></p><p style=\"text-align: center;line-height: 200%;text-indent: 28px\"><span style=\"font-family: 宋体;color: black;font-size: 16px\"><span style=\"font-family: 宋体;color: black;font-size: 16px\"><span style=\"font-family: 宋体;color: black;font-size: 16px\"><span style=\"font-family: 宋体;color: black;font-size: 16px\"><span style=\"font-family: 宋体;color: black;font-size: 16px\"><span style=\"font-family: 宋体;color: black;font-size: 16px\">校领导参加义务植树活动6</span></span></span></span></span></span></p><p style=\"text-align: center;line-height: 200%;text-indent: 28px\"><span style=\"font-family: 宋体;color: black;font-size: 16px\"><span style=\"font-family: 宋体;color: black;font-size: 16px\"><span style=\"font-family: 宋体;color: black;font-size: 16px\"><span style=\"font-family: 宋体;color: black;font-size: 16px\"><span style=\"font-family: 宋体;color: black;font-size: 16px\"><span style=\"font-family: 宋体;color: black;font-size: 16px\"><img alt=\"\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/142621038959322597.jpg\" width=\"500\" height=\"355\"/></span></span></span></span></span></span></p><p style=\"text-align: center;line-height: 200%;text-indent: 28px\"><span style=\"font-family: 宋体;color: black;font-size: 16px\"><span style=\"font-family: 宋体;color: black;font-size: 16px\"><span style=\"font-family: 宋体;color: black;font-size: 16px\"><span style=\"font-family: 宋体;color: black;font-size: 16px\"><span style=\"font-family: 宋体;color: black;font-size: 16px\"><span style=\"font-family: 宋体;color: black;font-size: 16px\">活动现场</span></span></span></span></span></span></p><p style=\"text-align: center;line-height: 200%;text-indent: 28px\"><span style=\"font-family: 宋体;color: black;font-size: 16px\"><span style=\"font-family: 宋体;color: black;font-size: 16px\"><span style=\"font-family: 宋体;color: black;font-size: 16px\"><span style=\"font-family: 宋体;color: black;font-size: 16px\"><span style=\"font-family: 宋体;color: black;font-size: 16px\"><span style=\"font-family: 宋体;color: black;font-size: 16px\"><img alt=\"\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/142621041493793595.jpg\" width=\"500\" height=\"350\"/></span></span></span></span></span></span></p><p style=\"text-align: center;line-height: 200%;text-indent: 28px\"><span style=\"font-family: 宋体;color: black;font-size: 16px\"><span style=\"font-family: 宋体;color: black;font-size: 16px\"><span style=\"font-family: 宋体;color: black;font-size: 16px\"><span style=\"font-family: 宋体;color: black;font-size: 16px\"><span style=\"font-family: 宋体;color: black;font-size: 16px\"><span style=\"font-family: 宋体;color: black;font-size: 16px\">校领导视察校园绿化和道路建设情况</span></span></span></span></span></span></p><p><br/></p>', '2018-05-24 21:18', '成都理工大学网上党支部', '校领导参加义务植树活动', null, '8', '184');
INSERT INTO `news` VALUES ('<p style=\"text-align: center;\"><strong><span style=\"font-size:24px\"><strong><span style=\"font-family:黑体;font-size:29px\">学校保密委召开2018年保密工作会</span></strong> &nbsp;</span></strong><strong> </strong> &nbsp;</p><p><br/> </p><p style=\"text-indent:32px\"><span style=\"font-size:16px\">5月</span><span style=\"font-size:16px\">23</span><span style=\"font-size:16px\">日</span><span style=\"font-size:16px\">9:30</span><span style=\"font-size:16px\">，学校保密委在第三会议室召开了</span><span style=\"font-size:16px\">2018</span><span style=\"font-size:16px\">年保密工作会。党委副书记、校长、保密委主任龚灏，以及保密委八个工作组成员单位负责人和各基层党委（党总支）书记参加了会议。本次会议由保密办（挂靠党委办公室、校长办公室）组织筹备。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px\">龚灏校长指出，学校一直高度重视保密工作，保密工作关系到党和国家安全，关系到学校的稳定。他强调各单位、各部门要明确新形势下保密工作的新要求、新任务，要正确认识保密工作的重要性，在日常工作中要时刻绷紧保密意识的弦，遵守保密纪律和法律法规，特别是要增强重点单位、重要部门（部位）工作人员，以及从事组织人事、科学研究、招生考试等相关工作的师生员工的保密意识和保密责任感，提高相关技能水平。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px\">会议通报了学校保密委员会调整组成人员及职责分工的事宜，强调保密委全体成员和八个保密工作组要按照龚灏校长指示，进一步结合学校</span><span style=\"font-size:16px\">2018</span><span style=\"font-size:16px\">年保密工作要点，切实落实主体职责，持续推进该项工作常态化、制度化，切实提高保密管理水平。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px\">根据省委保密委和四川省教育厅相关通知要求，会议对学校开展</span><span style=\"font-size:16px\">2018</span><span style=\"font-size:16px\">年保密自查自评自纠工作，以及学校迎接省委保密委和四川省教育厅</span><span style=\"font-size:16px\">2018</span><span style=\"font-size:16px\">年保密督查工作做了说明，进一步明确各保密工作组和各基层党委（党总支）的工作任务。</span> &nbsp;</p><p style=\"text-indent:32px\">&nbsp;</p><p style=\"text-align:center;text-indent:32px\"><span style=\"font-size:16px\"><img title=\"11\" alt=\"11\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180524/20180524173956_736.jpg\" width=\"500\" height=\"338\"/></span> &nbsp;</p><p style=\"text-align:center;text-indent:32px\"><span style=\"font-size:16px\">会议现场 </span></p><p><br/></p>', '2018-05-24 21:19', '成都理工大学网上党支部', '学校保密委召开2018年保密工作会', null, '8', '185');
INSERT INTO `news` VALUES ('<p style=\"text-align: center;\"><strong><span style=\"font-size:29px;font-family:黑体\"><strong><span style=\"font-size:29px;font-family:黑体\">我校举行第八届旅游文化节</span></strong> &nbsp;</span></strong><strong> </strong> &nbsp;</p><p style=\"text-align:left;text-indent:37px\"><br/></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">为深化校地合作，宣传、推介革命老区、秦巴山区连片扶贫开发地、国家级贫困县区—广元市朝天区的旅游资源，切实践行精准扶贫政策精神，在第八个中国旅游日到来之际，我校与广元市朝天区共同举办了“美丽中国·魅力广元·邀‘理’同行之朝天的诗和远方”第八届旅游文化节，本次活动由旅游与城乡规划学院承办。</span> &nbsp;</p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">5</span><span style=\"font-size:16px;font-family:宋体;line-height:200%\">月18日中午，第八届旅游文化节开幕式于三教广场举行，广元市旅游发展委员会党组成员、副主任乔大鸿，广元市朝天区人民政府副区长孙玉娟，我校党委副书记黄涛及党委宣传部、学生工作（部）处、校团委、旅游与城乡规划学院负责人、旅游与城乡规划学院的师生代表出席了本次活动。</span> &nbsp;</p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">开幕式上，旅游与城乡规划学院的节目独唱《大道朝天》与现场书法表演，表达了我校师生对嘉宾最诚挚的欢迎。朝天区旅游局的歌伴舞《栈道之都》和独唱《打开山门迎客来》，将“栈道之都、养生天堂”朝天的旖旎风光和朝天人民勤劳善良、热情好客的淳朴民风展现得淋漓尽致。</span> &nbsp;</p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">校党委副书记黄涛在为本届旅游文化节的致辞中，回顾了我校与广元市的深厚友谊，并向长期以来关心和支持我校建设发展的广元市旅游发展委员会及朝天区人民政府、朝天区旅游局表示衷心的感谢，希望本届旅游文化节能够办成一次助推朝天旅游发展、展示成理学子良好专业素质的盛会。</span> &nbsp;</p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">广元市朝天区人民政府孙玉娟副区长在致辞中感谢学校在助推广元建设发展中做出的突出贡献，感谢旅游与城乡规划学院为广元旅游、朝天旅游发展所做的工作，并代表朝天区人民向我校师生发出热情洋溢的邀请。</span> &nbsp;</p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">最后，广元市旅游发展委员会党组成员、副主任乔大鸿宣布本届旅游文化节正式开幕。</span> &nbsp;</p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">开幕式结束后，领导及嘉宾游览了由旅游与城乡规划学院师生共同搭建的“旅游文化街”。“文化街”形式多样，内容丰富，有学院各个专业的教学成果、设计模型、绘画作品展示，有朝天区的风光图片展，还有旅游体验小活动，充分体现了专业性与趣味性的融合。据悉，在开幕式结束后，还将举办朝天区曾家山手绘地图大赛、旅游文化衫创意图案大赛等活动。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">本届旅游文化节我校再次携手广元，以闻名全国的“栈道之都、养生天堂”——广元市朝天区的旅游资源和旅游文化为载体，充分展示了我校与广元市的合作成果，对促进广大学生强化专业学习、提升综合素质具有重要的意义。</span> &nbsp;</p><p style=\"text-indent:32px\">&nbsp;</p><p style=\"text-align:center\"><strong><img title=\"1、开幕式现场一\" alt=\"1、开幕式现场一\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180522/20180522115156_392.jpg\" width=\"500\" height=\"303\"/></strong> &nbsp;</p><p style=\"text-align:center\"><strong><span style=\"font-size:16px;font-family:仿宋;line-height:200%\">开幕式现场</span></strong> &nbsp;</p><p style=\"text-align:center\"><strong><span style=\"font-size:19px;font-family:仿宋;line-height:200%\"><img title=\"3、书法表演展示\" alt=\"3、书法表演展示\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180522/20180522115227_878.jpg\" width=\"500\" height=\"334\"/></span></strong> &nbsp;</p><p style=\"text-align:center\"><strong><span style=\"font-size:16px;font-family:仿宋;line-height:200%\">书法表演展示</span></strong> &nbsp;</p><p style=\"text-align:center\"><strong><span style=\"font-size:19px;font-family:仿宋;line-height:200%\"><img title=\"4、歌伴舞《栈道之都》\" alt=\"4、歌伴舞《栈道之都》\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180522/20180522115245_187.jpg\" width=\"500\" height=\"334\"/></span></strong> &nbsp;</p><p style=\"text-align:center\"><strong><span style=\"font-size:16px;font-family:仿宋;line-height:200%\">歌伴舞《栈道之都》</span></strong> &nbsp;</p><p style=\"text-align:center\"><strong><span style=\"font-size:19px;font-family:仿宋;line-height:200%\"><img title=\"6、孙玉娟副区长致辞\" alt=\"6、孙玉娟副区长致辞\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180522/20180522115327_48.jpg\" width=\"500\" height=\"334\"/></span></strong> &nbsp;</p><p style=\"text-align:center\"><strong><span style=\"font-size:16px;font-family:仿宋;line-height:200%\">孙玉娟副区长致辞</span></strong> &nbsp;</p><p style=\"text-align:center\"><strong><span style=\"font-size:19px;font-family:仿宋;line-height:200%\"><img title=\"7、乔大鸿副主任宣布开幕\" alt=\"7、乔大鸿副主任宣布开幕\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180522/20180522115349_629.jpg\" width=\"500\" height=\"331\"/></span></strong> &nbsp;</p><p style=\"text-align:center\"><strong><span style=\"font-size:16px;font-family:仿宋;line-height:200%\">乔大鸿副主任宣布开幕</span></strong> &nbsp;</p><p style=\"text-align:center\"><strong><span style=\"font-size:19px;font-family:仿宋;line-height:200%\"><img title=\"8、嘉宾游览旅游文化街\" alt=\"8、嘉宾游览旅游文化街\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180522/20180522115413_236.jpg\" width=\"500\" height=\"375\"/></span></strong> &nbsp;</p><p style=\"text-align:center\"><strong><span style=\"font-size:16px;font-family:仿宋;line-height:200%\">嘉宾游览旅游文化街</span></strong> &nbsp;</p><p><br/></p>', '2018-05-24 21:19', '成都理工大学网上党支部', '我校举行第八届旅游文化节', null, '8', '186');
INSERT INTO `news` VALUES ('<p style=\"text-align: center\"><strong><span style=\"font-family: 黑体;font-size: 29px\">康定6.3级地震 成都理工大学在行动</span></strong></p><p style=\"text-align: left\"><span style=\"font-size:16px;font-family:Calibri\">&nbsp;</span></p><p style=\"text-align: left;line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;background: white;color: #333333;font-size: 16px\">11</span><span style=\"line-height: 200%;font-family: 宋体;background: white;color: #333333;font-size: 16px\">月22日16时55分，四川省甘孜藏族自治州康定县（北纬30.3度，东经101.7度）发生6.3级地震，震源深度18千米。</span><span style=\"line-height: 200%;font-family: 宋体;color: #333333;font-size: 16px\">25</span><span style=\"line-height: 200%;font-family: 宋体;color: #333333;font-size: 16px\">日23时19分,康定县再次发生5.8级地震。成都震感强烈。</span></p><p style=\"text-align: left;line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">11</span><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">月22日18时10分，四川在线记者第一时间致电我校党委宣传部，联系到了我校地球物理学院朱介寿教授进行康定地震解读。朱介寿教授在接受采访时表示，</span><span style=\"line-height: 200%;font-family: 宋体;background: white;color: #333333;font-size: 16px\">康定6.3级地震震中位于塔公东约18公里、木格措西北约22公里处，位于北西向的鲜水河地震断裂带，“近百年来，这处断裂带发生过6、7 次7级及7级以上的大地震。”据了解，在此处地震断裂带，发生过破坏性最大的一次地震，“那是1973年，甘孜藏族自治州炉霍县7.6级强烈地震；至于康定，则在1955年，发生过一次7级以上的地震。”他说，在1976年后，鲜水河地震断裂带较少发生地震，但自汶川地震后，地震专家们都颇为关注此处，并多次提到此处断裂带存在危险性。同时，朱介寿教授</span><span style=\"border-bottom: windowtext 1px;border-left: windowtext 1px;padding-bottom: 0;line-height: 200%;padding-left: 0;padding-right: 0;font-family: 宋体;font-size: 16px;border-top: windowtext 1px;border-right: windowtext 1px;padding-top: 0\">认为由于塔公、折多山区域，人口较分散，因此此次地震或造成的破坏性不会很大。此次康定6.3级地震属于主震余震型地震，预计后期将会发生一些余震，“但应该不会比6.3级大。”</span></p><p style=\"text-align: left;line-height: 200%;text-indent: 32px\"><span style=\"border-bottom: windowtext 1px;border-left: windowtext 1px;padding-bottom: 0;line-height: 200%;padding-left: 0;padding-right: 0;font-family: 宋体;font-size: 16px;border-top: windowtext 1px;border-right: windowtext 1px;padding-top: 0\">学校官方微博当晚20时04分发出微博，向社会民众告知朱介寿教授的地震解读，缓解民众恐慌情绪。目前，该微博阅读量达到2.3万次。</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"border-bottom: windowtext 1px;border-left: windowtext 1px;padding-bottom: 0;line-height: 200%;padding-left: 0;padding-right: 0;font-family: 宋体;font-size: 16px;border-top: windowtext 1px;border-right: windowtext 1px;padding-top: 0\">为帮助社会民众更进一步了解灾区前方信息，学校新闻中心当晚紧急联系了</span><span style=\"line-height: 200%;font-family: 宋体;background: white;font-size: 16px\">地质灾害防治与地质环境保护国家重点实验室，获悉实验室五名研究生何源、高原、曹水合、赫子皓、贺建先正在康定县姑咱镇从事科研工作。23日一早将进入震中调查灾情，实验室也将根据灾情继续增派人手开展抗震救灾工作。23日中午，</span><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">随着震区的信息和图片的回传，官方微博在14点更新了五位同学在震区的亲历所见所闻。<span style=\"background: white\">最新消息显示：姑咱镇到瓦斯沟、大渡河沿岸有小规模崩塌、滚石，受灾情况较轻。瓦斯沟至康定途中鲜有地质灾害发生。具体情况还有待进一步调查。</span></span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;background: white;font-size: 16px\">为帮助校园广大师生科学理性认真地震，学校官方微博、微信也及时编发一组应急避震知识推送，受到好评。同时，官方微博为康定祈福</span><span style=\"line-height: 200%;font-family: 宋体;color: #333333;font-size: 16px\">推送的康定风情景点阅读量达到了3.7万次。</span></p><p style=\"text-align: center;line-height: 200%\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">&nbsp;</span></p><p style=\"text-align: center;line-height: 200%\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">&nbsp;<img alt=\"\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/141696689445338126.jpg\" width=\"440\" height=\"330\"/></span></p><p style=\"text-align: center;line-height: 200%\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">康定地震现场图1</span></p><p style=\"text-align: center;line-height: 200%\">&nbsp;</p><p style=\"text-align: center;line-height: 200%\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\"><img alt=\"\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/141696694204642937.jpg\" width=\"440\" height=\"586\"/></span></p><p><br/></p><p style=\"text-align: center;line-height: 200%\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">康定地震现场图2</span></p><p style=\"text-align: center;line-height: 200%\">&nbsp;</p><p style=\"text-align: center;line-height: 200%\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\"><img alt=\"\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/141696696915636345.jpg\" width=\"440\" height=\"330\"/></span></p><p><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\"> <p style=\"text-align: center;line-height: 200%\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">康定地震现场图3</span></p><p style=\"text-align: center;line-height: 200%\">&nbsp;</p><p style=\"text-align: center;line-height: 200%\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\"><img alt=\"\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/141696699026527998.jpg\" width=\"440\" height=\"330\"/></span></p><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\"> <p style=\"text-align: center;line-height: 200%\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">康定地震现场图4</span></p></span></span></p><p><br/></p>', '2018-05-24 21:20', '成都理工大学网上党支部', '康定6.3级地震 成都理工大学在行动', null, '4', '187');
INSERT INTO `news` VALUES ('<p style=\"text-align: center\"><strong><span style=\"font-size: 29px\"><span style=\"font-family:黑体\">信科学院青协组织参观成都血液中心</span></span></strong></p><p style=\"text-align: center\"><strong><span style=\"font-size: 29px\"><span style=\"font-family:黑体\"></span></span></strong></p><p style=\"text-align: left;line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">11</span><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">月23日，为了让学校新一批志愿者们了解成都市及全省医疗用血的采集、贮藏、检验和使用的基本情况，尤其是对无偿献血活动的了解，增加同学们关于献血及预防艾滋病方面的知识，也为了增强同学们义务献血、奉献社会的精神和责任感，信息科学与技术学院青年志愿者协会组织新成员参观了成都血液中心。</span></p><p style=\"text-align: left;line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">志愿者们在血液中心参观了血液中心的储血供血科、输血研究室、检验科血小板机采室等科室。在成分献血科,工作人员给大家具体地介绍了成分献血尤其是捐献机采血小板的情况；在血液存储库，参观者一行看到了血小板的特殊储藏方式----零上22度震荡保存。通过讲解，同学们详细了解了献血前的注意事项和无偿献血的安全性，以及众多的血液知识。</span><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">参观结束后，新志愿者们都觉得受益匪浅，并纷纷表示今后会更加积极参与校园内的志愿活动，让爱在血液中流动，把温暖传递给每一个人。 </span>&nbsp;</p><p><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\"> </span></p><p style=\"text-align: left;line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">据悉，信息科学与技术学院青年志愿者协会长期以来联合成都市血液中心在全校学生中大力宣扬“无偿献血，关爱他人”的志愿服务精神，将无偿献血志愿服务作为工作重点广泛开展了一系列形式多样、卓有成效的活动。从最初一个学院的单独活动，到现在团结了学校6个学院的共同参与，规模和影响力都得到了显著提升。目前我校无偿献血志愿服务队200余名志愿者骨干活跃在校园中，先后开展无偿献血宣传服务活动30余次，参与服务志愿者达5000余人次；同时还将无偿献血志愿服务融入到了“社会主义核心价值观”主题教育实践活动当中去，搭建了一个卓有成效的宣传教育平台，有力地促进了无偿献血事业的发展，以行动实践了价值观教育</span><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">。</span></p><p>&nbsp;</p><p style=\"text-align: center;line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\"><img alt=\"\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/141679407162587316.jpg\" width=\"500\" height=\"281\"/></span></p><p>&nbsp;</p><p style=\"text-align: center\">&nbsp;<strong><span style=\"line-height: 200%;font-family: 仿宋;font-size: 19px\">志愿者参观血液中心合影</span></strong></p><p style=\"text-align: center;line-height: 200%;text-indent: 37px\"><strong><span style=\"line-height: 200%;font-family: 仿宋;font-size: 19px\"><img alt=\"\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/141679434993721293.jpg\" width=\"500\" height=\"281\"/></span></strong></p><p style=\"text-align: center;line-height: 200%;text-indent: 37px\"><strong><span style=\"line-height: 200%;font-family: 仿宋;font-size: 19px\">志愿者参观血液中心</span></strong></p><p>&nbsp;</p><p><strong>关键字阅读</strong><span style=\"font-family: 宋体,Arial,sans-serif;padding-left:10px;font-size:14px\">青年志愿者 信科院 血液中心</span>·<a href=\"http://www.cdut.edu.cn/xww/news/null/news/152118830519860420.html\">身体力行，绿苗藏情————外国语学院青年志愿者协会开展植树节活动</a> <span style=\"color: #C0C0C0\">2018-03-16</span><br/>·<a href=\"http://www.cdut.edu.cn/xww/news/151609714881194949.html\">【十九大·大学习】信科院掀起学习宣传贯彻党的十九大精神热潮</a> <span style=\"color: #C0C0C0\">2018-01-16</span><br/>·<a href=\"http://www.cdut.edu.cn/xww/news/null/news/150849233042118908.html\">20171020信科院电子电工实验室6B608配电箱电路检测</a> <span style=\"color: #C0C0C0\">2017-10-20</span><br/>·<a href=\"http://www.cdut.edu.cn/xww/news/null/news/150848795801548670.html\">20171020信科院电子电工实验室</a> <span style=\"color: #C0C0C0\">2017-10-20</span><br/>·<a href=\"http://www.cdut.edu.cn/xww/news/null/news/150788775100531101.html\">20171013信科院ASIC应用实验室</a> <span style=\"color: #C0C0C0\">2017-10-13</span></p><p><br/></p>', '2018-05-24 21:21', '成都理工大学网上党支部', '信科学院青协组织参观成都血液中心', null, '4', '188');
INSERT INTO `news` VALUES ('<p style=\"text-indent:33px\"><strong><span style=\"font-size:24px\"><strong><span style=\"font-family:黑体;font-size:29px\"><strong><span style=\"line-height:150%;font-family:黑体;font-size:27px\">校侨联、致公党支部开展“同心·薪火”捐资助学</span></strong> &nbsp;</span></strong></span></strong></p><p><strong><strong> </strong></strong></p><p style=\"text-indent:33px\"><strong><strong> </strong></strong>&nbsp;</p><p style=\"text-indent:33px\"><span style=\"font-size:16px\">5</span><span style=\"font-size:16px\">月</span><span style=\"font-size:16px\">15</span><span style=\"font-size:16px\">日，由我校侨联、致公党支部牵头，新侨联谊会、民革支部、外国语学院、传艺学院、核自院等组织或单位参与的</span><span style=\"font-size:16px\">2018</span><span style=\"font-size:16px\">年度“同心·薪火”捐资助学活动，在<span style=\"font-size:16px\">金堂县转龙镇大桥举行。</span></span>&nbsp;</p><p style=\"line-height:200%;text-indent:32px\"><span style=\"font-size:16px\">在本次助学活动中，各组织单位共捐助了16000余元奖学金，惠及</span><span style=\"font-size:16px\">32</span><span style=\"font-size:16px\">名品学兼优的留守儿童</span><span style=\"font-size:16px\">。捐赠活动后，外国语学院爱尔兰籍英语教师</span><span style=\"font-size:16px\">Pierce</span><span style=\"font-size:16px\">还为大桥小学五年级的学生带来一堂别开生面的趣味英语互动课，极大激发了孩子们学习英语的兴趣。</span> &nbsp;</p><p style=\"line-height:200%;text-indent:32px\"><span style=\"font-size:16px\">成都市侨联副主席、我校侨联主席罗润田代表学校为孩子们送上殷切祝福，她希望孩子们珍惜来之不易的资助机会，将这份关爱化为奋发进取的不竭动力，好好学习，回报社会，把爱心的种子继续传播下去，将中华民族传统美德传承下去。</span> &nbsp;</p><p style=\"line-height:200%;text-indent:32px\"><span style=\"font-size:16px\">金堂县转龙镇大桥小学是我校侨联、致公党支部、民革党支部对口联系的社会服务点。</span><span style=\"font-size:16px\">2012</span><span style=\"font-size:16px\">年至今，侨联牵头联合相关民主党派、统战团体和学院，已连续七年开展“同心·薪火”捐资助学活动，累计捐助贫困留守儿童</span><span style=\"font-size:16px\">211</span><span style=\"font-size:16px\">名，资助金额已达</span><span style=\"font-size:16px\">13</span><span style=\"font-size:16px\">万余元。此次开展的“同心·薪火”活动是积极响应十九大“扶贫更要扶智和扶志”号召的一次具体行动，收到了良好的社会效果。</span> &nbsp;</p><p style=\"line-height:200%;text-indent:32px\">&nbsp;</p><p><br/> </p><p style=\"text-align:center;text-indent:33px\"><img title=\"爱心人士企业与受捐助学生和家长合影_副本\" alt=\"爱心人士企业与受捐助学生和家长合影_副本\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180518/20180518163537_662.jpg\"/> &nbsp;</p><p style=\"text-align:center;text-indent:33px\"><span style=\"font-size:16px\">发放奖学金仪式</span> &nbsp;</p><p style=\"text-align:center;text-indent:33px\"><img title=\"罗润田教授致辞_副本\" alt=\"罗润田教授致辞_副本\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180518/20180518163619_418.jpg\"/> &nbsp;</p><p style=\"text-align:center;text-indent:33px\"><span style=\"font-size:16px\">我校侨联主席罗润田讲话</span> &nbsp;</p><p style=\"text-align:center;text-indent:33px\"><img title=\"外教英语互动课堂_副本\" alt=\"外教英语互动课堂_副本\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180518/20180518163653_212.jpg\"/> &nbsp;</p><p style=\"text-align:center;text-indent:33px\"><span style=\"font-size:16px\">外教英语互动课</span> &nbsp;</p><p><br/></p>', '2018-05-24 21:25', '成都理工大学网上党支部', '校侨联、致公党支部开展“同心·薪火”捐资助学', null, '1', '194');
INSERT INTO `news` VALUES ('<p style=\"text-align:center;text-indent:43px\"><strong><span style=\"font-size:29px;font-family:黑体;line-height:150%\">倾听图片背后的故事</span></strong><span style=\"font-size:29px;font-family:黑体;line-height:150%\"> </span> </p><p style=\"text-align:center;text-indent:43px\"><strong><span style=\"font-size:21px;font-family:黑体;line-height:150%\">云立方学生传媒中心举办第七期“东三路1号”沙龙</span></strong> &nbsp;</p><p style=\"text-align:center;text-indent:43px\"><span style=\"font-size:21px;font-family:宋体;line-height:150%\"></span>&nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:仿宋;line-height:200%\"><img title=\"现场\" alt=\"现场\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20171229/20171229155856_275.jpg\" width=\"500\" height=\"334\"/></span> </p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:仿宋;line-height:200%\">会场现场</span> &nbsp;</p><p style=\"text-align:center\">&nbsp;</p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">为提高学生的新闻摄影水平，促进“云立方”学生传媒中心推广进度，12月27日下午4点，</span><span style=\"font-size:16px;font-family:宋体;line-height:200%\">“云立方”学生传媒中心举办以“倾听图片背后的故事”为主题，在第一行政楼大三会议室举办第七期“东三路一号”沙龙。党委宣传部相关老师、“云立方”学生传媒中心七大社团的同学们共同<span style=\"color:black\">参加。本次沙龙邀请到现米拍网联合创始人、运营总监，前成都商报摄影记者杨刚为大家作分享。</span></span> &nbsp;</p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">讲座开始，杨刚以“奇妙”与“冒险”二字开头，为大家分享了自己13年的职业摄影工作经历。他展示社会纪实摄影和体育新闻摄影中的典型作品，建议学生将来从事摄影工作时，在技术上要对光线敏感，灵活作用附件，找到最佳的角度；并且结合自身经历，告诉大家在新闻工作中坚持的意义。</span> &nbsp;</p><p style=\"text-align:left;text-indent:32px\">&nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:仿宋;line-height:200%\"><img title=\"阳刚\" alt=\"阳刚\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20171229/20171229155913_860.jpg\" width=\"500\" height=\"334\"/></span> </p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:仿宋;line-height:200%\">嘉宾杨刚</span> &nbsp;</p><p style=\"text-align:center\">&nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">“在今天，我们不谈论摄影技术的问题，只谈论在我职业生涯中有意义的故事。” &nbsp; 杨刚用一系列摄影图片分享自己经历中的奇遇和冒险，传达摄影的精神。从最初在云南《生活新报》实习时人生的第一张头版照片，讲述了他传奇的机遇：从一开始不了解数码相机，到经历通宵研究，并成为报社第一个会使用相机的人。他总结出好奇心能创造机遇，年轻人应该对事物保持更多的热爱。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">其中，他分享的都江堰街头的大熊猫事件，让大家印象深刻。2005年7月16日值班的凌晨，一条“都江堰街道这儿有一只大熊猫”的线报使得杨刚和他的同事措手不及。在线人的坚持下，他们赶到了现场，却并未发现大熊猫的踪影。根据经验和常识的判断大熊猫不可能出现在市区，满心失望的他们决定放弃。然而最终在一位男孩提供的线索下，他们在一条河边的树上发现了熊猫，杨刚老师立马拍下照片。就这个事例出发，杨刚老师提出了年轻人在工作中应当大胆假设小心求证等观点。杨刚表示，媒体人应该有着坚持不懈、敢拼敢做的精神。“你一懈怠，就是人家的奇遇，人家的冒险。”</span> &nbsp;</p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">在交流环节，杨刚详细地解答了成员们在新闻摄影中遇到的问题，建议新闻工作者在采访中要放低与被访者沟通的姿态，并希望年轻人快速成长起来，为新闻摄影注入新鲜血液。活动尾声，云立方指导老师黄于鉴则表示，希望通过本次沙龙，大家能借助云立方媒体平台打好摄影基础，夯实新闻摄影的基本功。 &nbsp;</span> </p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\"></span>&nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:仿宋;line-height:200%\"><img title=\"黄\" alt=\"黄\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20171229/20171229155930_969.jpg\" width=\"500\" height=\"333\"/></span> </p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:仿宋;line-height:200%\">黄于鉴老师讲话</span> &nbsp;</p><p style=\"text-align:center\">&nbsp;</p><p><span style=\"font-size:16px;font-family:宋体;line-height:200%\">本次沙龙旨在指导大学生在校园媒体工作中成熟地应对各种新闻摄影任务，整体地掌握对重大事件的采访摄影。来自新闻嗅觉图片社的大一学生刘颖谈到，通过此次活动，自己认识到新闻摄影不仅需要敏感度，还需要应对突发状况的能力。 </span></p><p><br/></p>', '2018-05-24 21:28', '成都理工大学网上党支部', '倾听图片背后的故事', null, '1', '199');
INSERT INTO `news` VALUES ('<p style=\"text-align:center\"><strong><span style=\"font-size:29px;font-family:黑体\">郭朝辉副校长宣讲党的十九大精神</span></strong> &nbsp;</p><p style=\"text-align:center\"><strong><span style=\"font-size:29px;font-family:黑体\"></span></strong> </p><p style=\"text-align:center\"></p><p style=\"text-indent:32px\"><em><span style=\"font-size:16px;font-family:宋体;background:white;font-style:normal;line-height:200%\">为深入学习宣传贯彻</span></em><span style=\"font-size:16px;font-family:宋体;background:white;line-height:200%\">党的第十九次全国代表大会<em><span style=\"font-family:宋体;font-style:normal\">精神</span></em></span><span style=\"font-size:16px;font-family:宋体;background:white;line-height:200%\">,</span><span style=\"font-size:16px;font-family:宋体;background:white;line-height:200%\">深刻领会习近平新时代中国特色社会主义思想</span><span style=\"font-size:16px;font-family:宋体;background:white;line-height:200%\">,</span><span style=\"font-size:16px;font-family:宋体;background:white;line-height:200%\"> 12</span><span style=\"font-size:16px;font-family:宋体;background:white;line-height:200%\">月25日下午，校党委常委、副校长郭朝辉在西区第九教学楼国际学术报告厅，以“深入学习贯彻十九大精神</span><span style=\"font-size:16px;font-family:宋体;background:white;line-height:200%\">,</span><span style=\"font-size:16px;font-family:宋体;background:white;line-height:200%\">推动学校本科教育事业发展”为主题对党的十九大精神进行了宣讲，教务处、学生工作部（处）、计划财务处、校团委、心理健康教育中心、大学生创新创业中心等职能部门工作人员以及地球科学学院、核技术与自动化工程学院、体育学院党政领导、教师和学生代表参加了宣讲会。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;background:white;line-height:200%\">宣讲中，郭朝辉围绕</span><span style=\"font-size:16px;font-family:宋体;background:white;line-height:200%\">深入学习宣传贯彻党的十九大精神，重点从“中国特色社会主义进入新时代与历史使命”、“新时代中国特色社会主义思想与基本方略”、“新时代建设教育强国的新要求新部署”、“面向新时代，学校‘三步走’的发展战略四个方面，结合学校实际情况，深入浅出地对党的十九大报告进行了系统、全面、深入的解读。</span> &nbsp;</p><p style=\"text-indent:32px\"><img title=\"现场\" alt=\"现场\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180104/20180104095906_674.jpg\"/> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px\">宣讲现场</span> &nbsp;</p><p style=\"text-indent:32px\">&nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;background:white;line-height:200%\">在报告中，郭朝辉首先对党的十九大召开的时代背景、大会主题、大会任务等内容进行讲解。她强调，党的十九大是在世情、国情、党情、军情和民情都发生深刻变化的特殊的时代背景下召开的一次十分重要的大会，明确回答了我们党在新时代举什么旗、走什么路、以什么样的精神状态、担负什么样的历史使命、实现什么样的奋斗目标的重大问题。同时郭朝辉以中国特色社会主义的历史发展为脉络，用“五个是”、“八个明确”对习近平新时代中国特色社会主义思想的历史地位进行了解读，并对新时代中国特色社会主义的战略安排进行了讲解。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;background:white;line-height:200%\">郭朝辉强调，党的十九大报告将教育工作摆在改善民生和加强社会建设之首阐述，体现了对发展教育事业的高度重视。她通过剖析新时代我国教育发展面临的“六个不平衡”和“六个不充分”、新时代高等教育面临新形势等问题，指出要努力打好“提高高等教育人才培养质量攻坚战”、“促进高等教育公平攻坚战”和“深化高等教育改革攻坚战”三大攻坚战，努力建设中国特色、世界水平的高等教育。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;background:white;line-height:200%\">郭朝辉在报告中还分析了我校本科教学工作成果、存在的问题与挑战，指出了我校本科教育教学工作的努力方向，即：进一步强化教学中心地位、打造一流本科，充分认识教学改革的艰巨性和系统性，建立完善各项体制机制、全力做好本科教学审核性评估工作。她最后总结中强调，只要我们不忘初心、牢记使命，撸起袖子加油干，就一定能够共同创造中国人民和中华民族更加幸福美好的未来，也一定能实现百年成理梦。</span> &nbsp;</p><p><span style=\"font-size:16px;font-family:宋体;background:white;line-height:200%\">聆听报告的参会人员纷纷表示，这次宣讲</span><span style=\"font-size:16px;font-family:宋体;background:white;line-height:200%\">主题鲜明、概括凝练、令人振奋，</span><span style=\"font-size:16px;font-family:宋体;background:white;line-height:200%\">为深入学习宣传贯彻落实党的十九大精神指明了方向，更坚定了大家</span><span style=\"font-size:16px;font-family:宋体;background:white;line-height:200%\">为实现学校“三步走”发展战略而努力奋斗的信念。</span></p><p><br/></p>', '2018-05-24 21:29', '成都理工大学网上党支部', '郭朝辉副校长宣讲党的十九大精神', null, '1', '200');
INSERT INTO `news` VALUES ('<p style=\"text-align:center\"><strong><span style=\"font-size:29px;font-family:黑体;line-height:150%\">重走地震之路</span></strong> &nbsp;</p><p style=\"text-align:center\"><strong><span style=\"font-size:21px;font-family:宋体;line-height:150%\">——汶川地震十周年地灾国重实验室野外考察记</span></strong> &nbsp;</p><p style=\"text-align:center\">&nbsp;</p><p style=\"text-align:left;text-indent:32px\"><br/></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:150%\">2018</span><span style=\"font-size:16px;font-family:宋体;line-height:150%\">年5月12日前后，全国各地的人来到汶川，纪念发生在10年前的大地震。</span> &nbsp;</p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:150%\">人群中，一队队伍显得很不一样——他们中的很多外国专家，在地震遗留的断桥、造成的滑坡、泥石流前查看着遥感影像图、翻看着论文数据资料……</span> &nbsp;</p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:150%\">这是由成都理工大学主办的第五届强震诱发的地质灾害及长期效应国际学术大会暨纪念汶川地震十周年国际学术研讨会的野外考察专家团队。地质灾害防治与地质环境保护国家重点实验室（以下简称地灾国重实验室）邀请来自意大利、荷兰、新西兰等13个国家和地区的350多名地质地灾专家学者参加大会，并于15、16日两天分为三组人马赴地震灾区进行野外考察。</span> &nbsp;</p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:150%\"></span>&nbsp;</p><p style=\"text-align:center;text-indent:32px\"><br/></p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:宋体;line-height:150%\"><span style=\"font-size:16px;font-family:宋体;line-height:150%\"><img title=\"7gpt8viogc4x~j9q}w%[[h5\" alt=\"7gpt8viogc4x~j9q}w%[[h5\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180521/20180521161915_857.png\" width=\"500\" height=\"325\"/></span></span> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:宋体;line-height:150%\"><span style=\"font-family:FangSong_GB2312\">中外专家学者查看滑坡现场</span></span> &nbsp;</p><p style=\"text-align:center\">&nbsp;</p><p style=\"text-align:center;text-indent:32px\"><br/></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:150%\">1号线由许强教授牵头带领60余位国内外专家学者参观。1933年叠溪地震遗址、堰塞湖古湖相沉积物、2017年新磨滑坡、汶川七盘沟泥石流、地震遗址漩口中学、牛眠沟泥石流和紫坪铺水电站……这条线路上地质现象丰富，趣味横生，甚至部分地质现象还系首次公开。</span> &nbsp;</p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:150%\">10</span><span style=\"font-size:16px;font-family:宋体;line-height:150%\">年前，汶川发生了里氏8.0级特大地震，地灾国重实验室的专家学者第一时间奔赴灾区，指导抢险救灾工作。</span> &nbsp;</p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:150%\">10</span><span style=\"font-size:16px;font-family:宋体;line-height:150%\">年来，地灾国重实验室与地震灾区的联系更加密切。实验室先后派出千余人次赴灾区进行地质灾害调查，发出数百篇科学论文，为汶川灾区重建提供了科学理论支撑。这里还培养了一批批年轻的科研人，他们和老一辈专家学者并肩作战，接过了防灾减灾的重任。</span> &nbsp;</p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:150%\">10</span><span style=\"font-size:16px;font-family:宋体;line-height:150%\">年后，重走地震之路，除了不能忘却的纪念，更重要的是进一步树立不断进取的科研精神，还有为国为民的防灾减灾决心。</span> &nbsp;</p><p style=\"text-align:left;text-indent:32px\">&nbsp;</p><p style=\"text-align:left;text-indent:32px\"><br/></p><p style=\"text-align:center\"><strong><span style=\"font-size:16px;font-family:宋体;line-height:150%\">揭秘古蜀真相&nbsp; 1号线是一座地质博物馆</span></strong> &nbsp;</p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:150%\">这次野外考察的一号线是一条景色动人有文化内涵的线路——翠绿巍峨的大山中间，镶嵌着宁静澄绿的堰塞湖；羌味十足的新磨古镇里，唐代的点将台摩崖造像至今栩栩如生；波光粼粼的水库大湖边，开发了新的旅游项目滑翔伞。</span> &nbsp;</p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:150%\">可是在这群地质类专家学者眼里，这里是另一番景象——古代滑坡形成的堰塞湖受地震的影响消失不见；大规模的高速远程滑坡形成世界少有的滑坡群景观；三万年来的湖相沉积物历经反复静水堆积、地震作用、滑坡堵江、河流冲刷后，在地形地貌上展现了独特的高山峡谷V字形、河流改道、阶地堆积等引人入胜的地质景观……</span> &nbsp;</p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:150%\">在地灾国重实验室持续对这些地质现象的研究中，已经取得了颇丰的成果。王兰生教授发表的《岷江叠溪古堰塞湖的发现》唱响了这片区域的研究号角，《岷江叠溪古堰塞湖的研究意义》《孢粉特征》《古气候演化》《新磨滑坡机理研究》等论文，继续加深了对这些区域的研究与探索。对于参与野外科考的这些地质专家来说，这条线路就是一座地质博物馆。</span> &nbsp;</p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:150%\"></span>&nbsp;</p><p style=\"text-align:center;text-indent:32px\"><br/></p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:宋体;line-height:150%\"><img title=\"qq图片20180521161951\" alt=\"qq图片20180521161951\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180521/20180521162149_774.jpg\" width=\"500\" height=\"337\"/> </span></p><p><span style=\"font-family:FangSong_GB2312\">许强教授在现场讲解</span><br/> &nbsp;</p><p style=\"text-align:center\">&nbsp;</p><p style=\"text-align:center;text-indent:32px\"><br/></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:150%\">在考察叠溪古堰塞湖的湖相沉积区域时，许强教授把专家们聚集到一起，向他们展示这个区域显示的多次泥石流、滑坡、潜涌、地下暗流对湖底的冲刷，当年的暗潮涌动早已凝固为了石块，“这条发白的弯弯曲曲的石纹，就是表象之一，我们称之为‘浊流’。”</span> &nbsp;</p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:150%\">多年的地质钻研，让许强早已把这些不起眼的石头品出了历史和文化，他讲起了叠溪和古蜀国的关系。通过对1999年叠溪地区古堰塞湖的湖相沉积物中的孢粉、有机质、碳和氧同位素、碳酸钙和颗粒大小等气候指标物的检验，地灾国重实验室确定了叠溪堰塞湖地区的七个气候周期，时间跨度为3000～15000年。</span> &nbsp;</p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:150%\">受地震干扰的沉积剖面与气候变化具有较好的一致性，这表明地质演化与气候及环境变化是相关联的。继而通过对比上下游岩土体成分，由此追溯成都平原的形成、发展和变迁，许强表示，继续深挖下去，有望还原金沙文明、三星堆文明被掩埋的真相，“从地质学角度解释历史文化是一件大事，将地质学同人文相结合，极具现实意义。之前一直是王兰生教授在研究这个方向，我们年轻的一代要把研究的‘棒子’接过来。”</span> &nbsp;</p><p style=\"text-align:left;text-indent:32px\">&nbsp;</p><p style=\"text-align:center\"><strong><span style=\"font-size:16px;font-family:宋体;line-height:150%\">传承成理精神&nbsp; 国重形成地质研究梯队</span></strong> &nbsp;</p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:150%\">这种传承早已是地灾国重实验室的传统。在许强的讲解过程中，经常提及他的老师黄润秋，说起他对于许多地质问题研究思路和方法。</span> &nbsp;</p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:150%\">这一点，深有体会的是来自荷兰ITC（国际航天测量与地球科学学院）的西斯（Cees van Westen）教授。他是继国际工程地质与环境协会前主席尼克（Niek Rengers）之后中荷地质学术共同发展的新桥梁、地灾国重实验室发展的推动者。谈及地灾国重实验室的传承文化，他非常羡慕，“实验室的唐川教授和朱静教授、唐晨晓博士一家三代都在研究泥石流，堪称‘泥石流世家’。”虽然西斯自己的三个女儿和两个儿子无一从事地质相关专业，但他自豪地把地灾国重实验室的范宣梅研究员当作自己的“第四个女儿”。原来，范宣梅研究员作为许强和西斯联合培养的优秀学生，斩获地质类国际国内大小奖项，主持和参与国家、省部级及欧盟科研项目10余项。</span> &nbsp;</p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:150%\">在泥石流领域，此次负责讲解之一的常鸣副教授师从地灾国重实验室的唐川教授。16日上午，考察团在常鸣的带领下来到了七盘沟。2013年7月11日，七盘沟暴发了大规模泥石流灾害,造成百座房屋倒塌，大量群众流离失所。灾害发生后，地灾重点实验室组织一大批专家到现场开展应急抢险工作，进行了详细的野外调查及室内物理实验，分析了七盘沟溃决型泥石流的形成机制，以期提出针对溃决型泥石流的防治工程措施的建议及监测预警的思路。</span> &nbsp;</p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:150%\">这些年来，对于常鸣来说，出野外阴雨天气在沟道两岸有石头滚落“是很常见的事”。就拿七盘沟来说，他已经来过5次，已经是这条路上的地质“老将”——每天一早就出发，带着学生爬坡上山，勘查取样，午餐就吃点自己带的干粮，晚上回来住在当地居民家中。</span> &nbsp;</p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:150%\">据常鸣介绍，实验室唐川教授团队一直在研究七盘沟的溃决特性和溃决型泥石流的定量风险评价。截止目前，唐川已有两位博士和两位硕士完成了关于溃决型泥石流研究的毕业论文，实验室师生在地质刊物上发表了若干相关文章，他所带领的团队也还在继续研究这一地质点。</span> &nbsp;</p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:150%\"></span>&nbsp;</p><p style=\"text-align:left;text-indent:32px\"><br/></p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:宋体;line-height:150%\"><img title=\"qq图片20180521162201\" alt=\"qq图片20180521162201\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180521/20180521162240_79.jpg\" width=\"500\" height=\"331\"/></span> </p><p><span style=\"font-family:FangSong_GB2312\">常鸣副教授在讲解遥感影像图</span><br/> &nbsp;</p><p style=\"text-align:center\">&nbsp;</p><p style=\"text-align:center;text-indent:32px\"><br/></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:150%\">更让人惊喜的是，此次同行的研一学生罗斌斌，承担了大量讲解、翻译、接待任务，他是范宣梅的学生，已经在一号线上来回奔走了数十次。</span> &nbsp;</p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:150%\">16</span><span style=\"font-size:16px;font-family:宋体;line-height:150%\">日考察结束，团队乘车返回成都。18日，罗斌斌又马不停蹄地赶往叠溪地区进行考察研究，以前出野外研究新磨村滑坡时，他曾住在当地村民王大姐家。</span> &nbsp;</p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:150%\">王大姐是新磨村滑坡的幸存者之一，夫妇二人的兄弟姐妹几乎全部丧生于那场灾难。曾经的伤痛使她认识到了防灾减灾的重要性，她曾跟罗斌斌说：“希望我的娃娃好好学习，考个好大学，如果他能考上成理，我一定要让他学地质专业，然后像你们一样，为家乡的防灾减灾事业做贡献。”</span> &nbsp;</p><p style=\"text-align:left;text-indent:32px\">&nbsp;</p><p style=\"text-align:center\"><strong><span style=\"font-size:16px;font-family:宋体;line-height:150%\">地质研究也要与大自然赛跑 专家也有遗憾</span></strong> &nbsp;</p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:150%\">对于众多地质专家来说，比读懂大自然的更重要的是和大自然抢时间。因此</span><span style=\"font-size:16px;font-family:宋体;line-height:150%\">新磨村的这场滑坡灾难，正是他们的遗憾。</span> &nbsp;</p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:150%\">2017年6月24日凌晨5点左右，一场巨大的岩崩摧毁了茂县新磨村，滑坡掩埋了64座房屋，造成10人死亡，75人失踪。</span> &nbsp;</p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:150%\">灾难发生两个多小时后，许强、裴向军立即出发赶往灾害现场分析滑坡原因、指导应急搜救。</span> &nbsp;</p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:150%\">新西兰地质调查局的莫瑞（Mauri McSaveney）是我校的客座教授，是世界大滑坡启动机理和运动机理的研究先驱。谈及此次的灾前预警，他说到：“坍塌位置距离村庄太远了，而且太多植被遮住了滑坡的裂缝路径，提前查找隐患几乎不可能。”而他充分肯定了地灾国重实验室第一时间进行灾后风险评估的担当和能力，对地灾国重实验室的研究员们给予了高度赞扬。</span> &nbsp;</p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:150%\"></span>&nbsp;</p><p style=\"text-align:left;text-indent:32px\"><br/></p><p style=\"text-align:center\"><img title=\"qq图片20180521163126\" alt=\"qq图片20180521163126\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180521/20180521163210_742.jpg\" width=\"500\" height=\"331\"/> </p><p><span style=\"font-family:FangSong_GB2312\">许强教授在汶川震中向专家现场作讲解</span> &nbsp;</p><p style=\"text-align:center\">&nbsp;</p><p><span style=\"font-size:16px;font-family:宋体;line-height:150%\">&nbsp;&nbsp;&nbsp;&nbsp;考察团在新磨村滑坡遗址处逗留了许久。</span><span style=\"font-size:16px;font-family:宋体;line-height:150%\">在遗址现场，许强对于这次滑坡的成因进行了详细的讲解，并将其定义为“高位隐蔽滑坡”：即普通遥感无法看见、即使动用人力到达也较难察觉隐患，且起始点距离村落具有一定距离的滑坡。“大滑坡在滑动之前一定会有裂缝，就算没有裂缝之前也会存在变形，灾害监测就是观察这些变化。”许强补充说，“不过我们现在已经找到了解决这种类型滑坡的预警办法，实验室正在加紧研究。”</span>&nbsp;</p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:150%\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></p><p style=\"text-align:center\"><strong><span style=\"font-size:16px;font-family:宋体;line-height:150%\">成理奇迹&nbsp; 抽掉7000余根锚杆的成都生命水库引热议</span></strong> &nbsp;</p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:150%\">这一次考察，最后一站选在了回程路上的都江堰紫坪铺水利枢纽。</span><span style=\"font-size:16px;font-family:宋体;line-height:150%\">紫坪铺水利枢纽位于成都上游的岷江，即是成都市主要饮用水来源，也是最重要的一个防洪屏障。坝顶长度达663.77米，总库容达11.12亿立方米的紫坪铺水利枢纽，设计之初就引人瞩目。</span> &nbsp;</p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:150%\">当年，地灾国重实验室汪家林教授带领着团队参与了水库大坝坝肩的边坡设计。那时，大坝已经按照安全标准定下了边坡锚杆（用于对边坡、隧道和坝体进行主体加固的杆件体系结构）的数量，成理团队研究后认为原来的设计过于粗略，便着力于优化设计：在原有的基础上进行了严密的计算和反复论证，减少了锚杆使用量，同时还对枢纽部分进行了加固。最后，在成理团队反复论证下，边坡设计足足减少了7000余根锚杆，节省了1000余万元的经费。</span> &nbsp;</p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:150%\">2006</span><span style=\"font-size:16px;font-family:宋体;line-height:150%\">年5月30日，紫坪铺水利枢纽工程四台发电机组全部投产发电。2008年5月12日，地震的消息瞬间传遍了大江南北。汪家林得知这个消息，坐不住了，和另外几位专家一起火速赶往位于都江堰的紫坪铺水库，查看坝体情况。&nbsp;&nbsp; </span> </p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:150%\">在安全标准的基础上减少大量锚杆是否科学？少了这么多锚杆的支持，能否保证坝体的稳定性？当天下午，汪家林驱车一个多小时赶到大坝现场，松了一口气，坝体完整。几天后，结论出来了：各工程边坡均受到地震影响，但所有边坡均未产生整体失稳，且此前加固过的枢纽部分完好无损——大坝是安全的！</span> &nbsp;</p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:150%\">在野外考察现场，外国专家们就这个问题围着紫坪铺水库相关负责人问个不停，“unbelievable”、“amazing”这样的词语不停在他们嘴里冒出来。坝体离5.12震中只有17.7km，如此强烈的地震，一旦坝体受损溃决，水库里的水便会一泻而下，直捣成都平原，将这片土地变为汪洋大海。</span> &nbsp;</p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:150%\"></span>&nbsp;</p><p style=\"text-align:left;text-indent:32px\"><br/></p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:宋体;line-height:150%\"><img title=\"qq图片20180521163227\" alt=\"qq图片20180521163227\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180521/20180521163309_942.jpg\" width=\"500\" height=\"332\"/></span> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:宋体;line-height:150%\"><span style=\"font-family:FangSong_GB2312\">许强教授、胡伟教授等向外国专家展示科研论文</span></span> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:宋体;line-height:150%\">&nbsp; </span></p><p style=\"text-align:center;text-indent:32px\"><br/></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:150%\">震后10年来，成理国重的师生团队仍一直致力于紫坪铺水库数期滑坡相关的国际博士课程培训班；数十人次在国际会议上作了关于汶川地震地质灾害研究成果的特邀报告和大会学术报告；实验室与26个国家和地区的学术研究机构建立了长期的交流与合作关系，多次外派师生留学、访学和进行学术交流……如今，实验室已成为国际上重要的地质灾害研究中心之一。</span> &nbsp;</p><p style=\"text-align:left;text-indent:32px\">&nbsp;</p><p style=\"text-align:left;text-indent:32px\"><br/></p><p style=\"text-align:center;text-indent:32px\"><br/></p><p style=\"text-align:center\"><strong><span style=\"font-size:16px;font-family:宋体;line-height:150%\">国内外专家赞赏 会议手册被珍藏</span></strong> &nbsp;</p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:150%\">在这次考察之路中，79岁的尼克教授走完了全程，不管是爬山还是上坝。他是这支考察队伍里面年龄最大的人，看到“古怪”的地质现象，立马就会开启手扶下巴的“思考模式”。</span> &nbsp;</p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:150%\">2008</span><span style=\"font-size:16px;font-family:宋体;line-height:150%\">年5月，尼克·伦格斯在汶川地震的前几天才到过成都，9月重返四川之时，他被中国政府的灾后重建速度所震撼，同时也高度赞赏了成理在灾后对于这片区域所做的灾后自然质量评估、滑坡风险评估和泥石流情况调研等工作。此次考察之路，他已经很熟悉了。他曾经多次和地灾国重实验室的老师们爬上这里的高山，去研究这里的地质现象，“成理带给世界丰富的经验，我能帮助成理同世界交流更多，我很享受这种互相学习互相借鉴的过程。”</span> &nbsp;</p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:150%\"><span style=\"font-size:16px;font-family:宋体;line-height:150%\"><span style=\"font-family:FangSong_GB2312\">&nbsp; </span></span></span></p><p style=\"text-align:center;text-indent:32px\"><br/></p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:宋体;line-height:150%\"><img title=\"qq图片20180521163323\" alt=\"qq图片20180521163323\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180521/20180521163512_531.jpg\" width=\"500\" height=\"329\"/></span> &nbsp;</p><p><span style=\"font-size:16px;font-family:宋体;line-height:150%\"> &nbsp; <span style=\"font-family:FangSong_GB2312\">中外专家学者探讨叠溪遥感影像图</span> &nbsp;<p style=\"text-align:center\">&nbsp;</p><p style=\"text-align:center;text-indent:32px\"><br/></p></span> &nbsp;</p><p style=\"text-align:center;text-indent:32px\"><br/></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:150%\">谈到此次地灾国重实验室举办的纪念汶川地震十周年系列活动，来自新疆工程学院的帕尔哈提教授和孟和教授都表示获益匪浅。帕尔哈提介绍到，新疆也是地质灾害多发区，新疆工程学院正在积极申报建立自治区地质灾害防灾减灾重点实验室，“获得了成理地灾国重实验室的重要支持”。对于本次研讨会和野外考察工作，他给予了高度评价，表示此次活动体现了强震地质灾害研究的国际最高水平，充分展示了地灾国重实验室的风采，“找到了我们自治区重点实验室下一步的研究方向和工作重点。”</span> &nbsp;</p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:150%\">这次会议让许多专家留下了深刻印象。来自香港地质协会的主编陈顺明是第一次来到灾区，在他的黑色皮包里，会议手册一直放在最重要的位置。每到一个地方，他就拿出这本全英文的手册细细比对。因为初次到来，第一天晚上，他一回到房间就立马查起了灾区各个地名，并用中文在拼音上标注出来，一本手册被他标注得密密麻麻。他表示，这次会议和考察，让他着实开放了视野。</span> &nbsp;</p><p style=\"text-align:left;text-indent:32px\"><br/></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:150%\">到达汶川映秀镇时正值晌午，撕裂造型的震中石也攫住了每个参观者的心。震中石背后的断桥瘫在河滩，剩下几块大的石块散落一旁。离震中石不远的地方，当地村民正在贩卖当季的水果，饱满的车厘子颜色鲜艳，红彤彤如太阳般炙热……</span> &nbsp;</p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:150%\">十年过去了，汶川也怀抱着伤痛重新出发。而研究地质构造，助力防灾减灾对于每一个成理科技工作者来说，依然任重道远。</span> &nbsp;</p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:150%\">&nbsp; </span></p><p style=\"text-align:center;text-indent:32px\"><br/></p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:宋体;line-height:150%\"><img title=\"qq图片20180521163547\" alt=\"qq图片20180521163547\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180521/20180521163624_42.jpg\" width=\"500\" height=\"331\"/></span> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:FangSong_GB2312;line-height:150%\">合照</span> &nbsp;</p><p><br/></p>', '2018-05-25 12:16', '成都理工大学网上党支部', '重走地震之路', null, '10', '201');
INSERT INTO `news` VALUES ('<h1 style=\"text-align:center\">习近平与德国总理默克尔举行会晤</h1><p style=\"text-align:center\">2018年05月24日 20:26:59来源： 新华社　作者： 记者 白洁</p><div class=\"col-md-4\"></div><p></p><p><span style=\"font-family:仿宋;color:navy\"><img style=\"margin-left:200px\" id=\"{8BD7267A-6F9D-41F1-A180-4BAB6B8658BC}\" title=\"\" alt=\"\" src=\"http://www.qstheory.cn/yaowen/2018-05/24/1122883897_15271726918271n.jpg\"/></span></p><p style=\"TEXT-ALIGN: center\"><span style=\"font-family:仿宋;color:navy\">5月24日，国家主席习近平在北京人民大会堂与德国总理默克尔举行会晤。 新华社记者 刘卫兵 摄</span></p><p>　　习近平欢迎默克尔第11次访华，赞赏默克尔对中德关系的高度重视。习近平指出，2014年中德建立全方位战略伙伴关系以来，两国关系得到长足发展，合作广度和深度达到前所未有的水平。中方愿同德方共同努力，推动双边关系不断迈上新高度。</p><p>　　习近平强调，中德两国要做合作共赢的示范者、中欧关系的引领者、新型国际关系的推动者、超越意识形态差异的合作者。这应当是双方下阶段推进两国关系共同努力的方向。双方要保持高层密切交往，加强对双边关系的顶层设计，鼓励两国各领域交流合作，用好机制性对话，我们欢迎德国抓住中国新一轮开放机遇；要以科技创新为引领，共同做大蛋糕、做大产业、做大市场，双方可以在未来产业领域开展更多合作，共同开拓第三方市场；要以人文合作为抓手，夯实两国友好的民意基础；要展现中德两国的责任和担当，共同应对各种全球性挑战，我们愿继续同德方在多边框架内一道推进全球治理和多边主义，在推动解决热点难点问题方面加强沟通合作。中方赞赏德方支持“一带一路”倡议，欢迎德国企业积极参与。</p><p>　　习近平指出，中国始终坚定支持欧洲自主选择的一体化道路，希望欧盟保持团结、稳定、开放、繁荣。中方愿同欧方一道，坚定倡导多边主义，向国际社会发出更多明确、可预期、提振信心的信息，推动国际形势朝着稳定和有利于各方的方向发展。</p><p>　　默克尔表示，很高兴连任德国总理后很快来华访问。我赞同习主席对德中关系的评价。中国是国际社会重要成员，是德国重要贸易伙伴。德中关系已进入新时代，德国愿抓住中国深化开放带来的新机遇，扩大德中贸易投资合作，扩大人文交流。在当前世界形势发生很大变化的背景下，德中两国要加强在国际事务中沟通协调，密切在二十国集团等多边框架内业已富有成效的合作。德国支持增进欧中合作关系。</p><p>　　双方还就国际贸易、伊朗核问题等共同关心的问题交换了看法。</p><p>　　杨洁篪、何立峰等参加会晤。</p><p><br/></p>', '2018-05-25 12:29', '成都理工大学网上党支部', '习近平与德国总理默克尔举行会晤', null, '10', '203');
INSERT INTO `news` VALUES ('\r\n<p style=\"text-align: center;\"><strong><span style=\"font-size:29px;font-family:黑体\"><strong><span style=\"font-size:24px;font-family:黑体\">诺奖得主中村修二教授讲述蓝光LED的“今生与未来”</span></strong></span></strong> &nbsp;</p><p style=\"text-align:center\"><br/></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">5</span><span style=\"font-size:16px;font-family:宋体;line-height:200%\">月4日上午，来自美国加州大学圣塔芭芭拉分校的诺贝尔物理学奖得主---被誉为“蓝光之父”的Shuji Nakamura（中村修二）教授莅临我校，在地质灾害防治与地质环境保护国家重点实验室国际学术报告厅为300余名师生作了“高效蓝光LED器件的发明以及固态发光器件的未来”专题学术报告。</span> &nbsp;</p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">报告会前，副校长郭朝辉会见了中村修二教授，对其表示热烈欢迎和诚挚感谢，并为中村修二颁发“成都理工大学名誉教授”聘书，报告会由地球物理学院院长曹俊兴教授主持。</span> &nbsp;</p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\"></span>&nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\"><img title=\"qq图片20180505130938\" alt=\"qq图片20180505130938\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180505/20180505130948_865.png\" width=\"500\" height=\"343\"/></span> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">副校长郭朝辉为中村修二教授颁发聘书</span> &nbsp;</p><p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<img style=\"margin-left:220px\" title=\"qq图片20180505160532\" alt=\"qq图片20180505160532\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180505/20180505160436_880.png\" width=\"500\" height=\"376\"/> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px\">地球物理学院院长曹俊兴主持报告会</span> &nbsp;</p><p style=\"text-align:center\">&nbsp;</p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">中村修二教授从他个人的成长经历及科研经历谈起，让与会者看到一个基础条件并不突出的学者依靠自身的努力不断在科研上取得进步，不断打破他人口中的“不可能”和“没前景”，并一步一步走向了诺贝尔的领奖台。他重点介绍了LED的发展历程，尤其是蓝光LED灯的研发阶段中所攻克的几个重难点，并结合生活实际，介绍了蓝光LED对能源，健康等领域的积极影响。最后给大家展示了基于激光光源的LED相关应用前景。</span> &nbsp;</p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\"></span>&nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\"><img title=\"qq图片20180505131038\" alt=\"qq图片20180505131038\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180505/20180505131125_488.png\" width=\"500\" height=\"368\"/></span> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">中村修二教授作报告1</span> &nbsp;</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<img style=\"margin-left:220px\" title=\"qq图片20180505160935\" alt=\"qq图片20180505160935\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180505/20180505160831_768.png\" width=\"500\" height=\"368\"/> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">中村修二教授作报告2</span> &nbsp;</p><p style=\"text-align:center\">&nbsp;</p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">报告会持续两个多小时，师生们对中村修二教授的研究表现出浓厚的兴趣，在现场提问和交流环节，大家踊跃提问，对</span><span style=\"font-size:16px;font-family:宋体;line-height:200%\">LED</span><span style=\"font-size:16px;font-family:宋体;line-height:200%\">光源的相关原理，应用问题进行了仔细的探讨，并就中美日三个国家的科研、教育等方面进行了全英文的充分交流。中村教授用自己的亲身经历告诉大家要敢于“take some risks and explore freely”，勇于探索，不要被自己的所处的环境限制住，敢于冒险，敢于自由探索，走出属于自己的道路。</span> &nbsp;</p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\"></span>&nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\"><img title=\"img_2666\" alt=\"img_2666\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180505/20180505160036_486.jpg\" width=\"500\" height=\"333\"/></span> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">学生提问1</span> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\"><img title=\"qq图片20180505160357\" alt=\"qq图片20180505160357\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180505/20180505160252_423.png\" width=\"500\" height=\"357\"/></span> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">学生提问2</span> &nbsp;</p><p style=\"text-align:center\">&nbsp;</p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">本次报告会是学校“砚湖大讲堂•聆听大师的声音”活动之一，学校依托活动将邀请诺贝尔奖获得者、院士、长江学者等来校开展学术交流，开阔师生研究视野，启迪学科发展，掌握科技前沿趋势，对于学校加快国际化办学进程、推进‘双一流’建设具有重要意义。</span> &nbsp;</p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\"></span>&nbsp;</p><p style=\"text-align:left;text-indent:32px\"><strong><span style=\"font-size:16px;font-family:宋体;background:white;line-height:200%\">专家简介：</span></strong> &nbsp;</p><p><span style=\"font-size:16px;font-family:楷体;line-height:200%\">中村修二，1954 年出生于日本伊方町，毕业于日本德岛大学，日裔美籍电子工程学家，美国加州大学圣芭芭拉分校工程学院材料系教授。中村修二1993 年在日本日亚化学工业株式会社就职期间基于GaN 开发了高亮度蓝色 LED，从而广为人知。当时，开发一种蓝色 LED 被认为是不可能的，此前的 20 年间只有红色和绿色 LED。中村修二因发明“高效蓝色发光二极管”获得 2014 年诺贝尔物理学奖。中村修二教授被誉为 21世纪的爱迪生，获得了一系列荣誉，包括仁科纪念奖（1996），IEEE Jack A.莫顿奖，英国顶级科学奖（1998）；富兰克林奖章（2002），2003 年中村教授入选美国国家工程院（NAE）院士，2006年获得千禧技术奖。2000年中村教授加入加州大学圣芭芭拉分校。</span></p><p><br/></p>', '2018-06-03 19:51', '成都理工大学网上党支部', '诺奖得主中村修二教授讲述蓝光LED的“今生与未来”', null, '10', '205');
INSERT INTO `news` VALUES ('<p style=\"text-align:center\"><strong><span style=\"font-size:29px;font-family:黑体\">关于加强网站内容审核和更新的通知</span></strong> &nbsp;</p><p style=\"margin-left:0\"><strong><span style=\"font-size:21px;font-family:宋体\"><br/> </span></strong> </p><p style=\"margin-left:0\"><strong><span style=\"font-size:21px;font-family:宋体\">各单位、各部门：</span></strong> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;line-height:200%;font-family:宋体\">为做好本科教学审核评估工作，充分发挥校园网在人才培养、教学科研、文化传承与创新等方面的窗口功能作用，就进一步加强各单位（部门）的网页内容审核、更新等事项通知如下：</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;line-height:200%;font-family:宋体\">一、高度重视校园网络信息化建设和管理工作，把此项工作列为重要工作安排部署，认真做好网络建设和管理工作。各单位（部门）主要负责人为本单位（部门）网络文化建设与信息管理的第一责任人，各单位（部门）须指定专（兼）职网络信息管理员（新闻信息联络员）一人，并报党委宣传部网络信息管理办公室备案。网络文化建设与信息管理的主管领导或网络信息管理员（新闻信息联络员）更换时，须及时报党委宣传部网络信息管理办公室备案登记。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;line-height:200%;font-family:宋体\">二、对网页的基础性内容、工作动态等进行全面细致的梳理、更新和改进，使网络建设能够及时反映单位（部门）的工作状况，起到良好的网络宣传作用。网站应体现单位（部门）全称，如涉及校标、校名、校训等内容和图案，应统一采用学校官方公布的文字、图案，不得擅自更改图案和字体，导航栏应清晰反映本网站的结构，避免在同一个网站出现不同形式的导航栏。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;line-height:200%;font-family:宋体\">三、切实加强对所辖网站的管理，确保所有网页的运行安全，网页内容及时更新，且无违反国家法规政策和校规校纪的不良信息。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;line-height:200%;font-family:宋体\">请各单位（部门）<strong>于5月20日前</strong>对负责网站进行自查，更新信息，完善内容，确保信息全面、准确、及时。学校将于5月21日至30日期间，对各单位（部门）网站信息发布及更新情况进行监督检查。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;line-height:200%;font-family:宋体\">如有技术问题可联系网络与信息服务中心，联系人：王老师，电话：028-84078860；如有内容建设方面问题可联系党委宣传部网络信息管理办公室，联系人：黄老师，电话：028-84079895。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;line-height:200%;font-family:宋体\">特此通知。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;line-height:200%;font-family:宋体\"><br/> </span> </p><p style=\"text-indent:32px\"><span style=\"font-size:16px;line-height:200%;font-family:宋体\"><br/> </span> </p><p style=\"text-indent:32px\"><br/></p><p style=\"text-align:right;text-indent:32px\"><span style=\"font-size:16px;line-height:200%;font-family:宋体\">党委宣传部</span> &nbsp;</p><p><span style=\"font-size:16px;line-height:200%;font-family:宋体\">2018</span><span style=\"font-size:16px;line-height:200%;font-family:宋体\">年3月26日</span></p><p><br/></p>', '2018-06-03 20:28', '成都理工大学网上党支部', '关于加强网站内容审核和更新的通知', null, '9', '206');
INSERT INTO `news` VALUES ('<p style=\"text-align:center\"><strong><span style=\"font-family:黑体;font-size:29px\">郭朝辉副校长一行赴黑水县开展精准扶贫工作</span></strong> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"line-height:200%;font-family:宋体;font-size:16px\">5</span><span style=\"line-height:200%;font-family:宋体;font-size:16px\">月30日—31日，副校长郭朝辉与学校组织部、扶贫办、机关党委、旅游与城乡规划学院、环境学院、创新创业中心、图书馆等部门（单位）主要负责同志一起，赴阿坝藏族羌族自治州黑水县开展精准扶贫工作。本次精准扶贫工作主要有：按照省委组织部要求，代表学校党委送我校后勤集团副总经理韩正林到黑水县挂职上任县委副书记；根据今年校地帮扶协议，举行“志智双扶”基金暨图书捐赠仪式，开展学校党性教育基地授牌暨“红色筑梦之旅”启动仪式以及考察藏香猪基地建设等相关帮扶工作。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"line-height:200%;font-family:宋体;font-size:16px\">在黑水县精准扶贫挂职干部座谈会上，郭朝辉副校长表示学校党政高度重视精准扶贫工作及干部选派工作，近年来选拔了一批作风过硬、素质能力强、能干事、能干成事的干部到精准扶贫定点帮扶县助力精准脱贫。韩正林临行前，学校党委在谈话中要求他站在政治高度接受任务和挑战，加强学习，尽快熟悉工作、融入工作中，脚踏实地把工作真正做到百姓心中，坚决完成好脱贫攻坚工作。座谈会由县委副书记、县长何晓兵主持。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"line-height:200%;font-family:宋体;font-size:16px\"></span>&nbsp;</p><p style=\"text-align:center;text-indent:13px\"><span style=\"line-height:200%;font-family:宋体;font-size:16px\"><img title=\"黑水县精准扶贫挂职干部座谈会现场\" alt=\"黑水县精准扶贫挂职干部座谈会现场\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180601/20180601162135_513.jpg\" width=\"500\" height=\"294\"/></span> &nbsp;</p><p style=\"text-indent:32px\"><strong><span style=\"line-height:200%;font-family:宋体;font-size:16px\">黑水县精准扶贫挂职干部座谈会现场</span></strong> &nbsp;</p><p style=\"text-indent:32px\"><strong><span style=\"line-height:200%;font-family:宋体;font-size:16px\"></span></strong>&nbsp;</p><p style=\"text-indent:32px\"><span style=\"line-height:200%;font-family:宋体;font-size:16px\">在第69个国际儿童节到来之际，郭副校长一行在何晓兵县长的陪同下，在黑水县晴朗乡中心校举行了“志智双扶”基金暨图书捐赠仪式。大手牵小手，小爱铸大爱，郭副校长一行为孩子们送去了去年国家扶贫日我校全体师生捐赠的134,873元现金和扶贫办、机关党委、图书馆组织的价值7万余的图书一批。在捐赠仪式上，郭副校长希望来自我校的这份微薄心意，能为黑水的教育事业锦上添花，为黑水的贫困学子成长成才，尽一份绵薄之力；鼓励偏远贫困山村的孩子们，在艰苦的条件下，一定要养成良好的道德品质和文明习惯，刻苦学习，自立自强，做家里的好孩子，学校的好学生，社会的好公民，并早日成为有理想、有道德、有文化、有才华的一代新人；希望孩子们愉快学习，热爱运动，爱上阅读，通过图书认识山外的世界，快乐地成长！黑水县人民政府县长何晓兵对我校的慷慨解囊表示衷心的感谢，同时对“志智双扶”和学校近期组织的“双百双梦”活动、乡村人才能力建设专题培训表示高度的肯定。捐赠仪式由县委副书记廖军</span> &nbsp;</p><p><span style=\"line-height:200%;font-family:宋体;font-size:16px\">主持。</span> &nbsp;</p><p style=\"text-align:center;text-indent:13px\"><span style=\"line-height:200%;font-family:宋体;font-size:16px\"><img title=\"郭朝辉副校长讲话\" alt=\"郭朝辉副校长讲话\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180601/20180601162200_444.jpg\" width=\"500\" height=\"333\"/></span> &nbsp;</p><p style=\"text-align:center;text-indent:32px\"><strong><span style=\"line-height:200%;font-family:宋体;font-size:16px\">郭朝辉副校长讲话</span></strong> &nbsp;</p><p style=\"text-align:center;text-indent:13px\"><span style=\"line-height:200%;font-family:宋体;font-size:16px\"><img title=\"黑水县委副书记、县长何晓兵同志讲话\" alt=\"黑水县委副书记、县长何晓兵同志讲话\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180601/20180601162222_773.jpg\" width=\"500\" height=\"295\"/></span> &nbsp;</p><p style=\"text-align:center;text-indent:32px\"><strong><span style=\"line-height:200%;font-family:宋体;font-size:16px\">黑水县委副书记、县长何晓兵讲话</span></strong> &nbsp;</p><p style=\"text-align:center;text-indent:13px\"><span style=\"line-height:200%;font-family:宋体;font-size:16px\"><img title=\"“志智双扶”基金捐赠现场\" alt=\"“志智双扶”基金捐赠现场\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180601/20180601162244_387.jpg\" width=\"500\" height=\"375\"/></span> &nbsp;</p><p style=\"text-align:center;text-indent:32px\"><strong><span style=\"line-height:200%;font-family:宋体;font-size:16px\">“志智双扶”基金捐赠现场</span></strong> &nbsp;</p><p style=\"text-align:center;text-indent:32px\"><strong><span style=\"line-height:200%;font-family:宋体;font-size:16px\"></span></strong>&nbsp;</p><p style=\"text-indent:32px\"><span style=\"line-height:200%;font-family:宋体;font-size:16px\">为学习贯彻习近平新时代中国特色社会主义思想和党的十九大精神，深入落实习近平总书记“青年红色筑梦之旅”大学生重要回信精神，郭副校长一行在黑水县沙石多乡昌德村开展了学校党性教育基地授牌暨“红色筑梦之旅”启动仪式。她希望我校党员干部和青年学生继承和弘扬红军精神“长征魂”，把82年前“战胜敌人、战胜自然、战胜自我”的长征精神融入到新时代的工作生活中，深入帮扶县的乡村和农户，结合学校专业、学科优势，从绿色兴农、科技兴农、电商兴农、教育兴农等多方面开展帮扶工作，推动当地社会经济发展，助力脱贫攻坚和乡村振兴，走好新时代长征路；另一方面通过研究红色文化、当地民族文化、自然文化以及党员干部的优秀精神品质，进一步提升我校人才培养的质量和水平。用长征精神解决实际工作中的难题，为建设“双一流”和高水平综合性大学而不断努力奋斗！学校党委组织部副部长、党校常务副校长邹功江代表学校向长征干部学院黑水分院现场教学点授牌，环境学院副教授韩智勇和旅游与城乡规划学院2016级旅游管理专业黑水籍研究生周正浩分别代表老师和学生发言。启动仪式由黑水县委常委、组织部长罗先全主持。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"line-height:200%;font-family:宋体;font-size:16px\"></span>&nbsp;</p><p style=\"text-align:center;text-indent:13px\"><span style=\"line-height:200%;font-family:宋体;font-size:16px\"><img title=\"学校党性教育基地现场授牌\" alt=\"学校党性教育基地现场授牌\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180601/20180601162321_496.jpg\" width=\"500\" height=\"325\"/></span> &nbsp;</p><p style=\"text-align:center;text-indent:32px\"><strong><span style=\"line-height:200%;font-family:宋体;font-size:16px\">学校党性教育基地现场授牌</span></strong> &nbsp;</p><p style=\"text-align:center;text-indent:32px\"><strong><span style=\"line-height:200%;font-family:宋体;font-size:16px\"></span></strong>&nbsp;</p><p style=\"text-indent:32px\"><span style=\"line-height:200%;font-family:宋体;font-size:16px\">郭副校长一行还深入到学校定点帮扶的晴朗乡二牛奶村，走访结对帮扶贫困户，考察了解藏香猪基地建设情况，听取晴朗乡脱贫攻坚情况汇报；调研考察奶子沟电商、文化、旅游扶贫情况，同时还在阿坝州长征干部学院黑水分院进行了一次别具一格的党性教育现场教学。与此同时，在黑水县委副书记廖军、副县长尔金初的陪同下，旅游与城乡规划学院院长朱创业教授一行还对麻窝乡别窝村和十古窝村的旅游资源进行了进一步的调研。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"line-height:200%;font-family:宋体;font-size:16px\"></span>&nbsp;</p><p style=\"text-align:center;text-indent:13px\"><span style=\"line-height:200%;font-family:宋体;font-size:16px\"><img title=\"慰问晴朗乡二牛奶村贫困户茸加拉\" alt=\"慰问晴朗乡二牛奶村贫困户茸加拉\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180601/20180601162349_489.jpg\" width=\"500\" height=\"333\"/></span> &nbsp;</p><p><strong><span style=\"line-height:200%;font-family:宋体;font-size:16px\">慰问晴朗乡二牛奶村贫困户茸加拉</span></strong></p><p><br/></p>', '2018-06-03 20:30', '成都理工大学网上党支部', '郭朝辉副校长一行赴黑水县开展精准扶贫工作', null, '5', '207');
INSERT INTO `news` VALUES ('<p style=\"text-align:center\"><strong><span style=\"font-size:29px;font-family:黑体\">“狮子”访“栗子”，共享经验话未来</span></strong> </p><p style=\"margin-left:0;text-align:center;text-indent:43px\"><strong><span style=\"font-size:21px;font-family:宋体\">四川师范大学全媒体中心来校交流调研</span></strong> </p><p style=\"text-indent:32px\"><span style=\"font-size:16px;line-height:200%;font-family:宋体\">5</span><span style=\"font-size:16px;line-height:200%;font-family:宋体\">月27日下午3点，四川师范大学校团委全媒体中心（狮山青年、师大之声、青春川师、图文中心）28名学生代表做客成理，与“云立方”学生传媒中心学生记者在行政楼第三会议室就校园媒体的管理模式、业务开展等方面开展座谈交流。</span> </p><p style=\"text-indent:32px\"><span style=\"font-size:16px;line-height:200%;font-family:宋体\">在播放了“云立方”学生传媒中心宣传片后，“云立方”事业发展规划处简要介绍了中心的组织架构、专业分工，并从育人文化、传承创新、媒介融合三方面分享了“云立方”四年融媒实战经验。</span> </p><p style=\"text-align:center\"><br/></p><p style=\"text-align:center;text-indent:37px\"><strong><span style=\"font-size:19px;line-height:200%;font-family:仿宋\"><img src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180529/20180529113428_836.jpg\" title=\"图一_500\" alt=\"图一_500\"/><br/> </span></strong> </p><p style=\"text-align:center;text-indent:37px\"><strong><span style=\"font-size:19px;line-height:200%;font-family:仿宋\">座谈会现场</span></strong> </p><p style=\"text-align:center;text-indent:37px\"><strong><span style=\"font-size:19px;line-height:200%;font-family:仿宋\"><img src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180529/20180529113442_319.jpg\" title=\"图二-500\" alt=\"图二-500\"/><br/> </span></strong> </p><p style=\"text-align:center\"><br/></p><p style=\"text-align:center;text-indent:37px\"><strong><span style=\"font-size:19px;line-height:200%;font-family:仿宋\">四川师范大学学生代表观看“云立方”宣传片</span></strong> </p><p style=\"text-indent:32px\"><span style=\"font-size:16px;line-height:200%;font-family:宋体\">座谈会后，两校社团开展了点对点的深入互动。学生记者们畅所欲言，气氛融洽，部分社团间还计划开展新合作。大学生记者站与《狮山青年》杂志社重点探讨了纸媒的栏目设置与新闻实践人才培养；理工之声广播台与师大之声广播台初步计划两校DJ共同直播节目；理工电视台与青春川师通过学习观摩相关视频作品，就双方此前合作总结经验；新媒体工作室与青春川师交流了管理运营模式，深入探讨了高校新媒体发展建设等话题；新闻嗅觉图片社与图文中心讨论了文、图对接过程中存在的问题，并分享了原创策划的思路。</span> </p><p style=\"text-indent:32px\"><span style=\"font-size:16px;line-height:200%;font-family:宋体\">四川师范大学新媒体与我校新媒体此前已在官微多次联动。2017年11月11日，两校借“双十一”热点，联合出品《告白气球》MV；2018年5月，双方在“5•20”节点合作推送微信“给大家介绍一下，这是我的（男）女朋友”，两次隔空互动均获广泛关注。《狮山青年》杂志社主编蒋祎玲表示，成理“云立方”既分工明确又合作统一的机制非常值得借鉴，希望未来能与“云立方”深度融合、资源共享、共同发展。</span> </p><p style=\"text-align:center\"><br/></p><p style=\"text-align:center;text-indent:37px\"><strong><span style=\"font-size:19px;line-height:200%;font-family:仿宋\"><img src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180529/20180529113458_919.jpg\" title=\"图三_500\" alt=\"图三_500\"/><br/> </span></strong> </p><p><strong><span style=\"font-size:19px;line-height:200%;font-family:仿宋\">双方合影留念</span></strong></p><p><br/></p>', '2018-06-03 21:35', '成都理工大学网上党支部', '“狮子”访“栗子”，共享经验话未来', null, '8', '210');
INSERT INTO `news` VALUES ('<p><span style=\"line-height:150%;font-family:黑体;font-size:29px\"><span style=\"line-height:150%;font-family:楷体;font-size:16px\">【编者按】</span><span style=\"line-height:150%;font-family:宋体;font-size:16px\">&nbsp;</span><span style=\"line-height:150%;font-family:楷体;font-size:16px\">所谓名师，是于传道授业解惑之上，不断深思教学理念之人；是于三尺讲台之上，不断创新教学方法之人；更是于课堂时间之外，不断提升专业素养，围绕育人育才深耕教学实践之人……值此成都理工大学迎接本科教学审核性评估之际，党委宣传部特此专访我校各级教学名师，期待以其各具特色的教学方法及实践为剖面，展现我校教师在立德树人、教书育人、严谨治学上的思考及做法。</span></span> &nbsp;</p><p style=\"text-align:center\"><br/></p><p style=\"text-align:center\"><span style=\"line-height:150%;font-family:黑体;font-size:29px\"></span>&nbsp;</p><p style=\"text-align:center\"><span style=\"line-height:150%;font-family:黑体;font-size:29px\"><strong>与物理相伴的</strong><strong>40</strong><strong>年</strong></span> &nbsp;</p><p style=\"text-align:center\"><span style=\"line-height:150%;font-family:宋体;font-size:21px\"><strong>记校级教学名师、地球物理学院教授张正阶</strong></span> &nbsp;</p><p style=\"text-align:center\">&nbsp;</p><p style=\"text-indent:32px\"><span style=\"line-height:150%;font-family:楷体;font-size:16px\">人物名片：</span><span style=\"line-height:150%;font-family:楷体;font-size:16px\">张正阶，1958年10月生，四川南部人，教授，硕士导师。主要从事凝聚态物理和理论物理研究及教学工作，为本科生及研究生主讲《大学物理》、《电磁场与电磁波》、《量子力学》、《固体物理》等课程。主持和主研包括国家自然科学基金、“百人计划”等在内的科研项目十余项，包括省级精品课程、省级精品开放课程及省级精品资源共享课程《电磁场与电磁波》在内的教研教改项目十余项。先后在国际国内各级刊物发表论文30余篇，其中作为第一作者的论文被SCI（光盘版）收录9篇，EI收录1篇。曾获成都理工大学教学名师称号，获市校级科研及教学成果一、二、三等个人或集体奖多项。</span> &nbsp;</p><p style=\"text-indent:32px\">&nbsp;</p><p style=\"text-indent:32px\"><span style=\"line-height:150%;font-family:宋体;font-size:16px\">框架眼镜、西服衬衣，远远望见张正阶，不觉特别。走近，肩头、袖口的粉笔灰暴露了他的教师身份。60岁的物理老师张正阶在1978年考入成都理工大学，毕业后留校任教。40年过去，他和成理的缘分、和师生的缘分、和《大学物理》《电磁场与电磁波》《量子力学》等课程的缘分一直持续。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"line-height:150%;font-family:宋体;font-size:16px\">36载教学芳华，不善言辞的张正阶，唯有谈论起物理中的千千世界时言语不断，在他看来，站上讲台是人生中最有价值的事。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"line-height:150%;font-family:宋体;font-size:16px\"></span>&nbsp;</p><p style=\"text-align:center\"><span style=\"line-height:150%;font-family:宋体;font-size:16px\"><img title=\"95@{mmiq7b7_ogx$gy{w0{k\" alt=\"95@{mmiq7b7_ogx$gy{w0{k\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180524/20180524164338_297.png\" width=\"500\" height=\"342\"/></span> &nbsp;</p><p style=\"text-align:center\"><span style=\"line-height:150%;font-family:FangSong_GB2312;font-size:16px\">课堂上的张正阶</span> &nbsp;</p><p style=\"text-align:center\">&nbsp;</p><p style=\"text-align:center\"><br/></p><p style=\"text-align:center;margin-left:0\"><span style=\"line-height:150%;font-family:宋体;font-size:16px\"><strong>36</strong></span><span style=\"line-height:150%;font-family:宋体;font-size:16px\"><strong>年大物课，讲题如同“剖麻雀”</strong></span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"line-height:150%;font-family:宋体;font-size:16px\">在《大学物理》例题课，张正阶迈上讲台，身后的投影幕上是一道物理题。对这门课的上法，张正阶已烂熟于心，自1982年起，他已经教了36年的《大学物理》。在他看来，讲好一道物理题，就像是“解剖麻雀”，得一步步来。粉笔落在黑板上哐哐作响，张正阶写下一个角动量守恒公式。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"line-height:150%;font-family:宋体;font-size:16px\">温习理论，张正阶拿出了生活中的例子——“角动量守恒体现在跳水运动员通过调整自身角动量，来完成空中翻转的不同动作；而直升机为了防止机身翻转，会在机尾装上侧向旋叶……”说到这，他举起双手比划起旋叶旋转的动作，学生的眼神也跟着张正阶的手移动。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"line-height:150%;font-family:宋体;font-size:16px\">开始讲题，题干不过短短几行，张正阶却逐句拆解——先解释题中涉及到的“塌缩、星体内核”等课外物理概念，而后逐步分析物理过程，说着便列出物理方程式，给学生一定思考时间后，张正阶开始一边分析题中的物理过程，一边在黑板上演算推导。不一会，一面黑板就被写得满满当当。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"line-height:150%;font-family:宋体;font-size:16px\">方程式解完，题却还不算讲完，“这种解法不是最简便的，还有没有同学能提出不同解法？”张正阶的发问使得台下又掀起了一轮新的讨论。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"line-height:150%;font-family:宋体;font-size:16px\">课间，有学生到讲台上向张正阶求问，一个刚问完，另一个又上来了。解答完学生的疑惑，张正阶还没来得及喝一口水，第二节课的铃声已敲响了。核自院的大二学生廖光辉曾在大一时听过张正阶一年的《大学物理》：“张老师讲课，细得很，就算是课后习题他也会一步步给我们讲清楚。”</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"line-height:150%;font-family:宋体;font-size:16px\">下课，张正阶总是最后走出教室。他拿出笔记本，记录本节课的教学进度，“课多，不记下来就忘了。”仅在本学期，他就要任教四个班，上1门公共课、1门专业课，最多的时候一学期教学时长达400多个学时。课上完，张正阶肩膀、衣袖上满是粉笔灰，他不在意地掸一掸，笑着调侃道：“我们教师就是一个吸灰的职业。”</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"line-height:150%;font-family:宋体;font-size:16px\"></span>&nbsp;</p><p style=\"text-align:center\"><span style=\"line-height:150%;font-family:宋体;font-size:16px\"><img title=\"img_3060\" alt=\"img_3060\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180524/20180524164415_369.jpg\" width=\"500\" height=\"281\"/></span> &nbsp;</p><p style=\"text-align:center\"><span style=\"line-height:150%;font-family:FangSong_GB2312;font-size:16px\">正在板书的张正阶</span> &nbsp;</p><p style=\"text-align:center\">&nbsp;</p><p style=\"text-indent:32px\"><br/></p><p style=\"text-align:center;margin-left:0\"><span style=\"line-height:150%;font-family:宋体;font-size:16px\"><strong>构建知识体系，欲见树木先见林</strong></span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"line-height:150%;font-family:宋体;font-size:16px\">“欲见树木先见林”是张正阶36年于三尺讲台之上坚守的教学理念。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"line-height:150%;font-family:宋体;font-size:16px\">“要站在高处来观察物理学科”，张正阶表示，若没有立足于学科整体，学生学到的知识将会是零碎的，是无法形成有机联结的。因此，《大学物理》课上，张正阶注重知识发散——讲牛顿力学，不仅讲它的定义，还会介绍它的诞生由来、变形延伸；谈共振，便从可见光、推墙、微波炉等生活实例中引申开来。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"line-height:150%;font-family:宋体;font-size:16px\">“张老师上课时的旁征博引，以及发散的教学思维，让我印象最为深刻。”在同事陈小凤眼中，这位资深的物理教师能将知识融会贯通，“旁听张老师的课，能感觉到他专业扎实，学识深厚。”</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"line-height:150%;font-family:宋体;font-size:16px\">物理教学因材施教，需要分层，因此对不同专业的同学来说，教师建立的“树林”也不同。在上非理工科专业的通识课时，张正阶更多地联系生活，把看似高深的理论通俗化，“这样定理原理公式就变得听得懂、看得见了”。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"line-height:150%;font-family:宋体;font-size:16px\">张正阶表示，自己不仅希望能引导学生了解物理科学的物质作用原理，还能帮助他们领会科学的哲学启发。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"line-height:150%;font-family:宋体;font-size:16px\">在《量子力学》专业课上，张正阶借助物理学中的哲学思辨来解释理论。“每个人在同一件事上意见不同，这跟量子力学的不确定性是一个道理，假若能用这种‘不确定’的眼光来看世界，也许我们能少一点对观点正确与否的争辩……”他曾把量子力学的不确定性原理应用到社会交往，也曾用“量子纠缠”理论解释孪生姐妹的心灵感应。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"line-height:150%;font-family:宋体;font-size:16px\">2009</span><span style=\"line-height:150%;font-family:宋体;font-size:16px\">级应用物理专业学生赵玉林正是在《量子力学》的课堂上获得物理启迪，毕业后推免至北京大学硕博连读的天体物理专业。回忆张正阶的课堂，她告诉记者，“张老师总是能从大的知识框架出发，打开我们的思路，并将抽象问题具体化，再难的知识点经他一解释，就能听明白。”</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"line-height:150%;font-family:宋体;font-size:16px\">虽然有些发散的知识点并不在教学大纲中，但张正阶觉得它们源于生活，自有其用，“学习不像挣钱，挣到以后马上就能发挥效用，教育不只是应试，而是在于拓宽人的知识体系。”</span> &nbsp;</p><p style=\"text-indent:32px\">&nbsp;</p><p style=\"text-align:center;margin-left:0\"><span style=\"line-height:150%;font-family:宋体;font-size:16px\"><strong>深入物理学科，首开《电磁场与电磁波》课程</strong></span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"line-height:150%;font-family:宋体;font-size:16px\">2002</span><span style=\"line-height:150%;font-family:宋体;font-size:16px\">年，为了推动信息工程专业的发展，搭建电子科学基础课与应用课之间的桥梁，学校决定开设《电磁场与电磁波》相关课程。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"line-height:150%;font-family:宋体;font-size:16px\">彼时，44岁的张正阶已经在《大学物理》课程领域耕耘20年，他主动请缨牵头《电磁场与电磁波》相关课程，依托四川省教改项目“电子信息专业群主干专业基础课——电磁场与电磁波系列课程体系建设”，开启了一段物理专业教学新征程。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"line-height:150%;font-family:宋体;font-size:16px\">要开课，首先要学习。张正阶在学校的支持下前往电子科技大学，就《电磁场》相关课程，进行了一学期的调研学习。张正阶回忆，即便是在从成理到电子科大的路上，自己脑子里都在思考电磁场专业问题，“那时候一边在成理教书，一边去电子科大学习，学习时间全是压缩课余时间而来。”</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"line-height:150%;font-family:宋体;font-size:16px\">先学后教，可如何使课程的教法更适应成理师生的教学模式？这让张正阶费尽了心思，毕竟做教学大纲、写教学计划，一切都要自己从头开始。他从其他课程借鉴模仿，到新课程的特色融合，马不停蹄地折腾了大半年，终于在成理首次开设了《电磁场与电磁波》课程。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"line-height:150%;font-family:宋体;font-size:16px\">课程初步建立之时，全校只有张正阶一个老师任教此课。两年后，陆续有教师加入进来，陈小凤就是其中之一。“最开始的方法就是旁听，”陈小凤回忆，“张老师上课条理清楚，听他的课不仅要听专业内容，更要观摩学习他的教学方法。” </span> </p><p style=\"text-indent:32px\"><span style=\"line-height:150%;font-family:宋体;font-size:16px\">在陈小凤看来，对于《电磁场与电磁波》这门课的教学，张正阶不仅是元老，更是“主心骨”。通过旁听习得教法后，陈小凤等几个老师分散到各个专业开展教学。每每在教学中遇到问题，陈小凤等年轻老师都会请教教学经验丰富的张正阶，她形容张正阶是一众年轻老师的“靠山”。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"line-height:150%;font-family:宋体;font-size:16px\">经过三年建设，《电磁场与电磁波》这门课程孕育出一支较为成熟的师资队伍，课程建设日益完善。2008年，《电磁场与电磁波》课程被评为四川省精品课程。2011年，张正阶承担起四川省质量工程“地球物理学专业综合改革”子项目“电磁场与电磁波省精品开放课程”，并在2013年获省级精品资源共享课程“电磁场与电磁波”资助。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"line-height:150%;font-family:宋体;font-size:16px\"></span>&nbsp;</p><p style=\"text-align:center\"><span style=\"line-height:150%;font-family:宋体;font-size:16px\"><img title=\"img_3074\" alt=\"img_3074\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180524/20180524164505_405.jpg\" width=\"500\" height=\"281\"/></span> &nbsp;</p><p style=\"text-align:center\"><span style=\"line-height:150%;font-family:宋体;font-size:16px\"><span style=\"font-family:FangSong_GB2312\">课下给学生</span><span style=\"font-family:FangSong_GB2312\">讲题</span></span> &nbsp;</p><p style=\"text-align:center\">&nbsp;</p><p style=\"text-align:center;margin-left:0\"><span style=\"line-height:150%;font-family:宋体;font-size:16px\"><strong>24</strong></span><span style=\"line-height:150%;font-family:宋体;font-size:16px\"><strong>小时待机，“有问题就问张老师”</strong></span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"line-height:150%;font-family:宋体;font-size:16px\">2011年，赵玉林与两名同学报名参加了成都市第四届大学生物理知识竞赛，张正阶作为指导老师的耐心，让赵玉林至今记忆犹新。</span> &nbsp;</p><p style=\"text-indent:32px\">&nbsp;<span style=\"line-height:150%;font-family:宋体;font-size:16px\"><span style=\"line-height:150%;font-family:宋体;font-size:16px\">赵玉林回忆，只要一下课，同学们都会涌上讲台问问题。课间五分钟，提问的同学还需要排队，“张老师风度翩翩，讲题非常耐心，同学们都争着和他交流，私下里还叫他大物男神。”</span></span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"line-height:150%;font-family:宋体;font-size:16px\">&nbsp;那一个月的晚自习，赵玉林和同学都在请教张正阶问题，“有时候留到很晚，张老师也从不会不耐烦。”就算是没在一起，张正阶的电话也24小时不关机，随时对学生开放。师生一起备战一个多月，团队最终摘得竞赛第一。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"line-height:150%;font-family:宋体;font-size:16px\">后来，大四的赵玉林准备考研，又跑去找张正阶咨询，“张老师不仅耐心，还很热心，我们有任何生活学习问题他都乐于帮助我们。”如今，赵玉林在北京大学攻读博士，与张正阶始终保持联系，两人不时在微信上相互分享链接，讨论物理问题：“正是张老师的启蒙，让我坚定了天体物理研究方向。”</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"line-height:150%;font-family:宋体;font-size:16px\">在张正阶看来，学生问问题是他们在思考的表现，教师不仅要解答问题，更要帮助学生发现问题。东区E2C206-2的教师休息室在张正阶上课教室的旁边，这里也是他的临时办公室，小房间的桌上、沙发上堆着6摞作业本。每一章，张正阶都会勾出重点例题，让同学们课后练手。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"line-height:150%;font-family:宋体;font-size:16px\">张正阶表示，通过课后习题，学生能够将课堂上学的知识加以运用，通过实践和反馈，老师也能发现学生的问题。一次作业批改发现问题还不够，学生纠错后，张正阶还要做二次批改。张正阶改作业认真，纠错也认真。在大一学生刘政宏眼中，张老师“就像有火眼金睛”，每次作业中未订正的错误几乎都能被张老师发现。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"line-height:150%;font-family:宋体;font-size:16px\">大二学生廖光辉说：“对于一些比较难的题他会逐一地评讲，我们一些没弄懂的问题就可以通过习题课得到解释。”在他看来，私下和张正阶的交流更像朋友。偶尔在qq群里，同学们会讨论学习中遇到的物理难题，张正阶要是看到了，也会“冒泡”参与大家的讨论。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"line-height:150%;font-family:宋体;font-size:16px\">就连同事陈小凤也说，向张正阶请教问题似乎不分时间，“无论上班还是假期，只要有问题，问张老师总能得到答案。”对此，张正阶笑言：“除了有时候开车不方便接电话，其他时间都欢迎大家讨论交流。”</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"line-height:150%;font-family:宋体;font-size:16px\"></span>&nbsp;</p><p style=\"text-align:center\"><span style=\"line-height:150%;font-family:宋体;font-size:16px\"><img title=\"img_3056\" alt=\"img_3056\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180524/20180524164557_252.jpg\" width=\"500\" height=\"281\"/></span> &nbsp;</p><p style=\"text-align:center\"><span style=\"line-height:150%;font-family:FangSong_GB2312;font-size:16px\">批改作业的张正阶</span> &nbsp;</p><p style=\"text-align:center\">&nbsp;</p><p style=\"text-indent:32px\"><br/></p><p style=\"text-align:center;margin-left:0\"><span style=\"line-height:150%;font-family:宋体;font-size:16px\"><strong>求学任教，坚守成理</strong><strong>40</strong><strong>年</strong></span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"line-height:150%;font-family:宋体;font-size:16px\">1977</span><span style=\"line-height:150%;font-family:宋体;font-size:16px\">年高中毕业后，成绩优异的张正阶回到乡上的小学做代课老师，半年后收到赵先碧校长辗转传来的讯息：高考恢复了！“赵校长知道我爱读书，让我回学校好好复习。”张正阶回忆道，“复习的时候没地方住，差点辍学，赵校长直接把自己宿舍让给我住，成就了我的大学梦。”</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"line-height:150%;font-family:宋体;font-size:16px\">怀揣着“出去看看”的美好愿景，张正阶不负所望，考上了成都理工大学（原成都地质学院）：“第一志愿想搞地质，进大山里探索冒险很能满足我当时少年的好奇心。”但是由于在入学考试中物理成绩突出，他被调配进新设的物理师资班。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"line-height:150%;font-family:宋体;font-size:16px\">1982年本科毕业，张正阶以全系专业课总分第一的成绩留校任教，至此叩开了走向讲坛的大门，开启与讲台结缘的36年，圆了心心念念的教师梦。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"line-height:150%;font-family:宋体;font-size:16px\">本科留校后教了两年课，张正阶开始觉得“当老师不能只是教书，还要做科研”，他渴望深造的心开始蠢蠢欲动。1986年夏天，张正阶考上成都地质学院矿物物理研究生，潜心深造。</span> <span style=\"line-height:150%;font-family:宋体;font-size:16px\">攻读研究生的最后一年半，张正阶被导师章正刚推荐至中国科学院高能物理研究所、北京大学及中国科技大学等单位学习“同步辐射谱学方法”，并参加北京正负电子对撞机及同步辐射实验室建设。在此阶段，张正阶在矿物物理的学习逐步深入，并在物理学科的理论修养及实验技能等方面得到提升，成功申报国家自然科学基金等科研项目，先后在国际国内各级刊物发表研究论文30余篇……</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"line-height:150%;font-family:宋体;font-size:16px\">40</span><span style=\"line-height:150%;font-family:宋体;font-size:16px\">年，张正阶是改革开放四十年的亲历者，也亲自目睹了成理40年间日月星辉。学生来了又去，只有他始终坚守一方讲台，教书育人的信念不曾摇移。就像每次物理课毕，学生一拨一拨离开教室，张正阶往往最后离开，他就像一面放大镜，用自己的韶华与智慧，为学子折射着物理世界的星光。</span> &nbsp;</p><p><br/></p>', '2018-06-03 21:36', '成都理工大学网上党支部', '与物理相伴的40年', null, '2', '211');
INSERT INTO `news` VALUES ('<p><span style=\"font-size:16px\"><p style=\"text-align:center\"><strong><span style=\"line-height:115%;font-family:黑体;font-size:29px\">四川省推进绿色矿业发展高端论坛在我校召开</span></strong> &nbsp;</p><p style=\"text-align:center\">&nbsp;</p><p style=\"text-align:left;text-indent:32px\"><span style=\"line-height:200%;font-family:宋体;font-size:16px\">为了进一步贯彻落实十九大提出的生态文明国家发展战略和绿色发展理念，推进四川绿色矿业建设，5月26日,以“新时代、新思想、新理念下的绿色矿业发展”为主题的四川省推进绿色矿业发展高峰论坛在我校第6教学楼报告厅（6C405）举行。论坛由四川矿产资源研究中心副主任、管理科学学院副院长张慧琴教授主持。 &nbsp;</span> </p><p style=\"text-align:left;text-indent:32px\"><span style=\"line-height:200%;font-family:宋体;font-size:16px\">四川省社科联规划评奖办赵静副主任在致辞中指出，本次论坛将深入贯彻习近平总书记在2018年全国生态环境保护大会上的重要讲话精神，秉持“人与自然和谐共生、绿水青山就是金山银山”的原则，推动四川绿色矿业发展。 </span> </p><p style=\"text-align:left;text-indent:32px\"><span style=\"line-height:200%;font-family:宋体;font-size:16px\">成都理工大学副校长刘树根在欢迎词中肯定了此次绿色矿业发展论坛的意义，他表示学校在部、省、市、校共同推进“双一流”建设的平台上，将积极支持学术交流，引领学术发展。</span> &nbsp;</p><p style=\"text-align:left;text-indent:32px\"><span style=\"line-height:200%;font-family:宋体;font-size:16px\">中国工程院陈毓川院士以“加快发展绿色矿业”为主题做了专题学术报告。他从经济社会发展阶段、矿业发展规律、实现绿色矿业已具备的基本条件以及实现矿业与环保双赢的具体建议四个方面论述了在习近平新时代发展绿色矿业的重要性、客观必然性和现实可行性，并提出了极具针对性的宝贵建议。 &nbsp;</span> </p><p style=\"text-align:left;text-indent:32px\"><span style=\"line-height:200%;font-family:宋体;font-size:16px\">中国国土经济研究院绿色矿业发展指导中心侯华丽主任从绿色矿山和绿色矿业发展示范区两方面对我国矿业绿色发展总体进展作了报告。她对绿色矿业推进中存在的问题进行了深入的原因剖析，并指出绿色矿业示范区建设方案编制不够理想的主要原因在于概念内涵认识不到位、建设要点把握不准和缺乏实施抓手。侯华丽主任提出“提高政治站位，发挥政府主导”、“强化组织领导，形成部门合力”、“积极创新管理，政策细化落地”等重要建议。</span> &nbsp;</p><p style=\"text-align:left;text-indent:32px\"><span style=\"line-height:200%;font-family:宋体;font-size:16px\">四川国土资源厅规划处郭强调研员做了以“四川省绿色矿山建设规划构思”为主题的学术报告，他从绿色矿山发展历程和绿色矿山建设基本思路两个角度对四川省绿色矿山建设进行了简要阐述，指出在2020年全省基本建成节约高效、环境友好、矿地和谐、符合生态文明建设要求的绿色矿业发展新模式的总体要求下，重点任务是探索建立标准体系、用足用好各类支持政策、大力推行绿色勘察、全面加强绿色矿山建设和整体推进绿色矿业发展示范区建设。 &nbsp;</span> </p><p style=\"text-align:left;text-indent:32px\"><span style=\"line-height:200%;font-family:宋体;font-size:16px\">邓玲教授做了以“生态文明制度建设与政策工具研究”为主题的学术报告，从生态文明制度建设总体研究思路、当前工作进度和矿产资源绿色发展三个方面进行了简要的阐述。邓玲教授团队通过对中央、地方文件的梳理，构建了包含矿产资源相关制度的生态文明相关政策的数据库，并实施了制度建设总览、资源相关制度、矿产资源相关制度等内容的制定。会上，邓玲教授对矿产资源绿色发展的制度建设提出了自己的建议。 &nbsp;</span> </p><p style=\"text-align:left;text-indent:32px\"><span style=\"line-height:200%;font-family:宋体;font-size:16px\">攀枝花市立宇矿业的高立民总经理就立宇矿业绿色矿山建设的生产基地、技术研发和综合利用产业链几个方面进行了介绍，并结合企业实践就推进绿色矿山建设思路进行了重点阐述，提出“四级联动+”和“一企一策”作为绿色矿山建设的组织保障，探索产业链一体化联动的新模式促进产业转型升级，呼吁出台矿产资源综合利用的支持政策，希望“产、学、研” &nbsp;创新合作助推绿色矿山建设。他最后强调立宇矿业对于绿色矿山建设有信心，也呼吁能够有政府各方的政策配套、组织保障、资金支持和税收减免等的保驾护航。 &nbsp;</span> </p><p style=\"text-align:left;text-indent:32px\"><span style=\"line-height:200%;font-family:宋体;font-size:16px\">四川矿产资源中心常务副主任、成都理工大学管理科学学院院长李璞教授作了题为“四川矿产资源研究中心关于推进四川绿色矿业发展的思考”的报告。作为四川省社科联依托建设的四川省社会科学重点研究基地，四川矿产资源研究中心是四川省社科联在矿产资源领域开展人文社会科学研究的重要布局，是为四川省委、省政府和各级部门提供决策咨询的重要智库。李璞教授基于对绿色矿业发展理论与实践的国内外对比、对党的十九大报告精神的解读和四川省绿色矿山发展的基础与问题，提出从空间开发格局、绿色矿业示范区、机制体制与政策和绿色矿业评价四个方面来推进四川省绿色矿业发展的研究。他强调要立足于四川省情，依靠大数据信息系统，将四川矿产资源研究中心建设成为四川省绿色矿业特色高端智库，以期为四川省委、省政府提供绿色矿业发展决策咨询，为四川省国土资源管理部门提供绿色矿业建设标准，为四川省内各级政府提供绿色矿业发展政策咨询和为四川省绿色矿山企业提供第三方咨询服务。 &nbsp;</span> </p><p style=\"text-align:left;text-indent:32px\"><span style=\"line-height:200%;font-family:宋体;font-size:16px\">据悉，此次论坛由四川省社会科学界联合会主办，四川矿产资源研究中心、成都理工大学社科处、成都理工大学管理科学学院、数学地质四川省重点实验室承办。论坛为国内矿产资源领域的管理者、专家、学者、企业家提供了一个学术交流、经验共享和合作发展的平台，在绿色矿业新技术、新业态、新模式和新内容等方面进行了学术探索和交流，为加快推进四川省绿色矿业发展的理论研究与实践探索提供智力支持。 &nbsp;<span style=\"line-height:200%;font-family:宋体;font-size:16px\">国内在绿色矿业、环境保护等领域的专家、学者、相关研究机构代表、高校代表、相关企业代表参加了此次高端发展论坛。</span> &nbsp;</span></p></span> &nbsp;</p><p style=\"text-align:left;text-indent:32px\">&nbsp;</p><p style=\"text-align:left;line-height:150%;text-indent:32px\">&nbsp;</p><p style=\"text-align:center;line-height:150%\"><img title=\"1_副本\" alt=\"1_副本\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180530/20180530174410_196.png\"/> &nbsp;</p><p style=\"text-align:center;line-height:150%\"><span style=\"font-size:16px\">论坛现场</span> &nbsp;</p><p><br/></p>', '2018-06-03 21:37', '成都理工大学网上党支部', '四川省推进绿色矿业发展高端论坛在我校召开', null, '5', '212');
INSERT INTO `news` VALUES ('<p style=\"text-align: center;\"><span style=\";font-size:12px\">&nbsp;</span><strong><span style=\"line-height:200%;font-family:黑体;font-size:29px\">英语辩论队获“外研社杯”全国大学生</span></strong> &nbsp;</p><p style=\"text-align: center;\"><strong><span style=\"line-height:200%;font-family:黑体;font-size:29px\">英语辩论赛全国总决赛三等奖</span></strong> &nbsp;</p><p><span style=\";font-size:12px\"></span>&nbsp;</p><p style=\"text-align:left;text-indent:32px\"><span style=\"line-height:200%;font-family:宋体;font-size:16px\"><span style=\"line-height:200%;font-family:宋体;font-size:16px\">5</span><span style=\"line-height:200%;font-family:宋体;font-size:16px\">月17日至22日，第21届“外研社杯”全国大学生英语辩论赛全国总决赛在北京外国语大学举行。</span> <span style=\"line-height:200%;font-family:宋体;font-size:16px\">我校英语辩论队辩手英语专业的马智灏和卢洋经过学校初赛、四川省复赛和华西赛区决赛层层选拔成功入围全国总决赛。</span> &nbsp;</span></p><p style=\"text-align:left;text-indent:32px\"><span style=\"line-height:200%;font-family:宋体;font-size:16px\">总决赛中，通过与全国各高校代表队8轮循环赛的激烈角逐，我校两位辩手凭借扎实的英语基础、严密的逻辑思辨以及默契的配合，斩获本次全国总决赛三等奖。通过这次比赛，选手充分展示了理工学子的风采，为我校争得了荣誉。</span><span style=\";font-size:12px\"> </span> </p><p style=\"text-indent:32px\"><span style=\"line-height:200%;font-family:宋体;font-size:16px\">据悉，该项比赛是目前全国最高级别的大学生英语辩论赛事，本次大赛共吸引了来自全国各高校通过层层选拔出的112支参赛队伍参加。</span> &nbsp;</p><p style=\"text-indent:32px\">&nbsp;</p><p style=\"text-align:center\"><span style=\"line-height:200%;font-family:宋体;font-size:16px\"><img title=\"全国总决赛照片1\" alt=\"全国总决赛照片1\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180531/20180531100511_881.jpg\" width=\"500\" height=\"375\"/></span> &nbsp;</p><p><span style=\"line-height:200%;font-family:宋体;font-size:16px\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 获奖合影</span></p><p><br/></p>', '2018-06-03 21:41', '成都理工大学网上党支部', '英语辩论队获“外研社杯”全国大学生    英语辩论赛全国总决赛三等奖', null, '8', '216');
INSERT INTO `news` VALUES ('<p style=\"text-align:center\"><strong><span style=\"line-height:200%;font-family:黑体;font-size:29px\">我校召开本科教学审核评估工作推进会</span></strong> &nbsp;</p><p style=\"text-indent:32px\"><br/></p><p style=\"text-indent:32px\"><span style=\"line-height:200%;font-family:宋体;font-size:16px\">为进一步推进学校审核评估工作，根据学校审核评估工作安排，6月1日下午，我校本科教学审核评估工作推进会在行政楼第三会议室召开。校领导龚灏、孙文、黄涛、刘树根、王敏出席会议，各学院院长、书记、分管教学副院长，各部门负责人，教学督导组全体成员，教务处处级干部，评估中心全体成员参加了会议。会议由副校长郭朝辉主持。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"line-height:200%;font-family:宋体;font-size:16px\">会上，各学院负责人结合预评估专家反馈的问题和整改意见，对照问题清单，从人才培养、师资队伍、教学资源、教学资料等方面汇报了本科教学工作中存在的问题，拟采取的整改措施和下一步评建工作中需要学校解决的问题。教学督导组组长孔繁津就教学资料检查、听课等本科教学工作情况进行了分析和总结。教务处处长李少达从教学质量、课堂纪律、教材使用、实践教学等方面对相关问题进行了补充说明。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"line-height:200%;font-family:宋体;font-size:16px\">郭朝辉副校长就人才培养方案修订等有关问题进行了统一部署和说明。她要求各学院、各职能部门要高度重视预评估反馈的问题，切实整改落实，进一步提升本科教学工作水平和人才培养质量，确保9月份高质量、高标准地通过审核评估。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"line-height:200%;font-family:宋体;font-size:16px\">在听取各学院汇报后，龚灏校长对下一步评建工作进行了部署：一是要将审核评估工作和学科发展、专业建设结合起来，以制度建设严格落实责任，问责到人、追责到底；二是要全面激发干部师生的工作积极性、主动性，深刻认识到审核评估工作与每一个成理人的命运息息相关，实现行政、系部、师生全校上下一盘棋；三是要统筹安排、抓住关键，根据专家反馈的意见和各专业的特点补齐短板；四是根据任务清单明确时间节点，学校将在期末和开学再次组织开展全面、深入、彻底的检查，确保发现的问题得到解决。他强调，此次审核评估是对我校人才培养质量的一次全面检验，关乎学校的前途命运和办学声誉，对于学校的可持续发展影响深远。目前距离审核评估专家进驻我校只有3个月，时间紧、任务重，各学院、各职能部门要有时不我待的紧迫感，凝心聚力、加快进度，严格按照整改任务分解表将工作落实到位，以审核评估为契机，增强“人才培养质量是学校生存和发展生命线”的意识，厘清思路、完善制度，深化教育教学改革，不断提高教学质量、办学水平和管理水平，不忘初心、砥砺前行，进一步推动学校“双一流”建设。</span> &nbsp;</p><p style=\"text-indent:32px\">&nbsp;</p><p style=\"text-align:center\"><img title=\"qq截图20180602005511\" alt=\"qq截图20180602005511\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180602/20180602005824_767.jpg\" width=\"500\" height=\"285\"/> &nbsp;</p><p style=\"text-align:center\"><span style=\"line-height:200%;font-family:宋体;font-size:16px\">会议现场</span> &nbsp;</p><p style=\"text-align:center\"><img title=\"qq截图20180602005409\" alt=\"qq截图20180602005409\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180602/20180602005843_264.jpg\" width=\"500\" height=\"365\"/> &nbsp;</p><p><span style=\"line-height:200%;font-family:宋体;font-size:16px\">龚灏校长讲话</span></p><p><br/></p>', '2018-06-05 21:21', '成都理工大学网上党支部', '我校召开本科教学审核评估工作推进会', null, '8', '217');
INSERT INTO `news` VALUES ('<p style=\"text-align:center\"><strong><span style=\"line-height:200%;font-family:黑体;font-size:29px\">我校召开哲学社会科学工作座谈会</span></strong> </p><p style=\"text-indent:32px\"><span style=\"line-height:200%;font-family:宋体;font-size:16px\">为进一步贯彻落实习近平总书记在哲学社会科学工作座谈会上的重要讲话精神，推动中央关于加快构建中国特色哲学社会科学的意见精神和省委有关实施意见在我校落地生根，加快学校哲学社会科学发展，5月25日上午，我校召开哲学社会科学工作座谈会。校领导龚灏、孙文、黄涛、刘树根出席会议，相关学院及职能部门负责同志、校社科重点研究基地负责人代表参加会议。会议由刘树根副校长主持。</span> </p><p style=\"text-indent:32px\"><span style=\"line-height:200%;font-family:宋体;font-size:16px\">与会代表结合工作实际，座谈了2年来持续深入学习贯彻习近平总书记5.17重要讲话精神的体会。一致认为习近平总书记重要讲话为繁荣发展哲学社会科学定向、定调、定任务，深刻阐释了哲学社会科学“是什么”“为什么”“发展什么”“怎么发展”等重大问题，明确了坚持马克思主义为指导是中国特色社会主义区别于其他哲学社会科学的标志，指导我校哲学社会科学快速发展，为我们构建中国特色哲学社会科学提供了根本遵循；学习贯彻总书记重要讲话精神，要不断结合中国特色社会主义伟大实践，以科学研究报效祖国，站在积极为党和人民述学立论，为构建中国特色哲学社会科学贡献力量的高度找准定位、明确方向；要结合学校发展目标和“双一流”建设，进一步明确任务，特别要在学科建设、人才建设、体系构建、平台搭建、成果评价等方面，进一步查找短板不足，细化目标措施，以目标导向和问题导向，规划建设具有特色性、融合性、一体性的哲学社会科学，以哲学社会科学推动学校内涵式发展。</span> </p><p style=\"text-indent:32px\"><span style=\"line-height:200%;font-family:宋体;font-size:16px\">会议指出，习近平总书记5.17重要讲话带来了哲学社会科学发展的春天，也赋予哲学社科工作者前所未有的责任，党和国家对哲学社会科学的重视提升到了新的高度，学校面临哲学社会科学发展难得的历史机遇，处在哲学社会科学发展最好的际遇，学校高度重视哲学社会科学的发展，全校要进一步坚定哲学社会科学的发展方向，要以“钉钉子”的精神将学习贯彻总书记重要讲话精神引向深入，准确把握哲学社会科学在落实学校“四个服务”的使命担当中的定位，准确把握哲学社会科学在党的教育事业和学校发展整体布局中的地位，要把坚持有方向、有灵魂、有信念的发展作为构建哲学社会科学的必由之路，以方向引领发展，以发展保障方向。</span> </p><p style=\"text-indent:32px\"><span style=\"line-height:200%;font-family:宋体;font-size:16px\"><span style=\"font-family:宋体;font-size:16px\">会议强调，要始终坚持以习近平新时代中国特色社会主义思想为指导，科学把握中央和省委对加快构建哲学社会科学的重大战略部署，充分认识哲学社会科学和哲学社会科学工作者在学校坚持社会主义办学方向、扎根中国大地办大学的全过程具有不可替代的重要作用，充分认识哲学社会科学和哲学社会科学工作者在落实“立德树人”根本任务、推动学校改革发展全过程具有不可替代的重要作用，坚持以马克思主义引领学校哲学社会科学各领域发展，把意识形态建设作为人才培养的前提，加强培育和践行社会主义核心价值观；要准确把握学校哲学社会科学发展的阶段性特征，真正抓住哲学社会科学发展的难得机遇，正确应对哲学社会科学发展面临的阶段性挑战，围绕哲学社会科学发展目标和定位找不足、补短板、强措施、促实效，增强哲学社会科学自信，营造浓厚的哲学社会科学发展氛围和学术风气；要加快推动我校哲学社会科学再上新台阶，始终坚持正确的政治方向和学术导向，切实增强“两个坚决维护”，强化“四个意识”，增强“四个自信”，努力提升哲学社会科学人才队伍建设水平，造就和培养一批高水平专门人才，引进和培养一批紧缺人才，提升马克思主义理论、意识形态、传统文化等领域研究水平，促进高质量学术成果产出和应用，探索形成具有我校特色的哲学社会科学新体系。</span></span> </p><p style=\"text-indent: 32px; text-align: center;\"><span style=\"line-height:200%;font-family:宋体;font-size:16px\"><img title=\"qq截图20180601205311\" alt=\"qq截图20180601205311\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180601/20180601205351_767.jpg\" width=\"500\" height=\"373\"/></span> </p><p style=\"text-indent: 32px; text-align: center;\"><span style=\"line-height:200%;font-family:宋体;font-size:16px\"><span style=\"font-family:宋体;font-size:16px\">会议现场</span></span> </p><p><br/></p>', '2018-06-05 21:25', '成都理工大学网上党支部', '我校召开哲学社会科学工作座谈会', null, '8', '219');
INSERT INTO `news` VALUES ('<p style=\"text-align: center;\"><strong><span style=\"font-size:29px;font-family:黑体;line-height:115%\"><strong><span style=\"font-size:29px;font-family:黑体\">我校首次以第一作者在国际地学Top期刊</span></strong></span></strong></p><p style=\"text-align: center;\"><strong><span style=\"font-size:29px;font-family:黑体;line-height:115%\"><strong><span style=\"font-size:29px;font-family:黑体\">《Earth-Science Reviews》发表文章</span></strong> </span></strong></p><p><strong> </strong> &nbsp;</p><p style=\"text-align:center\"><strong><span style=\"font-size:21px;font-family:宋体;line-height:115%\">“古地理重建团队”发表综述论文《中国古地理：现状与挑战》</span></strong> &nbsp;</p><p style=\"text-align:center\">&nbsp;</p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">日前，以我校沉积地质研究院教师、油气藏地质及开发工程国家重点实验室固定研究人员侯明才教授为论文第一作者，陈安清副教授和Jame &nbsp;G. Ogg（美国普渡大学）为论文通讯作者，以《China paleogeography: Current status and future &nbsp;challenges》为题在线发表在国际地球科学顶级期刊《Earth-Science &nbsp;Reviews》（Top期刊，影响因子7.051）上，这是我校首次以第一单位“油气藏地质及开发工程国家重点实验室（成都理工大学）”身份在该刊物上发表文章。共同作者还包括我校黄可可副教授、邢凤存副教授、陈洪德教授、时志强教授、黄虎副教授、刘欣春博士、中国石油大学（北京）金振奎教授、西北大学柳益群教授、中石化石油勘探开发研究院郑和荣和胡宗全两位教授级高工。该研究工作得到了国家自然科学基金和海外名师项目的支持。</span> &nbsp;</p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">本项研究系统回顾了前板块构造理论期和板块构造理论期国际古地理重建的历史与特点，介绍了中国科学家开展古地理重建所取得重大成就和最新成果，建立了大数据时代下实现多数据融合的数字可视化古地理重建蓝本，剖析了中国古地理重建的重大关键科学问题，提出了古地理重建面临的挑战和应对策略。</span> &nbsp;</p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:SimSun;line-height:200%\">古地理重建是再现地史时期地球表面系统的一门综合性学科，是地球系统科学的重要组成部分，一直是地球科学的前沿研究领域之一。在老一辈地质学家刘宝珺院士和曾允孚先生的开拓带领下，沉积地质研究院（所）近40年一直致力于中国的古地理重建研究，特别是在岩相古地理研究及编图方面形成了明显优势和特色，在国内享有盛誉。沉积地质研究院侯明才教授等人与多个国际古地理重建团队积极开展合作，致力于</span><span style=\"font-size:16px;font-family:SimSun;line-height:200%\">更好地将中国古地理研究与国际古地理重建结合，向国际同行介绍中国古地理研究与重建的最新进展，实现中国数字可视化古地理重建的宏伟目标。&nbsp; </span> </p><p style=\"text-align: center; text-indent: 32px;\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">文章链接：<a href=\"https://www.sciencedirect.com/science/article/pii/S0012825217302349\"><span style=\";color:windowtext\">https://www.sciencedirect.com/science/article/pii/S0012825217302349</span></a></span> &nbsp;</p><p style=\"text-align: center;\">&nbsp;&nbsp;</p><p style=\"text-align: center;\"><img title=\"1\" alt=\"1\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180423/20180423173326_108.bmp\" width=\"500\" height=\"491\"/>&nbsp;&nbsp;&nbsp;&nbsp;</p><p style=\"text-align: center;\"><span style=\"font-size:16px\">基于GPlates重建活动岩相古地理实例图</span> &nbsp;</p><p><br/></p>', '2018-06-05 21:30', '成都理工大学网上党支部', '我校首次以第一作者在国际地学Top期刊  《Earth-Science Reviews》发表文章', null, '6', '220');
INSERT INTO `news` VALUES ('<ul style=\"list-style-type: none;\" class=\" list-paddingleft-2\"><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; text-align: center;\"><strong style=\"margin: 0px; padding: 0px;\"><span style=\"margin: 0px; padding: 0px; font-family: 黑体; color: rgb(102, 102, 102); font-size: 29px;\">诺奖得主Richard R. Schrock教授</span></strong><strong style=\"margin: 0px; padding: 0px;\"><span style=\"margin: 0px; padding: 0px; font-family: 黑体; color: rgb(102, 102, 102); font-size: 29px;\">应邀来校讲学</span></strong>&nbsp;&nbsp;</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; text-align: center;\"><strong style=\"margin: 0px; padding: 0px;\"><span style=\"margin: 0px; padding: 0px; font-family: 黑体; color: rgb(102, 102, 102); font-size: 29px;\"></span></strong>&nbsp;</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px;\"><span style=\"margin: 0px; padding: 0px; font-family: 宋体;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"margin: 0px; padding: 0px; line-height: 32px; font-family: 宋体; font-size: 16px;\">4月19日下午，诺贝尔化学奖获得者、美国化学家、美国麻省理工学院Richard R. Schrock教授莅临我校，在九教国际学术报告厅做了题为“Adventures in Inorganic Chemistry and Catalysis（催化与无机化学探秘之旅）”的专题学术报告。报告会前，校长龚灏教授会见了Schrock教授，对其表示热烈欢迎和诚挚感谢，希望今后能继续关心、支持和帮助我校科学研究、学科建设及人才培养工作。</span>&nbsp;&nbsp;</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px;\"><span style=\"margin: 0px; padding: 0px; line-height: 32px; font-family: 宋体; font-size: 16px;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"margin: 0px; padding: 0px; line-height: 32px; font-family: 宋体; font-size: 16px;\">报告会吸引了400余名师生参加，其中包括附近中学对化学感兴趣的教师与学生。报告会由材料与化学化工学院副院长李崇瑛教授主持。</span>&nbsp;&nbsp;</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px;\"><span style=\"margin: 0px; padding: 0px; line-height: 32px; font-family: 宋体; font-size: 16px;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"margin: 0px; padding: 0px; line-height: 32px; font-family: 宋体; font-size: 16px;\">Schrock</span><span style=\"margin: 0px; padding: 0px; line-height: 32px; font-family: 宋体; font-size: 16px;\">教授从他的研究经历谈起，重点介绍了如何将钽、钨和钼类金属催化剂用于高效催化烯烃复分解反应，并形象地将其比喻为“化学反应中的交换舞伴”。正是由于Schrock教授发现的这类烯烃复分解反应催化剂，化学家们开始意识到烯烃的换位可以用于有机合成，可以取代大量传统的有机合成方法，他也因此获得了2005年诺贝尔化学奖。此外，Schrock教授还介绍了自己在钼系列催化剂高效催化还原氮气生成氨方面的研究进展。报告过程中，Schrock教授适当配以昔日的实验研究场景图片，研究团队、见证人的签名资料等，反映出Schrock教授严谨求实的科学态度和工作作风。</span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px;\"><span style=\"margin: 0px; padding: 0px; line-height: 32px; font-family: 宋体; font-size: 16px;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"margin: 0px; padding: 0px; line-height: 32px; font-family: 宋体; font-size: 16px;\">师生们对Schrock教授的研究表现出浓厚的兴趣，在现场提问和交流环节，大家踊跃提问，Schrock教授对师生们提出的十多个问题一一进行了解答。期间，当被问及数十年的科学研究何以能持之以恒的坚持下来，他风趣地说，是因为“总是期待着下一个重大发现”。</span>&nbsp;&nbsp;</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px;\"><span style=\"margin: 0px; padding: 0px; line-height: 32px; font-family: 宋体; font-size: 16px;\">&nbsp;&nbsp;&nbsp; 报告会持续两个小时，师生们表示获益匪浅，深受感染、深受启发，在科学研究道路上唯有孜孜以求，才能有所作为。</span>&nbsp;&nbsp;</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px;\"><span style=\"margin: 0px; padding: 0px; line-height: 18.4px; font-family: 楷体; background: white; color: rgb(51, 51, 51); font-size: 16px;\">&nbsp;&nbsp;&nbsp;<strong style=\"margin: 0px; padding: 0px;\">专家简介：</strong></span>&nbsp;&nbsp;</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px;\"><span style=\"margin: 0px; padding: 0px; line-height: 32px; font-family: 楷体; background: white; color: rgb(51, 51, 51); font-size: 16px;\">&nbsp;&nbsp;&nbsp; Richard R. Schrock</span><span style=\"margin: 0px; padding: 0px; line-height: 32px; font-family: 楷体; background: white; color: rgb(51, 51, 51); font-size: 16px;\">，1945年1出生，美国化学家，现任麻省理工学院教授。因为在有机化学中复分解反应法的贡献，成为2005年度诺贝尔化学奖获得者之一。1967年，在加州大学河滨分校获得学士学位；1971年，在哈佛大学获得博士学位；1971年-1972年，在剑桥大学进行博士后研究；1972-1975年受雇于杜邦公司研发中心。1975年加入麻省理工学院；1980年晋升为教授；1989年获得弗雷德里克·G·凯斯荣誉教授称号；2005年，由于在烯烃复分解反应——一种有机化学技术上的贡献，成为当年诺贝尔化学奖得主之一。</span>&nbsp;&nbsp;</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px;\">&nbsp;</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; text-align: center;\"><span style=\"margin: 0px; padding: 0px; line-height: 32px; font-family: 宋体; font-size: 16px;\"><img title=\"770470126422909581\" alt=\"770470126422909581\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180420/20180420171211_137.jpg\" width=\"500\" height=\"375\"/></span>&nbsp;&nbsp;</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px;\">&nbsp;</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; text-align: center;\"><span style=\"margin: 0px; padding: 0px; line-height: 32px; font-family: 宋体; font-size: 16px;\"><img title=\"img_8137\" alt=\"img_8137\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180420/20180420171355_834.jpg\" width=\"500\" height=\"333\"/></span>&nbsp;&nbsp;</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; text-align: center;\">&nbsp;</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; text-align: center;\"><span style=\"margin: 0px; padding: 0px; line-height: 32px; font-family: 宋体; font-size: 16px;\"><img title=\"img_8129\" alt=\"img_8129\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180420/20180420171450_730.jpg\" width=\"500\" height=\"333\"/></span>&nbsp;&nbsp;</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; text-align: center;\"><span style=\"margin: 0px; padding: 0px; line-height: 32px; font-family: 宋体; font-size: 16px;\">学术报告会现场</span></p></ul><p><br/></p>', '2018-06-06 16:37', '成都理工大学网上党支部', '诺奖得主Richard R. Schrock教授应邀来校讲学', null, '6', '221');
INSERT INTO `news` VALUES ('<p style=\"text-align:center\"><span style=\"font-size:29px;font-family:黑体;line-height:150%\"><strong>入地7018米！王成善</strong><strong>校友担任</strong><strong>亚洲</strong></span> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:29px;font-family:黑体;line-height:150%\"><strong>国家大陆科学钻井新纪录首席科学家</strong></span> &nbsp;</p><p style=\"text-align:center\">&nbsp;</p><p style=\"text-indent:32px\"><br/></p><p style=\"text-indent:32px\"><br/></p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:150%\">继“神舟”上天、“蛟龙”下海之后，中国科学家在地球深部的探索又迎来历史性突破。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:150%\">6</span><span style=\"font-size:16px;font-family:宋体;line-height:150%\">月2日，“地壳一号”万米钻机在黑龙江安达市正式宣布完成“首秀”：完钻井深7018米，创造了亚洲国家大陆科学钻井新纪录，标志着我国成为继俄罗斯和德国之后，世界上第三个拥有实施万米大陆钻探计划专用装备和相关技术的国家。 </span> </p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:150%\">7018</span><span style=\"font-size:16px;font-family:宋体;line-height:150%\">米深的松科二井，属于我国实施的松辽盆地白垩系国际大陆科学钻探工程，是国际大陆科学钻探计划（ICDP）实施22年以来最深钻井，也是全球首个钻穿白垩纪陆相地层的科学钻探井。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:150%\">这是中国入地工程的一项标志性成就，将为我国地球深部探测提供关键技术和装备，拓展松辽盆地深部页岩气、地热能等清洁能源勘查开发的新空间，引领全球白垩纪陆相古气候研究，显著提升我国在地质历史古气候研究领域的国际影响力。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:150%\">据悉，目前松科二井岩心已经全部采集完毕，这将会在研究白垩纪古气候演变以及未来人类生存环境演化方面发挥关键作用。我校知名校友、中国科学院院士王成善正是松井二科的首席科学家。</span> &nbsp;</p><p style=\"text-indent:32px\"><br/></p><p style=\"text-align:center\">&nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:仿宋;line-height:150%\"><img title=\"这是夜幕下的“地壳一号”万米钻机整机系统\" alt=\"这是夜幕下的“地壳一号”万米钻机整机系统\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180605/20180605175502_473.jpg\" width=\"500\" height=\"348\"/></span> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:仿宋;line-height:150%\">夜</span><span style=\"font-size:16px;font-family:仿宋;line-height:150%\">幕下的“地壳一号”万米钻机整机系统</span> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:仿宋;line-height:150%\"></span>&nbsp;</p><p style=\"text-indent:32px\"><br/></p><p style=\"text-indent:32px\">&nbsp;</p><p style=\"text-align:center\"><strong><span style=\"font-size:16px;font-family:宋体;line-height:150%\">入地7018米！王成善院士引领白垩纪古气候研究</span></strong> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:150%\">在5月21日进行的松科二井成果鉴定会上，李廷栋、康玉柱、武强、侯增谦、杨经绥等院士及有关专家一致认为，该成果实现了理论、技术、工程、装备的重大突破，对拓展我国深部能源勘查开发新空间、引领白垩纪古气候研究和服务“百年大庆”建设具有重要意义。成果总体达到国际先进水平，在深部钻探技术和白垩纪陆相古气候研究方面达到国际领先水平。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:150%\">王成善院士认为之所以是研究白垩纪，因为“这一时期是离我们最近的温室气候时期，也是高二氧化碳、高海平面和高温的‘三高’时期。搞清楚白垩纪，对于研究未来地球演化、可能会进入这种‘三高’时期具有重要借鉴意义。”他表示，研究过去地球首先是要建立过去地球演化的档案，未来人们有可能要在温室气候中生活，所以在研究中选择建立白垩纪的档案最重要，白垩纪有可能是未来地球演化的一个终点。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:150%\"></span>&nbsp;</p><p style=\"text-indent:32px\"><br/></p><p style=\"text-align:center\"><img title=\"王成善院士展示采自地球深部7018米的岩心\" alt=\"王成善院士展示采自地球深部7018米的岩心\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180605/20180605175540_334.jpeg\" width=\"500\" height=\"334\"/> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:仿宋;line-height:150%\">王成善院士展示采自地球深部7018米的岩心</span> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:仿宋;line-height:150%\"></span>&nbsp;</p><p style=\"text-indent:32px\"><br/></p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:150%\">王成善形容松科二井向整个地球科学界提供了一个大舞台——首次重建了白垩纪陆相百万年至十万年尺度气候演化历史，发现了各个时间尺度陆相气候变化的主要控制因素，为研究地球气候系统在温室气候条件下演变机制找到新证据。在他看来，过去是未来的窗口，松科二井最重要的研究意义在于为未来地球的研究提供了科学支撑和研究成果。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:150%\">通俗说，通过松科二井这一“时间隧道”，中国地质科学家基本还原了白垩纪的场景。</span> &nbsp;</p><p style=\"text-indent:32px\">&nbsp;</p><p style=\"text-align:center\"><strong><span style=\"font-size:16px;font-family:宋体;line-height:150%\">十年磨一“钻”！“向地球深部进军”路漫漫其修远</span></strong> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:150%\">科学钻探是获取地球深部物质、了解地球内部信息最直接、有效、可靠的方法，是地球科学发展不可缺少的重要支撑，也是解决人类社会发展面临的资源、能源、环境等重大问题不可缺少的重要技术手段。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:150%\">20</span><span style=\"font-size:16px;font-family:宋体;line-height:150%\">世纪70年代以来，很多发达国家陆续实施了多项科学钻探计划。具有代表性的有俄罗斯科拉半岛12262米超深钻，是目前世界上最深井；德国KTB超深钻，9101米，排名第二。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:150%\">1996</span><span style=\"font-size:16px;font-family:宋体;line-height:150%\">年2月，德国、美国和中国作为第一批成员，发起了国际大陆科学钻探计划。此前，我国地球物理的仪器主要依赖进口，但国外高精度的仪器对我国是封锁的。科学家们认为，这是国家发展无法回避与绕开的话题，必须突破发达国家的装备与技术封锁。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:150%\">经过四年多的技术攻关，2013年吉林大学成功研发了我国首台万米大陆科学钻探专用装备“地壳一号”万米钻机，填补了我国在深部大陆科学钻探装备领域空白，大大提高了我国超深井科学钻探装备的技术水平。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:150%\"></span>&nbsp;</p><p style=\"text-indent:32px\"><br/></p><p style=\"text-align:center\"><img title=\"王成善院士讲话\" alt=\"王成善院士讲话\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180605/20180605175559_535.jpeg\" width=\"500\" height=\"334\"/> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:FangSong_GB2312;line-height:150%\">王成善院士讲话</span> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:宋体;line-height:150%\"></span>&nbsp;</p><p style=\"text-indent:32px\"><br/></p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:150%\">2007</span><span style=\"font-size:16px;font-family:宋体;line-height:150%\">年10月20日，松科一井顺利完钻，总取心进尺为2577米，心长共计2485米，为白垩纪地球表层系统重大地质事件与温室气候变化的后续研究奠定了坚实基础。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:150%\">松科二井于2014年开钻，目标是打穿松辽盆地白垩系，探索松辽盆地深部能源潜力，建立松辽盆地深部地层结构，寻求白垩纪气候变化地质证据，研发深部探测技术。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:150%\">如何保障钻头在持续超高温下不“中暑罢工”，是科学家们面临的重要问题。松科二井工程总指挥、中国地质科学院勘探技术研究所所长张金昌介绍，钻井越深，温度越高，钻井液的技术难度越大。松科二井每往下钻进100米，地下温度会升高3至4℃，钻到孔底的时候温度已超过240℃。通过反复研究和实验，团队研发出新型钻井液配方，经受住了井底高温的考验，刷新了我国钻井液应用的最高温度纪录。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:150%\">“钻地”成功后，科学家们又面临“取心”的挑战。在一个极不均匀和复杂的球体上“动刀”，在保证钻的井眼不能坍塌和崩裂的同时，完整无缺地取出了深部岩心。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:150%\">据悉，中国科学家将继续研发15000米国产超深钻探装备系列。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:150%\"></span>&nbsp;</p><p style=\"text-indent:32px\"><br/></p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:150%\"><span style=\"font-family:KaiTi_GB2312\"><strong>人物简介：</strong>王成善 ，中国科学院院士，</span><span style=\"font-family:KaiTi_GB2312\">1951</span><span style=\"font-family:KaiTi_GB2312\">年</span><span style=\"font-family:KaiTi_GB2312\">11</span><span style=\"font-family:KaiTi_GB2312\">月生于哈尔滨。</span><span style=\"font-family:KaiTi_GB2312\">1974-1977</span><span style=\"font-family:KaiTi_GB2312\">年在成都地质学院</span><span style=\"font-family:KaiTi_GB2312\">找矿系学习，获学士学位。</span><span style=\"font-family:KaiTi_GB2312\">1977-1978</span><span style=\"font-family:KaiTi_GB2312\">年在西藏昌都工业局任地质组技术员。</span><span style=\"font-family:KaiTi_GB2312\">1978</span><span style=\"font-family:KaiTi_GB2312\">至</span><span style=\"font-family:KaiTi_GB2312\">1981</span><span style=\"font-family:KaiTi_GB2312\">年在成都地质学院地质系学习，师从著名沉积学家刘宝珺院士，并获地质学硕士学位。</span><span style=\"font-family:KaiTi_GB2312\">1981</span><span style=\"font-family:KaiTi_GB2312\">年起在成都地质学院找矿系任教，曾先后</span><span style=\"font-family:KaiTi_GB2312\">任我校沉积地质研究所所长、</span><span style=\"font-family:KaiTi_GB2312\">副校长、</span><span style=\"font-family:KaiTi_GB2312\">校长。</span><span style=\"font-family:KaiTi_GB2312\">2005</span><span style=\"font-family:KaiTi_GB2312\">年至今在中国地质大学</span><span style=\"font-family:KaiTi_GB2312\">(</span><span style=\"font-family:KaiTi_GB2312\">北京</span><span style=\"font-family:KaiTi_GB2312\">)</span><span style=\"font-family:KaiTi_GB2312\">任教。</span></span> &nbsp;</p><p><span style=\"font-size:16px;font-family:楷体\">&nbsp;&nbsp; 主要从事沉积学与大地构造研究，研究方向为沉积盆地分析、高原隆升机制以及大陆古海洋学。多年来在青藏高原隆升与演化机制、高原油气资源研究方面进行了系统深入的研究，具有独到的见解和成果；近年来积极进行大陆古海洋学研究，目前正在领导国际地质对比计划项目（IGCP463）,开展白垩纪大洋富氧问题研究，拓展了白垩纪研究的新领域。近年来承担了科技部、国家自然科学基金、国家科技攻关与部级重大基础项目与课题，现已获得省、部级科技成果一等奖1项，二等奖5项，三等奖2项，四等奖1项（7项排名第一，2项第二），国内外公开发表学术论文157篇（SCI收录14篇，EI收录13篇、ISTP收录1篇，GEOREF收录58篇）。出版中英文专著18部。</span> </p><p><br/></p>', '2018-06-06 17:04', '成都理工大学网上党支部', '入地7018米！王成善校友担任亚洲    国家大陆科学钻井新纪录首席科学家', null, '2', '222');
INSERT INTO `news` VALUES ('<p style=\"text-align: center;\"><span style=\"line-height:150%;font-family:宋体;font-size:24px\"><strong><span style=\"line-height:200%;font-family:黑体;background:white;color:#333333;font-size:29px\">新时代</span></strong><strong><span style=\"line-height:200%;font-family:黑体;background:white;color:#333333;font-size:29px\">·</span></strong><strong><span style=\"line-height:200%;font-family:黑体;background:white;color:#333333;font-size:29px\">新青年</span></strong><strong><span style=\"line-height:200%;font-family:黑体;background:white;color:#333333;font-size:29px\">·</span></strong><strong><span style=\"line-height:200%;font-family:黑体;background:white;color:#333333;font-size:29px\">新气象</span></strong><strong><span style=\"line-height:200%;font-family:黑体;background:white;color:#333333;font-size:29px\">·</span></strong><strong><span style=\"line-height:200%;font-family:黑体;background:white;color:#333333;font-size:29px\">新作为</span></strong> &nbsp;</span></p><p style=\"text-align:center\"><strong><span style=\"line-height:200%;font-family:宋体;background:white;color:#333333;font-size:20px\">校团委举办“青春之源”五四主题团日活动</span></strong> &nbsp;</p><p style=\"text-align:center\">&nbsp;</p><p style=\"text-indent:32px\"><span style=\"line-height:200%;font-family:宋体;background:white;color:#333333;font-size:16px\">为传承和弘扬“五四”精神，进一步引导青年学子成长成才，校团委于</span><span style=\"line-height:200%;font-family:宋体;background:white;color:#333333;font-size:16px\">5</span><span style=\"line-height:200%;font-family:宋体;background:white;color:#333333;font-size:16px\">月</span><span style=\"line-height:200%;font-family:宋体;background:white;color:#333333;font-size:16px\">4</span><span style=\"line-height:200%;font-family:宋体;background:white;color:#333333;font-size:16px\">日举办了“青春之源”五四主题团日活动。活动由网络安全学院团委、网络安全学院</span><span style=\"line-height:200%;font-family:宋体;background:white;color:#333333;font-size:16px\">2016</span><span style=\"line-height:200%;font-family:宋体;background:white;color:#333333;font-size:16px\">级计算机科学与技术二班团支部承办。共青团四川省委副书记任世强，成都理工大学校副校长王敏，共青团四川省委学校部副部长黄树良以及校团委、各学院团委负责老师出席本次活动。</span><span style=\"line-height:200%;font-family:宋体;background:white;color:#333333;font-size:16px\"> </span> </p><p style=\"text-indent:32px\"><span style=\"line-height:200%;font-family:宋体;background:white;color:#333333;font-size:16px\">伴随着激昂奋进的中国共产主义青年团团歌，本次主题团日活动正式拉开序幕。活动分为三个篇章：第一篇章——青年奋进新时代；第二篇章——团徽闪光新形象；第三篇章——青春建功新作为。在第一篇章中，学生孙佳欣以歌曲《马克思是个</span><span style=\"line-height:200%;font-family:宋体;background:white;color:#333333;font-size:16px\">90</span><span style=\"line-height:200%;font-family:宋体;background:white;color:#333333;font-size:16px\">后》带领大家与马克思展开一场跨越时空、别开生面的邂逅，唱响了马克思主义为人类自身解放而斗争的不朽乐章；在第二篇章中，视频《我的青春我的团》让团徽闪耀形象在每个共青团员的心目中扎根，互动环节《写给团的三行情书》所有同学都踊跃参与，纷纷向团组织表达内心的真挚情意；在第三篇章中，学生王彦铭带来《团结起来，振兴中华》书法展示，铿锵有力的八个字，如同一面旗帜，引领着广大青年坚定理想信念，志存高远，脚踏实地，勇做时代的弄潮儿。</span><span style=\"line-height:200%;font-family:宋体;background:white;color:#333333;font-size:16px\"> </span> </p><p style=\"text-indent:32px\"><span style=\"line-height:200%;font-family:宋体;background:white;color:#333333;font-size:16px\">共青团四川省委副书记任世强对本次团日活动进行了点评。他认为，本次团日活动主题鲜明、内容丰富、效果良好，展现了广大成理学子志存高远、胸怀天下、锐意进取、朝气蓬勃的精神面貌，反映了成都理工大学共青团改革发展工作推进稳步有序，形成了党政领导重视、组织体系健全、基础工作扎实、特色亮点突出、服务学生有力的良好局面。随后，他以毛泽东主席、周恩来总理等伟大革命先烈青年时期提出的远大志向为例，向广大共青团员提出了三点希望，一要做有理想、有真本领的新青年；二要做知行合一、格物致知、学以致用的新青年；三要做同新时代民族复兴共同前进的新青年。</span><span style=\"line-height:200%;font-family:宋体;background:white;color:#333333;font-size:16px\"> </span> </p><p style=\"text-indent:32px\"><span style=\"line-height:200%;font-family:宋体;background:white;color:#333333;font-size:16px\">据悉，本次“青春之源”五四主题团日活动，系共青团成都理工大学委员会开展“五四青春不一</span><span style=\"line-height:200%;font-family:宋体;background:white;color:#333333;font-size:16px\">Young</span><span style=\"line-height:200%;font-family:宋体;background:white;color:#333333;font-size:16px\">系列活动”之一。团日活动内涵丰富、形式多样、主题鲜明，集严肃性与活泼性、先进性与群众性为一体，用习近平新时代社会主义思想潜移默化滋养青年心灵，有效地增强了青年师生的节日获得感、团员身份感，引导青年学生将个人的青春梦融入“中国梦”。</span> &nbsp;</p><p style=\"text-indent:32px\">&nbsp;</p><p style=\"text-indent: 32px; text-align: center;\"><span style=\"line-height:150%;font-family:仿宋;font-size:16px\"><img title=\"312fa60abe34eff0d01ebcc4e68b7bb6\" alt=\"312fa60abe34eff0d01ebcc4e68b7bb6\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180507/20180507162347_713.jpg\" width=\"500\" height=\"334\"/></span> &nbsp;</p><p style=\"text-indent: 32px; text-align: center;\"><span style=\"font-size:16px\">团日活动现场</span>&nbsp; <br/></p><p><br/></p>', '2018-06-06 17:10', '成都理工大学网上党支部', '新时代·新青年·新气象·新作为', null, '1', '223');
INSERT INTO `news` VALUES ('<p><span style=\"color:#000000\"><span style=\"font-size:21px;font-family:黑体\"><strong><span style=\"font-size:29px;font-family:黑体\"><br/></span></strong></span></span></p><p style=\"font-size:12px;font-family:tahoma;background:white;white-space:normal;word-spacing:0px;font-weight:normal;color:#727272;font-style:normal;text-align:center;orphans:2;widows:2;letter-spacing:normal;text-indent:0px;font-variant-ligatures:normal;font-variant-caps:normal;-webkit-text-stroke-width:0px\"><span style=\"color:#000000\"><span style=\"font-size:21px;font-family:黑体\"><strong><span style=\"font-size:29px;font-family:黑体\"><span style=\"color:#000000\"><span style=\"font-size:21px;font-family:黑体\"><strong><span style=\"font-size:29px;font-family:黑体\">地灾国重实验室召开</span></strong></span></span>第二届学术委员会第三次会议</span></strong> &nbsp;</span></span></p><p style=\"font-size:12px;font-family:tahoma;background:white;white-space:normal;word-spacing:0px;font-weight:normal;color:#727272;font-style:normal;text-align:left;orphans:2;widows:2;letter-spacing:normal;line-height:18px;text-indent:32px;font-variant-ligatures:normal;font-variant-caps:normal;-webkit-text-stroke-width:0px\"><span style=\"color:#000000\"><span style=\"font-size:16px;font-family:宋体;line-height:24px\">2017</span><span style=\"font-size:16px;font-family:宋体;line-height:24px\">年12月16日，实验室第二届学术委员会第三次会议在实验室318会议室召开。殷跃平研究员、彭建兵教授、王功辉教授、李锡堤教授、黄鼎成研究员、冯夏庭教授、施斌教授、陈剑平教授、宋胜武教授等10名学术委员会委员出席了此次会议。龚灏校长、计划财务处向全珍副处长、科技处程孝良副处长以及实验室全体固定人员参加了本次会议。</span></span> &nbsp;</p><p style=\"font-size:12px;font-family:tahoma;background:white;white-space:normal;word-spacing:0px;font-weight:normal;color:#727272;font-style:normal;text-align:left;orphans:2;widows:2;letter-spacing:normal;line-height:18px;text-indent:32px;font-variant-ligatures:normal;font-variant-caps:normal;-webkit-text-stroke-width:0px\"><span style=\"color:#000000\"><span style=\"font-size:16px;font-family:宋体;line-height:24px\">在开幕式上，龚灏校长首先代表学校对学术委员会各位专家的到来表示热烈欢迎，对他们长期以来为实验室建设发展给予的大力支持和帮助表示最真挚的感谢。</span></span> &nbsp;</p><p style=\"font-size:12px;font-family:tahoma;background:white;white-space:normal;word-spacing:0px;font-weight:normal;color:#727272;font-style:normal;text-align:left;orphans:2;widows:2;letter-spacing:normal;line-height:18px;text-indent:32px;font-variant-ligatures:normal;font-variant-caps:normal;-webkit-text-stroke-width:0px\"><span style=\"color:#000000\"><span style=\"font-size:16px;font-family:宋体;line-height:24px\">实验室学术委员会副主任殷跃平研究员主持了学术委员会会议。实验室主任黄润秋教授首先向与会成员作了实验室2017年度工作总结报告，较系统地介绍了2017年实验室在科学研究、社会服务、队伍建设与人才培养、开放合作与交流、平台建设与运行管理等方面所开展的主要工作和取得的主要成效。</span></span> &nbsp;</p><p style=\"font-size:12px;font-family:tahoma;background:white;white-space:normal;word-spacing:0px;font-weight:normal;color:#727272;font-style:normal;text-align:left;orphans:2;widows:2;letter-spacing:normal;line-height:18px;text-indent:32px;font-variant-ligatures:normal;font-variant-caps:normal;-webkit-text-stroke-width:0px\"><span style=\"color:#000000\"><span style=\"font-size:16px;font-family:宋体;line-height:24px\">在主管部门与依托单位的大力支持下，2017年度实验室在科学研究、高层次人才培养、开放与交流、运行管理等方面均取得显著成绩，很好地完成了计划目标任务。新承担了包括国家自然科学基金、国家重大工程任务在内的科研项目114项，获四川省科技进步奖一等奖2项；授权发明专利28项，实用新型专利22项；计算机软件著作权25项；固定人员发表SCI收录论文126篇（一区6篇）；主办第二届iRALL国际博士课程班；1个团队获国土资源部科技创新团队称号；“碎裂岩体斜坡灾害四川省青年科技创新研究团队”获批，团队成员14人；1人入选2017年国家百千万人才工程；1人聘为“自然灾害防治与地质环境保护研究智库”负责人和首席专家；2人入选国土资源部杰出青年科技人才；1人获岩石力学与工程学会科普传播专家；1人获四川省青年科技奖；1人获首届优秀女地质科技工作者奖。</span></span> &nbsp;</p><p style=\"font-size:12px;font-family:tahoma;background:white;white-space:normal;word-spacing:0px;font-weight:normal;color:#727272;font-style:normal;text-align:left;orphans:2;widows:2;letter-spacing:normal;line-height:18px;text-indent:32px;font-variant-ligatures:normal;font-variant-caps:normal;-webkit-text-stroke-width:0px\"><span style=\"color:#000000\"><span style=\"font-size:16px;font-family:宋体;line-height:24px\">黄润秋教授还对重点实验室2018年度在科学研究、队伍建设与人才培养、国际合作与交流、开放运行与管理、以及平台建设等方面具体的发展指标规划作了详细介绍。与会成员和专家在充分肯定实验室2017年度取得成果的基础上，从实验室今后五年，乃至更长时间的学术发展方向、学科特色、科学理论提升、技术成果转化、人才培养与引进及实验室管理与运行等方面，提出了宝贵建议与意见。</span></span><span style=\"font-size:16px;font-family:宋体;color:black;line-height:24px\"></span> &nbsp;</p><p style=\"font-size:12px;font-family:tahoma;background:white;white-space:normal;word-spacing:0px;font-weight:normal;color:#727272;font-style:normal;text-align:left;orphans:2;widows:2;letter-spacing:normal;line-height:18px;text-indent:32px;font-variant-ligatures:normal;font-variant-caps:normal;-webkit-text-stroke-width:0px\"><span style=\"color:#000000\"><span style=\"font-size:16px;font-family:宋体;line-height:24px\">学术委员会成员也对实验室2017年度的自主研究课题和2018年度开放基金项目进行了评审。</span></span> &nbsp;</p><p style=\"font-size:12px;font-family:tahoma;background:white;white-space:normal;word-spacing:0px;font-weight:normal;color:#727272;font-style:normal;text-align:left;orphans:2;widows:2;letter-spacing:normal;line-height:18px;text-indent:32px;font-variant-ligatures:normal;font-variant-caps:normal;-webkit-text-stroke-width:0px\">&nbsp;</p><p style=\"font-size:12px;font-family:tahoma;background:white;white-space:normal;word-spacing:0px;font-weight:normal;color:#727272;font-style:normal;text-align:center;orphans:2;widows:2;letter-spacing:normal;line-height:18px;text-indent:32px;font-variant-ligatures:normal;font-variant-caps:normal;-webkit-text-stroke-width:0px\"><span style=\"color:#000000\"><span style=\"font-size:16px;font-family:宋体;line-height:24px\"><img alt=\"\" src=\"http://www.sklgp.com/upload/image/IMG_9535(1).JPG\" width=\"500\" height=\"333\"/></span></span> &nbsp;</p><p style=\"font-size:12px;font-family:tahoma;white-space:normal;word-spacing:0px;font-weight:normal;color:#727272;font-style:normal;orphans:2;widows:2;letter-spacing:normal;background-color:#ffffff;text-indent:0px;font-variant-ligatures:normal;font-variant-caps:normal;-webkit-text-stroke-width:0px\"><span style=\"color:#000000\"></span> </p><p style=\"font-size:12px;font-family:tahoma;background:white;white-space:normal;word-spacing:0px;font-weight:normal;color:#727272;font-style:normal;text-align:center;orphans:2;widows:2;letter-spacing:normal;line-height:18px;text-indent:32px;font-variant-ligatures:normal;font-variant-caps:normal;-webkit-text-stroke-width:0px\"><span style=\"font-size:16px;font-family:宋体;color:black\">龚灏校长致欢迎词</span> &nbsp;</p><p style=\"font-size:12px;font-family:tahoma;background:white;white-space:normal;word-spacing:0px;font-weight:normal;color:#727272;font-style:normal;text-align:center;orphans:2;widows:2;letter-spacing:normal;line-height:18px;text-indent:32px;font-variant-ligatures:normal;font-variant-caps:normal;-webkit-text-stroke-width:0px\"><span style=\"font-size:16px;font-family:宋体;color:black\"><img alt=\"\" src=\"http://www.sklgp.com/upload/image/IMG_9606(1).JPG\" width=\"500\" height=\"333\"/></span> &nbsp;</p><p><span style=\"font-size:16px;font-family:宋体;white-space:normal;word-spacing:0px;font-weight:normal;color:black;font-style:normal;orphans:2;widows:2;letter-spacing:normal;background-color:#ffffff;text-indent:0px;font-variant-ligatures:normal;font-variant-caps:normal;-webkit-text-stroke-width:0px\"> &nbsp;</span></p><p style=\"background:white;text-align:center;line-height:18px;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;color:black\">学术委员会副主任殷跃平研究院主持会议</span> &nbsp;</p><p style=\"background:white;text-align:center;line-height:18px;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;color:black\"><img alt=\"\" src=\"http://www.sklgp.com/upload/image/IMG_9573(1).JPG\" width=\"500\" height=\"333\"/></span> &nbsp;</p><p><span style=\"font-size:16px;font-family:宋体;color:black\"> &nbsp;<p style=\"background:white;text-align:center;line-height:18px;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;color:black\">实验室主任黄润秋教授作主任工作报告</span> &nbsp;</p><p style=\"background:white;text-align:center;line-height:18px;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;color:black\"><img alt=\"\" src=\"http://www.sklgp.com/upload/image/IMG_9483(1).JPG\" width=\"500\" height=\"333\"/></span> &nbsp;</p><span style=\"font-size:16px;font-family:宋体;color:black\"> &nbsp;<p style=\"background:white;text-align:center;line-height:18px;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;color:black\">评审实验实自主课题和开放基金</span> &nbsp;</p><p style=\"background:white;text-align:center;line-height:18px;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;color:black\"><img alt=\"\" src=\"http://www.sklgp.com/upload/image/IMG_9560(1).JPG\" width=\"500\" height=\"324\"/></span> &nbsp;</p></span><span style=\"font-size:16px;font-family:宋体;color:black\"> &nbsp;<p style=\"background:white;text-align:center;line-height:18px;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;color:black\">会议合影留念</span></p></span></span></p><p><br/></p>', '2018-06-06 17:13', '成都理工大学网上党支部', '地灾国重实验室召开第二届学术委员会第三次会议', null, '1', '224');
INSERT INTO `news` VALUES ('<p style=\"text-align:center\"><strong><span style=\"line-height:115%;font-family:黑体;font-size:29px\">校医院开展消防知识培训活动</span></strong> &nbsp;</p><p style=\"text-align:left;text-indent:32px\"><span style=\"line-height:200%;font-family:宋体;font-size:16px\">为贯彻“预防为主、防消结合”的消防工作方针，进一步提高校医院全体职工的消防安全知识及灭火技能， 4月4日下午，校医院开展了一次消防知识讲座和现场演练相结合的培训活动。 </span> </p><p style=\"text-align:left;text-indent:32px\"><span style=\"line-height:200%;font-family:宋体;font-size:16px\">14:40分，校医院协同学校保卫处在新图书馆后面的空旷地，进行了现场消防演练。保卫处的老师详细介绍了干粉灭火器的使用方法和技巧，在场的每一位参训人员在指导下，先将瓶体颠倒几次，使筒内干粉松动，接着除掉铅封，拔掉保险销。右手提着压把，在距火焰两米的地方，将喷管对准燃烧区，左右摇摆喷射，直至把火全部扑灭。</span> &nbsp;</p><p style=\"text-align:left;text-indent:32px\"><span style=\"line-height:200%;font-family:宋体;font-size:16px\">16:30分,所有参训人员回到校医院五楼大会议室继续参加消防知识讲座，此次医院特邀成都安全办公室全民防火安全中心的胡静老师主讲。她以图文并茂、新闻报道、事故分析等方式介绍了近年来国内外发生的多起重大火灾事故的实例视频和图片，一幕幕触目惊心的画面和数字让每一位参训人员的心灵受到了极大的震撼，深刻认识到了“隐患险于明火、防范胜于救灾”的重要性。\r\n &nbsp;</span> </p><p style=\"text-align:left;text-indent:32px\"><span style=\"line-height:200%;font-family:宋体;font-size:16px\">整个下午的消防培训，理论与实际的充分结合，进一步提高了校医院职工的消防安全意识，增强了自防、自救能力以及对于火灾隐患的发现处置能力，也为营造良好的工作、生活、居住环境打下了坚实的基础。 </span> </p><p>&nbsp;</p><p style=\"text-align: center; text-indent: 2em;\">&nbsp;</p><p style=\"text-align: center;\"><img title=\"11\" alt=\"11\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180409/20180409173311_219.jpg\"/> &nbsp;</p><p style=\"text-align: center;\">&nbsp;</p><p style=\"text-align: center;\"><img title=\"22\" alt=\"22\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180409/20180409173332_7.jpg\"/> &nbsp;</p><p style=\"text-align: center;\">&nbsp;</p><p style=\"text-align: center;\"><img title=\"33\" alt=\"33\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180409/20180409173353_810.jpg\" width=\"459\" height=\"296\"/></p><p><br/></p>', '2018-06-06 17:15', '成都理工大学网上党支部', '校医院开展消防知识培训活动', null, '1', '225');
INSERT INTO `news` VALUES ('<p style=\"text-align:center\"><span style=\"line-height:200%;font-family:宋体;font-size:16px\"><strong><span style=\"line-height:115%;font-family:黑体;font-size:29px\">校医院启动“健康直通车”下基层活动</span></strong> </span> </p><p style=\"text-align:center\"><span style=\"line-height:200%;font-family:宋体;font-size:16px\">&nbsp; </span></p><p style=\"text-align:left;text-indent:32px\"><span style=\"line-height:200%;font-family:宋体;font-size:16px\">根据国家提出的“大卫生”、“大健康”理念，不断提升校医院医疗质量和服务能力，校医院在新学期开始之际，启动了“健康直通车”下基层活动。 </span> </p><p style=\"text-align:left;text-indent:32px\"><span style=\"line-height:200%;font-family:宋体;font-size:16px\">3月16日一早，校医院“健康直通车”第一站来到了管理科学学院。慢病管理小组成员携带相关资料和仪器进行了两个多小时的健康咨询指导，为前来咨询的老师们提供了医疗健康服务，如测量血压和血糖，分发慢病健康宣传手册、控盐勺等，并根据咨询者的体检报告从生活习惯、饮食方式，运动锻炼等方面给予了指导。</span> </p><p style=\"text-align:left;text-indent:32px\"><span style=\"line-height:200%;font-family:宋体;font-size:16px\">据悉，管理科学学院是校医院“健康直通车”的第一站，</span><span style=\"font-family:宋体;font-size:16px\">校医院会陆续将“健康直通车”开往更多的学院、部门，把健康管理、防病未然、健康生活方式的理念</span><span style=\"font-family:宋体;font-size:16px\">传播给广大教职工。</span><span style=\"line-height:200%;font-size:16px\">&nbsp;&nbsp;&nbsp;</span>&nbsp; &nbsp;</p><p style=\"text-align:left;text-indent:32px\">&nbsp;</p><p style=\"text-align: center;\"><img title=\"1\" alt=\"1\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180320/20180320091554_440.jpg\" width=\"478\" height=\"345\"/> &nbsp;</p><p style=\"text-align: center;\">&nbsp;</p><p style=\"text-align: center;\"><img title=\"qq图片20180319090831\" alt=\"qq图片20180319090831\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180320/20180320092317_515.jpg\"/> &nbsp;</p><p style=\"text-align: center;\">&nbsp;</p><p style=\"text-align: center;\"><img title=\"3\" alt=\"3\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180320/20180320092608_32.jpg\"/> &nbsp;</p><p style=\"text-align: center;\">&nbsp;</p><p style=\"text-align: center;\"><img title=\"4\" alt=\"4\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180320/20180320092730_107.jpg\"/>&nbsp;<span style=\"font-family:&#39;Calibri&#39;, &#39;sans-serif&#39;;font-size:16px\">&nbsp;</span>&nbsp; <br/></p><p><br/></p>', '2018-06-06 17:16', '成都理工大学网上党支部', '校医院启动“健康直通车”下基层活动', null, '1', '226');
INSERT INTO `news` VALUES ('<p style=\"text-align:center\"><strong><span style=\"font-size:19px;font-family:宋体\">新思想引领新征程 &nbsp;加快推动内涵发展</span></strong> &nbsp;</p><p style=\"text-align:center\"><strong><span style=\"font-size:29px;font-family:黑体\">我校举行党委中心组学习（扩大）会</span></strong> &nbsp;</p><p style=\"text-align:center\"><strong><span style=\"font-size:29px;font-family:黑体\"></span></strong>&nbsp;</p><p><br/></p><p style=\"text-indent:43px\"><span style=\"font-size:16px;font-family:宋体;background:white;line-height:200%\">为深入学习贯彻党的十九大精神，自觉以习近平新时代中国特色社会主义思想武装头脑、指导实践、推动工作，</span><span style=\"font-size:16px;font-family:宋体;line-height:200%\">1</span><span style=\"font-size:16px;font-family:宋体;line-height:200%\">月29日全天，学校举行党委中心组学习（扩大）会，进行2018年第1次集中学习，统一思想，凝聚共识，</span><span style=\"font-size:16px;font-family:宋体;background:white;line-height:200%\">努力推动习近平新时代中国特色社会主义思想进教材进课堂进头脑，切实推动学校各项工作更上新台阶，统筹推进学校“双一流”和高水平综合性大学建设。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">29</span><span style=\"font-size:16px;font-family:宋体;line-height:200%\">日上午，党委中心组学习（扩大）会在西区第九教学楼国际学术报告厅举行。中央政治局第11次集体学习主讲专家、中共中央党校副教育长韩庆祥教授作题为《〈习近平谈治国理政〉（第二卷）与新时代中国特色社会主义思想》的辅导报告。校领导班子成员、校长助理、全校中层干部、全体辅导员和全体马克思主义学院教师参加报告会。党委书记谭书敏主持报告会。</span> &nbsp;</p><p style=\"text-indent: 32px; text-align: center;\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\"><img title=\"55\" alt=\"55\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180130/20180130172941_419.jpg\"/></span> &nbsp;</p><p style=\"text-indent: 32px; text-align: center;\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">辅导报告现场</span> &nbsp;</p><p style=\"text-indent:32px\">&nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">韩庆祥指出，《习近平谈治国理政》第一卷、第二卷是有机统一的整体，生动记录了以习近平同志为核心的党中央团结带领全党全国各族人民在新时代坚持和发展中国特色社会主义的伟大实践，与第一卷相比，第二卷的特点是：一是特别突出党的重要性，推动全面从严治党纵深发展篇目最多，共12篇；二是较为注重“四个全面”战略布局；三是较为注重新发展理念，首次对新发展理念作出了全面系统的阐述；四是较为注重构建人类命运共同体，为解决人类问题贡献中国智慧和中国方案；五是较为注重推进马克思主义中国化时代化大众化；六是在新时代坚持和发展中国特色社会主义实践的基础上，集中反映了新时代中国特色社会主义思想的发展脉络和主要内容；七是较为注重从政治、历史、哲学、战略和问题等方面阐述习近平新时代中国特色社会主义思想。</span> &nbsp;</p><p style=\"text-indent: 32px; text-align: center;\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\"><img title=\"22\" alt=\"22\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180130/20180130173112_978.jpg\"/></span> &nbsp;</p><p style=\"text-indent: 32px; text-align: center;\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">韩庆祥教授作报告</span> &nbsp;</p><p style=\"text-indent:32px\">&nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">韩庆祥强调，贯穿《习近平谈治国理政》的灵魂和精髓就是习近平新时代中国特色社会主义思想。他从历史方位论、民族复兴论、人民中心论、发展理念论、两大布局论、战略安排论、强军战略论、命运共同论、深化改革论和领导力量论十个方面深刻阐述了习近平新时代中国特色社会主义思想的核心要义。习近平新时代中国特色社会主义思想形成了围绕时代之问、聚焦当下实践的理论体系，是对改革开放以来中国特色社会主义伟大实践的系统总结，是党和人民实践经验和集体智慧的璀璨结晶，为发展21世纪马克思主义做出了原创性贡献。</span> &nbsp;</p><p style=\"background:white;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">谭书敏在总结讲话中要求，</span><span style=\"font-size:16px;font-family:宋体;background:white;line-height:200%\">全校上下要以更加饱满的热情、更加昂扬的斗志、更加努力的姿态，理解好、消化好、落实好韩庆祥教授的精彩报告，持续推动党的十九大精神入脑入心，自觉以习近平新时代中国特色社会主义思想武装头脑、指导实践、推动工作，将这次理论学习的成果切实转化为更加坚定的理论自信、理论自觉和行动力量，将党的十九大精神自觉运用到学校改革发展的现实奋斗中，从思想上和行动上，</span><span style=\"font-size:16px;font-family:宋体;line-height:200%\">坚定维护</span><span style=\"font-size:16px;font-family:宋体;background:white;line-height:200%\">以习近平同志为核心的</span><span style=\"font-size:16px;font-family:宋体;line-height:200%\">党中央</span><span style=\"font-size:16px;font-family:宋体;background:white;line-height:200%\">权威和</span><span style=\"font-size:16px;font-family:宋体;line-height:200%\">集中统一领导，</span><span style=\"font-size:16px;font-family:宋体;background:white;line-height:200%\">自觉在政治立场、政治方向、政治原则、政治道路上同</span><span style=\"font-size:16px;font-family:宋体;line-height:200%\">党中央</span><span style=\"font-size:16px;font-family:宋体;background:white;line-height:200%\">保持高度一致，让学校始终成为党领导下的坚强阵地，抓好“双一流”建设，加快内涵发展，提高发展质量，落实立德树人，培养时代新人，努力办好人民满意的高校，为建设教育强国、服务美丽繁荣和谐四川、实现伟大复兴的中国梦作出更大的成理贡献。</span> &nbsp;</p><p style=\"background: white none repeat scroll 0% 0%; text-indent: 32px; text-align: center;\"><span style=\"font-size:16px;font-family:宋体;background:white;line-height:200%\"><img title=\"44\" alt=\"44\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180130/20180130173137_226.jpg\"/></span> &nbsp;</p><p style=\"background: white none repeat scroll 0% 0%; text-indent: 32px; text-align: center;\"><span style=\"font-size:16px;font-family:宋体;background:white;line-height:200%\"><span style=\"font-size:16px\">党委书记谭书敏总结</span></span> &nbsp;</p><p style=\"background: white none repeat scroll 0% 0%; text-indent: 32px; text-align: center;\"><span style=\"font-size:16px;font-family:宋体;background:white;line-height:200%\">报告会上还举行了聘任仪式。谭书敏书记和龚灏校长分别为韩庆祥教授颁发了名誉主任和兼职教授聘书。</span> &nbsp;</p><p style=\"background: white none repeat scroll 0% 0%; text-indent: 32px; text-align: center;\"><span style=\"font-size:16px;font-family:宋体;background:white;line-height:200%\"><img title=\"33\" alt=\"33\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180130/20180130173202_10.jpg\"/></span> &nbsp;</p><p style=\"background: white none repeat scroll 0% 0%; text-indent: 32px; text-align: center;\"><span style=\"font-size:16px;font-family:宋体;background:white;line-height:200%\">谭书敏书记为韩庆祥教授颁发名誉主任聘书</span> &nbsp;</p><p style=\"background: white none repeat scroll 0% 0%; text-indent: 32px; text-align: center;\">&nbsp;</p><p style=\"background: white none repeat scroll 0% 0%; text-indent: 32px; text-align: center;\"><img title=\"11\" alt=\"11\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180130/20180130173231_534.jpg\"/> &nbsp;</p><p style=\"background: white none repeat scroll 0% 0%; text-indent: 32px; text-align: center;\"><span style=\"font-size:16px\">龚灏校长为韩庆祥教授颁发兼职教授聘书</span> &nbsp;</p><p style=\"background: white none repeat scroll 0% 0%; text-indent: 32px; text-align: center;\">&nbsp;</p><p style=\"text-align:left;text-indent:31px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">29</span><span style=\"font-size:16px;font-family:宋体;line-height:200%\">日下午，党委中心组学习（扩大）集中学习交流在行政楼第三会议室继续举行。学校领导班子成员、校长助理、校党委委员、校纪委委员、全校党政部门负责人、教辅单位负责人、各基层党委（党总支）书记参加会议。受谭书敏书记委托，黄涛副书记主持了学习讨论。</span> </p><p style=\"background: white none repeat scroll 0% 0%; text-indent: 32px; text-align: center;\"><img title=\"66\" alt=\"66\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180130/20180130173430_38.jpg\"/> &nbsp;</p><p style=\"background: white none repeat scroll 0% 0%; text-indent: 32px; text-align: center;\"><span style=\"font-size:16px\">学习现场1</span> &nbsp;</p><p style=\"background:white;text-indent:32px\">&nbsp;</p><p style=\"background:white;text-indent:32px\">&nbsp;<span style=\"font-size:16px;font-family:宋体;line-height:200%\">会前，校党委常委会作了专门研究，谭书敏书记和龚灏校长对学习计划、学习内容、学习参考资料和学习要求作出了具体指导，结合《习近平谈治国理政》（第二卷）学习，共安排了党的十九大报告关于全面从严治党重要论述，习近平总书记在瞻仰中共一大会址时重要讲话精神，习近平总书记给莫斯科大学中国留学生的回信内容，国家主席习近平发表的二〇一八年新年贺词内容，习近平总书记在学习贯彻党的十九大精神研讨班开幕式上重要讲话精神，习近平总书记在十九届中央纪委二次全会重要讲话精神，十九届二中全会、十九届中央纪委二次全会、省十一届二次全会等重要会议精神，省“双一流”建设实施意见，教育部等加强和改进高校思想政治工作座谈会精神等专题内容。党委宣传部精心编发了学习参考。参会人员进行了细致学习，精心准备了发言提纲。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">校领导班子成员、部分中层干部代表在发言过程中将发言主题与贯彻落实好省委巡视组整改反馈意见结合，与加强和改进学校新时代党的建设和思想政治工作结合，与深入推进“两学一做”学习教育常态化制度化结合，与深入开展“不忘初心、牢记使命”主题教育活动结合，与统筹推进做好学校“三大评估”工作结合，与高水平综合性大学建设目标结合，畅谈了学习体会和学习心得。大家一致认为，要坚持以习近平新时代中国特色社会主义思想统揽学校各项工作，坚持以人民为中心的发展立场，准确认识和把握新时代高等教育发展的阶段特点和主要矛盾，自觉回应更加迫切的科技创新需求、更加迫切的卓越人才需求，加快一流大学和一流学科建设，推动高校内涵式发展，办好人民满意的高等教育。要弘扬理论联系实际的学风，做到真学真懂真信真用，把学习成果转化为崇高的理想信念、坚定的核心意识、强烈的使命担当，转化为推动学校改革发展的实际成效。</span> &nbsp;</p><p style=\"text-indent: 32px; text-align: center;\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\"><img title=\"77\" alt=\"77\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180130/20180130173544_827.jpg\"/></span> &nbsp;</p><p style=\"text-indent: 32px; text-align: center;\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">学习现场2</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\"></span>&nbsp;</p><p><span style=\"font-size:16px;font-family:宋体;line-height:200%\">黄涛在总结发言中指出，学习贯彻习近平新时代中国特色社会主义思想和党的十九大精神关键在于贯彻，根本在于落实。一是要切实提升新时代学习质量，把增强学习本领置于增强执政本领的首要位置，把握好新时代脉搏，推进党委中心组学习规范化、经常化、制度化；二是要切实提升新时代党建质量，确保党的基本理论、基本路线、基本方略在学校全面贯彻落实，推进党建和业务相互融入，围绕发展抓党建，以党建引领发展；三是要切实提升新时代发展质量，以钉钉子的精神提高人才培养质量、科学研究和社会服务质量，提高学校办学水平和效益。</span></p><p><br/></p>', '2018-06-06 17:20', '成都理工大学网上党支部', '我校举行党委中心组学习（扩大）会', null, '5', '227');
INSERT INTO `news` VALUES ('<p style=\"text-align:center\"><strong><span style=\"font-family:黑体;font-size:29px\">学校党校举行第7期党员发展对象培训班</span></strong> &nbsp;</p><p style=\"text-indent:32px\">&nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"line-height: 200%; font-family: 宋体;\">为贯彻《中国共产党发展党员工作细则》，按学校党委的部署，根据校党校四级教育培训体系要求，学校党校于5月13日在体育馆举办了第7期党员发展对象培训班。四川大学马克思主义学院党委书记刘吕红教授、四川大学马克思主义学院李建华副教授、校党委组织部正处级组织员罗英教授受邀为671名党员发展对象做讲座。</span> &nbsp;</span></p><p style=\"text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"line-height: 200%; font-family: 宋体; font-size: 16px;\">副校长王敏出席了此次发展对象培训班开班典礼。党委组织部副部长、校党校常务副校长邹功江主持了此次培训，他在讲话中要求各位参训学员认真学习党的十九大精神、《中国共产党章程》以及习近平总书记在北京大学和纪念马克思诞辰200周年的重要讲话，增强为实现中华民族伟大复兴的中国梦的信心和决心，争做一名合格党员。</span> &nbsp;</span></p><p style=\"text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"line-height: 200%; font-family: 宋体; font-size: 16px;\">此次培训内容分为《共产党员的条件》《我在北京听总书记讲马克思》《严格履行党章规定的入党手续》三个专题。李建华教授围绕着共产党员的标准和条件、优秀共产党员的具体要求、共产党员的权利与义务、大学生党员的基本要求四个方面展开讲解。刘吕红教授以她参加纪念马克思诞辰200周年大会的亲身经历，结合习近平总书记的重要讲话，以饱满的热情向在场学员传递了马克思主义真理的力量。罗英教授结合发展党员工作实际，重点讲解了“中国共产党发展党员工作流程”“党员个人档案的填写要求”。</span> &nbsp;</span></p><p style=\"text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"line-height: 200%; font-family: 宋体; font-size: 16px;\">参训的学员们纷纷表示，此次培训主题突出、特色鲜明、内容丰富，既有理论性，又有实践性和针对性。通过培训，大家进一步端正了入党动机，更加坚定了入党的信心和决心。</span> &nbsp;</span></p><p style=\"text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"line-height: 200%; font-family: 宋体; font-size: 16px;\">按照此次培训班的总体安排，集中学习结束后，各分党校还将开展三次主题讨论，组织观看教学视频《不朽的马克思》。参训学员必须完成理论学习、小组讨论、视频教学、实践活动四个环节，成绩达到标准后，才有资格成为一名合格的预备党员。</span> &nbsp;</span></p><p style=\"text-indent:32px\"><span style=\"font-size: 18px;\">&nbsp;</span></p><p style=\"text-indent: 32px; text-align: center;\"><img title=\"李建华副教授授课\" alt=\"李建华副教授授课\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180515/20180515170833_536.jpg\"/><span style=\"font-size: 18px;\"> &nbsp;</span></p><p style=\"text-indent: 32px; text-align: center;\"><span style=\"font-size: 18px;\"><span style=\"font-size: 16px;margin-left:210px\">李建华副教授授课</span>&nbsp;&nbsp;</span></p><p style=\"text-align: center;\"><span style=\"font-size: 18px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><img title=\"刘吕红教授授课\" alt=\"刘吕红教授授课\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180515/20180515170852_26.jpg\"/><span style=\"font-size: 18px;\"> &nbsp;</span></p><p style=\"text-align: center;\"><span style=\"font-size: 18px;\"><span style=\"font-size: 16px;margin-left:230px\">刘吕红教授授课</span>&nbsp;&nbsp;</span></p><p style=\"text-align: center;\"><span style=\"font-size: 18px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><img title=\"罗英教授授课\" alt=\"罗英教授授课\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180515/20180515170908_933.jpg\"/><span style=\"font-size: 18px;\"> &nbsp;</span></p><p style=\"text-align: center;\"><span style=\"font-size: 18px;\"><span style=\"font-size: 16px;margin-left:230px\">罗英教授授课</span> &nbsp;</span></p><p style=\"text-align: center;\"><span style=\"font-size: 18px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><img title=\"互动环节_副本\" alt=\"互动环节_副本\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180515/20180515170929_273.jpg\"/><span style=\"font-size: 18px;\"> &nbsp;</span></p><p style=\"text-align: center;\"><span style=\"font-size: 18px;\"><span style=\"font-size: 16px;margin-left:230px\">互动环节</span>&nbsp;&nbsp;</span></p><p style=\"text-align: center;\"><span style=\"font-size: 18px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><img title=\"全景_副本\" alt=\"全景_副本\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180515/20180515170952_400.jpg\"/><span style=\"font-size: 18px;\"> &nbsp;</span></p><p style=\"text-align: center;\"><span style=\"font-size: 18px;\"><span style=\"font-size: 16px;margin-left:230px\">培训现场</span>&nbsp; <br/></span></p><p><br/></p>', '2018-06-06 17:23', '成都理工大学网上党支部', '学校党校举行第7期党员发展对象培训班', null, '6', '228');
INSERT INTO `news` VALUES ('<p style=\"text-align:center;text-indent:59px\"><strong><span style=\"font-size:29px;font-family:黑体;line-height:200%\">成理人的汶川十年</span></strong> &nbsp;</p><p style=\"text-align:right;text-indent:40px\"><strong><span style=\"font-size:20px;font-family:宋体;line-height:200%\">——写在“5·12”汶川地震十周年之际</span></strong> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size: 18px;\"><strong><span style=\"font-family: 宋体; line-height: 200%;\">编者按：</span></strong> &nbsp;</span></p><p style=\"text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"font-size: 16px; font-family: 仿宋; line-height: 200%;\">2008年5月12日14点28分，四川汶川发生了里氏8.0级特大地震，山崩地裂间，我校专家团队第一时间奔赴汶川、北川、青川等地，助力救灾工作；学者教授运用一己之长研究著书，为灾后重建建言献策；从地动山摇中走出的幸运者或因求学、或因工作来到成理，在全新的环境中开启自己的新生活……</span> &nbsp;</span></p><p style=\"text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"font-size: 16px; font-family: 仿宋; line-height: 200%;\">值此“5</span><span style=\"font-size: 16px; font-family: 宋体; line-height: 200%;\">·</span><span style=\"font-size: 16px; font-family: 仿宋; line-height: 200%;\">12”汶川地震十周年纪念日到来之际，我们撰文回望，只为记录灾难之下，救援专家星夜兼程的奔走，成理学者的社会担当和地震亲历者们那双助人助己的温暖之手。</span> &nbsp;</span></p><p style=\"text-indent:32px\"><span style=\"font-size: 18px;\">&nbsp;</span></p><p style=\"text-indent:32px\"><span style=\"font-size: 18px;\">&nbsp;</span></p><p style=\"text-indent:32px\"><span style=\"font-size: 18px;\">&nbsp;</span></p><p style=\"text-indent:32px\"><br/></p><p style=\"text-indent:32px\"><br/></p><p style=\"text-indent:32px\"><br/></p><p style=\"text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"font-size: 16px; font-family: 宋体; line-height: 200%;\">不知不觉，已然十年——北纬31.01度，东经103.42度这个圆点，依旧令人心有震颤。</span> &nbsp;</span></p><p style=\"text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"font-size: 16px; font-family: 宋体; line-height: 200%;\">回望十年，时间可以改变一个人，也可以塑造一代人。远眺西北方，大灾大难可以考验一个民族，也见证了我校地灾防治团队，教授专家的专业所长与社会担当。</span> &nbsp;</span></p><p style=\"text-indent:32px\"><span style=\"font-size: 18px;\">&nbsp;</span></p><p style=\"text-align:center;text-indent:43px\"><span style=\"font-size: 18px;\"><strong><span style=\"font-size: 21px; font-family: 黑体; line-height: 200%;\">十年防治，地灾学者共同坚守</span></strong> &nbsp;</span></p><p style=\"text-align:left;text-indent:37px\"><span style=\"font-size: 18px;\"><strong><span style=\"font-size: 19px; font-family: 宋体; line-height: 200%;\">聚焦地灾防治，开启学术国际路</span></strong> &nbsp;</span></p><p style=\"text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"font-size: 16px; font-family: 宋体; line-height: 200%;\">5月以来，地质灾害防治与地质环境保护国家重点实验室（以下简称：地灾国重实验室）的工作人员，时常穿梭在实验楼的大小会议室中，讨论着“5·12”汶川地震十周年系列活动的每一个细节——第五届强震地质灾害及其后效应国际研讨会（MEGE\r\n \r\n2018）的举办，学校与相关省厅局签订合作协议的对接、裴向军教授重点研发计划启动会的筹备……这是一场对汶川地震十周年的纪念，更是一次持续发力防灾减灾事业的集结。</span> &nbsp;</span></p><p style=\"text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"font-size: 16px; font-family: 宋体; line-height: 200%;\">2008年的汶川地震将一个陌生的研究领域摆在了地灾国重实验室学者的眼前。这个曾以工程地质、高边坡治理见长的实验室临危受命，第一时间组织专家深入北川、都江堰、青川、崇州、什邡等一线重灾区，对震后直接危害到灾区人民生命和财产安全的滑坡、崩塌、堰塞湖等重大地质灾害隐患点进行现场排查，最大限度地避免了灾区人民的二次伤亡。作为首批深入灾区的专家之一，时任实验室副主任的许强于5月13日凌晨2:00从成都出发赶往北川，眼前悲怆的场景撞击着他的心理承受底线。“我们是研究防灾减灾的。面对这个发生在‘家门口’的巨大灾情，我们当时就下定决心，一定要把汶川地震研究清楚，或是用我们的全力，尽量研究清楚。”</span> &nbsp;</span></p><p style=\"text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"font-size: 16px; font-family: 宋体; line-height: 200%;\">十年的持续研究让地灾国重实验室成为了汶川地震相关研究领域的“最强学术团队”，在获得国内同行称赞的同时，他们更赢得了越来越多国外学者和学术机构的关注。自2008年起，地灾国重实验室通过与国外学者交流、合作建立防灾研究基地、发表高水平论文等方式，推进实验室的学术国际化——五次主办汶川地震主题的国际学术研讨会，开办世界滑坡风险评价国际博士课程培训班(LARAM\r\n School)和大滑坡调查评价国际博士课程培训班(iRALL School) \r\n，与意大利、荷兰、日本、中国台湾等国家与地区共建震后科学研究基地，在全球范围内65篇最高引用率的汶川地震相关研究论文中，占据20篇的比重……</span> &nbsp;</span></p><p style=\"text-indent:32px\"><span style=\"font-size: 18px;\">&nbsp;</span></p><p style=\"text-indent: 32px; text-align: center;\"><img title=\"2唐川教授考察青川东河口\" alt=\"2唐川教授考察青川东河口\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180514/20180514085526_926.png\" width=\"500\" height=\"375\"/><span style=\"font-size: 18px;\"> &nbsp;</span></p><p style=\"text-align:center;text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"font-size: 16px; font-family: 宋体; line-height: 200%;\">唐川教授考察青川东河口</span> &nbsp;</span></p><p style=\"text-align:center;text-indent:32px\"><span style=\"font-size: 18px;\">&nbsp;</span></p><p style=\"text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"font-size: 16px; font-family: 宋体; line-height: 200%;\">如今，地灾国重实验室已与26个国家和地区的学术研究机构建立了长期的交流与合作关系，通过与国外知名高校、科研院所开展国际科研合作，支持实验室中青年教师和研究生出国访问、交流学习，邀请国内外知名专家学者来实验室讲学等多层次全方位的形式，践行自己的国际学术路。</span> &nbsp;</span></p><p style=\"text-indent:32px\"><span style=\"font-size: 18px;\">&nbsp;</span></p><p style=\"text-align:left;text-indent:37px\"><span style=\"font-size: 18px;\"><strong><span style=\"font-size: 19px; font-family: 宋体; line-height: 200%;\">灾后治理，中国方案解决世界难题</span></strong> &nbsp;</span></p><p style=\"text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"font-size: 16px; font-family: 宋体; line-height: 200%;\">为了实现“要将汶川地震研究清楚”这份承诺，十年来，地灾国重实验室的专家们无数次冒着生命危险前往汶川、北川、青川等灾区一线，与时间赛跑，在雨季来临前抢获现场一手资料。他们几乎都经过缺少食物、没有住处，连续数天翻越山峦排查灾害隐患，甚至几番身陷绝路等险境，九死一生的际遇。</span> &nbsp;</span></p><p style=\"text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"font-size: 16px; font-family: 宋体; line-height: 200%;\">十年来，结合汶川地震区灾害的特点和发展演化，地灾国重实验室在汶川地震地质灾害研究方面已经历了三个不同阶段。</span> &nbsp;</span></p><p style=\"text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"font-size: 16px; font-family: 宋体; line-height: 200%;\">其中，2008年—2009年同震地质灾害期，重点开展了汶川地震同震灾害的排查、灾后重建选址、地质灾害防治规划编制、重大地质灾害点的勘察和防治工程设计等工作，研究了汶川地震过程中触发的数万处滑坡崩塌、滑坡坝和堰塞湖的特征、发育分布规律及其成因机理。</span> &nbsp;</span></p><p style=\"text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"font-size: 16px; font-family: 宋体; line-height: 200%;\">2010—2013年为震后泥石流灾害集中爆发期，汶川地震触发的大量崩塌滑坡，使灾区新增上百亿立方米的松散固体物质堆积于坡麓和沟谷，一旦遭遇强降雨即以泥石流等方式向下游运动。因此，汶川地震灾区先后经历了2008年“9·\r\n \r\n24”北川特大泥石流、2010年“8·13”清平、映秀、龙池“三大片区”特大泥石流灾害、2012年“8·17”彭州银厂沟泥石流灾害，以及2013年7月8日至10日于都江堰、汶川等地发生的特大泥石流灾害。地灾国重实验室随即将部分研究重点转向震区泥石流调查评价、成因机理、监测预警和防治工程技术方法等方面的研究。</span> &nbsp;</span></p><p style=\"text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"font-size: 16px; font-family: 宋体; line-height: 200%;\">2013年至今为震裂山体高位滑坡期，汶川地震区的震裂山体在长期重力作用下开始产生滑坡崩塌灾害，灾区先后产生2013年“7·10”都江堰五里坡滑坡，死亡失踪166人；2017年“6·24”茂县新磨村滑坡，瞬间将新磨村掩埋，死亡失踪83人；2017年“8·6”茂县新店村滑坡，使G213国道中断数十天；2018年“4·8”龙溪乡阿尔寨滑坡，因成功避险无人员伤亡。这些滑坡往往地处大山顶部，且被植被覆盖，具有高位和高隐蔽性特点，防范难度极大。依托于973计划、国家自然科学基金委创新研究群体项目，地灾国重实验室提出并构建了基于“天—空—地”一体化的重大地质灾害隐患识别的“三查”体系，并牵头成功申报了科技部重点研发计划，目前“三查”体系已在四川、贵州等地开展示范应用。</span> &nbsp;</span></p><p style=\"text-indent:32px\"><span style=\"font-size: 18px;\">&nbsp;</span></p><p style=\"text-align:center;text-indent:32px\"><br/></p><p style=\"text-align:center;text-indent:32px\"><span style=\"font-family: 宋体; line-height: 200%; font-size: 18px;\"><img title=\"1许强教授与裴向军教授在茂县排查隐患\" alt=\"1许强教授与裴向军教授在茂县排查隐患\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180511/20180511185706_273.jpg\" width=\"500\" height=\"375\"/></span><span style=\"font-size: 18px;\"> &nbsp;</span></p><p style=\"text-align:center;text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"font-size: 16px; font-family: 宋体; line-height: 200%;\">许强教授与裴向军教授在茂县排查隐患</span> &nbsp;</span></p><p style=\"text-align:center;text-indent:32px\"><span style=\"font-size: 18px;\">&nbsp;</span></p><p style=\"text-align:center;text-indent:32px\"><br/></p><p style=\"text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"font-size: 16px; font-family: 宋体; line-height: 200%;\">2010年8月13日，绵竹清平乡文家沟特大泥石流的成功治理，是由地灾国重实验室参与解决的汶川地震灾区泥石流治理工程的重大难题之一。在汶川地震过程中，清平乡文家沟发生了特大型滑坡，其方量达到8600万立方米，其中5000万立方米的滑坡—碎屑流松散固体物质集中堆积于其下游的沟谷。巨量的松散物源在2010年8月13日强降雨过程中地表水流的冲刷下，形成了约450万立方米的特大山洪泥石流，其规模之大，世界罕见。地灾国重实验室的专家经现场调查后，创造性地提出“水砂分流”、“柔性排导槽”等泥石流治理新思路新工艺，成功治理了清平乡文家沟大型泥石流灾害。时任国土资源部部长的徐绍史评价\r\n \r\n&nbsp;“攻克了世界级的治理难题，建造了世界级的宏伟工程，创造了治理史上的奇迹。”这样的工作案例不胜枚举，“6·24”茂县新磨村滑坡治理、都江堰龙池特大泥石流治理……在日夜兼程的奔走中，多地的地质灾害警报被一个个解除，而国外学术界更是看到了防灾减灾领域的中国方案。</span> &nbsp;</span></p><p style=\"text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"font-size: 16px; font-family: 宋体; line-height: 200%;\">近年来，地灾国重实验室的部分专家又将研究视野转到地质灾害链和长期效应方面，将对地震灾区地质灾害动态演化规律进行持续长期关注和研究。同时，他们还将未来的学术视野凝集于地质灾害国际化对比、地灾快速评价以及从纯地质灾害研究向主动抗震设防、多灾种监测预警等方面。“对我们实验室而言，最重要的是如何把研究成果运用于实际挽救人的生命财产安全。我们心里最大的愿望就是可以通过对滑坡研究和防灾减灾技术的不断升级，真正实现对未来地质灾害的快速评估，减少地质灾害对人类的破坏或危害。这是我们每一个做地质灾害研究的学者所具备的使命感。”研究员范宣梅说道。</span> &nbsp;</span></p><p style=\"text-align:center;text-indent:43px\"><span style=\"font-size: 18px;\">&nbsp;</span></p><p style=\"text-align:center;text-indent:43px\"><span style=\"font-size: 18px;\"><strong><span style=\"font-size: 21px; font-family: 黑体; line-height: 200%;\">十年探索，一座城的过去和未来</span></strong> &nbsp;</span></p><p style=\"text-indent:37px\"><span style=\"font-size: 18px;\"><strong><span style=\"font-size: 19px; font-family: 宋体; line-height: 200%;\">八旬“银厂沟之父”的汶川记忆</span></strong> &nbsp;</span></p><p style=\"text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"font-size: 16px; font-family: 宋体; line-height: 200%;\">汶川地震十周年之际，我校87岁的地质学家刘兴诗教授携新书在东区图书馆和师生们见面。这是十年来他写的关于汶川地震的第二本著作：《我走过震后的汶川——一个地质学家的“5·12”记忆》，该书包括了“不能忘却的纪念”“我的5·12记忆”“汶川地震的地质学解读”“废墟上的重建”“地震中的生命奇迹”“答南开中学老同学们”。字里行间，有对灾区场景的重现，有对生命的礼赞，亦有专业地学解读。</span> &nbsp;</span></p><p style=\"text-indent: 32px; text-align: center;\"><img title=\"748934607\" alt=\"748934607\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180514/20180514171840_165.jpg\" width=\"500\" height=\"375\"/><span style=\"font-size: 18px;\"> &nbsp;</span></p><p style=\"text-indent: 32px; text-align: center;\"><span style=\"font-family: 宋体; line-height: 200%; font-size: 18px;\">刘兴诗的汶川地震相关著作</span></p><p style=\"text-indent:32px\"><span style=\"font-size: 18px;\">&nbsp;</span></p><p style=\"text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"font-size: 16px; font-family: 宋体; line-height: 200%;\">十年前的5月，在北京大学开会的刘兴诗得知汶川地震的消息后十分震惊。</span> &nbsp;</span></p><p style=\"text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"font-size: 16px; font-family: 宋体; line-height: 200%;\">当刘兴诗在电视上目睹灾情后，立即拿起电话联系灾区。但此时，灾区通讯完全瘫痪。几日后才联系上龙门山镇宝山集团副总任彬。“我是地质工作者，也是灾区一些县市的专家顾问，不能躲在遥远的未名湖边。”5月17日，刘兴诗不顾朋友们的劝阻，乘机飞回了四川。</span> &nbsp;</span></p><p style=\"text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"font-size: 16px; font-family: 宋体; line-height: 200%;\">回川后，刘兴诗先后奔忙于各个灾区，捐物资、做调研，成书的素材也大都是在这个阶段积累的。6月，刘兴诗进第一次进入余震不断的银厂沟，当时的他特意穿了一身红衣，没有带助手，做好了特殊的心理准备。他只身一人爬到废墟里，发现映秀中学教学楼好好的，而学生宿舍全跨了，心想一定是建筑有问题。随后，刘兴诗又想爬到山上去，看看有没有发生次生灾害的可能性。救援的官兵把他拉开，说山上危险，随时有可能塌方，刘兴诗却执意要去看看，用自己的专业知识服务救援。期间他两次受伤，得到沈阳军区和兰州军区野战医院处理，才无大碍。</span> &nbsp;</span></p><p style=\"text-indent: 32px; text-align: center;\"><img title=\"3兰州军区野战医务人员为刘兴诗教授处理伤口\" alt=\"3兰州军区野战医务人员为刘兴诗教授处理伤口\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180514/20180514090107_408.jpg\" width=\"500\" height=\"375\"/><span style=\"font-size: 18px;\"> &nbsp;</span></p><p style=\"text-align:center;text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"font-size: 16px; font-family: 宋体; line-height: 200%;\">兰州军区野战医院医务人员为刘兴诗教授处理伤口</span> &nbsp;</span></p><p style=\"text-align:center;text-indent:32px\"><span style=\"font-size: 18px;\">&nbsp;</span></p><p style=\"text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"font-size: 16px; font-family: 宋体; line-height: 200%;\">进入彭州，刘兴诗第一次见到当地旅游局局长，她愁容满面地对刘兴诗说，景区破坏严重，准备放弃龙门山镇以上的整个地段，将未来的发展的重点撤退到龙门山镇以下。刘兴诗安慰道，“不要轻言放弃，要有信心才行。”“那就等地质专家的报告吧。”旅游局局长十分颓丧。刘兴诗急了，“还等什么地质专家说话？现在许多地质专家都是我们的学生，难道老师不如学生？难道人退休了，学识和经验都不管用了？难道大难当前，还分什么退休不退休，有没有职务不成？你给我一台车，尽可能往前面开。车开不过去，我走进去，让我自己去看看吧。”</span> &nbsp;</span></p><p style=\"text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"font-size: 16px; font-family: 宋体; line-height: 200%;\">刘兴诗明白，要了解真实情况，必须走到破坏最厉害的地方，要在那里仔细观察山崩、滑坡发生的具体情况，才能取得第一手材料，做出正确判断。</span> &nbsp;</span></p><p style=\"text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"font-size: 16px; font-family: 宋体; line-height: 200%;\">十年间，刘兴诗几十次出入于北川、绵竹、彭州等地，埋头做自己的事，用一个退休地质工作者的热情和余力服务了灾区和民众。</span> &nbsp;</span></p><p style=\"text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"font-size: 16px; font-family: 宋体; line-height: 200%;\">刘兴诗曾问自己，一次次冒险进入灾区，仅仅得到了一派悲惨景象吗？他明白，他还得到了另外的东西，那就是重建的信心。正是一次次的冒险探索和深入现场的反复勘察，才对灾情愈发清晰，心里才有了底气。</span> &nbsp;</span></p><p style=\"text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"font-size: 16px; font-family: 宋体; line-height: 200%;\">也因为这样的底气，在余震未息期间，刘兴诗在受邀参加成都电视台33频道《叩问地球》节目时曾笃定地回答道:“放心吧！只要政策和资金到位，明年夏天又可以到龙门山中休闲避暑了。”如今，龙门山镇的农家乐早已重新开了张，证实了刘兴诗所言不虚。</span> &nbsp;</span></p><p style=\"text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"font-size: 16px; font-family: 宋体; line-height: 200%;\">十年过去，刘兴诗忘不了自己曾深入灾区的所见所闻，忘不了作为一个地质工作者疲惫而满足的奉献时光，他把自己的救灾的责任和心中的遗憾写进书里，写给每一位的读者，也写给自己。</span> &nbsp;</span></p><p style=\"text-align:center;text-indent:32px\"><br/></p><p><span style=\"font-size: 18px;\">&nbsp;</span></p><p><br/> </p><p style=\"text-indent:37px\"><span style=\"font-size: 18px;\"><strong><span style=\"font-size: 19px; font-family: 宋体; line-height: 200%;\">灾后援建，铭记往昔更要把握未来</span></strong> &nbsp;</span></p><p style=\"text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"font-size: 16px; font-family: 宋体; line-height: 200%;\">旅游与城乡规划学院的教授傅广海和刘兴诗教授一样，曾因自己在灾区一线的所见而郁结于心，也因天灾面前人类力量的有限而遗憾不已。长期以来，只有沉浸在灾后重建的工作时，傅广海心中才得以有些许的踏实。</span> &nbsp;</span></p><p style=\"text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"font-size: 16px; font-family: 宋体; line-height: 200%;\">2018年1月，傅广海新书《十年重建:“5·12”汶川特大地震遗址区旅游开发研究及震后旅游的社会经济效应分析》正式出版，由刘兴诗作序，“该书在总结灾后重建成果，分析龙门山构造活动断裂带地震遗址区旅游开发的社会经济效应，具有重要的历史意义，同时也填补了这方面研究的空白。”</span> &nbsp;</span></p><p style=\"text-indent: 32px; text-align: center;\"><span style=\"font-size: 18px;\">&nbsp;</span></p><p style=\"text-indent: 32px; text-align: center;\"><img title=\"4.于北川地震灾区考察的傅广海教授\" alt=\"4.于北川地震灾区考察的傅广海教授\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180514/20180514090226_489.jpg\" width=\"500\" height=\"336\"/><span style=\"font-size: 18px;\"> &nbsp;</span></p><p style=\"text-align:center;text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"font-size: 16px; font-family: 宋体; line-height: 200%;\">于北川地震灾区考察的傅广海教授</span> &nbsp;</span></p><p style=\"text-indent:32px\"><span style=\"font-size: 18px;\">&nbsp;</span></p><p style=\"text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"font-size: 16px; font-family: 宋体; line-height: 200%;\">2008年7月，傅广海受四川省旅游协会的委托，深入都江堰二王庙、青城后山、彭州银厂沟、北川县城，什邡红白镇、蓥华镇穿心店、绵竹汉旺镇地等进行实地调研，取得了大量一手资料。在此基础上，傅广海对龙门山构造活动断裂带地震旅游产品开发的原则、思路、战略、策略等提出了系统的构想。十年来，傅广海和他的研究生团队对上述区域灾后重建工作的关注从未中断。</span> &nbsp;</span></p><p style=\"text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"font-size: 16px; font-family: 宋体; line-height: 200%;\">“5·12”汶川地震给居住在龙门山构造活动断裂带上的居民带来了深重的灾难，对该区域旅游业的发展带来了巨大的冲击。在研究中，傅广海发现，地震灾害的发生改变了龙门构造活动断裂带原有地貌景观，但同时也创造了诸多震迹资源。从自己的专业出发，傅广海发现眼前这些震迹是可被开发、助力灾后重建的宝贵资源——在灾后恢复发展十年间，此处的地震遗址区旅游业便成为了援建创收的新亮点。</span> &nbsp;</span></p><p style=\"text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"font-size: 16px; font-family: 宋体; line-height: 200%;\">2016年，傅广海萌生了著书的想法：一方面，著书旨在总结汶川十年重建过程中的效果与启示。另一方面，组稿成书也算是给未来留下一点史料和参考。</span> &nbsp;</span></p><p style=\"text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"font-size: 16px; font-family: 宋体; line-height: 200%;\">在自发写书的过程中，经费来源、信息采集和信息形变等难题一一摆在傅广海眼前，即便傅广海自驾车带着研究生几次回访灾区，但因涉及调研的区域太多、范围太广，随时随处都存在不可控因素。好在事情最终出现了转机，四川省旅发委的胡斌副主任向他伸来了援助之手。在胡斌的支持下，傅广海获得了较为了丰富而详实的各地资料。他坦言，这些资料对成书十分关键，而获得帮助也在一定程度说明了自己所做之事得到了他人的认可。</span> &nbsp;</span></p><p style=\"text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"font-size: 16px; font-family: 宋体; line-height: 200%;\">经过一整年紧锣密鼓的筹备，在汶川十周年到来之际，新书终于得以出版。拿着油墨香的新书，傅广海明白，写书出版并非自己一时兴起，而是十年积累的水到渠成，是将自己所学所知回报给社会的最好办法。“一直沉湎于过去并不能改变什么，我只是希望这本书的出版，对于人们去了解当时的情形，了解龙门山构造活动断裂带灾后重建和当地旅游业发展，能够发挥有益的引导作用。”</span> &nbsp;</span></p><p style=\"text-align:center;text-indent:32px\"><br/></p><p style=\"text-align:center;text-indent:32px\"><br/></p><p style=\"text-align:center;text-indent:32px\"><span style=\"font-size: 18px;\">&nbsp;</span></p><p style=\"text-align:center;text-indent:32px\"><br/></p><p style=\"text-align:center;text-indent:43px\"><span style=\"font-size: 18px;\"><strong><span style=\"font-size: 21px; font-family: 黑体; line-height: 200%;\">十年重生，活下去就有新可能</span></strong> &nbsp;</span></p><p style=\"text-indent:37px\"><span style=\"font-size: 18px;\"><strong><span style=\"font-size: 19px; font-family: 宋体; line-height: 200%;\">传递感恩，唯有努力才能不负生命</span></strong> &nbsp;</span></p><p style=\"text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"font-size: 16px; font-family: 宋体; line-height: 200%;\">又到5月，距离那场山河破碎整整十年。汶川、北川、绵竹、都江堰……这些城市和小镇承受了最为沉重的关注与哀伤。地震对于亲历者们而言是一块不忍触碰但又无法忽视的伤疤。逝者已然安息，而生者的生活仍在继续。“当时我六年级，我觉得那是我人生中经历的最恐怖的一幕，唯一的念头就是快点往外跑。”作为汶川地震的亲历者，管理科学学院学生何富琨的家乡在绵阳市平武县坝子村，她所在的坝子小学就位于群山环绕之中。回忆起过去，仿佛一切就在眼前：操场中间蹲满了学生，抱着头相互依偎；街上一片狼藉，路边的房屋或已坍塌，或悬空着残垣断壁；远处的山上不断有石头滚落，卷起尘土冒着黄烟。</span> &nbsp;</span></p><p style=\"text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"font-size: 16px; font-family: 宋体; line-height: 200%;\">停课后，学校变成了人们临时的安置地，大家都住在简易帐篷里。2008年暑假，很多大学生志愿者来到当地，为当地学生补课。从这群大学生口中，何富琨听到了外面世界的精彩。分别时，一位志愿者告诉何富琨“上天让你活下来了，就一定要努力学习，走出大山。”此时，12岁的何富琨第一次有了考大学的念头。“我们村几乎没有大学生，大多数人读完初中就出去打工，如果没有那些志愿者，我可能根本不会去考大学。”志愿者的到来，让何富琨坚信每一段不努力的时光都是对生命的辜负。</span> &nbsp;</span></p><p style=\"text-indent:32px\"><span style=\"font-size: 18px;\">&nbsp;</span></p><p style=\"text-indent: 32px; text-align: center;\"><img title=\"5.何富琨在”双百双梦“扶贫活动中作励志演讲\" alt=\"5.何富琨在”双百双梦“扶贫活动中作励志演讲\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180514/20180514090328_81.jpg\" width=\"500\" height=\"308\"/><span style=\"font-size: 18px;\"> &nbsp;</span></p><p style=\"text-align:center;text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"font-size: 16px; font-family: 宋体; line-height: 200%;\">何富琨在“双百双梦”扶贫活动中作励志演讲</span> &nbsp;</span></p><p style=\"text-indent:32px\"><span style=\"font-size: 18px;\">&nbsp;</span></p><p style=\"text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"font-size: 16px; font-family: 宋体; line-height: 200%;\">进入大学后，在各类志愿服务活动中总能看见何富琨的身影：她加入大学生志愿团、筹划并举办慈善募捐晚会、参加志愿陪伴留守儿童活动……在何富琨看来，志愿服务不仅仅是一场不求回报的劳动，更是一次对他人精神的感染，一次感恩行动的接力。十年前，大学生志愿者的话语激励着她努力走出大山；十年后，她想将这一份爱，这份感恩延续下去，带给更多的人。</span> &nbsp;</span></p><p style=\"text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"font-size: 16px; font-family: 宋体; line-height: 200%;\">如今，何富琨已被保研至电子科技大学，继续自己研究生的学业。对她而言，生活画卷中的无数可能，才刚刚展开。</span> &nbsp;</span></p><p style=\"text-align:center;text-indent:32px\"><br/></p><p style=\"text-align:center;text-indent:32px\"><br/></p><p style=\"text-indent:37px\"><span style=\"font-size: 18px;\">&nbsp;</span></p><p style=\"text-indent:37px\"><span style=\"font-size: 18px;\"><strong><span style=\"font-size: 19px; font-family: 宋体; line-height: 200%;\">期待花开，从女儿成长为一位母亲</span></strong> &nbsp;</span></p><p style=\"text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"font-size: 16px; font-family: 宋体; line-height: 200%;\">同样作为地震亲历者，我校法学院教师杨一说起这段满目疮痍的记忆则是更难以平复。作为一位北川羌族女子，她是幸运的，因为活着；但曾几时她又会怀疑自己是不幸的，因为那段经历太过惨痛。</span> &nbsp;</span></p><p style=\"text-indent: 32px; text-align: center;\"><img title=\"6.儿时的杨一与母亲在北川旧城\" alt=\"6.儿时的杨一与母亲在北川旧城\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180514/20180514090431_248.jpg\" width=\"400\" height=\"575\"/><span style=\"font-size: 18px;\"> &nbsp;</span></p><p style=\"text-align:center;text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"font-size: 16px; font-family: 宋体; line-height: 200%;\">儿时的杨一与母亲在北川旧城</span> &nbsp;</span></p><p style=\"text-align:center;text-indent:32px\"><span style=\"font-size: 18px;\">&nbsp;</span></p><p style=\"text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"font-size: 16px; font-family: 宋体; line-height: 200%;\">“一阵剧烈摇晃后，我迅速打开大门，楼梯不见了，楼道里全是灰尘，什么也看不见，逃出去是我唯一的信念。”慌乱中的杨一来不及穿鞋就直接从三楼跳下来，此时的她才发现，整个小区已经垮塌，楼体倾斜着沉入地面。强震一波接着一波，人们只得全身紧贴着地面，趴在地上以保持身体的平衡。地震后，镇上的房屋垮了一大半，通往外界的公路被山上滚下的巨石堵住了，整个镇子变成一片与世隔绝的瓦砾场。</span> &nbsp;</span></p><p style=\"text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"font-size: 16px; font-family: 宋体; line-height: 200%;\">傍晚，下起了小雨，县城内仍旧丝毫没有外界的消息。此时的温度越来越低，四周余震不断，不时的滚石声令人们不约而同用惊恐的眼神望着彼此，但大家尽量保持平静，没有哭喊、没有混乱，甚至连8、9岁的小学生也很安静的坐在草坪上，将自己的红领巾解下，用作包扎伤员。期间，杨一与父亲、母亲投入自救与抢救他人的工作中，有陌生人看她光着脚，递给了她一双小了许多的拖鞋。</span> &nbsp;</span></p><p style=\"text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"font-size: 16px; font-family: 宋体; line-height: 200%;\">过分的宁静反而令人不安，次日凌晨四点，始终无法与外界取得联系的人们决定：自己走出去，往绵阳走，总会遇见人的。于是大家分成四个小分队，每支队伍里有青壮年，也有老人和小孩。考虑到余震不断，人们决定分开行动，每队出发间隔十分钟，为的是如果有不幸遇到滑坡滚石，大家不至于来不及分散逃生。</span> &nbsp;</span></p><p style=\"text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"font-size: 16px; font-family: 宋体; line-height: 200%;\">从北川老县城到绵阳约有70公里，这一路上他们需要躲避山上不时滚下的足球大小的碎石，还要搀扶着老人，背着孩子，深一脚浅一脚翻过早已砸毁的道路，朝山外走去。杨一穿着不合脚的拖鞋，强行镇定地走了30多公里后，终于看到了第一批救援的队伍。然而，由于救援车队有限，这批救援队只能先行接走老人和孩子，其余人员只能靠自己继续前行，等待下一批救援。</span> &nbsp;</span></p><p style=\"text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"font-size: 16px; font-family: 宋体; line-height: 200%;\">“2008年对我十分重要，我经历了生死，我研究生毕业了，同时我也来到成都理工大学工作，开始了自己的教师生涯。”在最为艰难的时光里，同事的关心和学生的体贴令杨一感到温暖，只是在夜深人静之时，噩梦和失眠却常常击中她内心的脆弱。</span> &nbsp;</span></p><p style=\"text-indent:32px\"><span style=\"font-family: 宋体; line-height: 200%; font-size: 18px;\">女儿的到来让杨一开始对生命有了全新的认识。2008年毕业结婚后，对生命的无力感令她结婚八年中，始终不敢迎接一个新生命的到来。八年里，她尝试重游故居、找人倾诉、咨询心理医生……一次次用力与自己和解，去接纳生命的不可知。“2016年，我觉得我准备好迎接自己的孩子了，我想让她感受这个世界的丰富多彩，每份经历无论喜悲都将是她一生中的一个部分。”新生命的到来让杨一逐渐对过去释然，“我现在不会带女儿回旧城，那些残垣断壁对于一个2岁的孩童而言还太过直接。但我会在女儿18岁时带她回北川老县城，作为送给她的成人礼，告诉她这就是妈妈成长的地方。” </span></p><p style=\"text-indent: 32px; text-align: center;\"><span style=\"font-family: 宋体; line-height: 200%; font-size: 18px;\"><img title=\"7\" alt=\"7\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180514/20180514090602_784.jpg\" width=\"500\" height=\"322\"/> &nbsp;</span></p><p style=\"text-align: center;\"><span style=\"font-family: 宋体; line-height: 200%; font-size: 18px;\">&nbsp;&nbsp;&nbsp; 杨一与女儿近照</span></p><p><span style=\"font-size: 18px;\"><span style=\"font-size: 16px; font-family: 宋体; line-height: 200%;\"></span>&nbsp;</span></p><p style=\"text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"font-size: 16px; font-family: 宋体; line-height: 200%;\">在汶川地震十周年到来之际，杨一与父亲商议是否要回到家乡看看。想到即将回到家乡，杨一眼中泛起了光。这个或许会令旁人害怕的地方，杨一的却分外想念，她想念北川温暖的阳光，还有带着紫荆花香味的山风。</span> &nbsp;</span></p><p><span style=\"font-family: 宋体; line-height: 200%; font-size: 18px;\">思考半月后，杨一艰难地决定暂不回北川。也许是近乡情怯，也许是更多不足为外人道来的情愫。“我可能暂时还迈不过那个坎，但我知道那里就是我的家。”</span></p><p><br/></p>', '2018-06-06 17:26', '成都理工大学网上党支部', '成理人的汶川十年', null, '6', '229');
INSERT INTO `news` VALUES ('<p style=\"text-align: center;\"><strong><span style=\"font-family:黑体;font-size:29px\">第十二届读书节系列活动启动</span></strong> &nbsp;</p><p>&nbsp;</p><p style=\"text-indent:32px\"><span style=\"line-height:200%;font-family:宋体;font-size:16px\">为激发我校师生的读书学习热情，营造浓郁的书香校园，在第23个世界读书日到来之际，图书馆与校团委、党委宣传部、学生工作部（处）、管科院、传艺学院等单位联合举办的“成都理工大学第十二届读书节暨第一届四川高校阅读文化节”系列游园活动在4月23日早上9：00在东区图书馆门前广场启动，标志着为期一个月的读书节与阅读文化节正式拉开帷幕。</span> &nbsp;</p><p style=\"text-indent: 32px; text-align: center;\">&nbsp;</p><p style=\"text-indent: 32px; text-align: center;\"><img title=\"1\" alt=\"1\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180428/20180428094646_375.jpg\"/> &nbsp;</p><p style=\"text-indent: 32px; text-align: center;\">&nbsp;</p><p style=\"text-indent: 32px; text-align: center;\"><img title=\"2\" alt=\"2\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180428/20180428095903_37.jpg\"/>&nbsp;</p><p style=\"text-align:center\"><strong><span style=\"line-height:150%;font-family:仿宋;font-size:19px\">&nbsp;&nbsp;<span style=\"font-size:16px\">&nbsp; 游园活动现场</span></span></strong> &nbsp;</p><p style=\"text-align: center;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <img title=\"3\" alt=\"3\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180428/20180428094833_535.jpg\"/>&nbsp;</p><p style=\"text-align:center;line-height:150%\"><strong><span style=\"line-height:150%;font-family:仿宋;font-size:19px\">&nbsp;&nbsp;<span style=\"font-size:16px\">&nbsp;&nbsp;活动现场咨询</span></span></strong> &nbsp;</p><p style=\"text-align:center;line-height:150%\">&nbsp;</p><p style=\"text-indent:32px\"><span style=\"line-height:200%;font-family:宋体;font-size:16px\"><span style=\"font-family:宋体;background:white;color:#191919;font-size:16px\">本届读书节以“慧·阅读”为主题，开幕日当天，以丰富多彩的小活动为宣传形式，通过诗朗诵快闪、VR体验、摄影打卡、你比我猜、即兴背诗、扔沙包、写毛笔字等游戏活动获得体验卡及小奖品，吸引众多同学积极参与到读书节期间开展的一系列活动。</span></span>&nbsp;<span style=\"line-height:200%;font-family:宋体;font-size:16px\"><span style=\"font-family:宋体;background:white;color:#191919;font-size:16px\">&nbsp; </span></span></p><p style=\"text-indent:37px\"><span style=\"line-height:200%;font-family:宋体;color:#333333;font-size:16px\">当天在图书馆一楼大厅还举行了“读者选书、图书馆买单”活动，现场共展出200余个品种、600余册的文学类图书和大学英语四六级考试、公务员考试等考试辅导类用书，以及心理学、成功学、青春励志、旅游等热门图书和科普类图书等，供我校师生挑选后，由图书馆购买提供给读者借阅。经过短短一天的选书荐书，共有200多名师生向图书馆推荐了300余种畅销、经典图书。</span> &nbsp;</p><p style=\"text-indent:37px\">&nbsp;</p><p style=\"text-indent: 37px; text-align: center;\"><img title=\"5\" alt=\"5\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180428/20180428094935_791.jpg\"/> &nbsp;</p><p style=\"text-indent: 37px; text-align: center;\"><strong><span style=\"font-size:16px;margin-left:250px\">荐书活动现场</span></strong><span style=\"font-size:16px\">&nbsp;</span><span style=\"font-size:16px\">&nbsp;</span>&nbsp;</p><p style=\"text-indent: 37px; text-align: center;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<img title=\"6\" alt=\"6\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180428/20180428095607_101.jpg\"/>&nbsp;<span style=\"line-height:200%;font-family:宋体;font-size:16px\"><span style=\"line-height:200%;font-family:宋体;color:#333333;font-size:16px\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span style=\"color:#000000\"><strong>&nbsp;&nbsp;</strong> </span></span></span> </p><p style=\"text-align: center;\"><span style=\"line-height:200%;font-family:宋体;font-size:16px\"><span style=\"font-family:宋体;background:white;color:#191919;font-size:16px\"> &nbsp;</span></span></p><p style=\"text-align:center;text-indent:37px\"><strong><span style=\"font-family:仿宋;font-size:19px\"><span style=\"font-size:16px\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 同学选书荐书</span></span><span style=\"font-size:16px\">&nbsp;</span>&nbsp;</strong><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</strong> &nbsp;</p><p>&nbsp;<span style=\"line-height:200%;font-family:宋体;font-size:16px\"><span style=\"font-family:宋体;background:white;color:#191919;font-size:16px\"> &nbsp;<p style=\"line-height:200%;text-indent:37px\"><span style=\"line-height:200%;font-family:宋体;color:#333333;font-size:16px\">晚上19：00，《网上报告厅》——中科院老科学家校园系列讲座在九教国际学术报告厅举行。中国科学院中国遥感卫星地面站研究员潘习哲教授到场为理工学子作题为《遥感与智慧城市》的专题讲座，我校360余名师生代表到场聆听。潘习哲教授是我国航天遥感领域的元老级专家，曾任中科院中国遥感卫星地面站站长，堪称我国遥感领域的“活字典”。潘教授从航天摇感、智慧城市、摇感技术与现代战争三个方面阐述了遥感技术在环境保护、城市建设、军事战争等方面的重要意义。两个小时的讲座，全场气氛热烈，现场互动环节更将报告会氛围推向高潮。同学们纷纷表示将持续深入关注现代科学领域的新知识新热点，拓宽视野，与时俱进。</span> &nbsp;</p><p style=\"line-height:200%;text-indent:37px\"><span style=\"line-height:200%;font-family:宋体;color:#333333;font-size:16px\"></span>&nbsp;</p><p style=\"text-align:center;line-height:150%\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p><p style=\"text-align:center;text-indent:37px\"><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <img title=\"8\" alt=\"8\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180428/20180428151703_624.jpg\"/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <br/> </strong> </p><p style=\"text-align: center;\"><span style=\"line-height:200%;font-family:宋体;font-size:16px\"><span style=\"font-family:宋体;background:white;color:#191919;font-size:16px\"><strong><span style=\"font-family:仿宋;font-size:16px;margin-left:250px\">潘老师讲座 </span></strong></span></span><span style=\"line-height:200%;font-family:宋体;font-size:16px\"><span style=\"font-family:宋体;background:white;color:#191919;font-size:16px\"><strong> </strong></span></span></p><strong> </strong> &nbsp;<p style=\"text-align: center;\">&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;<img title=\"9\" alt=\"9\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180428/20180428151957_868.jpg\"/> &nbsp;</p><p style=\"text-align: center;\"><span style=\"line-height:200%;font-family:宋体;font-size:16px\"><span style=\"font-family:宋体;background:white;color:#191919;font-size:16px\"> &nbsp;</span></span><span style=\"line-height:200%;font-family:宋体;font-size:16px\"><span style=\"font-family:宋体;background:white;color:#191919;font-size:16px\"> &nbsp;<p style=\"text-indent:37px\"><strong><span style=\"font-family:仿宋;font-size:16px\"><span style=\"line-height:200%;font-family:宋体;font-size:16px\"><span style=\"font-family:宋体;background:white;color:#191919;font-size:16px\"><strong><span style=\"font-family:仿宋;font-size:16px\"><strong><span style=\"font-family:仿宋;font-size:16px;margin-left:230px\">同学提问</span></strong></span></strong></span></span></span></strong> <strong><span style=\"font-family:仿宋;font-size:19px\"><br/> </span></strong> </p><p style=\"text-indent:37px\"><strong><span style=\"font-family:仿宋;font-size:19px\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<img title=\"10\" alt=\"10\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180428/20180428152052_186.jpg\"/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></strong> </p><p style=\"text-align:center;text-indent:37px\"><strong><span style=\"font-family:仿宋;font-size:19px\"><span style=\"font-size:16px\">潘老师给提问学生赠送礼物</span></span></strong> &nbsp;</p><p style=\"text-align:center;text-indent:37px\">&nbsp;</p></span></span></p>&nbsp;<span style=\"line-height:200%;font-family:宋体;font-size:16px\"><span style=\"font-family:宋体;background:white;color:#191919;font-size:16px\"> &nbsp;<p style=\"text-indent:37px\">&nbsp;<span style=\"line-height:200%;font-family:宋体;color:#333333;font-size:16px\"><span style=\"line-height:200%;font-family:宋体;color:#333333;font-size:16px\">本届读书节活动将持续到5月底，在为期一个月的时间里，将围绕主题开展“慧听”、“慧说”、“慧读”、“慧看”、“慧玩”等六大板块开展20余项阅读活动，期待激发广大师生阅读热情，为推进全民阅读培养计划，助力全民阅读建设，建设书香校园、书香社会贡献力量。</span></span><span style=\"line-height:200%;font-family:宋体;font-size:16px\"><span style=\"font-family:宋体;background:white;color:#191919;font-size:16px\">&nbsp;</span></span> &nbsp;</p><p style=\"text-indent:37px\"><span style=\"line-height:200%;font-family:宋体;font-size:16px\"><span style=\"font-family:宋体;background:white;color:#191919;font-size:16px\">&nbsp; </span></span></p><p style=\"text-indent: 37px; text-align: center;\">&nbsp;&nbsp; <img title=\"11\" alt=\"11\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180428/20180428152315_605.jpg\"/> &nbsp;</p><p style=\"text-indent: 37px; text-align: center;\">&nbsp;&nbsp;&nbsp;<span style=\"font-size:16px\">&nbsp;</span><span style=\"line-height:200%;font-family:宋体;font-size:16px\"><span style=\"font-family:宋体;background:white;color:#191919;font-size:16px\"><strong><span style=\"font-family:仿宋;font-size:16px;margin-left:250px\">合影留念</span></strong></span></span></p></span></span></span></span></p><p><br/></p>', '2018-06-06 17:29', '成都理工大学网上党支部', '第十二届读书节系列活动启动', null, '6', '230');
INSERT INTO `news` VALUES ('<p style=\"text-align:center\"><strong><span style=\"font-family:黑体;font-size:29px\">黑水县、高县乡村人才能力建设</span></strong><strong><span style=\"font-family:黑体;font-size:29px\">专题培训启动</span></strong> &nbsp;</p><p style=\"text-indent:32px\">&nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"line-height: 200%; font-family: 宋体;\">5月23日上午，由我校精准扶贫工作领导小组办公室承办的2018年帮扶项目——“黑水县、高县乡村人才能力建设专题培训”开班仪式在逸夫楼6C405举行。宜宾市高县人民政府副县长何彬，高县扶贫移民局、黑水县委组织部相关负责人以及来自黑水县小黑水片区和高县庆符镇的52名乡干部、村干部和致富带头人，我校党委副书记孙文和精准扶贫工作领导小组部分成员、相关职能部门负责人、参与此次培训工作的部分学院领导及授课教师共计70余人出席了开班仪式。仪式由学校对外合作与交流处副处长、精准扶贫办公室常务副主任刘晓玲主持。</span> &nbsp;</span></p><p style=\"text-indent:32px\"><span style=\"line-height: 200%; font-family: 宋体; font-size: 18px;\">开班仪式上，孙文副书记代表学校对来校参培的学员表示热烈的欢迎，简要梳理了学校精准扶贫定点帮扶两县的工作举措和成效，介绍了学校为本次培训所做的组织筹备工作，并对办好此次培训提出了具体要求和殷切希望。高县人民政府副县长何彬对学校积极开展对高县的帮扶并取得的良好成效，表示充分的肯定和由衷的感谢。参训学员代表、黑水县晴朗乡人大主席兰东成感谢学校精心组织举办本次乡村人才培训，他代表学员表示，将倍加珍惜学校提供的培训机会，勤于思、敏于学，不负学校的期望，努力提升自身素质，积极带领贫困乡亲脱贫奔康，切实践行乡村振兴伟大使命！\r\n &nbsp;</span></p><p style=\"text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"line-height: 200%; font-size: 16px; font-family: 仿宋;\"><span style=\"font-family: SimSun; font-size: 16px;\">为了让学员学有所得、学有所成，此次培训将以案例教学为主，采取课堂教学、研讨教学、现场教学相结合。在现场教学环节，学校将邀请两县扶贫、农业、发改、环保、旅游等部门负责同志和学员一道，走进中国乡村振兴示范村--郫都区战旗村、中国农家乐旅游发源地--郫都区农科村、中国绿茶第一乡——“西部茶都”蒲江县成佳茶园、中国乡村旅游创客示范基地--蒲江县明月村及蒲江县部分乡、村级电商站点考察学习。结合省委彭清华书记提出的开展“大学习、大讨论、大调研”的总体要求，培训期间还将安排参加</span><span style=\"font-family: SimSun; color: black; font-size: 16px;\">大学生创新创业大赛</span><span style=\"font-family: SimSun; font-size: 16px;\">“红色筑梦之旅”项目组指导教师与两县相关部门负责同志，围绕学校今年拟开展的“黑水县晴朗乡二牛奶村藏香猪基地建设”、“青山绿水美丽乡村建设”、“电子商务平台建设”、“红色昌德旅游产品开发设计”4个帮扶项目进行交流研讨，共商帮扶之策，用心答好学校对两县的精准脱贫精准帮扶的答卷。</span></span> &nbsp;</span></p><p style=\"text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"line-height: 200%; font-family: 宋体; font-size: 16px;\"></span><span style=\"font-size: 18px; font-family: SimSun;\">&nbsp;</span> &nbsp;</span></p><p style=\"text-align: center;\"><span style=\"font-size: 18px;\">&nbsp;</span></p><p style=\"text-align: center;\"><img title=\"孙文\" alt=\"孙文\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180524/20180524090753_240.jpg\" width=\"500\" height=\"333\"/><span style=\"font-size: 18px;\"> &nbsp;</span></p><p style=\"text-align: center;\"><span style=\"font-size: 18px;\"><span style=\"font-family: 宋体; font-size: 16px;\"><strong>孙文副书记讲话</strong></span>&nbsp;&nbsp;</span></p><p style=\"text-align: center;\"><img title=\"何彬\" alt=\"何彬\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180524/20180524090857_254.jpg\" width=\"500\" height=\"333\"/><span style=\"font-size: 18px;\"> &nbsp;</span></p><p style=\"text-align: center;\"><span style=\"font-size: 18px;\"><span style=\"font-family: 宋体; font-size: 16px;\"><strong>高县人民政府副县长何彬讲话</strong></span> &nbsp;</span></p><p style=\"text-align: center;\"><span style=\"font-size: 18px;\">&nbsp;</span></p><p><span style=\"font-size: 18px;\">&nbsp;</span></p><p style=\"text-indent:37px\"><span style=\"font-size: 18px;\"><strong><span style=\"line-height: 200%; font-family: 宋体; font-size: 16px;\">新闻背景：</span></strong> &nbsp;</span></p><p><span style=\"font-size: 18px;\"><span style=\"line-height: 200%; font-family: 仿宋; font-size: 16px;\">根据省委工作安排，国家级贫困县阿坝州黑水县和四川省贫困县宜宾市高县是我校精准扶贫定点帮扶县。为贯彻落实中央和省委坚决打赢、打好脱贫攻坚战的工作部署，学校充分发挥教育、科研、人才、信息等资源优势，主动作为，精准实施对两县的教育扶贫和人才培训等智力帮扶。习近平总书记在党的十九大报告中提出了乡村振兴战略。脱贫奔康，人才为本，乡村振兴，人才先兴。打好打赢脱贫攻坚战，必须依靠一支熟悉乡情村情、素质过硬、能打硬仗的乡村干部人才队伍。达到”产业兴旺、生态宜居、乡风文明、治理有效、生活富裕“的乡村振兴总体要求，必须培养一批农村领导人才、产业经营管理人才和专业技术人才。学校深谙智力帮扶的战略意义，根据年初制定的年度帮扶计划，结合精准扶贫和乡村振兴战略的要求，整合优质师资力量，从乡村旅游、农村环境治理、电子商务、农特产品营销、乡风文明建设和农村治理现代化目标等方面，对两县部分村镇干部和致富带头人进行为期一周的集中培训。</span><span style=\"line-height: 200%; font-family: 仿宋; color: black; font-size: 16px;\">旨在提升两县乡村人才的综合素质和产业发展能力，充分发挥乡土人才引领农民致富的示范带动作用，助力提升两地贫困乡亲脱贫致富的能力。</span></span></p><p><br/></p>', '2018-06-06 19:57', '成都理工大学网上党支部', '黑水县、高县乡村人才能力建设专题培训启动', null, '4', '232');
INSERT INTO `news` VALUES ('<p style=\"text-align: center;\"><strong><span style=\"font-size: 22pt\">星斗其文 赤子其人</span></strong></p><p style=\"text-align: center;\">&nbsp;</p><p style=\"text-align: center;\"><strong><span style=\"font-size: 16pt\">——记“先教”典型、材化院教授汪模辉</span></strong></p><p style=\"text-align: center;\">&nbsp;</p><p style=\"text-align: center;\"><strong><span style=\"font-size: 12pt\">周图伽</span></strong></p><p>&nbsp;</p><p><span style=\"line-height: 150%; font-size: 12pt\">&nbsp;&nbsp;&nbsp;&nbsp;为人半生，62岁的汪模辉似有书生意气，但谈笑间的那份豁达与洒脱却无半分书生的拘谨和儒懦。</span></p><p><span style=\"line-height: 150%; font-size: 12pt\">　</span></p><p style=\"text-align: center;\"><strong><span style=\"line-height: 150%; font-size: 12pt\">先生之风山高水长</span></strong></p><p>&nbsp;</p><p><span style=\"line-height: 150%; font-size: 12pt\">&nbsp; &nbsp;汪教授的课在材化院是出了名的受欢迎，他治学严谨，研究生的课，有本科生来蹭座；本科生的课，常常是齐刷刷地坐满了一屋；上试验课，永远都有学生七嘴八舌地围着问问题；连和入党积极分子谈如何写“先教”材料，都能把大家说得心服口服。他反对教授一门心思只带研究生，“如果本科生的课都上不好，也就上不好研究生的课”。这个学校“三育人”、“十佳园丁”称号的获得者喜欢戏剧行当里的一句老话,“戏比天大”。教书，应该像舞台上的“好角儿”一样绝不“欺场”，学堂之上应把自己的最佳状态交给学生，正所谓“教书比天大”。</span></p><p><span style=\"line-height: 150%; font-size: 12pt\">&nbsp; 1960</span><span style=\"line-height: 150%; font-size: 12pt\">年进入四川师范大学化学系，1964年到地矿部从事地质物料分析测试及生产管理工作，1973年“三十而立”的汪模辉执起教鞭，开始了在成都地质学院的教书生涯。32年后在秋日里追忆起这段时光的印迹，他依然不悔，“若是来生为人，我还是愿意选择教师这个职业。”事实上，在这几十年中，他在地质、环境、生物等领域已有50余篇论文问世，其中8篇被SCI收录，同时承担了多项国家和省部级科研项目。他的主要研究方向是“痕量及超痕量元素的分离富集与分析测试方法研究和资源综合利用以及微生物浸出强化的动力学理论及其应用研究”，在渐入佳境的汪模辉眼中，这个佶屈聱牙的名称却像一蓬花儿般鲜活地绽放。</span></p><p><span style=\"line-height: 150%; font-size: 12pt\">&nbsp; 著作等身的汪模辉多年来始终不忘人才培养，就门下的硕士研究生和博士研究生就有近30名。80年代的工资两、三百元，寄一封国际邮件就得3－5元，为给青年教师联系出国进修，他作为国际合作项目中方负责人自掏腰包，不断给美国、加拿大有关合作高校写信联系，平时脑子里总是想着如何把他们送出去的思路。侯贤灯、邓天龙、郎春燕三名青年教师就是靠着无数封英文信函，在他企盼的目光中奔赴大洋彼岸，在美国康涅狄克大学和加拿大诺伦西大学攻读博士学位和做博士后研究。“先生之风，山高水长”，并非汪模辉门下弟子的侯贤灯和邓天龙现已自立门户，成为学科带头人，多年后忆起汪老师不拘一格降人才的伯乐精神，仍然心怀感激，“汪老师是材化院的一面旗帜，他永远值得尊敬和学习”。而在汪模辉看来，学科发展是长江后浪推前浪，学校的明天属于年轻人，“我们谈爱祖国爱人民，自然也应该爱学生。”</span></p><p><span style=\"line-height: 150%; font-size: 12pt\">　</span></p><p style=\"text-align: center;\"><strong><span style=\"line-height: 150%; font-size: 12pt\">冰心一片忧思难忘</span></strong></p><p><span style=\"line-height: 150%; font-size: 12pt\">　</span></p><p><span style=\"line-height: 150%; font-size: 12pt\">&nbsp; 作为我国八十年代早期公派访问学者，汪模辉于1983年6月至1985年6月赴加拿大地质调查局和能源矿山资源部矿物能源技术中心。从红豆葳蕤的故乡到雪花如掌的异国，这位本分而本色的学人由此进入了学术生涯的辉煌时期。离开加拿大地调局时，一个女士诚恳地告诉他，“汪，现在我终于明白你们国家为什么要派你出来学习了。”\r\n \r\n1992年至1993年在美国康涅狄克大学化学系修研期间，他晚上常常熬夜搞科研，甚至有时从早晨8点连续干到凌晨两点，第二天早上照样精神抖擞地出现在国际学术会议上。米歇尔教授，美国分析化学界最杰出的原子光谱分析科学家之一，曾动情地对他说：“汪，别太累了，开会时你可以躺在我们的桌子上，你有这个权利。”临别前他在给汪模辉的信中写道：人 格高尚，具有崇高的科研精神，永远把工作放在第一位，为科研团队作出了杰出的贡献，汪推荐过来学习的人我都放心。</span></p><p><span style=\"line-height: 150%; font-size: 12pt\">&nbsp; &nbsp;时任渥太华西区留学生组组长，汪模辉和留学生们开座谈会想起自己可爱的祖国常常忍不住泪流满面。中国人并不笨，为什么不能自行搞研究发展壮大？祖国人民用血汗钱支持自己出国留学，说什么也得卯足劲奋发图强，无愧于国家的培养啊。学习结束后面对“回去和留下来”的两难抉择，祖国的重托犹在耳边，到底是国家的事业重要还是自己的事业重要？最终，他选择回到祖国。</span></p><p><span style=\"line-height: 150%; font-size: 12pt\">对比起时下“海归”的风景独好，作为老海归人的汪模辉并无对比之后的萧索与失落，反而深味自己的爱国豪情。“那时的海归啊，就一点，爱国，听从组织的统一指挥和号召，能很好地处理国家、集体和个人的关系。”</span></p><p><span style=\"line-height: 150%; font-size: 12pt\">　</span></p><p style=\"text-align: center;\"><strong><span style=\"line-height: 150%; font-size: 12pt\">爱党情深行者无疆</span></strong></p><p><span style=\"line-height: 150%; font-size: 12pt\">　</span></p><p><span style=\"line-height: 150%; font-size: 12pt\">&nbsp; 今年7月11日我校召开“先教”动员大会。当时汪模辉已向学院请假办好去加拿大的探亲手续，并预订了往返机票。作为一名党员，是留下来参加保持党员先进性教育，还是去和三年未曾谋面的女儿团聚？忖度思量后，他选择了留下。4月份已去加拿大的老伴打电话回来，“再请个假吧？”“不行啊，‘先教’是党的大事，自己是党员不能请假，要参加‘先教’全过程，这样对思想提高很大。”</span></p><p><span style=\"line-height: 150%; font-size: 12pt\">&nbsp; 他的这番举动引起了相当争议有人叫好，有人却不理解。面对此情此景，汪模辉却显得气定神闲。在美国修研期间，他每月都会主动到在中国驻纽约总领事馆缴纳党费，向教育组党组织负责人汇报思想工作。回国后当了多年系主任，国家现在遇到很多困难，他觉得理应顾全大局带头学习。目前还有一些老师对“先教”有不同的看法，这个坚定的马克思主义者却从不怀疑党的执政能力，“自己如果不走过场，大家就都不会走过场。”\r\n 为把“先教”个人党性分析上升到理论高度，国庆期间他把自己关在家里足足三天，写出了3000多字的个人分析材料。</span></p><p><span style=\"line-height: 150%; font-size: 12pt\">&nbsp; &quot;平易近人，敬业。党员的先进性就该是像汪老师那样，在平常工作的点点滴滴中反映出来。”本科生们这样评价他们的老师。</span></p><p><span style=\"line-height: 150%; font-size: 12pt\">&nbsp; “宽于待人严于待己，设身处地的为年轻人着想。在科研上追求完美，永远都拿放大镜寻找自己的问题”。跟随他多年从事科研的得意弟子、材化院郎春燕副教授如是说。</span></p><p><span style=\"line-height: 150%; font-size: 12pt\">&nbsp; “书斋学人，宠辱皆忘。他是我见过的最讲原则，党性观念最强的共产党人。”材化院前任党总支书记张勇感慨系之。</span></p><p><span style=\"line-height: 150%; font-size: 12pt\">&nbsp; 和化学打了一辈子交道的汪模辉自比为“水”，尤其喜欢“上善若水，厚德载物，水善利万物而不争”的从容境界。仁者乐山智者乐水，喜好“水”的汪模辉由此寻访到他人生无境的高度。</span></p><p><br/></p>', '2018-06-06 20:02', '成都理工大学网上党支部', '星斗其文 赤子其人', null, '4', '234');
INSERT INTO `news` VALUES ('<p style=\"text-align: center;\"><strong><span style=\"font-size: 22pt\">生命淡如水 日月清似芳</span></strong></p><p>&nbsp;</p><p style=\"text-align: center;\"><strong><span style=\"font-size: 12pt\">———记“先教”典型易惠芳</span></strong></p><p>&nbsp;</p><p><span style=\"line-height: 150%; font-size: 12pt\">&nbsp; 现年</span><span style=\"line-height: 150%; font-size: 12pt\">68</span><span style=\"line-height: 150%; font-size: 12pt\">岁的易惠芳老师是此次省上的“先教”典型，现为我校退休干部，学校“关工委”副秘书长。她在做思想政治工作，尤其是大学生思想政治工作方面付出了巨大的心血，获得了师生的好评。</span></p><p><span style=\"line-height: 150%; font-size: 12pt\">&nbsp; 易老师是原成都地质学院招收的第一批大学生，也是学校成立后第一批入党的学生。从</span><span style=\"line-height: 150%; font-size: 12pt\">1956</span><span style=\"line-height: 150%; font-size: 12pt\">年至今，能为党的事业奋斗近五十载，仍是易老师此生最大的骄傲和荣幸。这些年来，无论是从事行政工作还是青年学生教育工作，易老师最大的体会是：其身正，不令而行；其身不正，虽令不从。党员只有率先发挥模范带头作用，以自身的高洁和无私感染人、鼓舞人、团结人，才能得到人民群众的真心的拥戴和尊重。</span></p><p><span style=\"line-height: 150%; font-size: 12pt\">　</span></p><p style=\"text-align: center;\"><strong><span style=\"line-height: 150%; font-size: 12pt\">教书和管理都能育人</span></strong></p><p style=\"text-align: center;\"><strong><span style=\"line-height: 150%; font-size: 12pt\">———易老师在图书馆的育人岁月</span></strong></p><p><span style=\"line-height: 150%; font-size: 12pt\">　</span></p><p><span style=\"line-height: 150%; font-size: 12pt\">&nbsp; 早在担任原成都地质学院</span><span style=\"line-height: 150%; font-size: 12pt\">78</span><span style=\"line-height: 150%; font-size: 12pt\">级哲学师资班班主任的时候，易老师就开始慢慢琢磨如何加强和学生的沟通，帮助他们走出心理的困境。</span></p><p><span style=\"line-height: 150%; font-size: 12pt\">&nbsp; 在担任图书馆副馆长期间，易老师曾经碰到过一个神色怪异的男孩。在图书馆上自习时，他一直趴在桌子上没精打采，心事重重，连续几天都是如此。暗暗跟踪他四天后，易老师就坐下来试图和他聊聊，结果他一点也不领情。可易老师想，家长把孩子送到学校来，易老师们可要为他们负责啊！易老师就继续和颜悦色跟他谈话。他慢慢地也就信任易老师了，然后说出他的困惑和烦恼。当时他已经打算要从图书馆楼上跳下去了，如果不及时了解他的思想动态，并采取有效措施，后果不堪设想。</span></p><p><span style=\"line-height: 150%; font-size: 12pt\">&nbsp; 在图书馆时，易老师勤勤恳恳多年如一日。</span><span style=\"line-height: 150%; font-size: 12pt\">1991</span><span style=\"line-height: 150%; font-size: 12pt\">年，我校建立了水上图书馆，要组织搬家。易老师除了亲手对藏书安排进行组织部署外，还带头和大家一起干了两三个月时间，终于圆满完成了任务。</span></p><p><span style=\"line-height: 150%; font-size: 12pt\">&nbsp; 图书馆在每年的开学和毕业时是最忙的。当新生来时，易老师总喜欢到第一线。易老师还通过自学，自己编写了“如何利用好图书馆”的资料，以便让同学们尽早利用好图书馆。而在毕业生离校的高峰期，易老师又以身作则，亲临第一线，与工作人员一起，耐心处理层出不穷的问题，使学生能顺利离校。</span></p><p><span style=\"line-height: 150%; font-size: 12pt\">　</span></p><p style=\"text-align: center;\"><strong><span style=\"line-height: 150%; font-size: 12pt\">未满花甲退而不休</span></strong></p><p style=\"text-align: center;\"><strong><span style=\"line-height: 150%; font-size: 12pt\">———易老师在女生公寓中的“妈妈”经</span></strong></p><p><span style=\"line-height: 150%; font-size: 12pt\">　</span></p><p><span style=\"line-height: 150%; font-size: 12pt\">&nbsp; 易老师于</span><span style=\"line-height: 150%; font-size: 12pt\">1994</span><span style=\"line-height: 150%; font-size: 12pt\">年</span><span style=\"line-height: 150%; font-size: 12pt\">2</span><span style=\"line-height: 150%; font-size: 12pt\">月退休后，当时校内外好有几个单位准备高薪返聘易老师，但易老师接受了校公寓管理部门的较低薪水的聘请，担任女生公寓管理员。</span></p><p><span style=\"line-height: 150%; font-size: 12pt\">&nbsp; 当时的十一公寓，是全校唯一的女生公寓。从那个时候起，“易妈妈”的称号便叫开了。</span></p><p><span style=\"line-height: 150%; font-size: 12pt\">&nbsp; 易老师积极与学生公寓管理部门配合，利用公寓的门厅，创办宣传橱窗和黑板报，六年多时间共办宣传橱窗</span><span style=\"line-height: 150%; font-size: 12pt\">56</span><span style=\"line-height: 150%; font-size: 12pt\">期，黑板报</span><span style=\"line-height: 150%; font-size: 12pt\">600</span><span style=\"line-height: 150%; font-size: 12pt\">多期，开设了十几个栏目、举办了多次展览，在大学生中产生了很大的反响，取得了良好的效果，受到了学校领导的表扬。</span></p><p><span style=\"line-height: 150%; font-size: 12pt\">&nbsp; 易老师担心姑娘们不了解天气变化易患感冒，就将每天的天气预报写在公寓大厅的小黑板上；并在公寓二楼搭了全校第一个公寓通宵自习室。</span></p><p><span style=\"line-height: 150%; font-size: 12pt\">&nbsp; 为了帮助女生克服各种心理障碍，易老师利用休息时间，认真学习心理学方面的知识，并利用自己的办公室设立大学生心理健康咨询室，坚持每周定时为她们服务，帮助她们处理好遇到的各种问题，保持良好的心态。</span></p><p><span style=\"line-height: 150%; font-size: 12pt\">&nbsp; 大理白族女孩赵海燕家庭非常困难，她大学四年的所有衣服都是易老师想尽办法，一点一点为她解决的。就这样，易老师这个“易妈妈”也成为贫困女大学生心中的“希望工程妈妈”。这</span><span style=\"line-height: 150%; font-size: 12pt\">6</span><span style=\"line-height: 150%; font-size: 12pt\">年多来，易老师为</span><span style=\"line-height: 150%; font-size: 12pt\">64</span><span style=\"line-height: 150%; font-size: 12pt\">名贫困学生解决了生活上的困难和由此产生的各种心理障碍，使她们不仅顺利完成学业，还取得了优异的成绩。</span></p><p><span style=\"line-height: 150%; font-size: 12pt\">&nbsp; 几年间，易老师总共接待青年大学生</span><span style=\"line-height: 150%; font-size: 12pt\">6000</span><span style=\"line-height: 150%; font-size: 12pt\">多人次，并尽量想办法维护她们的合法权益，解决她们的后顾之忧。举办了各种讲座</span><span style=\"line-height: 150%; font-size: 12pt\">15</span><span style=\"line-height: 150%; font-size: 12pt\">次，参加听课近千人次，深受女大学生们的欢迎。</span></p><p><span style=\"line-height: 150%; font-size: 12pt\">&nbsp; 6</span><span style=\"line-height: 150%; font-size: 12pt\">年多的女生公寓管理工作，易老师先后为四五十名女大学生建立了四年成长跟踪档案。易老师奉行的原则是：只要对社会有益的，对别人有帮助的，就是自己该干的事情。</span></p><p><span style=\"line-height: 150%; font-size: 12pt\">&nbsp; 现在，虽然易老师早已不在公寓上班，但每个月至少有二三十人次打电话或亲自上门，甚至青年教师们也希望能够从易老师这个易妈妈的妈妈经中得到一些帮助和启示。</span></p><p><span style=\"line-height: 150%; font-size: 12pt\">　</span></p><p style=\"text-align: center;\"><strong><span style=\"line-height: 150%; font-size: 12pt\">退休以后的再次退休</span></strong></p><p style=\"text-align: center;\"><strong><span style=\"line-height: 150%; font-size: 12pt\">———易老师在“关工委”牵肠挂肚的日子</span></strong></p><p style=\"text-align: center;\"><span style=\"line-height: 150%; font-size: 12pt\">　</span></p><p><span style=\"line-height: 150%; font-size: 12pt\">&nbsp; 2000</span><span style=\"line-height: 150%; font-size: 12pt\">年，易老师又从公寓管理工作岗位上退了下来。在国外期间，易老师随时都牵挂着学校的发展。</span></p><p><span style=\"line-height: 150%; font-size: 12pt\">&nbsp; 回国后易老师又担任了关工委的副秘书长，并和其他老同志一起建立学生心理健康咨询室，每周为大学生开展两次心理健康咨询服务活动。易老师还多次主动找到过去自己关心过的学生谈心。</span></p><p><span style=\"line-height: 150%; font-size: 12pt\">&nbsp; 在关工委，易老师作为心理咨询组组长，和另外七位离退休老师一起把学生当成孩子、孙子，非常热情地接待，帮她们出点子。</span></p><p><span style=\"line-height: 150%; font-size: 12pt\">&nbsp; 为了做好新生的心理咨询工作，近两年来，易老师积极组织和参与校关心下一代工作委员会的“新生及新生家长咨询服务活动”，接待新生及家长上千人次。为做好新生及家长咨询服务，易老师走遍学校机关部门收集各方面的资料，汇编成册发给参与咨询活动的其他老同志，还把自己的电话号码及联系方式留给新生及家长，便于对这些学生的关心帮助。</span><span style=\"line-height: 150%; font-size: 12pt\">2001</span><span style=\"line-height: 150%; font-size: 12pt\">年和</span><span style=\"line-height: 150%; font-size: 12pt\">2004</span><span style=\"line-height: 150%; font-size: 12pt\">年易老师分别被评为四川省、全国教育系统关心下一代先进个人。</span></p><p><span style=\"line-height: 150%; font-size: 12pt\">&nbsp; 现在，易老师还常会回到女生公寓看看。从易老师到易妈妈再到易奶奶，此时此刻，易老师突然觉得，易妈妈是谁其实已经不再重要，重要的是年轻的大学生们需要有人来用心解读，需要有人真诚地关心和帮助。易老师相信，无论时代如何更新，用全部生命倡导的爱心实践，是永远不会过时的。</span></p><p style=\"text-align: center;\"><strong><span style=\"line-height: 150%; font-size: 12pt\">（周图伽 </span><span style=\"line-height: 150%; font-size: 12pt\">黄寰）</span></strong></p><p style=\"text-align: center;\"><img alt=\"\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/136430191721743565.jpg\"/></p><p><br/></p>', '2018-06-06 20:05', '成都理工大学网上党支部', '生命淡如水 日月清似芳', null, '4', '235');
INSERT INTO `news` VALUES ('<p style=\"text-align: center;\"><strong><span style=\"font-size: 22pt\">一名老党员最后的党费 </span></strong></p><p><span style=\"font-size: 12pt\">　</span></p><p><span style=\"font-size: 12pt\">&nbsp; “</span><span style=\"font-size: 12pt\">这就是我的党费。”离退休老党员程善禹晚年攒下2010元积蓄，而这笔钱最终由女儿程勤计代交给了党组织，这是他最后的党费。<br/></span><span style=\"font-size: 12pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><span style=\"font-size: 12pt\">哀乐低回，泪雨纷飞</span><span style=\"font-size: 12pt\">……</span></p><p><span style=\"font-size: 12pt\">&nbsp; 八月丹枫迎秋，春种秋收。可校医院退休干部程善禹一家，却无心欣赏这怡人的秋色，他们沉浸在沉重的悲痛和追思中。</span></p><p><span style=\"font-size: 12pt\">&nbsp; 2009</span><span style=\"font-size: 12pt\">年</span><span style=\"font-size: 12pt\">8</span><span style=\"font-size: 12pt\">月</span><span style=\"font-size: 12pt\">18</span><span style=\"font-size: 12pt\">日，一生爱国爱党，视病人为亲人的老党员程善禹，因病抢救无效，溘然辞世，享年</span><span style=\"font-size: 12pt\">80</span><span style=\"font-size: 12pt\">岁。</span></p><p><span style=\"font-size: 12pt\">&nbsp; 程善禹一生经历颇多。</span><span style=\"font-size: 12pt\">1929</span><span style=\"font-size: 12pt\">年，他出生在山东掖县的一个农民家庭，</span><span style=\"font-size: 12pt\">1946</span><span style=\"font-size: 12pt\">年，年仅</span><span style=\"font-size: 12pt\">17</span><span style=\"font-size: 12pt\">岁的程善禹成为华东第三野战军</span><span style=\"font-size: 12pt\">20</span><span style=\"font-size: 12pt\">军</span><span style=\"font-size: 12pt\">58</span><span style=\"font-size: 12pt\">师三团的一名卫生指导员，在全国解放那年他光荣入党了，时至</span><span style=\"font-size: 12pt\">2009</span><span style=\"font-size: 12pt\">年，已经有</span><span style=\"font-size: 12pt\">63</span><span style=\"font-size: 12pt\">年的党龄。</span><span style=\"font-size: 12pt\">1950</span><span style=\"font-size: 12pt\">年，程老抗美援朝，入朝作战。</span><span style=\"font-size: 12pt\">1956</span><span style=\"font-size: 12pt\">年，就职在成都地质学院职工医院，直至离休、去世。</span></p><p><span style=\"font-size: 12pt\">&nbsp; 女儿程勤计向记者谈到，父亲患心脏病多年，</span><span style=\"font-size: 12pt\">5</span><span style=\"font-size: 12pt\">月</span><span style=\"font-size: 12pt\">28</span><span style=\"font-size: 12pt\">号那天，心绞痛送往医院抢救。在途中，父亲戴着呼吸机，意识虽清醒，但已失去行动能力，奇怪的是，一个钱夹却紧紧拽在他的胸前。</span><span style=\"font-size: 12pt\">“</span><span style=\"font-size: 12pt\">这是我的党费</span><span style=\"font-size: 12pt\">”</span><span style=\"font-size: 12pt\">。父亲反复呢喃着这句话，直至弥留之际。</span></p><p><span style=\"font-size: 12pt\">&nbsp; 程善禹生活俭朴，一生保持劳动人民的本色，他节衣缩食，在平时的开销余额里，用几近苛刻的方式积攒了一些余钱，他心中早已明确这笔积蓄的用途，而这笔钱直到程老病重时才被女儿程勤计开启。</span><span style=\"font-size: 12pt\">“</span><span style=\"font-size: 12pt\">说实话，父亲在病床前把钱夹交给我之后，我一直不想去触动它，因为我始终相信父亲会好起来，始终相信父亲不会就这么离开我们，直到那晚，为了让他不留有遗憾，我还是</span><span style=\"font-size: 12pt\">……”</span><span style=\"font-size: 12pt\">程勤计眼泛泪花地说。</span></p><p><span style=\"font-size: 12pt\">&nbsp; 人到耄耋之年总醉心于回忆往事，程老也是如此。战争年代一夫当关、万夫莫开的场景仿佛重演在老人心头，而此时儿孙们总促膝聆听，听程善禹回忆着讲述着。</span><span style=\"font-size: 12pt\">“</span><span style=\"font-size: 12pt\">父亲在去世前不久，时常念叨着他年轻时的人、事、物。</span><span style=\"font-size: 12pt\">”</span><span style=\"font-size: 12pt\">在回忆里，老人仿佛回到当年的峥嵘岁月和磅礴气势之中。</span></p><p><span style=\"font-size: 12pt\">&nbsp; 程善禹为人十分低调，从不好大喜功，不讲个人得失。朝鲜战争爆发后不久，他便随军前往朝鲜，进行医疗工作，在一次野外扎营时，他和一名战友无意中发现一枚细菌弹，要知道当时在战争中发现了细菌弹是可以立功的，程善禹不但没有独自领功，反而将这一立功机会让给了他的战友。程善禹生前多次对子女说：</span><span style=\"font-size: 12pt\">“</span><span style=\"font-size: 12pt\">我是一名党员，要把国家的利益放在最前头，我有没有功劳不重要，重要的是党和人民的利益。</span><span style=\"font-size: 12pt\">”</span><span style=\"font-size: 12pt\">程善禹不仅对自己要求严格，对子女、儿孙更是有过之而无不及，他时常教育晚辈们要热爱共产主义事业，要向党组织靠拢，搞好工作与学习，争做一名对党和国家有贡献的人，他也要儿孙们艰苦奋斗，不要有点小成绩就沾沾自喜，要不断追求进步。晚辈们每每认真听取，却又心酸老人的蹉跎人生，心中无不敬佩。</span></p><p><span style=\"font-size: 12pt\">&nbsp; 工作中的程善禹更是一丝不苟，兢兢业业。女儿依稀记得，当年自己还小，母亲工作也很忙，父亲为了工作、家庭两不误，就把她带到单位方便照看，由于年纪小，不懂事，记忆中，看到那么多穿白大褂的人来来往往，和穿梭的人流，自己就觉得很害怕。</span><span style=\"font-size: 12pt\">“</span><span style=\"font-size: 12pt\">但我从没埋怨过我的父亲，他是位好大夫，好爸爸</span><span style=\"font-size: 12pt\">”</span><span style=\"font-size: 12pt\">。程善禹一向很守时，对病患认真负责。有一次，医院有紧急事件，为了节省路途，他抄小路前往医院，哪知因天黑不小心摔倒在水沟里，从此留下久疾。离休之后的程善禹，并没有停下爱党爱国的热情。平日里，老人在家看书学习新知识，看报了解国家大事，积极配合参加单位的党员活动，</span></p><p><span style=\"font-size: 12pt\">&nbsp; 自程善禹去世后，前去吊唁的人络绎不绝，程老生前的好友一直感叹程善禹生前的种种事迹。离休干部党支部书记张振卿动容地说：</span><span style=\"font-size: 12pt\">“</span><span style=\"font-size: 12pt\">真是位好同志啊，他从不给组织添麻烦，对了，抗震救灾时他还交了</span><span style=\"font-size: 12pt\">600</span><span style=\"font-size: 12pt\">元特殊党费，那天去老程家，实在是很朴素，这样他还想着要交党费，太难得了。</span><span style=\"font-size: 12pt\">”</span><span style=\"font-size: 12pt\">学校离退休处处长徐曼库同样表示对程善禹去世的惋惜，他感叹道：</span><span style=\"font-size: 12pt\">“</span><span style=\"font-size: 12pt\">像这样的老同志，我们真应该好好学习，尤其是你们年轻一代更要学习这样的先进事迹！</span><span style=\"font-size: 12pt\">”</span></p><p><span style=\"font-size: 12pt\">&nbsp; 根据相关规定，中共党员自愿一次交纳</span><span style=\"font-size: 12pt\">1000</span><span style=\"font-size: 12pt\">元以上的党费，全部上缴中央。目前学校组织部正积极办理相关手续，将程善禹同志的党费通过省委组织部转交中央组织部，届时，由中央组织部出具的收据将交到程善禹家人手中。</span></p><p><span style=\"font-size: 12pt\">&nbsp; 光阴如水，岁月如梭。今年正值国家</span><span style=\"font-size: 12pt\">60</span><span style=\"font-size: 12pt\">周年大庆，我们回忆、追寻过往的人和事，不仅仅是要了解他们的奋斗历程，更重要的是要发扬他们身上无私奉献、为共产主义事业而奋斗的精神。是因为有了他们的挺拔身躯和坚定信念，历史的丰碑、巍峨的华表、雄伟的天安门、锦绣的山河才会在这片土地上，勃发出震惊世界的力量。而我们也将前赴后继，一往无前。</span></p><p><br/></p>', '2018-06-06 20:07', '成都理工大学网上党支部', '一名老党员最后的党费', null, '4', '236');
INSERT INTO `news` VALUES ('<p style=\"text-align: center;\"><strong><span style=\"font-size: 22pt\">“</span></strong><strong><span style=\"font-size: 22pt\">我始终相信付出总有回报”</span></strong></p><p style=\"text-align: center;\"><span style=\"line-height: 150%; font-size: 12pt\"><br/></span><strong><span style=\"line-height: 150%; font-size: 16pt\">——</span></strong><strong><span style=\"line-height: 150%; font-size: 16pt\">专访我校</span></strong><strong><span style=\"line-height: 150%; font-size: 16pt\">2009</span></strong><strong><span style=\"line-height: 150%; font-size: 16pt\">年</span></strong><strong><span style=\"line-height: 150%; font-size: 16pt\">“</span></strong><strong><span style=\"line-height: 150%; font-size: 16pt\">新世纪百千万人才工程</span></strong><strong><span style=\"line-height: 150%; font-size: 16pt\">” </span></strong><strong><span style=\"line-height: 150%; font-size: 16pt\">入选者李忠权教授</span></strong></p><p style=\"text-align: center;\"><span style=\"line-height: 150%; font-size: 12pt\">　</span></p><p><strong><span style=\"line-height: 150%; font-size: 12pt\">□ </span></strong><strong><span style=\"line-height: 150%; font-size: 12pt\">学生记者&nbsp; </span></strong><strong><span style=\"line-height: 150%; font-size: 12pt\">张</span></strong><strong><span style=\"line-height: 150%; font-size: 12pt\">建</span></strong><strong><span style=\"line-height: 150%; font-size: 12pt\">滕</span></strong></p><p><strong><span style=\"line-height: 150%; font-size: 12pt\">人物速览</span></strong></p><p><span style=\"line-height: 150%; font-size: 12pt\">　</span><span style=\"line-height: 150%; font-size: 12pt\">李忠权，博士生导师，双博士后，我校地科院副院长，四川省学术和技术带头人。\r\n \r\n1986年7月毕业于南京大学地球科学系，获理学学士学为；1989年7月毕业于成都地质学院地质学系，获理学硕士学位；1999年6月毕业于成都理工学院石油系，获工学博士学位；1999年7月至2002年6月在北京大学与大庆油田做博士后研究工作；2005年5月至2006年11月在美国St.\r\n Louis University 做博士后与访问学者。现为中国地质学会会员、</span><span style=\"line-height: 150%; font-size: 12pt\">“</span><span style=\"line-height: 150%; font-size: 12pt\">油气藏地质及开发工程</span><span style=\"line-height: 150%; font-size: 12pt\">”</span><span style=\"line-height: 150%; font-size: 12pt\">国家重点实验室固定研究成员。<br/><br/><br/></span></p><p><span style=\"line-height: 150%; font-size: 12pt\">　</span></p><p><span style=\"line-height: 150%; font-size: 12pt\">&nbsp; 2010</span><span style=\"line-height: 150%; font-size: 12pt\">年</span><span style=\"line-height: 150%; font-size: 12pt\">1</span><span style=\"line-height: 150%; font-size: 12pt\">月</span><span style=\"line-height: 150%; font-size: 12pt\">25</span><span style=\"line-height: 150%; font-size: 12pt\">日，人力资源和社会保障部等七部门公布了</span><span style=\"line-height: 150%; font-size: 12pt\">2009</span><span style=\"line-height: 150%; font-size: 12pt\">年</span><span style=\"line-height: 150%; font-size: 12pt\">“</span><span style=\"line-height: 150%; font-size: 12pt\">新世纪百千万人才工程</span><span style=\"line-height: 150%; font-size: 12pt\">”</span><span style=\"line-height: 150%; font-size: 12pt\">国家级人选名单，我校李忠权教授名列其中。李忠权教授为什么能够入选</span><span style=\"line-height: 150%; font-size: 12pt\">“</span><span style=\"line-height: 150%; font-size: 12pt\">新世纪百千万人才工程</span><span style=\"line-height: 150%; font-size: 12pt\">”</span><span style=\"line-height: 150%; font-size: 12pt\">？在他入选的背后有着怎样的故事？带着这些问题，本报记者日前采访了李忠权教授。</span></p><p><span style=\"line-height: 150%; font-size: 12pt\">　</span></p><p><strong><span style=\"line-height: 150%; font-size: 12pt\">&nbsp; 【</span></strong><strong><span style=\"line-height: 150%; font-size: 12pt\">24</span></strong><strong><span style=\"line-height: 150%; font-size: 12pt\">个春秋诠释事业历程】</span></strong></p><p><span style=\"line-height: 150%; font-size: 12pt\">　</span></p><p><span style=\"line-height: 150%; font-size: 12pt\">&nbsp; “</span><span style=\"line-height: 150%; font-size: 12pt\">其实，事先我一点都不知情。</span><span style=\"line-height: 150%; font-size: 12pt\">”</span><span style=\"line-height: 150%; font-size: 12pt\">当被告知入选</span><span style=\"line-height: 150%; font-size: 12pt\">2009</span><span style=\"line-height: 150%; font-size: 12pt\">年</span><span style=\"line-height: 150%; font-size: 12pt\">“</span><span style=\"line-height: 150%; font-size: 12pt\">新世纪百千万人才工程</span><span style=\"line-height: 150%; font-size: 12pt\">”</span><span style=\"line-height: 150%; font-size: 12pt\">时，</span><span style=\"line-height: 150%; font-size: 12pt\">45</span><span style=\"line-height: 150%; font-size: 12pt\">岁的李忠权表情很淡然。</span></p><p><span style=\"line-height: 150%; font-size: 12pt\">&nbsp; 1986</span><span style=\"line-height: 150%; font-size: 12pt\">年，毕业于南京大学地球科学系的李忠权考入成都地质学院（我校前身）攻读硕士研究生。三年后，他选择了留校。从一名普通的教师逐步成长为我校地质学研究的骨干，他用</span><span style=\"line-height: 150%; font-size: 12pt\">24</span><span style=\"line-height: 150%; font-size: 12pt\">个春秋诠释了这一历程。</span></p><p><span style=\"line-height: 150%; font-size: 12pt\">&nbsp; 李忠权主要从事构造地质学和石油地质学领域的研究工作，形成了两个相对稳定的具一定特色和优势的研究方向，即成盆构造动力学与油气成藏动力学。</span></p><p><span style=\"line-height: 150%; font-size: 12pt\">&nbsp; 几十年如一日的坚持，只因为</span><span style=\"line-height: 150%; font-size: 12pt\">“</span><span style=\"line-height: 150%; font-size: 12pt\">做好自己的工作</span><span style=\"line-height: 150%; font-size: 12pt\">”</span><span style=\"line-height: 150%; font-size: 12pt\">。</span><span style=\"line-height: 150%; font-size: 12pt\">每天，他都埋头于工作中，奔走在教学、科研的第一线。</span><span style=\"line-height: 150%; font-size: 12pt\">“</span><span style=\"line-height: 150%; font-size: 12pt\">很多时候是吃不好，也睡不好，甚至凌晨两点都还不能休息。</span><span style=\"line-height: 150%; font-size: 12pt\">” </span><span style=\"line-height: 150%; font-size: 12pt\">尽管在外风餐露宿，在李忠权看来，这些已经变成他生活不可分割的一部分，而且这么多年来，这些困难也丝毫没能让他献身科研的脚步变得犹豫。这句看似简单的话语，几乎成了执着于科学钻研的专家学者们描述自己工作的专属语句。</span></p><p><span style=\"line-height: 150%; font-size: 12pt\">&nbsp; 2009</span><span style=\"line-height: 150%; font-size: 12pt\">年</span><span style=\"line-height: 150%; font-size: 12pt\">“</span><span style=\"line-height: 150%; font-size: 12pt\">新世纪百千万人才工程</span><span style=\"line-height: 150%; font-size: 12pt\">” </span><span style=\"line-height: 150%; font-size: 12pt\">国家级人选</span><span style=\"line-height: 150%; font-size: 12pt\">806</span><span style=\"line-height: 150%; font-size: 12pt\">人。作为我校国家级人选，李忠权教授谦逊地说：</span><span style=\"line-height: 150%; font-size: 12pt\">“</span><span style=\"line-height: 150%; font-size: 12pt\">其实我也没有做什么了不起的事情，只是尽自己的全力做好自己的工作罢了。</span><span style=\"line-height: 150%; font-size: 12pt\">”</span></p><p><span style=\"line-height: 150%; font-size: 12pt\">&nbsp; “</span><span style=\"line-height: 150%; font-size: 12pt\">百千万人才工程</span><span style=\"line-height: 150%; font-size: 12pt\">”</span><span style=\"line-height: 150%; font-size: 12pt\">是针对改革开放以来，我国中青年专业技术人才尤其是学术和技术带头人相对于经济和社会发展的需要来说十分缺乏的问题而实施的中青年高层次人才培养工程。从</span><span style=\"line-height: 150%; font-size: 12pt\">1995</span><span style=\"line-height: 150%; font-size: 12pt\">年至今，共遴选出</span><span style=\"line-height: 150%; font-size: 12pt\">7</span><span style=\"line-height: 150%; font-size: 12pt\">批</span><span style=\"line-height: 150%; font-size: 12pt\">“</span><span style=\"line-height: 150%; font-size: 12pt\">百千万人才工程</span><span style=\"line-height: 150%; font-size: 12pt\">”</span><span style=\"line-height: 150%; font-size: 12pt\">国家级人选，共</span><span style=\"line-height: 150%; font-size: 12pt\">4113</span><span style=\"line-height: 150%; font-size: 12pt\">名。</span></p><p><span style=\"line-height: 150%; font-size: 12pt\">&nbsp;　</span></p><p><strong><span style=\"line-height: 150%; font-size: 12pt\">&nbsp; 【野外考察</span></strong><strong><span style=\"line-height: 150%; font-size: 12pt\">体质被</span></strong><strong><span style=\"line-height: 150%; font-size: 12pt\">“</span></strong><strong><span style=\"line-height: 150%; font-size: 12pt\">考察</span></strong><strong><span style=\"line-height: 150%; font-size: 12pt\">”</span></strong><strong><span style=\"line-height: 150%; font-size: 12pt\">】</span></strong></p><p><span style=\"line-height: 150%; font-size: 12pt\">　</span></p><p><span style=\"line-height: 150%; font-size: 12pt\">&nbsp; 个人的成就和光环背后都有不为人知的艰辛，这一点，李忠权也不例外。</span></p><p><span style=\"line-height: 150%; font-size: 12pt\">&nbsp; 1996</span><span style=\"line-height: 150%; font-size: 12pt\">年，他负责的一个项目在新疆地区上马。较大的昼夜温差对长期生活在天府之国的李忠权是一个极大的考验。</span></p><p><span style=\"line-height: 150%; font-size: 12pt\">&nbsp; 由于项目考察都是在戈壁滩上进行的，李忠权一行的吃、住都在戈壁滩上：生活用品十分简陋，住在帐篷里，条件极其艰苦。三个月后，李忠权的体重直线下降，锐减</span><span style=\"line-height: 150%; font-size: 12pt\">20</span><span style=\"line-height: 150%; font-size: 12pt\">公斤。也是在这个时候，他患上了糖尿病。回想起来，他不禁感慨道：</span><span style=\"line-height: 150%; font-size: 12pt\">“</span><span style=\"line-height: 150%; font-size: 12pt\">就是在这样一次次的野外考察中，我的体质下降了很多。</span><span style=\"line-height: 150%; font-size: 12pt\">”</span></p><p><span style=\"line-height: 150%; font-size: 12pt\">　</span></p><p><strong><span style=\"line-height: 150%; font-size: 12pt\">&nbsp; 【科研：天道酬勤】</span></strong></p><p><span style=\"line-height: 150%; font-size: 12pt\">　</span></p><p><span style=\"line-height: 150%; font-size: 12pt\">&nbsp; 通过多年的钻研，李忠权在川东构造演化、松辽盆地深层研究等方面都取得了创新性的研究成果。在北京大学做博士后参与大庆油田研究工作期间，通过与大庆科研人员的广泛合作，李忠权首次在松辽盆地深层发现了三叠</span><span style=\"line-height: 150%; font-size: 12pt\">—</span><span style=\"line-height: 150%; font-size: 12pt\">早中侏罗纪古前½盆地。这一发现不仅对松辽盆地深层天然气勘探具有十分重要的现实意义，而且对整个东部地区的盆山藕合研究都有借鉴意义。该成果经教育部鉴定：总体达国际先进水平，其中部分成果达到国际领先水平。</span></p><p><span style=\"line-height: 150%; font-size: 12pt\">&nbsp; 对于荣誉和成就，李忠权似乎并没有什么特别的见地。</span><span style=\"line-height: 150%; font-size: 12pt\">“</span><span style=\"line-height: 150%; font-size: 12pt\">只有踏实认真地去做了，荣誉和成就那都是水到渠成、自然而然的。</span><span style=\"line-height: 150%; font-size: 12pt\">”</span><span style=\"line-height: 150%; font-size: 12pt\">他一脸笑意地对我们说，</span><span style=\"line-height: 150%; font-size: 12pt\">“</span><span style=\"line-height: 150%; font-size: 12pt\">我始终相信付出总有回报。</span><span style=\"line-height: 150%; font-size: 12pt\">”</span><span style=\"line-height: 150%; font-size: 12pt\">李忠权这样形容自己这次入选：一个院士会平白无故的就成为院士吗？经历无数的艰辛，他才会硕果累累。人们往往只看到他的成功却忽略了他的付出。</span><span style=\"line-height: 150%; font-size: 12pt\">”</span></p><p><span style=\"line-height: 150%; font-size: 12pt\">&nbsp; 对自己的学生，李忠权也是这样要求的。</span><span style=\"line-height: 150%; font-size: 12pt\">“</span><span style=\"line-height: 150%; font-size: 12pt\">不要老是考虑自身的利益和报酬，一个人只有努力付出了，创造出了同等的价值，才有足够的底气去接受荣誉及其他。</span><span style=\"line-height: 150%; font-size: 12pt\">”</span></p><p><span style=\"line-height: 150%; font-size: 12pt\">　</span></p><p><strong><span style=\"line-height: 150%; font-size: 12pt\">&nbsp; 【教学：育教于理】</span></strong></p><p><span style=\"line-height: 150%; font-size: 12pt\">　</span></p><p><span style=\"line-height: 150%; font-size: 12pt\">&nbsp; 虽然李忠权执着于科学领域的研究，但他也不忘将自己的知识传授于人，注重人才培养。</span></p><p><span style=\"line-height: 150%; font-size: 12pt\">&nbsp; 2005</span><span style=\"line-height: 150%; font-size: 12pt\">年</span><span style=\"line-height: 150%; font-size: 12pt\">5</span><span style=\"line-height: 150%; font-size: 12pt\">月，李忠权前往美国</span><span style=\"line-height: 150%; font-size: 12pt\">St. Louis University </span><span style=\"line-height: 150%; font-size: 12pt\">做博士后与访问学者。他发现国外开放式的教学模式非常有助于学生的成长和发展。回国后，在自己的教学中，他试图改变填鸭式的教学模式，</span><span style=\"line-height: 150%; font-size: 12pt\">“</span><span style=\"line-height: 150%; font-size: 12pt\">我认为根据学生自己的特点和长处去培养和发展，对学生不要有过多的限制，这方面很重要。</span><span style=\"line-height: 150%; font-size: 12pt\">”</span><span style=\"line-height: 150%; font-size: 12pt\">在实际教学中，李忠权不要求学生都向自己研究的方向发展。如果他们对其他方面感兴趣，或者想有更深入的学习研究，他都非常支持，</span><span style=\"line-height: 150%; font-size: 12pt\">“</span><span style=\"line-height: 150%; font-size: 12pt\">要注重一种个性的培养</span><span style=\"line-height: 150%; font-size: 12pt\">”</span><span style=\"line-height: 150%; font-size: 12pt\">。</span></p><p><span style=\"line-height: 150%; font-size: 12pt\">&nbsp; 在完成科研任务的同时，他始终坚持为本科生和研究生授课，主讲了《地质学基础》、《成矿规律与成矿预测》、《成盆构造动力学》、《造山带结构与构造分析》等课程，并将自己的科研成果和国内外本学科的前沿科技及时融入到自己的教学中。</span></p><p><span style=\"line-height: 150%; font-size: 12pt\">　</span></p><p><span style=\"line-height: 150%; font-size: 12pt\">&nbsp; 记得国学大师季羡林先生曾经说过：</span><span style=\"line-height: 150%; font-size: 12pt\">“</span><span style=\"line-height: 150%; font-size: 12pt\">知识真火何以不灭，就是一代代那么多崇尚真理学问的人，在把自己的心肝脑汁都掏出来不断地添加于知识之火中，从而使之薪火传存。</span><span style=\"line-height: 150%; font-size: 12pt\">”</span></p><p><span style=\"line-height: 150%; font-size: 12pt\">&nbsp; 采访李忠权教授，我们收获更多的是感动。与他身上环绕的荣誉光环相比，他的执着和谦逊更容易给我们留下深刻的印象。</span></p><p>&nbsp;</p><p><br/></p>', '2018-06-06 20:10', '成都理工大学网上党支部', '“我始终相信付出总有回报”', null, '4', '237');
INSERT INTO `news` VALUES ('<p style=\"text-align: center;\"><span style=\"line-height:150%;font-family:宋体;color:black;font-size:16px\"><strong><span style=\"line-height:200%;font-family:黑体;font-size:29px\">外院学子获全国日语演讲大赛</span></strong><strong><span style=\"line-height:200%;font-family:黑体;font-size:29px\">西南赛区三等奖</span></strong></span> </p><p><span style=\"line-height:150%;font-family:宋体;color:black;font-size:16px\">&nbsp; </span></p><p>&nbsp;&nbsp;&nbsp; 2018<span style=\"line-height:150%;font-family:宋体;color:black;font-size:16px\">年“中华杯”全国日语演讲比赛西南赛区决赛于5月18-20日在贵州大学举办，西南赛区共三十所学校参加了比赛，2017级日语系学生王沅芷代表成都理工大学参加比赛。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"line-height:150%;font-family:宋体;font-size:16px\">本次比赛分为命题演讲和即席演讲两部分，比赛中各位选手充分展示了各自出色的语言功底和思想深度，王沅芷同学最终以优异的成绩获得“中华杯”全国日语演讲比赛西南赛区决赛三等奖，胡君平老师获得“优秀指导教师奖”。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"line-height:150%;font-family:宋体;font-size:16px\">通过此次比赛，选手进一步锻炼了自己的日语应用能力和演讲技能，展示了理工学子风采，同时也为我校争得了荣誉。</span> &nbsp;</p><p style=\"text-align: center;\">&nbsp;<br/> <img title=\"image003\" alt=\"image003\" src=\"http://www.cdut.edu.cn:8003/wgyxy/UserFiles/Image/20180530/20180530155548_313.jpg\" width=\"500\" height=\"577\"/><br/> <span style=\"font-size:16px\">获奖学生与指导老师合影</span>&nbsp; <br/></p><p><br/></p>', '2018-06-06 20:11', '成都理工大学网上党支部', '外院学子获全国日语演讲大赛西南赛区三等奖', null, '4', '238');
INSERT INTO `news` VALUES ('<p style=\"text-align: center;\"><strong><span style=\"line-height:200%;font-family:黑体;font-size:29px\">我校学生蝉联中国（四川）高校传媒联盟副主席</span></strong> &nbsp;</p><p style=\"text-indent: 0px; text-align: center;\"><strong><span style=\"line-height:200%;font-size:20px\">“云立方”学生传媒中心斩获四川校媒年度多个新闻奖项</span></strong> &nbsp;</p><p style=\"text-indent:0\">&nbsp;</p><p style=\"text-indent:32px\"><br/></p><p style=\"text-align:justify;text-indent:32px\"><span style=\"line-height:200%;font-size:16px\">5</span><span style=\"line-height:200%;font-size:16px\">月5日、6日，四川校媒2018年会暨第九届四川高校传媒论坛于四川师范大学成龙校区举行，我校党委宣传部“云立方”学生传媒中心下设大学生记者站、理工之声广播台、理工电视台、新闻嗅觉图片社、新媒体工作室、原·设创意工作室、事业发展规划处，以及成都理工大学青年传媒中心、大学生通讯社、《成理就业》共10家媒体获邀参会。</span> &nbsp;</p><p style=\"text-align:justify;text-indent:32px\"><span style=\"line-height:200%;font-size:16px\">5</span><span style=\"line-height:200%;font-size:16px\">日晚，四川校媒新一届主席团竞选展演在四川师范大学龙湖剧场举行。大学生记者站副部长、传播科学与艺术学院广播电视系大二学生何妍以视频的创新方式进行自我介绍，并在提问环节中表现出众，最终在12位竞选者中脱颖而出，成功竞选为中国（四川）高校传媒联盟第十一届主席团副主席。据悉，大学生记者站已连续八年蝉联该联盟副主席。</span> &nbsp;</p><p style=\"text-align:justify;text-indent:32px\"><span style=\"line-height:200%;font-size:16px\"></span>&nbsp;</p><p style=\"text-indent: 0px; text-align: center;\"><img title=\"tim图片20180509090331\" alt=\"tim图片20180509090331\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180509/20180509090354_563.png\" width=\"500\" height=\"332\"/> &nbsp;</p><p style=\"text-indent: 0px; text-align: center;\"><span style=\"line-height:200%;font-size:16px\">新一届主席团合影（后排右二为何妍）</span> &nbsp;</p><p style=\"text-indent:0\"><span style=\"line-height:200%;font-size:16px\"></span>&nbsp;</p><p style=\"text-align:justify;text-indent:32px\"><span style=\"line-height:200%;font-size:16px\">此外，“云立方”学生传媒中心下设社团在本次四川校媒新闻奖评选中收获颇丰，成为川内高校获奖面最大的校媒组织。大学生记者站、新媒体工作室在“四川省十佳校园媒体”中占据两席，大学生记者站理事长、传播科学与艺术学院广播电视系大三学生雷宇航获评全川唯一“年度最佳记者”称号。理工电视台作品《青春献给远方|黄琅天空最亮的“星”》获“年度最佳报道”，该片记录了我校首批赴雷波支教的三名学生在当地近一年的工作生活，既讲述了支教师生间的动人故事，也反映出成理青年为支教事业贡献力量的坚定信念。</span> &nbsp;</p><p style=\"text-align:justify;text-indent:32px\"><span style=\"line-height:200%;font-size:16px\"></span>&nbsp;</p><p style=\"text-indent: 0px; text-align: center;\"><span style=\"line-height:200%;font-size:16px\"><img title=\"图二\" alt=\"图二\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180509/20180509090156_582.jpg\" width=\"500\" height=\"375\"/></span> &nbsp;</p><p style=\"text-indent: 0px; text-align: center;\"><span style=\"line-height:200%;font-size:16px\">四川校媒新闻奖颁奖现场</span> &nbsp;</p><p style=\"text-indent:0\"><span style=\"line-height:200%;font-size:16px\"></span>&nbsp;</p><p style=\"text-align:justify;text-indent:32px\"><span style=\"line-height:200%;font-size:16px\">6</span><span style=\"line-height:200%;font-size:16px\">日的2017快乐学校分享会上，大学生记者站副部长、外国语学院翻译专业大二学生金涵作为小组代表，分享了去年夏天在快乐学校14天的难忘经历。她从学生记者的角度出发，围绕“讲述、看见、情谊”三个关键词，畅谈自己在新闻实践中的所思所想，其丰富的资料图片、独特的分享视角，获得台下数次热烈掌声。</span> &nbsp;</p><p style=\"text-align:left;text-indent:32px\"><span style=\"line-height:200%;font-size:16px\">党委宣传部“云立方”学生传媒中心以“置身日常，却有新闻之心”为理念，聚合文字、音画、影像、视觉、新媒体专业优势，服务于成都理工大学官方微博微信、QQ公众平台、理工新闻网、理工图片网、《成都理工大学报》等多维度校园主流媒介，以提升传播力、公信力、影响力为己任，在创新构建立体多样、融合发展的媒介传播格局、助力我校内外宣传的进程中不断砥砺前行。</span> &nbsp;</p><p style=\"text-align: center; text-indent: 32px;\"><span style=\"line-height:200%;font-size:16px\"></span>&nbsp;</p><p style=\"text-indent: 0px; text-align: center;\"><span style=\"line-height:200%;font-size:16px\"><img title=\"图三\" alt=\"图三\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180509/20180509090222_664.jpg\" width=\"500\" height=\"263\"/></span> &nbsp;</p><p style=\"text-indent: 0px; text-align: center;\"><span style=\"line-height:200%;font-size:16px\">会员媒体合影</span> &nbsp;</p><p style=\"text-indent:0\"><span style=\"line-height:200%;font-size:16px\"></span>&nbsp;</p><p><strong><span style=\"line-height:200%;font-family:楷体;font-size:16px\">背景介绍：</span></strong><span style=\"line-height:200%;font-family:楷体;font-size:16px\">中国（四川）高校传媒联盟（简称四川校媒）成立于2008年11月，是中国高校传媒联盟在国内最早成立的省级组成部分，由四川省各高校学生媒体发起，中国青年报社四川记者站为指导单位。该联盟旨在为川内高校校园媒体搭建一个交流、共享、提高、互惠的合作平台，四川校媒会员媒体包括了四川大学、西南交通大学、电子科技大学、四川农业大学、西南财经大学、西南民族大学、成都理工大学等在内的40多所高校的124家校园媒体，涵盖了报纸、杂志、电视台、广播站、新闻网、微博、微信公众号等多种校园媒体。自成立以来，四川校媒在中国高校传媒联盟、中国青年报社、共青团四川省委、四川各高校的指导和支持下，主办、承办了诸多活动与项目，为川内高校校园媒体提供了广阔的交流平台与机会。</span></p><p><br/></p>', '2018-06-06 20:13', '成都理工大学网上党支部', '我校学生蝉联中国（四川）高校传媒联盟副主席', null, '3', '239');
INSERT INTO `news` VALUES ('<p style=\"text-align: center;line-height: 200%\"><strong><span style=\"line-height: 200%;font-family: 黑体;font-size: 29px\">校工会举办庆“三八”教职工趣味运动会</span></strong></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">在温暖的春日中，3月13日中午，由校工会女工委主办的庆祝“三八”妇女节全校教职工趣味运动会在体育场举行。</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">趣味运动会“鼓动人心”共分为两个比赛项目，全校22个部门工会（直属工会小组）的396名教职工参加了比赛。赛前，学校党委常委、工会主席王敏在动员讲话中鼓励选手们在运动会中展现巾帼风采，取得好成绩。此次运动会的目的在于欢度节日为契机，倡导全校教职工多锻炼，以良好的身体素质和精神面貌在为学校建设做出自己的贡献。</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">整个运动会洋溢着团结奋进、和谐向上的精神，充分展现了我校女教职工美丽、健康的精神风貌。经过紧张激烈的较量，最终十个部门工会获得一、二、三等奖，获奖名单如下。</span></p><p style=\"text-align: center;line-height: 200%;text-indent: 32px\"><strong><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">获奖名单</span></strong></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">一等奖&nbsp; 信息科学与技术学院&nbsp;&nbsp;&nbsp;&nbsp; 继续教育学院</span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">二等奖&nbsp; 后勤服务集团&nbsp;&nbsp;&nbsp;&nbsp; 机关二&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 机关一 </span></p><p style=\"line-height: 200%;text-indent: 32px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">三等奖&nbsp; 沉积地质研究院&nbsp;&nbsp; 旅游与城乡规划学院&nbsp;&nbsp; 体育学院</span></p><p style=\"line-height: 200%;text-indent: 96px\"><span style=\"line-height: 200%;font-family: 宋体;font-size: 16px\">政治学院&nbsp;&nbsp;&nbsp; 商学院</span>&nbsp;&nbsp;</p><p style=\"line-height: 200%;text-indent: 96px\">&nbsp;</p><p style=\"text-align: center\"><span style=\"font-family: 宋体;font-size: 16px\"><img alt=\"\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/142656518989069147.jpg\" width=\"500\" height=\"331\"/></span>&nbsp;</p><p style=\"text-align: center\">&nbsp;&nbsp;&nbsp;<span style=\"font-size: 19px\">校工会主席王敏做赛前动员</span>&nbsp;</p><p style=\"text-align: center;\"><span style=\"font-size: 19px\"><img alt=\"\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/142656526175036764.jpg\" width=\"500\" height=\"332\"/></span> &nbsp; <span style=\"font-size: 19px\"><span style=\"font-size: 19px\"><br/></span></span></p><p style=\"text-align: center;\"><span style=\"font-size: 19px\"><span style=\"font-size: 19px\">比赛现场</span></span></p><p><br/></p>', '2018-06-06 20:15', '成都理工大学网上党支部', '校工会举办庆“三八”教职工趣味运动会', null, '3', '240');
INSERT INTO `news` VALUES ('<p style=\"text-align:center\"><strong><span style=\"font-size:29px;font-family:黑体;color:black;line-height:200%\">扬帆起航，踏上新征程</span></strong> &nbsp;</p><p style=\"text-align:center\"><strong><span style=\"font-size:21px;font-family:宋体;color:black;line-height:200%\">我校隆重举行2018届毕业典礼暨授位仪式</span></strong> &nbsp;</p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">年年岁岁花相似，岁岁年年人不同。伴着清晨的和风，</span><span style=\"font-size:16px;font-family:宋体;line-height:200%\">骊歌已悄然响起，20</span><span style=\"font-size:16px;font-family:宋体;line-height:200%\">18</span><span style=\"font-size:16px;font-family:宋体;line-height:200%\">届学子带着对未来的期许和对母校的不舍，收拾好行囊，即将离开成理，走向远方。</span><span style=\"font-size:16px;font-family:宋体;line-height:200%\">&nbsp; </span></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">6</span><span style=\"font-size:16px;font-family:宋体;line-height:200%\">月25日，成都理工大学在西区运动场隆重举行2018届毕业典礼暨授位仪式。上午8点，庄严的国歌声奏响，拉开了毕业典礼的序幕。</span> &nbsp;</p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\"></span>&nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\"><img title=\"img_9013\" alt=\"img_9013\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180625/20180625151553_379.jpg\" width=\"500\" height=\"281\"/></span> &nbsp;</p><p style=\"text-align:center\">&nbsp;</p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;color:black;line-height:200%\">名誉校长刘宝珺院士，校领导龚灏、黄涛、刘树根、郭朝辉、王敏，校长助理徐仕海、施泽进，校学位评定委员会委员、博士生导师代表、职能部门代表及教师代表出席了仪式。典礼由郭朝辉副校长主持。</span> </p><p style=\"text-align:left;text-indent:32px\">&nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:宋体;color:black;line-height:200%\"><img title=\"郭朝晖主持毕业典礼\" alt=\"郭朝晖主持毕业典礼\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180626/20180626085448_61.jpg\" width=\"500\" height=\"360\"/></span> </p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:KaiTi_GB2312;color:black;line-height:200%\">郭朝辉副校长主持</span> </p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;color:#0d0d0d;line-height:200%\">我校今年共有本、专科毕业生6406名，46人获博士学位，685人获硕士学位，425人获全日制硕士学位，129人获在职硕士学位。今年有110篇毕业论文获得2018年成都理工大学“百篇优秀学士学位论文（设计）”，85名同学获得“四川省优秀大学毕业生” 荣誉称号，1127名同学获得“成都理工大学优秀毕业生”荣誉称号。</span> &nbsp;</p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;color:#0d0d0d;line-height:200%\"></span>&nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:宋体;color:#0d0d0d;line-height:200%\"><img title=\"黄涛领导讲话\" alt=\"黄涛领导讲话\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180625/20180625151209_688.jpg\" width=\"500\" height=\"334\"/></span> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:楷体;color:black;line-height:200%\">黄涛副书记宣读《2018届本、专科生毕业的决定》</span> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:楷体;color:black;line-height:200%\"><img title=\"校领导宣读表彰优秀毕业生的的决定\" alt=\"校领导宣读表彰优秀毕业生的的决定\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180625/20180625151315_987.jpg\" width=\"500\" height=\"370\"/></span> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:楷体;color:black;line-height:200%\">校长助理徐仕海宣读《成都理工大学关于表彰2018届优秀毕业生的决定》</span> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:楷体;color:black;line-height:200%\"><img title=\"校领导宣读关于表彰优秀博士、硕士学位论文的决定\" alt=\"校领导宣读关于表彰优秀博士、硕士学位论文的决定\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180625/20180625151647_348.jpg\" width=\"500\" height=\"342\"/></span> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:楷体;color:black;line-height:200%\">校长助理施泽进宣读《关于表彰2018届优秀博士、硕士学位论文的决定》、</span> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:楷体;color:black;line-height:200%\">《关于表彰2018年成都理工大学</span><span style=\"font-size:16px;color:black;line-height:200%\">“</span><span style=\"font-size:16px;font-family:楷体;color:black;line-height:200%\">百篇优秀学士学位论文（设计）</span><span style=\"font-size:16px;color:black;line-height:200%\">”</span><span style=\"font-size:16px;font-family:楷体;color:black;line-height:200%\">的决定》</span> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:楷体;color:black;line-height:200%\"></span>&nbsp;</p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;color:black;line-height:200%\">校长龚灏为本、专科毕业生代表颁发毕业证书；优秀毕业生代表走上台，庄重领奖；优秀博士、硕士学位论文获奖者依次上台领奖；百篇优秀学士学位论文（设计）代表上台领奖。</span> &nbsp;</p><p style=\"text-align:left;text-indent:32px\">&nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:宋体;color:black;line-height:200%\"><img title=\"校长向优秀毕业生代表颁发证书 (1)\" alt=\"校长向优秀毕业生代表颁发证书 (1)\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180625/20180625151849_913.jpg\" width=\"500\" height=\"332\"/></span> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:楷体;color:black;line-height:200%\">优秀毕业生代表上台领奖</span> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:楷体;color:black;line-height:200%\"><img title=\"img_9003\" alt=\"img_9003\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180625/20180625152300_326.jpg\" width=\"500\" height=\"333\"/></span> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:楷体;color:black;line-height:200%\">优秀博士、硕士学位论文获得者上台领奖</span> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:楷体;color:black;line-height:200%\"><img title=\"img_9009\" alt=\"img_9009\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180625/20180625152329_407.jpg\" width=\"500\" height=\"333\"/></span> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:楷体;color:black;line-height:200%\">百篇优秀学士学位论文（设计）代表上台领奖</span> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:楷体;color:black;line-height:200%\"></span>&nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">外国语学院2014级英语专业陈柯孝</span><span style=\"font-size:16px;font-family:宋体;line-height:200%\">作为毕业生代表，细数了自己大学四年经历的理工</span><span style=\"font-size:16px;font-family:宋体;line-height:200%\">重要历史节点，表达了对母校跨越式发展的自豪，对成理人坚守校训“穷究于理，成就于工”初心的感动，并号召全体毕业生传承成理前辈</span><span style=\"font-size:16px;font-family:宋体;line-height:200%\">的家国情怀</span><span style=\"font-size:16px;font-family:宋体;line-height:200%\">，做一名有着一流胸襟，一流气魄，一流情怀的“双一流”成理人。他以一首诗中的名句“请乘理想之马，挥鞭从此起程，路上春色正好，天上太阳正晴”与毕业生们共勉，祝愿大家在新的人生节点扬帆再起航！</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\"></span>&nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\"><img title=\"毕业生代表发言\" alt=\"毕业生代表发言\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180625/20180625152419_822.jpg\" width=\"500\" height=\"346\"/></span> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:楷体;color:#0d0d0d;line-height:200%\">毕业生代表</span><span style=\"font-size:16px;font-family:楷体;color:#0d0d0d;line-height:200%\">陈柯孝</span><span style=\"font-size:16px;font-family:楷体;color:#0d0d0d;line-height:200%\">发言</span> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:楷体;color:#0d0d0d;line-height:200%\"></span>&nbsp;</p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;color:#0d0d0d;line-height:200%\">地球科学学院杨武年教授作为教师代表为毕业生送上祝福。作为在成理学习、工作了43个春秋的教师，他给毕业生们提出了四点希望：一是要“低调做人，高调做事”；二是在信息化时代，要做到学无止境；三是要保持永不言败的斗志；四是要不忘初心，砥砺前行。他希望大家学习理工人传承的责任担当和家国情怀，学习无数理工先贤前辈坚守原则，坚守正义，不甘人后，敢为人先之精神，坚守母校所赋予的优秀品质，以良好的精神状态和突出的工作业绩为母校增光添彩。</span> &nbsp;</p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;color:#0d0d0d;line-height:200%\"></span>&nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:宋体;color:#0d0d0d;line-height:200%\"><img title=\"教师代表讲话\" alt=\"教师代表讲话\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180625/20180625152458_609.jpg\" width=\"500\" height=\"346\"/></span> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:楷体;color:black;line-height:200%\">教师代表杨武年教授发言</span> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:楷体;color:black;line-height:200%\"></span>&nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;color:#303030;line-height:200%\">校长龚灏在讲话中，期望</span><span style=\"font-size:16px;font-family:宋体;background:white;color:#222222;line-height:200%\">全体毕业生</span><span style=\"font-size:16px;font-family:宋体;line-height:200%\">不忘成理初心，不负家国梦想</span><span style=\"font-size:16px;font-family:宋体;background:white;color:#222222;line-height:200%\">。他讲到，2018届作为“双一流”建设高校的第一批毕业生，经历和见证了学校进入国家一流学科建设高校的激动时刻与催人奋进的过程。他</span><span style=\"font-size:16px;font-family:宋体;color:black;line-height:200%\">发出了“学会感恩，知道感谢”的倡议，要求毕业生们感恩感谢党、祖国和伟大的时代，父母和家人，母校和老师，同窗和室友，并且感谢自己、欣赏自己。他希望同学们要不忘穷究于理的求知之心、不忘敢为人先的创新之心、不忘勇争一流的进取之心；坚持报国为民的责任担当、坚持成就于工的实干精神、坚持久久为功的奋斗韧劲。他深情地告诉毕业生：“成都理工大学永远是你们最坚强的精神支柱、最温暖的精神家园！”</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;background:white;color:#666666;line-height:200%\"></span>&nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:宋体;background:white;color:#666666;line-height:200%\"><img title=\"校长讲话\" alt=\"校长讲话\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180625/20180625152537_772.jpg\" width=\"500\" height=\"349\"/></span> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:楷体;color:black;line-height:200%\">龚灏校长讲话</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;color:black;line-height:200%\">怀着对母校的不舍和感恩，能源学院石油工程专业的毕业生代表们向学校赠送了礼物。由毕业生代表制作的牌匾上书写着校训“穷究于理，成就于工”，代表着毕业生们将在今后的生活中践行八字校训所凝聚的信仰；牌匾上四年的照片排成两颗心，代表毕业生无论身在何方，他们的心永远与母校在一起。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;color:black;line-height:200%\"></span>&nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:宋体;color:black;line-height:200%\"><img title=\"img_1047\" alt=\"img_1047\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180625/20180625152806_908.jpg\" width=\"500\" height=\"333\"/></span> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:楷体;color:black;line-height:200%\">毕业生赠礼</span> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:楷体;line-height:200%\"><img title=\"校领导宣读关于授予2018届博士、硕士学位的决定\" alt=\"校领导宣读关于授予2018届博士、硕士学位的决定\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180625/20180625152854_806.jpg\" width=\"500\" height=\"354\"/></span> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:楷体;line-height:200%\">刘树根副校长宣读《成都理工大学关于授予2018届博士、硕士学位的决定》、</span> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:楷体;line-height:200%\">《关于授予2018届本科毕业生学士学位的决定》</span> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:楷体;line-height:200%\"><img title=\"img_1209\" alt=\"img_1209\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180625/20180625153022_264.jpg\" width=\"500\" height=\"333\"/></span> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:楷体;line-height:200%\">刘宝珺院士和龚灏校长为博士学位获得者授位</span> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:楷体;line-height:200%\"><img title=\"img_1306\" alt=\"img_1306\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180625/20180625153153_605.jpg\" width=\"500\" height=\"333\"/></span> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:楷体;line-height:200%\">学校领导、校院学位评定委员会委员、教师代表为硕士学位获得者授位</span> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:楷体;line-height:200%\"><img title=\"img_1475\" alt=\"img_1475\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180625/20180625153257_530.jpg\" width=\"500\" height=\"333\"/></span> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:楷体;line-height:200%\">学校领导、校院学位评定委员会委员、教师代表为学士学位获得者代表授位</span> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:楷体;line-height:200%\"></span>&nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;color:#0d0d0d;line-height:200%\">在学位授予仪式上，46名博士、685名硕士依次上台，师长为他们一一拨穗。成理是一方教育的沃土，同样在这里播下种子，辛勤耕耘的本科生也在今年六月收获果实。带着殷殷期望，刘宝珺院士、龚灏校长为博士学位获得者授位，学校领导、校院学位评定委员会委员、教师代表为硕士学位获得者授位，学校领导、校院学位评定委员会委员、教师代表为学士学位</span><span style=\"font-size:16px;font-family:宋体;color:black;line-height:200%\">获得者代表授位。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">典礼结束后，2018届毕业生们争相与老师、同学合影，为即将结束的大学生活留下难忘的瞬间。2014级法学院法学专业的翟蓉回忆了四年来在成理的点滴，“感谢母校使我成长，感谢在成理度过了人生中最青春灿烂的日子，祝愿未来越来越好！”地球科学学院地质学专业硕士研究生刘子燕表达了对学校的感激：“硕士这三年，老师带我走进了科研的殿堂，教会我把地质学的理论与实际相结合，让我更深刻的认识到地质学的神奇与奥妙。理工地质人的严谨态度与科研精神，一代一代在传承。”</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\"></span>&nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\"><img title=\"花絮 (8)\" alt=\"花絮 (8)\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180625/20180625153433_188.jpg\" width=\"500\" height=\"334\"/></span> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:KaiTi_GB2312;line-height:200%\">我们毕业啦</span> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\"><img title=\"花絮 (6)\" alt=\"花絮 (6)\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180625/20180625153528_325.jpg\" width=\"500\" height=\"334\"/></span> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:KaiTi_GB2312;line-height:200%\">全家庆祝妈妈毕业</span> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\"><img title=\"花絮 (4)\" alt=\"花絮 (4)\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180625/20180625153642_528.jpg\" width=\"500\" height=\"334\"/></span> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:KaiTi_GB2312;line-height:200%\">以后我也是博士</span> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\"><img title=\"花絮 (17)\" alt=\"花絮 (17)\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180625/20180625153748_778.jpg\" width=\"500\" height=\"333\"/></span> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:KaiTi_GB2312;line-height:200%\">我和校长来张自拍</span> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\"><img title=\"花絮 (13)\" alt=\"花絮 (13)\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180625/20180625153916_130.jpg\" width=\"500\" height=\"333\"/></span> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:KaiTi_GB2312;line-height:200%\">同窗情谊</span> &nbsp;</p><p style=\"text-align:center\"><img title=\"与家人合影留念\" alt=\"与家人合影留念\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180625/20180625154221_733.jpg\" width=\"500\" height=\"351\"/> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:KaiTi_GB2312\">妈妈陪我毕业</span> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\"><img title=\"花絮 (9)\" alt=\"花絮 (9)\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180625/20180625154350_199.jpg\" width=\"500\" height=\"334\"/></span> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:KaiTi_GB2312;line-height:200%\">好兄弟一起毕业</span>&nbsp;</p><p style=\"text-align:center\">&nbsp;</p><p><span style=\"font-size:16px;font-family:宋体;line-height:200%\">时光荏苒，岁月如梭，毕业生们即将离开成都理工大学，这个承载了他们奋斗的青春，记录着最美时光的殿堂。二仙桥东三路一号的日子，已经成为了再也不能重来的故事。今朝一别，他日归来已是校友。愿成理学子离开校园，都如鱼入海，如鸟飞天，在更宽广的舞台上尽情展示，在人生的下一个阶段书写各自的新篇章！</span></p><p><br/></p>', '2018-06-26 09:13', '成都理工大学网上党支部', '我校隆重举行2018届毕业典礼暨授位仪式', null, '8', '241');
INSERT INTO `news` VALUES ('<p style=\"text-align:center\"><strong><span style=\"font-size:29px;font-family:黑体\"><strong><span style=\"font-size:21px;font-family:宋体\">计划2021年竣工 “川味”十足超“酷炫”</span></strong></span></strong> &nbsp;</p><p style=\"text-align:center\"><strong><span style=\"font-size:29px;font-family:黑体\">成都自然博物馆设计方案正式亮相</span></strong> </p><p style=\"text-align:center\">&nbsp;</p><p style=\"text-align:center\"><strong><span style=\"font-size:21px;font-family:宋体\"></span></strong>&nbsp;</p><p><br/></p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体\"><span style=\"font-size:16px;font-family:宋体\">2017</span><span style=\"font-size:16px;font-family:宋体\">年，成都自然博物馆项目向全球征集概念性设计方案，计划打造一个国内一流、国际领先的科普“文化综合体”。近日，经过国内外6家顶尖设计院激烈竞争，世界知名建筑设计公司佩里·克拉克·佩里建筑师事务所（PCPA）与中国建筑西南设计研究院有限公司（CSWADI）合作，共同赢得了成都自然博物馆的国际设计竞赛。</span> &nbsp;</span></p><p style=\"text-indent:32px\">&nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体\"></span>&nbsp;</p><p style=\"text-align:center\"><span style=\"font-family:宋体\"><img title=\"1\" alt=\"1\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180606/20180606104747_664.png\" width=\"500\" height=\"302\"/></span> &nbsp;</p><p style=\"text-align:center\">&nbsp;</p><p style=\"text-align:center\"><span style=\"font-family:宋体\"><img title=\"2\" alt=\"2\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180606/20180606104823_153.jpg\" width=\"500\" height=\"353\"/></span> &nbsp;</p><p style=\"text-align:center\">&nbsp;</p><p style=\"text-align:center\"><span style=\"font-family:宋体\"><img title=\"3\" alt=\"3\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180606/20180606104900_20.jpg\" width=\"500\" height=\"345\"/></span> &nbsp;</p><p style=\"text-align:center\"><strong><span style=\"font-size:19px;font-family:仿宋\">最终选定方案效果图</span></strong> &nbsp;</p><p style=\"text-align:center\"><strong><span style=\"font-size:19px;font-family:仿宋\"></span></strong>&nbsp;</p><p style=\"text-align:center\">&nbsp;</p><p style=\"text-align:center\"><strong><span style=\"font-size:19px;font-family:仿宋\">8</span></strong><strong><span style=\"font-size:19px;font-family:仿宋\">分钟视频展示</span></strong> &nbsp;</p><p style=\"text-align:center\"><strong><span style=\"font-size:19px;font-family:仿宋\"></span></strong>&nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体\"><span style=\"font-size:16px;font-family:宋体\">成都自然博物馆选址于成都市成华区成华大道十里店路以东，二仙桥东三路以北，成都理工大学西侧。整个地块呈三角形，左临成华大道，右临东风渠滨河景观带。将在成理博物馆现有建筑基础上建设。建设中，将拆除原有建筑的穹顶及外墙，保留框架楼板，拆掉建筑东南角部的部分主体，向河流方向打开，加入新的拱形支撑，围合出圆形室外剧场。大跨度的弧形拱断面构成室外剧场的投影界面，激发演出活力。</span> &nbsp;</span></p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体\"></span>&nbsp;</p><p style=\"text-align:center\"><span style=\"font-family:宋体\"><img title=\"位置\" alt=\"位置\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180606/20180606105210_565.jpg\" width=\"500\" height=\"363\"/></span> &nbsp;</p><p style=\"text-align:center\"><strong><span style=\"font-size:19px;font-family:仿宋\">成都自然博物馆选址于此</span></strong> &nbsp;</p><p style=\"text-align:center\"><strong><span style=\"font-size:19px;font-family:仿宋\"></span></strong>&nbsp;</p><p style=\"text-align:center\"><span style=\"font-family:宋体\"><img title=\"4\" alt=\"4\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180606/20180606105253_101.jpg\" width=\"500\" height=\"173\"/></span> &nbsp;</p><p style=\"text-align:center\"><strong><span style=\"font-size:19px;font-family:仿宋\">新老博物馆对比图</span></strong> &nbsp;</p><p style=\"text-align:center\">&nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体\"><span style=\"font-size:16px;font-family:宋体\">成都自然博物馆总投资约12亿，净用地约51.06亩，园区面积92亩。据悉，未来它将是一个创新性的展览和教育空间，常设临时展厅、互动展厅、礼品店、咖啡厅、电影院和室外空间。在这群设计灵感爆棚又充分尊重场地策略的设计师们的匠心独运之下，成都自然博物馆一步步从概念变为现实。</span></span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体\"></span>&nbsp;</p><p style=\"text-indent:43px\"><strong><span style=\"font-size:21px;font-family:宋体\">“川味”博物馆——蜀山、蜀道、蜀水</span></strong> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体\"><span style=\"font-size:16px;font-family:宋体\">博物馆外型设计以四川地区的山水特色为灵感，将挺拔巍峨的雪山融入博物馆的外部造型，用山峰岩石的形态与蜿蜒参差的裂缝肌理表达出板块的运动与地质的变迁；行走栈道的体验被带入博物馆的内部空间，用天桥和长廊象征蜿蜒盘旋的蜀道意境；将理水引灌的古代智慧引入博物馆的室外园区，用弯曲的水流和倒影水池体现成都千年的蜀水文化。设计师运用抽象地理的方式，再现自然的伟大与人工的神奇。</span> &nbsp;</span></p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体\">博物馆的植物群象征着四川农业传统的丰富色彩，将现代农业产业的光环带入城市。倒影池和水路象征着岷江和东风渠。景观设计反映了复杂的四川地形和植物世界的多样性。博物馆的建筑材料也采用了四川本地典型的石材，外部纹理质感与石材的天然沉积特征相一致。石材表面有机地增加透光孔，在白天就好似山顶的皑皑白雪，在夜晚就好似点点繁星，象征天体和地质存在的演变。一位专家评委称赞道：“本设计方案的最大特点是在保持了博物馆垂直的视觉印象的同时，打造了空间横向的人行尺度体验。该方案将建筑、景观和周围环境很好地结合起来。”</span> &nbsp;</p><p style=\"text-indent:32px\">&nbsp;</p><p style=\"text-indent:32px\">&nbsp;</p><p style=\"text-align:center\"><span style=\"font-family:宋体\"><img title=\"5\" alt=\"5\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180606/20180606105643_163.jpg\" width=\"500\" height=\"325\"/></span> &nbsp;</p><p style=\"text-align:center\">&nbsp;</p><p style=\"text-align:center\"><span style=\"font-family:宋体\"><img title=\"6\" alt=\"6\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180606/20180606105711_570.jpg\" width=\"500\" height=\"266\"/></span>&nbsp;</p><p style=\"text-align:center\"><strong><span style=\"font-size:19px;font-family:仿宋\">博物馆外部造型设计灵感来源</span></strong> &nbsp;</p><p style=\"text-align:center\">&nbsp;</p><p style=\"text-indent:43px\"><strong><span style=\"font-size:21px;font-family:宋体\">“国际范儿”博物馆——科技、互动、体验</span></strong> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体\">博物馆内将采用AR技术、全息技术、4D影院、球幕或VR影院等技术手段让“展品说话”、让“化石复活”。展教并重、互动探究、多元体验的自然博物馆，将成为成都市又一个文化地标。这里既可演绎地球的诞生与地质的变迁，又可展现生命的诞生和物种的演化，还可进行时空漂移、文明史诗等科普体验。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体\">除此之外，馆内将通过增强现实技术，进行破解展品年代（如恐龙蛋）、给墙上的化石赋予生命等科技展示。馆内还将加入先进科技互动，如实时反映东风渠大数据成像，参观者站在屏幕前就能了解到东风渠的水质状况、河水成分等。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体\">根据展厅流线设计规划，博物馆共有四楼：一层为中央大厅及临时展厅；二层为地球的诞生与地质变迁；三层为生命的诞生和物种的演化；四层为展望未来和科普体验。临时展厅位于一层，便于单独开放。24小时开放的商业与文创区结合地铁接口，设置于负一层；科普教育区集中于一层与负一层，增强其公共性与易达性。负一层藏品修复区也可向教育区开放，观众可现场观摩化石与标本的制作。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体\">博物馆主要展厅布局于中庭南北的六个人工山体，展陈体块的水平分布暗合了生命进化各阶段的横向分类逻辑，竖向动线则对应46亿年生物进化的纵向时间线。建筑东侧设置面向河流的空中玻璃长廊，观众的视线既可以与展厅互动，又可以180度全方位观赏河流与城市的美景。长廊向上倾斜，也示喻了一场在恐龙时代的穿越之旅。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体\">此外，环保贯穿设计理念。馆内的每一处，都可能有可持续发展的影子。既利用太阳能做自然采光设计，也利用其进行光电转换；通过中庭热压拔风的效应促进自然通风；通过雨水的收集、处理及再利用以达到节水的目的；最后还利用东侧河流的优势进行水源热泵的设计，并利用ERV 进行空气预热或预冷，以达到节能的目的。</span> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-family:宋体\"></span>&nbsp;</p><p style=\"text-align:center\"><strong><img title=\"7\" alt=\"7\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180606/20180606105740_962.jpg\" width=\"500\" height=\"354\"/></strong> &nbsp;</p><p style=\"text-align:center\"><strong><span style=\"font-size:19px;font-family:仿宋\">展厅流线图</span></strong> &nbsp;</p><p style=\"text-align:center\">&nbsp;</p><p style=\"text-indent:43px\"><strong><span style=\"font-size:21px;font-family:宋体\">配套设施24小时接力</span></strong> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体\"><span style=\"font-size:16px;font-family:宋体\">博物馆靠近地铁接口的地下一层将布置商业服务与文创业态。其中，活力商街将与下沉广场串联，形成属于城市的24小时活力空间。夜晚，博物馆关闭后，大家还能在下沉广场，活力商业以及室外展览、室外剧场进行娱乐活动。将来，地铁8号线会在博物馆西侧设置站点并预留有地下接口，可与博物馆实现无缝对接。</span> &nbsp;</span></p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体\"></span>&nbsp;</p><p style=\"text-indent:28px\"><br/></p><p style=\"text-indent:32px\"><strong><span style=\"font-size:16px;font-family:宋体\">新闻背景：</span></strong><span style=\"font-size:16px;font-family:宋体\">1960</span><span style=\"font-size:16px;font-family:宋体\">年的成都地质学院陈列室是成都理工大学博物馆的起点，经过近六十年的陈列充实，它从“为学校的教学实习和收藏保管标本”，到“学校重要的专业认识实习基地”“致力于为教学科研和科普教育服务”，至今拥有国家级乃至世界级的精品和珍品，如“合川马门溪龙”“大竹重庆鱼”“隆昌铁陨石”等，是在中国西部乃至全国享有较高知名度、以地学类藏品为主的自然博物馆。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体\">历史钩沉，这座文化殿堂的外表斑驳，却因凝结了数代人心血，让人徜徉其中依然深深感受到宇宙的博大、自然的奥秘、生命的奇迹。</span> &nbsp;</p><p><span style=\"font-size:16px;font-family:宋体\">2016</span><span style=\"font-size:16px;font-family:宋体\">年，成都市人民政府与我校签订《深入推进全面创新改革，共建世界一流大学战略合作框架协议》。协议中明确“成都市人民政府与成都理工大学在校区内共同策划、筹备、建设一所成都自然博物馆，将其打造成为城市重要的文化地标”。</span></p><p><br/></p>', '2018-06-26 09:15', '成都理工大学网上党支部', '成都自然博物馆设计方案正式亮相', null, '8', '242');
INSERT INTO `news` VALUES ('<p><span style=\"font-size:16px\"><p style=\"text-align:center\"><strong><strong><span style=\"font-size:32px;font-family:SimHei\">招生举措新突破，助力学校一流人才培养</span></strong></strong> &nbsp;</p><p style=\"text-align:center\"><strong><strong><span style=\"font-size:24px\">2018年成都理工大学招生政策解读</span></strong></strong> &nbsp;</p><p style=\"text-align:center\"><br/></p><p style=\"text-align:center\">&nbsp;</p><p style=\"text-indent:32px\">随着全国高考招生制度改革和国家“双一流”建设的推进，我校积极深化改革，完善创新创业教育培养体系，提高本科教育质量和水平，打造世界一流学科建设高校。招生作为人才培养的重要环节，选拔一批高质量的适合我校的生源对提高人才培养至关重要，对建设高水平综合性大学具有重要意义。</p><p style=\"text-indent:32px\">近年来，学校办学实力不断提升，受到社会各界的广泛认可，学校本科招生一直保持稳中向好的形势。2018年我校面向31个省（市、自治区）招收本、专科生7700人，其中本科招生计划7500人（含本科第一批6535人、本科第二批320人、艺体类本科645人），专科计划200人。&nbsp;包括国家专项、地方专项、普通类本专科、艺术体育类本科、少数民族预科等10种招生类别。</p><p style=\"text-align:center\"><strong><span style=\"font-size:16px;font-family:宋体\">80</span></strong><strong><span style=\"font-size:16px;font-family:宋体\">个本科专业在一本批次招生</span></strong> </p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">我校经过60余年的发展，已成为一所以理工为主，以地质、能源、资源科学、核技术、环境科学为优势，以化工、材料、电子、机械、信息科学、管理科学等学科专业为特色的多科性大学。今年本科招生专业共91个，普通类80个，包括12个招生大类的分流专业，全部在一本批次招生；中英合作办学本科专业2个，在二本批次招生；艺体类本科招生专业9个。</span> </p><p style=\"text-align:center;text-indent:32px\"><strong>新增</strong><strong>4</strong><strong>个本科专业，</strong><strong> </strong><strong>聚焦国家发展战略需求</strong> &nbsp;</p><p style=\"text-indent:32px\">我校今年新增4个本科专业，海洋油气工程、网络空间安全、智能科学与技术、环境生态工程。海洋油气工程专业是为满足中国向深海进军和向海外拓展的人才需要，是国家战略性新兴专业。网络空间安全专业培养“互联网+”时代下网络空间安全领域的工程技术人才。智能科学与技术专业是顺应人工智能技术的快速发展而产生的新兴专业，培养从事大数据科学分析、智能机器人研发的复合型高级专门人才，是计算机大类的分流专业。环境生态工程专业发挥我校传统地学优势，针对“山水林田湖草”修复的重大需求，在矿山生态环境保护与治理、环境灾害监测与恢复方面形成特色，培养环境生态学科的复合型人才，该专业是环境科学与工程类的分流专业。</p><p style=\"text-align:center;text-indent:32px\"><strong>单独招生批次，服务特定考生群体</strong> &nbsp;</p><p style=\"text-indent:32px\">我校今年还有国家专项、地方专项、新疆兵团计划、四川少数民族预科、内地新疆班等特殊类型招生计划面向特定的高考考生群体。国家专项计划主要面向四川省边远、贫困、民族地区的考生，53个专业（类），350个计划；地方专项计划主要面向四川省119个民族地区、集中连片特殊困难地区和革命老区、艰苦边远地区县、市、区的考生，52个专业（类），110个计划。面向新疆考生实施新疆建设兵团定向就业招生计划20名；面向四川少数民族地区招收少数民族预科77名。</p><p style=\"text-align:center;text-indent:32px\"><strong>中英合作办学单设招生代码，引导学生分类填报</strong> &nbsp;</p><p style=\"text-indent:32px\">我校与英国牛津布鲁克斯大学合作设立会计学专业（本科），与英国斯泰福厦大学合作设立工商管理（本科）、商务管理（专科）专业。为了引导学生分类填报，我校在四川、重庆单设中英合作项目招生代码，在四川，会计学招生代码是5802，工商管理及专科招生代码是5502；在重庆，会计学招生代码是9604，工商管理及专科是9606。中英合作办学2个本科专业在二本批次招生，会计学（中英合作办学）学费60000元/生·年，工商管理（中英合作办学）学费45000元/生·年。</p><p style=\"text-align:center;text-indent:32px\"><strong>注重人才培养质量，构建完善的人才培养体系</strong> &nbsp;</p><p style=\"text-indent:32px\">我校坚持“通专结合、三提一塑”的人才培养理念，将通识教育与专业教育有机结合，通过创新人才培养模式，优化课程体系，强化实践教学，着力提高学生整合知识的能力、发现问题和解决问题的能力、国际交流与竞争的能力，塑造学生优秀品格和人格魅力。我校今年共有地质学类、核工程类、电子信息类等12个招生大类，包含35个本科专业。按大类招生的学生进校后先学习公共基础课和学科基础课，进入二年级后按照相关规定进行专业分流。学校积极探索拔尖创新人才培养机制，在土木工程、地质工程、勘查技术与工程、资源勘查工程、石油工程、机械工程等专业组建创新实验班。地质工程、土木工程、资源勘查工程、化学工程与工艺四个专业已通过了工程教育专业认证，标志着这些专业的质量实现了国际实质等效，进入全球工程教育的“第一方阵”，可为我校学生走向世界提供了具有国际公认质量标准的“通行证”。我校的“核工程”大类入选教育部首批“新工科”建设项目，成为工程学科专业的升级版。</p><p style=\"text-align:center;text-indent:32px\"><strong>以人为本，招生服务多样快捷</strong> &nbsp;</p><p style=\"text-indent:32px\">我校今年将继续秉承以人为本的招生录取政策，一是在平行志愿投档的情况下，严格控制调档比例，进档考生，在思想政治品德考核合格、身体健康状况检查合格、符合相关专业培养要求且服从专业调剂的情况下，尽最大可能减少退档；二是录取专业时，实行“分数优先”的原则，专业志愿之间不设分数级差，最大程度满足考生专业志愿；三是对有政策性加分的考生，实行“一加到底”的政策，在提档和录取专业时均以加分后的分数为依据。</p><p style=\"text-indent:32px\">为解决考生和家长难以拨通招生咨询热线的难题，我校建立招生咨询呼叫中心平台，咨询电话028-84078927（20名工作人员同时接听），并对外公布各专业咨询电话，便于考生咨询专业信息。同时，我校在“两网两微”即招生信息网、手机网、微信、微博的基础上开通招生办公室知乎、QQ公众号和喜马拉雅成小理FM新媒体平台，配套推出“教授与你聊专业”视频和“我与专业的故事”招生宣传音频等栏目，便于考生和家长全方位了解招生资讯，方便快捷进行招生信息查询。</p></span> &nbsp;</p><p><br/> </p><p><span style=\"font-size:16px\">志愿填报注意事项：</span> &nbsp;</p><p><span style=\"font-size:16px\">1、学校对考生身体健康状况的要求，执行教育部、卫生部、中国残疾人联合会印发的《普通高等学校招生体检工作指导意见》的有关规定，在此基础上，我校根据各专业实际情况做出补充规定，部分专业对色觉异常情况有限制条件，建议考生填报志愿时，阅读我校2018年招生章程二十二条。</span> &nbsp;</p><p><span style=\"font-size:16px\">2、建筑类专业学制5年，色盲、色弱不录，该大类新生进校后须参加学校组织的徒手画加试，加试成绩不合格者，根据其高考成绩、专业志愿调整到其它专业就读。</span> &nbsp;</p><p><span style=\"font-size:16px\">3、艺体类录取规则，报考我校艺术、体育类专业的考生，我校使用生源所在省市统一组织的艺术、体育类专业考试成绩，对进档考生采用“专业志愿优先” &nbsp; 的方式进行录取。广播电视编导、戏剧影视文学专业录取原则是在文化考试和专业考试成绩均上线的基础上，按照文化成绩从高分到低分择优录取。播音与主持艺术、表演、视觉传达设计、环境设计、产品设计、社会体育指导与管理、休闲体育专业录取原则是在文化考试和专业考试成绩均上线的基础上，按照专业成绩从高分到低分择优录取。</span> &nbsp;</p><p><span style=\"font-size:16px\">4、报考播音与主持艺术、表演专业要求男生身高不低于1.70米，女生身高不低于1.60米。体育类专业考生要求身高男生不低于1.68米，女生不低于1.58米，身体无残疾，年龄不超过22周岁（1996年8月31日以后出生）。</span> &nbsp;</p><p><span style=\"font-size:16px\"><br/> </span> </p><p style=\"text-align:center\"><img title=\"成都理工大学招生办公室qq公众号二维码\" alt=\"成都理工大学招生办公室qq公众号二维码\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180622/20180622084746_109.png\" width=\"200\" height=\"200\"/> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px\">成都理工大学招生办公室QQ公众号二维码</span> &nbsp;</p><p style=\"text-align:center\">&nbsp;<img title=\"成都理工大学招生办公室微博二维码\" alt=\"成都理工大学招生办公室微博二维码\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180622/20180622085123_691.jpg\" width=\"200\" height=\"201\"/> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px\">&nbsp;成都理工大学招生办公室微博二维码</span> &nbsp;</p><p style=\"text-align:center\">&nbsp;</p><p style=\"text-align:center\">&nbsp; &nbsp;&nbsp;<img title=\"成都理工大学招生办公室微信公众号二维码\" alt=\"成都理工大学招生办公室微信公众号二维码\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180622/20180622085252_373.jpg\" width=\"200\" height=\"200\"/> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px\">&nbsp;成都理工大学招生办公室微信公众号二维码</span> &nbsp;</p><p style=\"text-align:center\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p><p style=\"text-align:center\">&nbsp;&nbsp;<img title=\"成都理工大学招生手机网二维码\" alt=\"成都理工大学招生手机网二维码\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180622/20180622085406_417.jpg\" width=\"200\" height=\"199\"/> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px\">&nbsp;成都理工大学招生手机网二维码</span> &nbsp;</p><p><br/></p>', '2018-06-26 09:18', '成都理工大学网上党支部', '2018年成都理工大学招生政策解读', null, '9', '243');
INSERT INTO `news` VALUES ('<p style=\"text-align: center;\"><strong><span style=\"font-size:21px;font-family:黑体;line-height:200%\"><strong><span style=\"font-size:24px;font-family:黑体;line-height:200%\">我校四个专业进入全球工程教育“第一方阵”</span></strong> &nbsp;</span></strong><strong> </strong></p><p style=\"text-align:center\"><strong> </strong>&nbsp;</p><p style=\"text-align:center\"><br/></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">近日，记者从教育部获悉，截至2017年底，教育部高等教育教学评估中心和中国工程教育专业认证协会共认证了全国198所高校的846个工科专业。我校地质工程、土木工程、资源勘查工程、化学工程与工艺四个专业均已通过专业认证，此认证标志着这些专业的质量实现了国际实质等效，进入全球工程教育的“第一方阵”。</span> &nbsp;</p><p style=\"text-align:left;text-indent:32px\"><br/></p><p style=\"text-align:left;text-indent:32px\">&nbsp;</p><p style=\"text-align:left;text-indent:32px\"><strong><span style=\"font-size:16px;font-family:宋体;line-height:200%\">地质工程专业：</span></strong> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">地质工程专业门类为工科，一级学科为地质资源与地质工程，是地质学与工程学相互渗透交叉的学科，主要研究人类工程活动与地质环境相互关系，以地质学及机械学原理为基础，认识、分析和解决地质工程问题，为工程建设、资源开发和地质环境保护服务。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">我校地质工程专业是在原成都地质学院“水文地质与工程地质”“探矿工程”两个专业的基础上，经过60余年的艰苦奋斗发展起来的，设有工程地质和钻掘工程两个方向。工程地质方向主要在山区复杂工程地质问题分析、工程地质勘察、地质灾害评价与防治、地质环境评价与保护等方面形成了鲜明的特色和优势；钻掘工程方向在复杂地质条件下岩土钻掘工艺、护壁堵漏、定向钻进、非开挖、高温高压油气井、地热井、隧洞掘进等领域新技术新方法的开发与研究形成了鲜明的特色和优势。</span> &nbsp;</p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\"></span>&nbsp;</p><p style=\"text-align:center\"><br/></p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\"><img title=\"qq截图20180622191708\" alt=\"qq截图20180622191708\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180622/20180622191729_664.png\" width=\"500\" height=\"333\"/></span> &nbsp;</p><p style=\"text-align:center\">&nbsp;</p><p style=\"text-align:center\"><br/></p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">该专业已形成“重视地质基础，强化工程素养培养”的办学理念和“基于地质过程机制分析-量化评价学术思想，解决复杂地质环境中地质工程问题”的办学特色，入选国家级卓越工程师教育培养计划；人才质量保障体系实现了国家级本科教学质量工程全覆盖：国家级精品课程、国家级特色专业、国家级教学名师、国家级实验教学示范中心、国家级教学团队、教育部专业综合改革试点专业；建有国家重点实验室、国家级工程实践教育中心、国家级虚拟仿真实验教学中心等科研和教学平台；建立峨眉山、马角坝两个固定野外实习基地和国土资源部野外科学观测研究基地，并与企业单位共建20个实践基地，大学四年每年实习不间断，实践学分超过总学分的1/3。该专业是国内第一批通过工程教育认证的地学类专业之一，分别于2014和2017年两次通过工程教育认证，是我校国家双一流学科“地球科学”的重要支撑专业。</span> &nbsp;</p><p style=\"text-align:center\"><br/></p><p style=\"text-align:center\">&nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\"><img title=\"野外实习-2014级\" alt=\"野外实习-2014级\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180622/20180622191814_15.jpg\" width=\"500\" height=\"375\"/></span> &nbsp;</p><p style=\"text-align:center\">&nbsp;</p><p style=\"text-align:center\"><br/></p><p style=\"text-align:left;text-indent:32px\">&nbsp;</p><p style=\"text-align:left;text-indent:32px\"><strong><span style=\"font-size:16px;font-family:宋体;line-height:200%\">土木工程专业：</span></strong> &nbsp;</p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">土木工程是建造各类建筑和交通枢纽等基础设施的科学技术的统称。我校于1993年创办“岩土工程”本科专业、“房屋建筑工程”和“公路与城市道路工程”专科专业（1996年升本），1998年按政策调整为土木工程专业，包含“岩土工程、建筑工程、道路工程”三个方向。经过长期教学实践和科研生产积累，已逐步形成“山区复杂环境条件下的土木工程”特色。</span> &nbsp;</p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">我校土木工程专业为国家级特色专业、高等学校土木工程教育认证专业、省级卓越工程培养计划专业，具有一级学科“博士、硕士和学士”三级学位授予权，“岩土工程”四川省和国土资源部重点学科、省级教学团队；拥有地质灾害防治与地质环境保护国家重点实验室、国家级虚拟仿真实验教学中心、土木工程技能实训及创新实践基地等培养平台。</span> &nbsp;</p><p style=\"text-align:center\"><br/></p><p style=\"text-align:center\">&nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\"><img title=\"qq截图20180622191933\" alt=\"qq截图20180622191933\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180622/20180622191937_227.png\" width=\"500\" height=\"333\"/></span> &nbsp;</p><p style=\"text-align:center\">&nbsp;</p><p style=\"text-align:center\"><br/></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">该专业旨在培养德智体美全面发展，掌握土木工程学科基本理论和专业知识，获得土木工程基本技能训练和一定的工程实践锻炼，具有较强的分析、评价和解决复杂环境条件下的土木工程问题能力，具有良好的创新意识、协作精神和国际视野，能够在建筑、交通、水利水电、地质灾害防治、环境保护等领域从事土木工程的勘测、设计、施工、管理、项目规划及研究开发等工作，能够适应国家需求和社会发展的高素质人才。</span> &nbsp;</p><p style=\"text-align:center\"><br/></p><p style=\"text-align:center\">&nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\"><img title=\"qq截图20180622192041\" alt=\"qq截图20180622192041\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180622/20180622192046_247.png\" width=\"500\" height=\"333\"/></span> &nbsp;</p><p style=\"text-align:center\">&nbsp;</p><p style=\"text-align:center\"><br/></p><p style=\"text-align:left;text-indent:32px\">&nbsp;</p><p style=\"text-align:left;text-indent:32px\"><strong><span style=\"font-size:16px;font-family:宋体;line-height:200%\">资源勘查工程专业：</span></strong> &nbsp;</p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">资源勘查工程是融地质理论、勘查技术、矿业经济与环境及矿业政策法规于一体的综合性、应用性很强的工科专业。我校资源勘查工程专业由1956年建校时开设的两个骨干专业石油天然气地质及勘查专业和地质找矿专业合并而成，是我校最早开设的专业之一，设石油地质和固体矿产两个方向，分属在能源学院和地球科学学院招生。石油地质方向强调石油地质基础理论、勘探开发的新方法和新技能等方面的培养，依托我校油气藏地质及开发工程国家重点实验室、国家级特色专业，注重国际视野、融合知识、实践能力，以适应新形势下油气资源勘探开发的需要；固体矿产方向主要培养“固体矿产勘查”、“矿山地质与环境评价”和“地热资源勘查”方面的专业人才。</span> &nbsp;</p><p style=\"text-align:center\"><br/></p><p style=\"text-align:center\">&nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\"><img title=\"qq截图20180622192153\" alt=\"qq截图20180622192153\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180622/20180622192145_544.png\" width=\"500\" height=\"332\"/></span> &nbsp;</p><p style=\"text-align:center\">&nbsp;</p><p style=\"text-align:center\"><br/></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">该专业依托“国家级地质学实验教学示范中心”、四川省“地矿勘查”实验教学示范中心、四川省“地质矿产资源三维立体勘探开发虚拟仿真”实验教学中心、四川省“石油与天然气地质”实验教学中心、四川省“页岩气勘探开发工程实验室”及“四川盆地及周缘优质页岩气资源潜力评价协同创新中心”等平台，及“峨眉山国家级地学校外实践教育基地”、国土资源部“重庆城口巴山锰钡矿”“马角坝地质填图实习基地”等野外基地，构建了“课程实验-野外地质实习-综合性课程设计-毕业实习-科技创新活动”系列完整的实践教学体系；依托“卓越工程师教育培养计划”构建了创新班，针对创新班设计独立的培养方案，并建立了导师制管理，创新班最近两年毕业生考研率达80%。2017年，该专业通过工程教育认证，是国家“双一流”学科“地球科学”和国家一级重点学科地质资源与地质工程的重要支撑专业，全国排名稳居前三名。</span> &nbsp;</p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">该专业着力培养适应国家能源、矿产战略需求，系统掌握资源勘查工程专业基本理论和方法，获得资源勘查工程基本技能和扎实工程训练及实践，具有较强分析、评价、解决资源勘查工程问题的能力和一定的国际视野，具有团队协作和创新思维，能在能源、地矿等领域的企事业单位、科研院所等从事油气、矿产资源勘查评价、开发生产、科学研究及经营管理等方面工作能力的专业技术人才。</span> &nbsp;</p><p style=\"text-align:center\"><br/></p><p style=\"text-align:center\">&nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\"><img title=\"qq截图20180622192317\" alt=\"qq截图20180622192317\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180622/20180622192320_886.png\" width=\"500\" height=\"332\"/></span> &nbsp;</p><p style=\"text-align:center\">&nbsp;</p><p style=\"text-align:center\"><br/></p><p style=\"text-align:left;text-indent:32px\">&nbsp;</p><p style=\"text-align:left;text-indent:32px\"><strong><span style=\"font-size:16px;font-family:宋体;line-height:200%\">化学工程与工艺专业：</span></strong> &nbsp;</p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">化学工程与工艺专业是研究化学工业生产过程中的共同规律，并用化学方法改变物质组成或性质来生产化学产品的一门工程学科。我校化学工程与工艺专业创办于1995年，为四川省特色专业，四川省卓越工程师培养计划和国家级卓越工程师培养计划专业，列入第一批次招生计划。该专业所在学科为四川省重点学科，具有“化学工程与技术”一级学科硕士学位授予权、“化学工程”专业学位工程领域招生权和“矿产资源化学”博士学位授予权。</span> &nbsp;</p><p style=\"text-align:center\"><br/></p><p style=\"text-align:center\">&nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\"><img title=\"img_0342\" alt=\"img_0342\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180622/20180622192400_228.jpg\" width=\"500\" height=\"375\"/></span> &nbsp;</p><p style=\"text-align:center\">&nbsp;</p><p style=\"text-align:center\"><br/></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">该专业教师近5年承担国家“863”计划、国家自然科学基金等各类科研项目88项；发表SCI论文260余篇，授权国家发明专利29项，获省部级科研成果奖2项。承建四川省攀西战略矿产资源综合利用协同创新中心、矿产资源化学四川省高校重点实验室、四川省攀西碲铋选冶及应用重点实验室、国家环境保护水土污染协同控制与联合修复重点实验室、硫酸盐型盐湖资源综合利用青海省重点实验室等学科平台。</span> &nbsp;</p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">该专业培养具备自然科学基础知识和化学工程与技术学科的基本理论、专门知识，通晓化工产品制备、生产装置设计、化工技术经济、过程安全与环境保护的理论和方法，具有良好的人文科学素养、社会责任感、创新意识和较强的实践动手能力，能够在化学工业及其相关过程工业领域从事产品研制与开发、工程设计、生产过程控制及经营管理等方面工作的工程技术人才。</span> &nbsp;</p><p style=\"text-align:center\"><br/></p><p style=\"text-align:center\">&nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\"><img title=\"dpp_0002\" alt=\"dpp_0002\" src=\"http://www.cdut.edu.cn/xww/UserFiles/Image/20180622/20180622192439_552.jpg\" width=\"500\" height=\"333\"/></span> &nbsp;</p><p style=\"text-align:center\">&nbsp;</p><p style=\"text-align:center\"><br/></p><p style=\"text-align:left;text-indent:32px\">&nbsp;</p><p style=\"text-align:left;text-indent:32px\"><strong><span style=\"font-size:16px;font-family:宋体;line-height:200%\">新闻链接：</span></strong> &nbsp;</p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">2016</span><span style=\"font-size:16px;font-family:宋体;line-height:200%\">年，我国正式加入国际工程教育《华盛顿协议》组织，标志着国内工程教育质量认证体系实现了国际实质等效，工程专业质量标准达到国际认可，成为我国高等教育的一项重大突破。作为《华盛顿协议》正式成员，中国工程教育认证的结果已得到其他18个成员国（地区）认可。</span> &nbsp;</p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">《华盛顿协议》是国际上最具影响力的工程教育学位互认协议之一，1989年由美国、英国、澳大利亚国等6个英语国家的工程教育认证机构发起，其宗旨是通过多边认可工程教育认证结果，实现工程学位互认，促进工程技术人员国际流动。</span> &nbsp;</p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:200%\">通过认证专业的毕业生在《华盛顿协议》相关国家和地区申请工程师执业资格或申请研究生学位时，将享有当地毕业生同等待遇，为中国工科学生走向世界提供了国际统一的“通行证”。同时，认证结果在行业及企业内有较高的权威性，在部分行业工程师资格考试或能力评价中享有不同程度的减免和优惠。</span></p><p><br/></p>', '2018-06-26 09:20', '成都理工大学网上党支部', '我校四个专业进入全球工程教育“第一方阵”', null, '4', '244');
INSERT INTO `news` VALUES ('<p style=\"text-align:center\"><span style=\"font-size:29px;font-family:黑体\"><span style=\"font-family:SimHei\"><strong>第五届非常规油气地质评价学术研讨会</strong></span></span> &nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:24px;font-family:黑体\"><span style=\"font-family:SimHei\"><strong>一号通知</strong></span></span> &nbsp;</p><p><br/></p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:宋体;line-height:150%\">非常规油气资源（包括页岩油气、致密砂岩油气、煤层气、天然气水合物等）的地质评价理论和勘探和开发技术已成为国际上油气领域的研究热点。近年来我国学者在致密油气、页岩油气、煤层气、天然气水合物等领域的理论研究取得了突出的进展，取得了一系列重大勘探开发突破。为充分展示中国在非常规油气资源领域的最新研究成果，促进非常规油气地质评价领域的新理论、新方法、新技术、新成果、新观点、新思路的交流，推动非常规油气勘探、开发，经</span><span style=\"font-size:16px\">“</span><span style=\"font-size:16px;font-family:宋体;line-height:150%\">非常规油气地质评价学术研讨会</span><span style=\"font-size:16px\">”</span><span style=\"font-size:16px;font-family:宋体;line-height:150%\">学术委员会和指导委员会联席会议讨论，决定于</span><span style=\"font-size:16px\">2018</span><span style=\"font-size:16px;font-family:宋体;line-height:150%\">年</span><span style=\"font-size:16px\">10</span><span style=\"font-size:16px;font-family:宋体;line-height:150%\">月由油气藏地质及开发工程国家重点实验等承办</span><span style=\"font-size:16px\">“</span><span style=\"font-size:16px;font-family:宋体;line-height:150%\">第五届非常规油气地质评价学术研讨会</span><span style=\"font-size:16px\">”</span><span style=\"font-size:16px;font-family:宋体;line-height:150%\">。热忱欢迎有关专家、同仁参加会议。</span> &nbsp;</p><p><strong><span style=\"font-size:16px;font-family:宋体;line-height:150%\">一、主办单位</span></strong> &nbsp;</p><p><span style=\"font-size:16px;font-family:宋体;line-height:150%\">中国石油学会石油地质专业委员会</span> &nbsp;</p><p><span style=\"font-size:16px;font-family:宋体;line-height:150%\">中国地质学会石油地质专业委员会</span> &nbsp;</p><p><span style=\"font-size:16px;font-family:宋体;line-height:150%\">中国石油学会非常规油气专业委员会</span> &nbsp;</p><p><span style=\"font-size:16px;font-family:宋体;line-height:150%\">中国地质学会非常规油气地质专业委员会</span> &nbsp;</p><p><span style=\"font-size:16px;font-family:宋体;line-height:150%\">油气藏地质及开发工程国家重点实验室</span> &nbsp;</p><p><span style=\"font-size:16px;font-family:宋体;line-height:150%\">中国石油学会天然气专业委员会</span> &nbsp;</p><p><strong><span style=\"font-size:16px;font-family:宋体;line-height:150%\">二、承办单位</span></strong> &nbsp;</p><p><span style=\"font-size:16px;font-family:宋体;line-height:150%\">成都理工大学</span> &nbsp;</p><p><span style=\"font-size:16px;font-family:宋体;line-height:150%\">西南石油大学</span> &nbsp;</p><p><span style=\"font-size:16px;font-family:宋体;line-height:150%\">四川省石油学会</span> &nbsp;</p><p><strong><span style=\"font-size:16px;font-family:宋体;line-height:150%\">三、协办单位</span></strong> &nbsp;</p><p><span style=\"font-size:16px;font-family:宋体;line-height:150%\">见</span><span style=\"font-size:16px\">2</span><span style=\"font-size:16px;font-family:宋体;line-height:150%\">号通知（预计</span><span style=\"font-size:16px\">2018</span><span style=\"font-size:16px;font-family:宋体;line-height:150%\">年</span><span style=\"font-size:16px\">4</span><span style=\"font-size:16px;font-family:宋体;line-height:150%\">月中旬发出）。</span> &nbsp;</p><p><strong><span style=\"font-size:16px;font-family:宋体;line-height:150%\">四、会议指导委员会</span></strong><strong> </strong> </p><p><span style=\"font-size:16px;font-family:宋体;line-height:150%\">主任：贾承造</span> &nbsp;</p><p><span style=\"font-size:16px;font-family:宋体;line-height:150%\">委员（按拼音升序排列）：</span> &nbsp;</p><p><span style=\"font-size:16px;font-family:宋体;line-height:150%\">戴金星</span><span style=\"font-size:16px\">&nbsp; </span><span style=\"font-size:16px;font-family:宋体;line-height:150%\">杜金虎</span><span style=\"font-size:16px\">&nbsp; </span><span style=\"font-size:16px;font-family:宋体;line-height:150%\">高德利</span><span style=\"font-size:16px\">&nbsp; </span><span style=\"font-size:16px;font-family:宋体;line-height:150%\">郝</span><span style=\"font-size:16px\">&nbsp; </span><span style=\"font-size:16px;font-family:宋体;line-height:150%\">芳</span><span style=\"font-size:16px\">&nbsp; </span><span style=\"font-size:16px;font-family:宋体;line-